CREATE TABLE `tin_tuc` (
  `id_news` int(11) NOT NULL,
  `title_news` text NOT NULL,
  `desc_news` text NOT NULL,
  `image` varchar(200) NOT NULL,
  `total_views` int(11) NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4;

--
-- Đang đổ dữ liệu cho bảng `tin_tuc`
--

INSERT INTO `tin_tuc` (`id_news`, `title_news`, `desc_news`, `image`, `total_views`) VALUES
(1, '<p>Cẩm nang du lịch Đ&agrave; Nẵng một ng&agrave;y cho hội bạn vui chơi &ldquo;sập&rdquo; Đ&agrave; th&agrave;nh</p>', '<p><em><strong>Đ&agrave; Nẵng c&oacute; cả n&uacute;i, đồng bằng v&agrave; biển, chỉ nằm c&aacute;ch Hội An 30 km. Bao quanh bởi h&agrave;ng loạt điểm tham quan du lịch cũng như danh lam thắng cảnh nổi tiếng n&ecirc;n kh&ocirc;ng kh&oacute; hiểu v&igrave; sao nơi đ&acirc;y lại thu h&uacute;t kh&aacute;ch du lịch đến vậy. Dưới đ&acirc;y, Traveloka sẽ mang đến cho c&aacute;c bạn một &ldquo;cuốn&rdquo;&nbsp;cẩm nang du lịch Đ&agrave; Nẵng một ng&agrave;y đầy ắp những gợi &yacute; cho chuyến đi ho&agrave;n hảo, c&ugrave;ng nh&oacute;m bạn &ldquo;t&aacute;i tạo&rdquo; lại năng lượng để quay về học tập &ndash; l&agrave;m việc nh&eacute;!</strong></em></p>\r\n<p>Thời điểm đẹp nhất trong năm để gh&eacute; thăm Đ&agrave; Nẵng l&agrave; v&agrave;o m&ugrave;a kh&ocirc;, tiết trời m&aacute;t mẻ, dễ chịu, trong xanh, gi&uacute;p bạn v&agrave; bạn b&egrave; vui chơi thoải m&aacute;i hơn, c&ugrave;ng nhau cho ra nhiều tấm ảnh để đời hơn nữa đ&oacute;! C&ograve;n b&acirc;y giờ th&igrave; c&ugrave;ng Evo Tour kh&aacute;m ph&aacute; &ldquo;cuốn b&iacute; k&iacute;p&rdquo; cẩm nang du lịch Đ&agrave; Nẵng một ng&agrave;y n&agrave;y n&agrave;o.</p>\r\n<h2><strong>Kh&aacute;m ph&aacute; n&eacute;t đẹp Đ&agrave; Th&agrave;nh</strong></h2>\r\n<p>L&agrave; một th&agrave;nh phố trẻ trung, nhộn nhịp, Đ&agrave; Nẵng kh&ocirc;ng hề thiếu chỗ cho bạn kh&aacute;m ph&aacute;, vui chơi. M&agrave; c&oacute; khi lại c&ograve;n kh&ocirc;ng đủ thời gian để kh&aacute;m ph&aacute; hết ấy chứ! Dưới đ&acirc;y, Traveloka sẽ tổng hợp những nơi c&aacute;c bạn n&ecirc;n đến nhất khi du lịch ở xứ Đ&agrave; n&agrave;y nh&eacute;!</p>\r\n<p><strong>Dạo biển Mỹ Kh&ecirc;</strong></p>\r\n<p>Kh&ocirc;ng chỉ được Forbes vinh danh l&agrave; một trong những b&atilde;i biển hấp dẫn nhất h&agrave;nh tinh, Mỹ Kh&ecirc; c&ograve;n được tạp ch&iacute; Australia &ndash; tờ Sunday Herald Sun b&igrave;nh chọn l&agrave; một trong những b&atilde;i biển đẹp nhất ch&acirc;u &Aacute;. N&ecirc;n sẽ thật đ&aacute;ng tiếc nếu như c&aacute;c bạn đ&atilde; đến Đ&agrave; Nẵng, vui chơi mọi nơi nhưng lại kh&ocirc;ng dạo qua biển Mỹ Kh&ecirc; một lần.</p>\r\n<p><img style=\"height: auto;\" src=\"https://bizweb.dktcdn.net/100/372/532/files/bai-bien-my-khe2.jpg?v=1575897609150\" data-thumb=\"original\" /></p>\r\n<p>Biển Mỹ Kh&ecirc; nổi tiếng với d&ograve;ng nước tĩnh, b&atilde;i c&aacute;t trắng phẳng mịn, lại ở gần khu trung t&acirc;m dễ tiếp cận, th&iacute;ch hợp cho mọi hoạt động vui chơi, nghỉ dưỡng v&agrave; c&aacute;c tr&ograve; chơi tập thể. Chưa kể với vẻ đẹp lung linh khi ho&agrave;ng h&ocirc;n xuống, tha hồ cho m&aacute;y ảnh &ldquo;t&aacute;c nghiệp&rdquo;.</p>\r\n<p><strong>Cầu Rồng</strong></p>\r\n<p>Được mệnh danh l&agrave; &ldquo;th&agrave;nh phố của những c&acirc;y cầu&rdquo; n&ecirc;n cứ v&agrave;i c&acirc;y số l&agrave; bạn sẽ gặp ngay một c&acirc;y cầu. Trong đ&oacute;, nổi tiếng v&agrave; thu h&uacute;t nhiều kh&aacute;ch tham quan nhất l&agrave; c&acirc;y cầu Rồng. Với thiết kế độc nhất v&ocirc; nhị c&ugrave;ng nhiều hoạt động th&uacute; vị xoay quanh, cầu Rồng ch&iacute;nh l&agrave; điểm đến tiếp theo m&agrave; Traveloka muốn giới thiệu đến cho bạn trong cẩm nang du lịch Đ&agrave; Nẵng n&agrave;y.</p>\r\n<p><img style=\"height: auto;\" src=\"https://bizweb.dktcdn.net/100/372/532/files/cau-rong-da-nang-3.jpg?v=1575897643266\" width=\"827\" data-thumb=\"original\" /></p>\r\n<p>Ngo&agrave;i h&igrave;nh d&aacute;ng v&agrave; chiều d&agrave;i kinh ngạc, cầu Rồng c&ograve;n c&oacute; một hoạt động cực th&uacute; vị, được v&iacute; như &ldquo;thỏi nam ch&acirc;m h&uacute;t kh&aacute;ch&rdquo;: phun lửa v&agrave; phun nước.&nbsp;Cầu Rồng sẽ phun 18 ngọn lửa v&agrave; 3 ngọn nước v&agrave;o hai ng&agrave;y cuối tuần &ndash; thứ Bảy v&agrave; Chủ Nhật, bắt đầu từ 21 giờ. Bạn nhớ lưu ngay lại v&agrave;o cẩm nang du lịch Đ&agrave; Nẵng một ng&agrave;y của bạn nh&eacute;!</p>\r\n<p>Ngo&agrave;i ra, một số địa điểm đẹp để ngắm được to&agrave;n cảnh cầu Rồng gồm c&oacute;: tr&ecirc;n cầu T&igrave;nh Y&ecirc;u, đường Trần Hưng Đạo, đường Bạch Đằng v&agrave; c&aacute;c nh&agrave; h&agrave;ng cao tầng đ&oacute;.</p>\r\n<p><strong>B&aacute;n đảo Sơn Tr&agrave;</strong></p>\r\n<p>Nằm c&aacute;ch trung t&acirc;m th&agrave;nh phố chỉ 8 km, b&aacute;n đảo Sơn Tr&agrave; l&agrave; khu rừng nguy&ecirc;n sinh xanh ng&uacute;t ng&agrave;n, kh&iacute; hậu m&aacute;t mẻ quanh năm, xứng đ&aacute;ng nằm trong danh s&aacute;ch những điểm n&ecirc;n đến khi gh&eacute; Đ&agrave; Nẵng.&nbsp;C&aacute;c bạn n&ecirc;n gh&eacute; b&aacute;n đảo Sơn Tr&agrave; v&agrave;o khoảng th&aacute;ng 3 &ndash; th&aacute;ng 9, bởi l&uacute;c n&agrave;y thời tiết đẹp, trời trong xanh, &iacute;t mưa, rất thuận tiện cho việc di chuyển. Từ Đ&agrave; Nẵng, c&oacute; rất nhiều con đường dẫn đến b&aacute;n đảo, rất tiện cho c&aacute;c bạn lựa chọn cho h&agrave;nh tr&igrave;nh cả nh&oacute;m đ&oacute;.</p>\r\n<p>V&agrave; d&agrave;nh cho c&aacute;c bạn ưa mạo hiểm, th&iacute;ch cảm gi&aacute;c mạnh v&agrave; muốn thử bay lượn tự do như chim trời, chắc chắn nhảy d&ugrave; l&agrave; hoạt động kh&ocirc;ng n&ecirc;n bỏ qua ở Sơn Tr&agrave;. Đ&acirc;y l&agrave; một hoạt động ho&agrave;n to&agrave;n mới mẻ, kh&ocirc;ng phải ai đến b&aacute;n đảo Sơn Tr&agrave; cũng biết đ&acirc;u nha!</p>\r\n<p>Hoạt động n&agrave;y do duy nhất c&acirc;u lạc bộ d&ugrave; lượn Đ&agrave; Nẵng &ndash; Danang Paragliding tổ chức tại đỉnh n&uacute;i Sơn Tr&agrave;, kh&ocirc;ng qua dịch vụ hay đi k&egrave;m tour du lịch n&agrave;o cả.&nbsp;Do sức h&uacute;t của n&oacute;, bạn sẽ phải &ldquo;cạnh tranh&rdquo; với rất nhiều bạn trẻ kh&aacute;c để được thử sức m&igrave;nh n&ecirc;n bạn cần li&ecirc;n hệ với phi c&ocirc;ng để đặt bay trước tối thiểu l&agrave; 2 tuần đến 1 th&aacute;ng nh&eacute;.</p>\r\n<p>Chi ph&iacute; cho một lần chơi l&agrave; 1.500.000 VND / người / lượt cho thời gian khoảng 10-15 ph&uacute;t, c&ograve;n t&ugrave;y theo hướng gi&oacute;. Gi&aacute; kh&ocirc;ng rẻ, nhưng cho một trải nghiệm tuổi trẻ kh&oacute; qu&ecirc;n th&igrave; lại v&ocirc; c&ugrave;ng xứng đ&aacute;ng đ&oacute;.</p>', '28.jpg', 2000),
(2, '<p>B&iacute; quyết du lịch Đ&agrave; Lạt tự t&uacute;c gi&aacute; rẻ để x&aacute;ch t&uacute;i v&agrave; đi ngay kh&ocirc;ng cần suy nghĩ</p>', '<div>\r\n<p>Th&agrave;nh phố ng&agrave;n hoa Đ&agrave; Lạt lu&ocirc;n đ&oacute;n lượng kh&aacute;ch đ&ocirc;ng đảo v&agrave;o mọi m&ugrave;a, mọi ng&agrave;y trong năm, nằm trong top đầu những địa điểm đi ngắn ng&agrave;y &ldquo;xả hơi&rdquo; của rất nhiều người. D&aacute;m chắc rằng c&oacute; rất nhiều bạn hễ cứ c&oacute; dịp cuối tuần đi chơi l&agrave; lại &ldquo;l&ecirc;n Đ&agrave; Lạt&rdquo;! Vậy th&igrave; hẳn bạn phải lu&ocirc;n &ldquo;rủng rỉnh hầu bao&rdquo; để lu&ocirc;n sẵn s&agrave;ng đi Đ&agrave; Lạt? Thật ra l&agrave; kh&ocirc;ng cần, với những b&iacute; quyết du lịch Đ&agrave; Lạt tự t&uacute;c gi&aacute; rẻ dưới đ&acirc;y, bạn c&oacute; thể l&ecirc;n Đ&agrave; Lạt bất kỳ l&uacute;c n&agrave;o m&agrave; kh&ocirc;ng cần chuẩn bị qu&aacute; nhiều &ldquo;m&aacute;u&rdquo;!</p>\r\n</div>\r\n<div>\r\n<p><strong>1. Thời tiết, kh&iacute; hậu m&aacute;t mẻ</strong></p>\r\n<p>Đa phần c&aacute;c th&agrave;nh phố du lịch kh&aacute;c đều c&oacute; c&aacute;c m&ugrave;a n&oacute;ng &ndash; lạnh đặc biệt v&agrave; biến đổi li&ecirc;n tục, c&aacute;c th&agrave;nh phố biển c&ograve;n c&oacute; c&aacute;c m&ugrave;a b&atilde;o g&acirc;y biển động. Nhưng nhờ vị thế nằm c&aacute;ch mặt nước biển đến 1.500 m, th&agrave;nh phố Đ&agrave; Lạt lại sở hữu thời tiết m&aacute;t mẻ, l&agrave;nh lạnh quanh năm, &iacute;t khi thay đổi, c&oacute; chăng cũng chỉ l&agrave; m&ugrave;a mưa rơi kh&aacute; nặng hạt.</p>\r\n<p>Tuy nhi&ecirc;n với những ai th&iacute;ch l&ecirc;n Đ&agrave; Lạt để nghỉ ngơi, rời xa th&agrave;nh thị ồn &agrave;o th&igrave; m&ugrave;a mưa Đ&agrave; Lạt cũng kh&ocirc;ng cản được bước ch&acirc;n họ. C&ograve;n với những ai sống ở th&agrave;nh phố c&oacute; thời tiết n&oacute;ng bức quanh năm hoặc đang rơi v&agrave;o m&ugrave;a h&egrave; &ldquo;gắt gỏng&rdquo;, Đ&agrave; Lạt lại l&agrave; nơi tr&aacute;nh n&oacute;ng cực kỳ hợp l&yacute;.</p>\r\n<p><img style=\"height: auto;\" src=\"https://bizweb.dktcdn.net/100/372/532/files/du-lich-da-lat-tu-tuc-gia-re-5.jpg?v=1575902953218\" width=\"827\" data-thumb=\"original\" /></p>\r\n<p>Từ đ&oacute;, bạn c&oacute; thể thấy, thời tiết th&agrave;nh phố Đ&agrave; Lạt chiều được nhu cầu v&agrave; mong muốn của hầu hết những người đến đ&acirc;y, d&ugrave; l&agrave; vui chơi kh&aacute;m ph&aacute; hay nghỉ dưỡng &ldquo;đổi gi&oacute; để đổi đời&rdquo; v&agrave;o bất kỳ th&aacute;ng n&agrave;o trong năm. Tuy nhi&ecirc;n, để mọi thứ thật ho&agrave;n hảo, bạn n&ecirc;n tr&aacute;nh đi v&agrave;o c&aacute;c th&aacute;ng h&egrave; m&ugrave;a mưa (thường sẽ từ th&aacute;ng 6 đến th&aacute;ng 10).</p>\r\n<h2><strong>2.&nbsp;</strong>Muốn du lịch Đ&agrave; Lạt tự t&uacute;c gi&aacute; rẻ? H&atilde;y chọn chỗ ở thật kỹ</h2>\r\n<p>Với lợi thế địa h&igrave;nh v&agrave; lượt kh&aacute;ch gh&eacute; thăm, c&aacute;c kh&aacute;ch sạn / khu nghỉ dưỡng được đầu tư x&acirc;y dựng rất nhiều tại Đ&agrave; Lạt. Bạn c&oacute; thể lựa chọn c&aacute;c kh&aacute;ch sạn gần trung t&acirc;m để thoải m&aacute;i dạo phố, hoặc một khu nghỉ dưỡng ven hồ để tận hưởng kỳ nghỉ thư th&aacute;i. Dưới đ&acirc;y l&agrave; một số kh&aacute;ch sạn / khu nghỉ dưỡng cao cấp, dịch vụ cực chuẩn đang rất được ưa th&iacute;ch trong thời gian gần đ&acirc;y:</p>\r\n<ul style=\"list-style-type: none;\">\r\n<li><strong>Swiss &ndash; Belresort Tuyen Lam:</strong></li>\r\n</ul>\r\n<p>Khu nghỉ dưỡng Swiss &ndash; Belresort Tuyen Lam với phong c&aacute;ch Anh cực l&atilde;ng mạn, vẻ đẹp l&atilde;ng mạn v&agrave; trong veo sẽ mang đến cảm gi&aacute;c cho&aacute;ng ngợp ngay từ &aacute;nh mắt đầu ti&ecirc;n. Khu resort nằm b&ecirc;n trong một thung lũng xanh r&igrave;, bao quanh bởi rừng th&ocirc;ng bạt ng&agrave;n, thảm cỏ xanh mướt cho kh&ocirc;ng kh&iacute; trong l&agrave;nh bất ngờ. C&aacute;ch trung t&acirc;m th&agrave;nh phố khoảng 14 km, c&aacute;ch hồ Tuyền L&acirc;m khoảng 10 km n&ecirc;n khu nghỉ dưỡng tĩnh lặng v&agrave; v&ocirc; c&ugrave;ng thoải m&aacute;i với những ai muốn một kỳ nghỉ đ&uacute;ng nghĩa.</p>\r\n<p><img style=\"height: auto;\" src=\"https://bizweb.dktcdn.net/100/372/532/files/du-lich-da-lat-tu-tuc-gia-re-da-lat-swiss-belresort.jpg?v=1575902964634\" width=\"827\" data-thumb=\"original\" /></p>\r\nNằm s&aacute;t b&ecirc;n hồ tĩnh lặng v&agrave; rừng c&acirc;y bạt ng&agrave;n, Swiss Belresort mang đến kh&ocirc;ng gian trong l&agrave;nh v&agrave; tho&aacute;ng đ&atilde;ng. @Internet\r\n<p>Kh&aacute;ch sạn sở hữu 7 hạng ph&ograve;ng từ cơ bản đến cao cấp, bao gồm l&ecirc;n đến hơn 100 ph&ograve;ng với tầm nh&igrave;n bao la, tho&aacute;ng đ&atilde;ng, c&oacute; cả ban c&ocirc;ng để bạn ngắm cảnh v&agrave; thưởng thức c&agrave; ph&ecirc; buổi s&aacute;ng. Swiss Belresort c&ograve;n sở hữu đến ba nh&agrave; h&agrave;ng, phục vụ c&aacute;c bữa ăn ch&iacute;nh trong ng&agrave;y, tr&agrave; chiều v&agrave; c&aacute;c m&oacute;n uống thơm ngon, bao gồm cả vang Đ&agrave; Lạt. Kh&ocirc;ng gian sang trọng, ấm c&uacute;ng chắc chắn sẽ khiến trải nghiệm ẩm thực của bạn th&ecirc;m th&uacute; vị.</p>\r\nThưởng thức bữa s&aacute;ng ngon l&agrave;nh trong khung cảnh rộng lớn, hướng thẳng xuống thẳm cỏ xanh mượt v&agrave; bầu trời cao vời vợi.&nbsp;\r\n<p><strong>Du Parc Hotel:</strong></p>\r\n<p>Với lợi thế cực lớn l&agrave; nằm ngay trung t&acirc;m Đ&agrave; Lạt, n&ecirc;n nếu bạn muốn đến th&agrave;nh phố n&agrave;y để vui chơi v&agrave; kh&aacute;m ph&aacute; th&igrave; Du Parc Hotel sẽ l&agrave; sự lựa chọn cực ổn. Mang phong c&aacute;ch Ph&aacute;p cổ xưa, l&atilde;ng mạn, Du Parc Hotel mang n&eacute;t cổ k&iacute;nh e ấp giữa th&agrave;nh phố ng&agrave;n hoa. Kh&aacute;ch sạn do ch&iacute;nh người Ph&aacute;p x&acirc;y dựng n&ecirc;n, v&agrave; những c&ocirc;ng tr&igrave;nh b&ecirc;n trong vẫn được giữ nguy&ecirc;n đến tận h&ocirc;m nay, c&aacute;ch b&agrave;i tr&iacute;, t&ocirc;ng m&agrave;u chủ đạo v&agrave; nội thất cũng đậm phong c&aacute;ch Ph&aacute;p.</p>\r\n<p><img style=\"height: auto;\" src=\"https://bizweb.dktcdn.net/100/372/532/files/du-lich-da-lat-tu-tuc-gia-re-da-lat-duparc-3.jpg?v=1575903006590\" width=\"827\" data-thumb=\"original\" /></p>\r\nSự cổ k&iacute;nh &ldquo;rất Ph&aacute;p&rdquo; to&aacute;t l&ecirc;n từ kiến tr&uacute;c b&ecirc;n ngo&agrave;i kh&aacute;ch sạn. @Internet\r\n<p><img style=\"height: auto;\" src=\"https://bizweb.dktcdn.net/100/372/532/files/du-lich-da-lat-tu-tuc-gia-re-da-lat-duparc-2.jpg?v=1575903017180\" width=\"827\" data-thumb=\"original\" /></p>\r\nChiếc thang m&aacute;y c&oacute; ni&ecirc;n đại từ năm 1932. Hiện ở Việt Nam chỉ c&ograve;n 2 c&aacute;i: một ở Du Parc Hotel v&agrave; một ở Bảo t&agrave;ng mỹ thuật S&agrave;i G&ograve;n. @pate.le\r\n<p><img style=\"height: auto;\" src=\"https://bizweb.dktcdn.net/100/372/532/files/du-lich-da-lat-tu-tuc-gia-re-da-lat-duparc-5-768x1152.jpg?v=1575903026449\" width=\"827\" data-thumb=\"original\" /></p>\r\nThiết kế cổ k&iacute;nh ở kh&aacute;ch sạn nhất định sẽ l&agrave;m h&agrave;i l&ograve;ng c&aacute;c t&iacute;n đồ cổ điển.&nbsp;\r\n<p>Với 140 ph&ograve;ng đầy đủ tiện nghi, c&ugrave;ng hệ thống dịch vụ đa dạng như: nh&agrave; h&agrave;ng Le Cafe De La Poste với c&aacute;c bữa tr&agrave; chiều, b&aacute;nh ngọt do ch&iacute;nh tay c&aacute;c đầu bếp của kh&aacute;ch sạn l&agrave;m; Le Bistro với c&aacute;c bữa ăn ngon miệng, chất lượng; ph&ograve;ng karaoke trong nh&agrave;,&hellip;</p>\r\n<p><strong>Dalat Wonder Resort:</strong></p>\r\n<p>Nếu bạn chưa biết, MV &ldquo;Anh nh&agrave; ở đ&acirc;u thế&rdquo; nổi đ&igrave;nh đ&aacute;m của AMEE v&agrave; Bray kh&ocirc;ng phải quay tại bất kỳ th&agrave;nh phố n&agrave;o ơ ch&acirc;u &Acirc;u m&agrave; l&agrave; ngay Dalat Wonder Resort. Được v&iacute; như &ldquo;ng&ocirc;i l&agrave;ng ch&acirc;u &Acirc;u thu nhỏ&rdquo; b&ecirc;n cạnh một nh&aacute;nh hồ Tuyền L&acirc;m, trong khu&ocirc;n vi&ecirc;n khu nghỉ dưỡng l&agrave; những căn hộ nhỏ nằm đan xen, mang đậm hơi thở cổ điển.</p>\r\n</div>', '23.jpg', 1200),
(4, '<p>Vui chơi thả ga c&ugrave;ng bạn b&egrave; tại Phan Thiết với cẩm nang du lịch chi tiết n&agrave;y</p>', '<p><strong><em>Phan Thiết l&agrave; một th&agrave;nh phố thuộc tỉnh B&igrave;nh Thuận, nằm tr&ecirc;n quốc lộ 1A c&aacute;ch TP Hồ Ch&iacute; Minh 183 km về hướng Đ&ocirc;ng Bắc.&nbsp;</em></strong><em><strong>Gần th&agrave;nh phố, tiện lợi cho việc di chuyển v&agrave; kh&ocirc;ng qu&aacute; tốn k&eacute;m, Phan Thiết l&agrave; sự lựa chọn qu&aacute; ổn cho những chuyến du lịch ngắn cuối tuần xả stress.&nbsp;</strong></em></p>\r\n<p>Nếu y&ecirc;u du lịch, hẳn bạn đ&atilde; kh&ocirc;ng c&ograve;n xa lạ g&igrave; với t&ecirc;n của &ldquo;địa danh v&agrave;ng trong l&agrave;ng du lịch&rdquo; n&agrave;y. Với vẻ đẹp thi&ecirc;n nhi&ecirc;n c&ugrave;ng sự đa dạng văn h&oacute;a, ẩm thực hấp dẫn, Phan Thiết chắc chắn sẽ l&agrave; một điểm đến l&yacute; tưởng cho chuyến vui chơi c&ugrave;ng b&egrave; bạn.</p>\r\n<h2><strong>Đến Phan Thiết v&agrave;o th&aacute;ng mấy th&igrave; đẹp?</strong></h2>\r\n<p>Nằm ở khu vực Nam Trung Bộ, th&agrave;nh phố Phan Thiết mang đầy đủ đặc trưng thời tiết của v&ugrave;ng n&agrave;y với nắng v&agrave; gi&oacute; c&ugrave;ng nhiệt độ cao nhất nước. Phan Thiết vẫn c&oacute; hai m&ugrave;a mưa &ndash; nắng nhưng m&ugrave;a nắng k&eacute;o d&agrave;i hơn m&ugrave;a mưa. Đặc biệt, th&agrave;nh phố Phan Thiết &iacute;t chịu ảnh hưởng của b&atilde;o n&ecirc;n những chuyến du lịch biển đến đ&acirc;y kh&aacute; an to&agrave;n.</p>\r\n<p>D&ugrave; bạn c&oacute; thể gh&eacute; Phan Thiết bất kỳ m&ugrave;a n&agrave;o trong năm, nhưng c&oacute; những khoảng thời gian cần lưu &yacute; như sau:</p>\r\n<ul style=\"list-style-type: none;\">\r\n<li>Th&aacute;ng 4 v&agrave; th&aacute;ng 5: Nắng n&oacute;ng đỉnh điểm với mức nhiệt tới hơn 30 độ.</li>\r\n<li>Th&aacute;ng 6 &ndash; th&aacute;ng 7 hoặc từ th&aacute;ng 12 &ndash; th&aacute;ng 2: Thời gian đẹp nhất, nắng đỡ hanh, &iacute;t mưa, biển lặng, nước biển trong xanh v&agrave; trời đẹp hơn nhiều.</li>\r\n<li>Th&aacute;ng 7 v&agrave; đến th&aacute;ng 8: Thường c&oacute; hiện tượng thủy triều đỏ, tảo biển nở l&agrave;m nước biển đục kh&ocirc;ng th&iacute;ch hợp để tắm biển. Nếu chỉ nghỉ dưỡng v&agrave; vui chơi loanh quanh nội th&agrave;nh th&igrave; thời gian n&agrave;y vẫn ổn để bạn ngao du nơi n&agrave;y nh&eacute;.</li>\r\n</ul>\r\n<h2><strong>Ăn g&igrave; ở Phan Thiết?</strong></h2>\r\n<p><strong>B&aacute;nh canh chả c&aacute;:</strong></p>\r\n<p>B&aacute;nh canh l&agrave; m&oacute;n ăn đơn giản, bổ dưỡng, dễ ăn. Tại th&agrave;nh phố biển Phan Thiết, m&oacute;n ăn n&agrave;y c&ograve;n được ưu &aacute;i th&ecirc;m chả c&aacute; dai gi&ograve;n &ndash; được l&agrave;m từ thịt c&aacute; tươi ngon, đặc trưng xứ biển. Nước d&ugrave;ng thơm ngọt được hầm từ c&aacute; tươi, người d&acirc;n ở đ&acirc;y thường chấm b&aacute;nh m&igrave; ăn để th&ecirc;m chắc bụng cho một ng&agrave;y d&agrave;i l&agrave;m việc. Đừng qu&ecirc;n th&ecirc;m một ch&uacute;t nước mắm ớt ng&acirc;m, một gắp rau sống để tận hưởng trọn vẹn hương vị tươi nguy&ecirc;n của b&aacute;nh canh chả c&aacute; nh&eacute;!</p>\r\n<p><img style=\"height: auto;\" src=\"https://blog.traveloka.com/source/uploads/sites/9/2019/10/cam-nang-du-lich-phan-thiet-8.jpg\" sizes=\"(max-width: 683px) 100vw, 683px\" srcset=\"https://blog.traveloka.com/source/uploads/sites/9/2019/10/cam-nang-du-lich-phan-thiet-8.jpg 2000w, https://blog.traveloka.com/source/uploads/sites/9/2019/10/cam-nang-du-lich-phan-thiet-8-200x300.jpg 200w, https://blog.traveloka.com/source/uploads/sites/9/2019/10/cam-nang-du-lich-phan-thiet-8-768x1152.jpg 768w, https://blog.traveloka.com/source/uploads/sites/9/2019/10/cam-nang-du-lich-phan-thiet-8-427x640.jpg 427w, https://blog.traveloka.com/source/uploads/sites/9/2019/10/cam-nang-du-lich-phan-thiet-8-585x878.jpg 585w\" alt=\"Cẩm nang du lịch Phan Thiết - B&aacute;nh canh chả c&aacute; D&igrave; L&yacute;\" width=\"683\" height=\"1025\" /></p>\r\n<p>M&oacute;n b&aacute;nh canh chả c&aacute; đơn giản, dễ ăn nhưng cung cấp đầy đủ năng lượng để bạn vui chơi.</p>\r\n<p><img style=\"height: auto;\" src=\"https://blog.traveloka.com/source/uploads/sites/9/2019/10/cam-nang-du-lich-phan-thiet-9.jpg\" sizes=\"(max-width: 2000px) 100vw, 2000px\" srcset=\"https://blog.traveloka.com/source/uploads/sites/9/2019/10/cam-nang-du-lich-phan-thiet-9.jpg 2000w, https://blog.traveloka.com/source/uploads/sites/9/2019/10/cam-nang-du-lich-phan-thiet-9-300x200.jpg 300w, https://blog.traveloka.com/source/uploads/sites/9/2019/10/cam-nang-du-lich-phan-thiet-9-768x512.jpg 768w, https://blog.traveloka.com/source/uploads/sites/9/2019/10/cam-nang-du-lich-phan-thiet-9-640x427.jpg 640w, https://blog.traveloka.com/source/uploads/sites/9/2019/10/cam-nang-du-lich-phan-thiet-9-585x390.jpg 585w\" alt=\"Cẩm nang du lịch Phan Thiết - B&aacute;nh canh chả c&aacute; D&igrave; L&yacute;\" width=\"2000\" height=\"1333\" /></p>\r\n<p>B&aacute;nh canh chả c&aacute; gồm nhiều m&oacute;n chả kh&aacute;c nhau.</p>\r\n<p><img style=\"height: auto;\" src=\"https://blog.traveloka.com/source/uploads/sites/9/2019/10/cam-nang-du-lich-phan-thiet-10.jpg\" sizes=\"(max-width: 683px) 100vw, 683px\" srcset=\"https://blog.traveloka.com/source/uploads/sites/9/2019/10/cam-nang-du-lich-phan-thiet-10.jpg 2000w, https://blog.traveloka.com/source/uploads/sites/9/2019/10/cam-nang-du-lich-phan-thiet-10-200x300.jpg 200w, https://blog.traveloka.com/source/uploads/sites/9/2019/10/cam-nang-du-lich-phan-thiet-10-768x1152.jpg 768w, https://blog.traveloka.com/source/uploads/sites/9/2019/10/cam-nang-du-lich-phan-thiet-10-427x640.jpg 427w, https://blog.traveloka.com/source/uploads/sites/9/2019/10/cam-nang-du-lich-phan-thiet-10-585x878.jpg 585w\" alt=\"Cẩm nang du lịch Phan Thiết - B&aacute;nh canh chả c&aacute; D&igrave; L&yacute;\" width=\"683\" height=\"1025\" /></p>\r\n<p>Qu&aacute;n b&aacute;nh canh D&igrave; L&yacute; l&agrave; một trong những qu&aacute;n ăn l&acirc;u đời v&agrave; mở cửa khuya nhất ở Phan Thiết.</p>\r\n<p>Địa chỉ gợi &yacute;:</p>\r\n<ul style=\"list-style-type: none;\">\r\n<li>Qu&aacute;n b&aacute;nh canh X&iacute;u: Số 1 HA Đường Kim Đồng</li>\r\n<li>Qu&aacute;n B&aacute;nh Canh B&agrave; L&yacute;: 566 Trần Hưng Đạo</li>\r\n</ul>\r\n<p><strong>Lẩu c&aacute; bớp:</strong></p>\r\n<p>L&agrave; &ldquo;thi&ecirc;n đường&rdquo; của hải sản, m&oacute;n lẩu c&aacute; l&agrave; m&oacute;n ăn kh&ocirc;ng thể n&agrave;o thiếu trong danh mục c&aacute;c m&oacute;n ăn phải thử ở Phan Thiết. Đặc biệt trong đ&oacute; phải kể đến lẩu c&aacute; bớp &ndash; m&oacute;n ăn được b&aacute;n nhiều nơi, từ nh&agrave; h&agrave;ng sang trọng cho đến c&aacute;c qu&aacute;n nhậu b&igrave;nh d&acirc;n vỉa h&egrave;. C&aacute; bớp c&oacute; thịt m&agrave;u trắng, mềm nhưng kh&ocirc;ng bở, ngon ngọt tự nhi&ecirc;n, được c&aacute;c qu&aacute;n ăn chế biến th&agrave;nh rất nhiều m&oacute;n. Trong đ&oacute; lẩu c&aacute; bớp l&agrave; được ưa th&iacute;ch nhất.</p>\r\n<p><img style=\"height: auto;\" src=\"https://blog.traveloka.com/source/uploads/sites/9/2019/10/cam-nang-du-lich-phan-thiet-46.jpg\" sizes=\"(max-width: 2000px) 100vw, 2000px\" srcset=\"https://blog.traveloka.com/source/uploads/sites/9/2019/10/cam-nang-du-lich-phan-thiet-46.jpg 2000w, https://blog.traveloka.com/source/uploads/sites/9/2019/10/cam-nang-du-lich-phan-thiet-46-300x200.jpg 300w, https://blog.traveloka.com/source/uploads/sites/9/2019/10/cam-nang-du-lich-phan-thiet-46-768x512.jpg 768w, https://blog.traveloka.com/source/uploads/sites/9/2019/10/cam-nang-du-lich-phan-thiet-46-640x427.jpg 640w, https://blog.traveloka.com/source/uploads/sites/9/2019/10/cam-nang-du-lich-phan-thiet-46-585x390.jpg 585w\" alt=\"Cẩm nang du lịch Phan Thiết - Lẩu c&aacute; May Huệ\" width=\"2000\" height=\"1333\" /></p>\r\n<p>Bữa trưa ở Phan Thiết kh&ocirc;ng biết ăn g&igrave; th&igrave; đừng qu&ecirc;n m&oacute;n lẩu c&aacute; nh&eacute;!</p>\r\n<p><img style=\"height: auto;\" src=\"https://blog.traveloka.com/source/uploads/sites/9/2019/10/cam-nang-du-lich-phan-thiet-45.jpg\" sizes=\"(max-width: 683px) 100vw, 683px\" srcset=\"https://blog.traveloka.com/source/uploads/sites/9/2019/10/cam-nang-du-lich-phan-thiet-45.jpg 2000w, https://blog.traveloka.com/source/uploads/sites/9/2019/10/cam-nang-du-lich-phan-thiet-45-200x300.jpg 200w, https://blog.traveloka.com/source/uploads/sites/9/2019/10/cam-nang-du-lich-phan-thiet-45-768x1152.jpg 768w, https://blog.traveloka.com/source/uploads/sites/9/2019/10/cam-nang-du-lich-phan-thiet-45-427x640.jpg 427w, https://blog.traveloka.com/source/uploads/sites/9/2019/10/cam-nang-du-lich-phan-thiet-45-585x878.jpg 585w\" alt=\"Cẩm nang du lịch Phan Thiết - Lẩu c&aacute; May Huệ\" width=\"683\" height=\"1025\" /></p>\r\n<p>M&oacute;n lẩu c&aacute; bớp thơm ngon bắt vị &ndash; đặc sản của miền biển n&oacute;i chung v&agrave; Phan Thiết n&oacute;i ri&ecirc;ng.</p>\r\n<p><img style=\"height: auto;\" src=\"https://blog.traveloka.com/source/uploads/sites/9/2019/10/cam-nang-du-lich-phan-thiet-47.jpg\" sizes=\"(max-width: 683px) 100vw, 683px\" srcset=\"https://blog.traveloka.com/source/uploads/sites/9/2019/10/cam-nang-du-lich-phan-thiet-47.jpg 2000w, https://blog.traveloka.com/source/uploads/sites/9/2019/10/cam-nang-du-lich-phan-thiet-47-200x300.jpg 200w, https://blog.traveloka.com/source/uploads/sites/9/2019/10/cam-nang-du-lich-phan-thiet-47-768x1152.jpg 768w, https://blog.traveloka.com/source/uploads/sites/9/2019/10/cam-nang-du-lich-phan-thiet-47-427x640.jpg 427w, https://blog.traveloka.com/source/uploads/sites/9/2019/10/cam-nang-du-lich-phan-thiet-47-585x878.jpg 585w\" alt=\"Cẩm nang du lịch Phan Thiết - Lẩu c&aacute; May Huệ\" width=\"683\" height=\"1025\" /></p>\r\n<p>Một miếng c&aacute; sẽ khiến bạn &ldquo;ứa nước miếng&rdquo;.</p>\r\n<p>Lẩu c&aacute; bớp c&oacute; vị chua ngọt vừa miệng. &ldquo;Tinh hoa&rdquo; của nồi lẩu c&aacute; nằm ở từng khứa c&aacute; bớp tươi r&oacute;i đ&atilde; được x&agrave;o săn từ trước, kết hợp với vị chua thanh dịu của me v&agrave; c&agrave; chua trong nước lẩu, cay mặn của nước mắm chấm, c&ugrave;ng vị thơm của c&aacute;c loại rau ăn k&egrave;m. Trong tiết trời dịu m&aacute;t của biển Phan Thiết m&agrave; l&agrave;m một nồi lẩu c&aacute; bớp, c&ugrave;ng bạn b&egrave; tr&ograve; chuyện tr&ecirc;n trời dưới biển th&igrave; c&ograve;n g&igrave; bằng!</p>', '24.jpg', 1000),
(5, '<p>Bỏ t&uacute;i ngay 8 ứng dụng du lịch kh&ocirc;ng thể thiếu n&agrave;y nếu bạn l&agrave; kẻ hay &ldquo;x&ecirc; dịch&rdquo;</p>', '<p><strong><em>Trong thời đại 4.0 th&igrave; tất nhi&ecirc;n ch&uacute;ng ta cũng phải &ldquo;upgrade&rdquo; danh s&aacute;ch h&agrave;nh trang du lịch của m&igrave;nh đ&uacute;ng kh&ocirc;ng n&agrave;o? Với sự trợ gi&uacute;p của c&aacute;c ứng dụng tiện lợi được t&iacute;ch hợp trong chiếc điện thoại th&ocirc;ng minh của m&igrave;nh, ch&uacute;ng ta c&oacute; thể tha hồ du lịch bụi xuống biển l&ecirc;n rừng v&agrave; giảm thiểu được rất nhiều cản trở, kh&oacute; khăn.</em></strong></p>\r\n<h2><strong>1. TripAdvisor</strong></h2>\r\n<p><strong>TripAdvisor</strong>&nbsp;giống như một diễn đ&agrave;n &ndash; nơi mọi người c&oacute; thể t&igrave;m thấy rất nhiều lời khuy&ecirc;n cũng như &ldquo;feedback&rdquo; về th&ocirc;ng tin kh&aacute;ch sạn, điểm tham quan, địa điểm ăn uống. Ứng dụng du lịch n&agrave;y sẽ gi&uacute;p bạn tham khảo c&aacute;c đ&aacute;nh gi&aacute; của những du kh&aacute;ch kh&aacute;c về điểm dịch vụ du lịch sau khi họ trải nghiệm, với g&oacute;c nh&igrave;n thật sự của người đ&atilde; trải nghiệm.&nbsp;<strong>TripAdvisor</strong>&nbsp;được xem l&agrave; cộng đồng du lịch lớn nhất thế giới với lượt truy cập h&agrave;ng th&aacute;ng tới hơn 60 triệu lượt v&agrave; hơn 100 triệu đ&aacute;nh gi&aacute;.</p>\r\n<figure id=\"attachment_55801\"><img style=\"height: auto;\" src=\"https://blog.traveloka.com/source/uploads/sites/9/2019/10/1-4-768x960.jpg\" alt=\"ứng dụng du lịch\" width=\"819\" height=\"1024\" />\r\n<figcaption>TripAdvisor l&agrave; ứng dụng du lịch hữu &iacute;ch được rất nhiều du kh&aacute;ch tr&ecirc;n thế giới sử dụng. @internet</figcaption>\r\n</figure>\r\n<p>Bạn c&oacute; thể xem trực tiếp nhận x&eacute;t, h&igrave;nh ảnh về chất lượng dịch vụ kh&aacute;ch sạn, nh&agrave; h&agrave;ng, địa điểm du lịch trước khi đặt ch&acirc;n đến đ&oacute;. Tất cả th&ocirc;ng tin chia sẻ đến bạn đều mang t&iacute;nh x&aacute;c thực cao từ du kh&aacute;ch đ&atilde; đi.</p>\r\n<h2><strong>2. Traveloka &ndash; ứng dụng đặt ph&ograve;ng kh&aacute;ch sạn v&agrave; v&eacute; m&aacute;y bay</strong></h2>\r\n<p>V&igrave; sao ứng dụng&nbsp;<strong>Traveloka</strong>&nbsp;lại nằm trong danh s&aacute;ch những ứng dụng du lịch kh&ocirc;ng thể thiếu nếu bạn sắp l&ecirc;n lịch cho một chuyến đi xa? Bởi ch&iacute;nh những t&iacute;nh năng v&agrave; sự tiện lợi tối ưu nhất m&agrave; Traveloka mang lại.</p>\r\n<p>Với giao diện th&acirc;n thiện với người d&ugrave;ng, khi thực tế trải nghiệm bạn sẽ cảm nhận ngay sự đơn giản nhưng đầy đủ v&agrave; ho&agrave;n thiện chức năng m&agrave; Traveloka cố gắng mang đến cho bạn.</p>\r\n<figure id=\"attachment_55803\"><img style=\"height: auto;\" src=\"https://blog.traveloka.com/source/uploads/sites/9/2019/10/ung-dung-du-lich-traveloka-768x432.jpg\" alt=\"ứng dụng du lịch\" width=\"1024\" height=\"576\" />\r\n<figcaption>Traveloka mang những t&iacute;nh năng v&agrave; sự tiện lợi tối ưu nhất cho một chuyến du lịch.</figcaption>\r\n</figure>\r\n<p>Với phần&nbsp;<strong>đặt v&eacute; m&aacute;y bay</strong>&nbsp;v&agrave;&nbsp;<strong>đặt ph&ograve;ng kh&aacute;ch sạn</strong>, bạn chỉ đơn giản cần nhập v&agrave;o địa điểm nơi đến, thời gian lưu tr&uacute; k&egrave;m với c&aacute;c th&ocirc;ng tin như số người / số v&eacute;, loại ph&ograve;ng&hellip; mọi chuyện c&ograve;n lại, cứ để Traveloka lo! Một danh s&aacute;ch cụ thể c&aacute;c chuyến bay sẽ ngay lập tức được hiển thị để bạn lựa chọn.</p>\r\n<p>Bi&ecirc;n lai sẽ được gửi đến email của bạn dưới dạng v&eacute; điện tử v&agrave; bạn chỉ cần xuất tr&igrave;nh qua điện thoại khi l&agrave;m thủ tục, kh&ocirc;ng c&ograve;n cần gọi điện x&aacute;c định hoặc in v&eacute; / h&oacute;a đơn ra như trước nữa. Với sự tiện lợi v&agrave; hỗ trợ tối đa về c&aacute;c mức v&eacute; m&aacute;y bay v&agrave; ph&iacute; kh&aacute;ch sạn,&nbsp;<strong>Traveloka</strong>&nbsp;cam kết mang lại những trải nghiệm chất lượng v&agrave; kinh tế nhất cho chuyến đi của bạn.</p>\r\n<p>Nhưng nếu chỉ như thế th&igrave; Traveloka chỉ đồng h&agrave;nh l&uacute;c chuẩn bị chuyến đi c&ugrave;ng bạn được th&ocirc;i. Dặt trước tour v&agrave; v&eacute; tham quan&nbsp; trong khi đang đi vi vu nữa!</p>\r\n<p>Dịch vụ đưa đ&oacute;n s&acirc;n bay cho ph&eacute;p bạn đặt trước dịch vụ xe di chuyển, bạn c&oacute; thể biết trước gi&aacute; tiền, b&aacute;o gi&aacute; cuối c&ugrave;ng đ&atilde; bao gồm ph&iacute; cầu đường v&agrave; đậu xe, bạn kh&ocirc;ng cần lo lắng phải trả th&ecirc;m c&aacute;c loại ph&iacute; kh&aacute;c khi l&ecirc;n xe.</p>\r\n<figure id=\"attachment_55819\"><img style=\"height: auto;\" src=\"https://blog.traveloka.com/source/uploads/sites/9/2019/10/6-768x579.jpeg\" alt=\"traveloka\" width=\"1024\" height=\"772\" />\r\n<figcaption>Dịch vụ đưa đ&oacute;n s&acirc;n bay tiện lợi của Traveloka.</figcaption>\r\n</figure>\r\n<p>C&ograve;n với Xperience, Traveloka mang đến cho bạn v&ocirc; v&agrave;n trải nghiệm tr&ecirc;n to&agrave;n thế giới, từ v&eacute; tham quan, v&agrave;o cửa trải nghiệm, khu vui chơi đến tour v&agrave; c&aacute;c hoạt động du lịch hấp dẫn kh&aacute;c&hellip; Lựa chọn nhanh ch&oacute;ng, thanh to&aacute;n dễ d&agrave;ng; bạn c&oacute; thể đặt trước chuyến đi, hoặc thậm ch&iacute; ngay trong chuyến du lịch của m&igrave;nh với mức gi&aacute; tốt nhất nữa!</p>\r\n<figure id=\"attachment_55802\"><img style=\"height: auto;\" src=\"https://blog.traveloka.com/source/uploads/sites/9/2019/10/4-4-768x512.jpg\" alt=\"ứng dụng du lịch\" width=\"1024\" height=\"683\" />\r\n<figcaption>Xperience t&iacute;ch hợp tr&ecirc;n app của Traveloka. @Internet</figcaption>\r\n</figure>\r\n<h2><strong>3. Google Translate (Google Dịch)</strong></h2>\r\n<p>Đ&acirc;y thậm ch&iacute; l&agrave; ứng dụng &ldquo;sinh tồn&rdquo; của bạn, đặc biệt l&agrave; c&aacute;c chuyến đi nước ngo&agrave;i.&nbsp;<strong>Google Translate</strong>&nbsp;c&oacute; khả năng dịch được 103 ng&ocirc;n ngữ viết kh&aacute;c nhau, bằng c&aacute;ch nhập v&agrave;o c&aacute;c k&iacute; tự, &ldquo;vẽ&rdquo; k&iacute; tự (với chữ tượng h&igrave;nh) hoặc n&oacute;i trực tiếp v&agrave;o microphone của điện thoại. Đặt biệt,&nbsp;<strong>Google Translate</strong>&nbsp;c&ograve;n c&oacute; khả năng dịch c&aacute;c k&iacute; tự nhận diện được trong một h&igrave;nh ảnh được người d&ugrave;ng ghi lại th&ocirc;ng qua camera.</p>\r\n<figure id=\"attachment_55804\"><img style=\"height: auto;\" src=\"https://blog.traveloka.com/source/uploads/sites/9/2019/10/shutterstock_427072051-e1572336336619.jpg\" alt=\"ứng dụng du lịch\" width=\"1024\" height=\"645\" />\r\n<figcaption>Google Translate c&oacute; thể coi l&agrave; ứng dụng du lịch &ldquo;sinh tồn&rdquo; của bạn, đặc biệt l&agrave; c&aacute;c chuyến đi nước ngo&agrave;i. @Shutter Stock</figcaption>\r\n</figure>\r\n<p>Bạn cũng c&oacute; thể lưu trữ sẵn kho ng&ocirc;n ngữ&nbsp;<strong>Google Translate</strong>&nbsp;trong m&aacute;y để đề ph&ograve;ng trường hợp kh&ocirc;ng c&oacute; Internet vẫn c&oacute; thể sử dụng v&agrave; tra cứu từ.</p>\r\n<h2><strong>4. MAPS.ME</strong></h2>\r\n<p>Một sự thật &ldquo;phũ ph&agrave;ng&rdquo; l&agrave; ch&uacute;ng ta mạnh dạn đi v&agrave; kh&aacute;m ph&aacute; bởi lu&ocirc;n lu&ocirc;n tin rằng &ldquo;c&aacute;i g&igrave; kh&ocirc;ng biết th&igrave;&hellip; tra Google&rdquo;. Thế nhưng nếu nơi bạn muốn đến, hoặc bạn đang lạc giữa một nơi kh&ocirc;ng c&oacute; WiFi, kh&ocirc;ng c&oacute; s&oacute;ng 3G, nghĩa l&agrave; kh&ocirc;ng c&oacute; kết nối Internet th&igrave; sẽ ra sao? Kh&ocirc;ng phải ở đ&acirc;u cũng c&oacute; s&oacute;ng kết nối kh&ocirc;ng d&acirc;y, chưa kể c&aacute;c địa điểm y&ecirc;u th&iacute;ch của phượt thủ đ&ocirc;i khi nằm ở chốn &ldquo;khỉ ho kh&ocirc;ng tới, c&ograve; g&aacute;y kh&ocirc;ng nghe&rdquo; n&ecirc;n t&iacute;n hiệu sẽ rất chập chờn. Ch&iacute;nh v&igrave; vậy m&agrave; nhiều người &ldquo;điều hướng&rdquo; sang sử dụng&nbsp;<strong>MAPS.ME</strong>&nbsp;&ndash; ứng dụng bản đồ v&agrave; chỉ đường offline.</p>\r\n<figure id=\"attachment_55805\"><img style=\"height: auto;\" src=\"https://blog.traveloka.com/source/uploads/sites/9/2019/10/6-4-768x576.jpg\" alt=\"app du lịch hữu &iacute;ch\" width=\"1024\" height=\"768\" />\r\n<figcaption>Với MAPS.ME bạn kh&ocirc;ng cần phải lăn tăn chuyện kết nối 3G hoặc s&oacute;ng wifi qu&aacute; yếu. @internet</figcaption>\r\n</figure>\r\n<p>Bạn chỉ cần t&igrave;m chọn bản đồ quốc gia bạn đang phượt, nhấn chọn download (với bản đồ Việt Nam, dung lượng khoảng 60 MB), sau đ&oacute; kết hợp c&ugrave;ng GPS của m&aacute;y l&agrave; bạn thoải m&aacute;i d&ograve; đường, xem bản đồ, v.v&hellip; bới MAPS.ME</p>\r\n<p><strong>MAPS.ME</strong>&nbsp;l&agrave; một ứng dụng kh&ocirc;ng thể thiếu khi đi phượt, lại c&ograve;n miễn ph&iacute; v&agrave; cực dễ sử dụng &ndash; ho&agrave;n to&agrave;n tương đồng với c&aacute;c ứng dụng bản đồ chỉ đồ phổ biến kh&aacute;c.</p>\r\n<p>Đặc biệt, ứng dụng miễn ph&iacute; n&agrave;y c&ograve;n hỗ trợ bạn t&igrave;m c&aacute;c địa điểm xung quanh theo c&aacute;c ph&acirc;n mục như: c&acirc;y xăng, nh&agrave; h&agrave;ng, c&acirc;y ATM, v.v&hellip; Vừa c&oacute; thể xem bản đồ, vừa c&oacute; hệ thống chỉ đường (nhập vị tr&iacute; v&agrave; điểm đến), lại vừa c&oacute; th&ocirc;ng tin c&aacute;c địa điểm gần bạn, đ&acirc;y đ&iacute;ch thị l&agrave; người bạn đường khi phượt tuyệt vời nhất rồi!</p>\r\n<h2><strong>5. XE currency</strong></h2>\r\n<p>Ứng dụng n&agrave;y rất thuận tiện đối với những ai đi l&ecirc;n kế hoạch cho chuyến du lịch nước ngo&agrave;i.&nbsp;<strong>XE Currency</strong>&nbsp;l&agrave; một trong những trang web v&agrave; ứng dụng chuyển đổi tỉ gi&aacute; đ&aacute;ng tin cậy nhất, được sử dụng v&ocirc; c&ugrave;ng phổ biến. Bạn sẽ c&oacute; kết quả chuyển đổi ra nhiều loại tiền tệ từ một đồng tiền ban đầu.</p>\r\n<figure id=\"attachment_55806\"><img style=\"height: auto;\" src=\"https://blog.traveloka.com/source/uploads/sites/9/2019/10/shutterstock_416859097-e1572336877740.jpg\" alt=\"ứng dụng du lịch XE Currency\" width=\"1024\" height=\"683\" />\r\n<figcaption>XE Currency l&agrave; ứng dụng chuyển đổi ngoại tệ miễn ph&iacute; rất phổ biến v&agrave; tiện lợi khi đi du lịch nước ngo&agrave;i. @Shutter Stock</figcaption>\r\n</figure>\r\n<p><strong>XE Currency</strong>&nbsp;l&agrave; ứng dụng ho&agrave;n to&agrave;n miễn ph&iacute;, c&oacute; thể sử dụng tr&ecirc;n cả hệ điều h&agrave;nh iOS v&agrave; Android. Đặc biệt XE cũng tương tự với Google Translate; bạn c&oacute; thể sử dụng offline nếu tải trước về m&aacute;y c&aacute;c loại tiền tệ muốn đổi.</p>\r\n<figure id=\"attachment_55807\"><img style=\"height: auto;\" src=\"https://blog.traveloka.com/source/uploads/sites/9/2019/10/8-4-768x576.jpg\" alt=\"app du lịch th&ocirc;ng minh\" width=\"1024\" height=\"768\" />\r\n<figcaption>Bạn c&oacute; thể sử dụng offline nếu tải trước về m&aacute;y c&aacute;c loại tiền tệ muốn đổi. @internet</figcaption>\r\n</figure>\r\n<h2><strong>6. Evernote</strong></h2>\r\n<p><strong>Evernote</strong>&nbsp;c&ograve;n được gọi l&agrave; &ldquo;ứng dụng miễn ph&iacute; d&agrave;nh cho d&acirc;n du lịch&rdquo;. Một quyển nhật k&yacute; n-trong-1 ngay tr&ecirc;n điện thoại của bạn. Bạn c&oacute; thể th&ecirc;m ghi ch&uacute;, h&igrave;nh ảnh v&agrave; video trong một ứng dụng duy nhất; ngo&agrave;i viết nh&aacute;p b&agrave;i đăng tr&ecirc;n blog, bạn c&ograve;n c&oacute; thể lưu b&agrave;i viết từ trang web, h&igrave;nh ảnh, video, tips hoặc c&aacute;c mẹo vặt về những nơi đang v&agrave; sẽ đi du lịch&hellip;</p>\r\n<figure id=\"attachment_55808\"><img style=\"height: auto;\" src=\"https://blog.traveloka.com/source/uploads/sites/9/2019/10/9-4-768x1311.jpg\" alt=\"ứng dụng du lịch evernote\" width=\"600\" height=\"1024\" />\r\n<figcaption>Bạn c&oacute; thể lưu trữ tất tần tật th&ocirc;ng tin du lịch v&agrave;o Evernote. @internet</figcaption>\r\n</figure>\r\n<p><strong>Evernote</strong>&nbsp;c&oacute; thể đồng bộ với c&aacute;c thiết bị kh&aacute;c sử dụng Android, iOS v&agrave; cả PC của bạn nữa. Với chức năng t&igrave;m kiếm trực quan, tất cả những g&igrave; bạn cần l&agrave;m l&agrave; nhập một từ kh&oacute;a v&agrave; phần mềm sẽ cho kết quả chỉ trong v&agrave;i gi&acirc;y. Một điều th&uacute; vị kh&aacute;c l&agrave; khi bạn t&igrave;m kiếm trong tr&igrave;nh duyệt của m&igrave;nh, n&oacute; sẽ đi qua ghi ch&uacute; của bạn v&agrave; đưa ra gợi &yacute; li&ecirc;n quan.</p>\r\n<figure id=\"attachment_55809\"><img style=\"height: auto;\" src=\"https://blog.traveloka.com/source/uploads/sites/9/2019/10/10-4-768x1024.jpg\" alt=\"ứng dụng du lịch\" width=\"768\" height=\"1024\" />\r\n<figcaption>Bạn c&oacute; thể lưu b&agrave;i viết từ trang web, h&igrave;nh ảnh, video v.v&hellip; @internet</figcaption>\r\n</figure>\r\n<p>Bộ widgets mới của&nbsp;<strong>Evernote</strong>&nbsp;gi&uacute;p bạn c&oacute; thể theo d&otilde;i ghi ch&uacute; ngay từ ngo&agrave;i m&agrave;n h&igrave;nh nền. Bạn sẽ kh&ocirc;ng phải ti&ecirc;u tốn kh&aacute; nhiều thời gian để lưu trữ nội dung h&agrave;nh tr&igrave;nh của bạn, hay loay hoay để bi&ecirc;n tập v&agrave; chia sẻ được tr&ecirc;n c&aacute;c mạng x&atilde; hội.</p>\r\n<h2><strong>7. WeatherBug</strong></h2>\r\n<p>Khi đi du lịch, việc cập nhật, nắm bắt th&ocirc;ng tin thời tiết cũng l&agrave; điều v&ocirc; c&ugrave;ng quan trọng, bởi bạn đ&acirc;u muốn những cơn mưa kh&ocirc;ng dứt hay kh&ocirc;ng kh&iacute; &ocirc; nhiễm nặng nề ảnh hưởng đến chuyến đi của m&igrave;nh đ&uacute;ng kh&ocirc;ng?</p>\r\n<p><strong>WeatherBug</strong>&nbsp;c&oacute; c&aacute;c th&ocirc;ng tin thời tiết cơ bản về nhiệt độ, lượng mưa, độ ẩm, thời gian b&igrave;nh minh v&agrave; ho&agrave;ng h&ocirc;n trong ng&agrave;y, v.v&hellip; Ứng dụng du lịch n&agrave;y cũng t&iacute;ch hợp bản đồ vệ tinh để c&aacute;c bạn biết về t&igrave;nh h&igrave;nh thời tiết của một khu vực nhất định nữa.</p>\r\n<figure id=\"attachment_55810\"><img style=\"height: auto;\" src=\"https://blog.traveloka.com/source/uploads/sites/9/2019/10/11-768x512.png\" alt=\"ứng dụng du lịch\" width=\"1024\" height=\"682\" />\r\n<figcaption>Giao diện to&agrave;n diện với c&aacute;c th&ocirc;ng tin v&agrave; cảnh b&aacute;o nếu c&oacute; tr&ecirc;n ứng dụng WeatherBug. @internet</figcaption>\r\n</figure>\r\n<p>Ngo&agrave;i th&ocirc;ng tin thời tiết cơ bản, n&oacute; c&ograve;n c&oacute; hỗ trợ những t&iacute;nh năng như t&iacute;ch hợp với danh s&aacute;ch li&ecirc;n lạc của bạn, hay cho ph&eacute;p bạn g&otilde; v&agrave;o bản đồ để hiện thị thời tiết nơi bạn sắp đến. Đặc biệt, WeatherBug c&oacute; cảnh b&aacute;o thi&ecirc;n tai. N&oacute; sẽ cảnh b&aacute;o khi sắp c&oacute; thi&ecirc;n tai, hoặc hiển thị ngay tr&ecirc;n bản đồ khu vực gần bạn sắp c&oacute; thi&ecirc;n tai xảy ra, v&ocirc; c&ugrave;ng hữu &iacute;ch v&agrave; đảm bảo an to&agrave;n cho người d&ugrave;ng.</p>\r\n<h2><strong>8. Sổ thu chi MISA</strong></h2>\r\n<p>Cuối c&ugrave;ng, việc quản l&yacute; t&agrave;i ch&iacute;nh v&agrave; ti&ecirc;u x&agrave;i th&ocirc;ng minh cũng l&agrave; một trong những yếu tố tạo n&ecirc;n chuyến du lịch trọn vẹn.&nbsp;<strong>MISA</strong>&nbsp;cung cấp ứng dụng miễn ph&iacute; gi&uacute;p bạn quản l&yacute; chi ti&ecirc;u bằng c&aacute;ch ghi ch&eacute;p lại c&aacute;c khoản thu chi hằng ng&agrave;y, thống k&ecirc; c&aacute;c khoản chi theo mục v&agrave; ghi ch&uacute; cụ thể.</p>\r\n<figure id=\"attachment_55813\"><img style=\"height: auto;\" src=\"https://blog.traveloka.com/source/uploads/sites/9/2019/10/15-4-768x374.jpg\" alt=\"ứng dụng du lịch misa\" width=\"1024\" height=\"499\" />\r\n<figcaption>MISA cung cấp ứng dụng miễn ph&iacute; gi&uacute;p bạn quản l&yacute; chi ti&ecirc;u h&agrave;ng ng&agrave;y trong chuyến đi. @MISA</figcaption>\r\n</figure>\r\n<p>Với điều đ&oacute;, cộng th&ecirc;m giao diện th&acirc;n thiện, trực quan; MISA kh&ocirc;ng chỉ gi&uacute;p bạn quản l&yacute; tiền bạc trong chuyến đi, m&agrave; c&ograve;n c&oacute; thể đ&oacute;ng vai tr&ograve; một quyển nhật k&yacute; để bạn xem lại c&aacute;c khoản chi trong chuyến đi; rất ph&ugrave; hợp nếu bạn muốn viết blog du lịch hay chia sẻ kinh nghiệm vi vu của m&igrave;nh.</p>', '25.jpg', 1000);
INSERT INTO `tin_tuc` (`id_news`, `title_news`, `desc_news`, `image`, `total_views`) VALUES
(6, '<p>Vạn vật ph&iacute;a Bắc sắp bừng tỉnh trong xu&acirc;n mới, đi đ&acirc;u để &ldquo;mở h&agrave;ng&rdquo; năm mới thật may mắn đ&acirc;y?</p>', '<p>Tiết trời đ&ocirc;ng gi&aacute; chuyển dần sang sắc xu&acirc;n bừng tỉnh, tươi non trong nắng ấm b&aacute;o hiệu một năm mới lại sang. Kh&ocirc;ng c&oacute; g&igrave; tuyệt hơn l&agrave; khởi đầu mọi thứ bằng một chuyến du lịch c&ugrave;ng người th&acirc;n, gia đ&igrave;nh hoặc bạn b&egrave;, t&igrave;m kiếm những trải nghiệm mới, niềm vui, khai mở sự s&aacute;ng tạo v&agrave; cảm hứng cho cuộc sống lẫn c&ocirc;ng việc.&nbsp;</p>\r\n<h2>1. Phố cổ H&agrave; Nội</h2>\r\n<p>Tất nhi&ecirc;n l&agrave; kh&ocirc;ng thể bỏ qua kh&ocirc;ng kh&iacute; tết cổ truyền tại đất H&agrave; Th&agrave;nh ng&agrave;n năm văn hiến. Đặc biệt nếu l&agrave; bạn một người con phương Nam th&igrave; n&ecirc;n thử &iacute;t nhất một lần trong đời ăn một c&aacute;i tết đ&uacute;ng điệu miền Bắc.</p>\r\n<p>Đừng qu&ecirc;n rằng&nbsp;săn v&eacute; Tết&nbsp;l&agrave; phải c&oacute; chiến thuật v&agrave; tốc độ nh&eacute; v&igrave; đ&acirc;y l&agrave; m&ugrave;a cao điểm nhất trong năm.</p>\r\n<p><img style=\"height: auto;\" src=\"https://blog.traveloka.com/source/uploads/sites/9/2019/11/goi-y-nhung-dia-diem-du-lich-ngay-tet-o-phia-bac-1-300x200.jpg\" sizes=\"(max-width: 1024px) 100vw, 1024px\" srcset=\"https://blog.traveloka.com/source/uploads/sites/9/2019/11/goi-y-nhung-dia-diem-du-lich-ngay-tet-o-phia-bac-1-300x200.jpg 300w, https://blog.traveloka.com/source/uploads/sites/9/2019/11/goi-y-nhung-dia-diem-du-lich-ngay-tet-o-phia-bac-1-768x512.jpg 768w, https://blog.traveloka.com/source/uploads/sites/9/2019/11/goi-y-nhung-dia-diem-du-lich-ngay-tet-o-phia-bac-1-640x427.jpg 640w, https://blog.traveloka.com/source/uploads/sites/9/2019/11/goi-y-nhung-dia-diem-du-lich-ngay-tet-o-phia-bac-1-585x390.jpg 585w\" alt=\"địa điểm du lịch tết\" /></p>\r\n<p>Vườn đ&agrave;o Nhật T&acirc;n v&agrave;o vụ m&ugrave;a chuẩn bị c&ugrave;ng người H&agrave; Nội đ&oacute;n Tết. @To&agrave;n Vũ</p>\r\n<p><img style=\"height: auto;\" src=\"https://blog.traveloka.com/source/uploads/sites/9/2019/11/goi-y-nhung-dia-diem-du-lich-ngay-tet-o-phia-bac-2.jpg\" sizes=\"(max-width: 800px) 100vw, 800px\" srcset=\"https://blog.traveloka.com/source/uploads/sites/9/2019/11/goi-y-nhung-dia-diem-du-lich-ngay-tet-o-phia-bac-2.jpg 800w, https://blog.traveloka.com/source/uploads/sites/9/2019/11/goi-y-nhung-dia-diem-du-lich-ngay-tet-o-phia-bac-2-300x200.jpg 300w, https://blog.traveloka.com/source/uploads/sites/9/2019/11/goi-y-nhung-dia-diem-du-lich-ngay-tet-o-phia-bac-2-768x512.jpg 768w, https://blog.traveloka.com/source/uploads/sites/9/2019/11/goi-y-nhung-dia-diem-du-lich-ngay-tet-o-phia-bac-2-640x426.jpg 640w, https://blog.traveloka.com/source/uploads/sites/9/2019/11/goi-y-nhung-dia-diem-du-lich-ngay-tet-o-phia-bac-2-585x390.jpg 585w\" alt=\"địa điểm du lịch tết\" /></p>\r\n<p>Người người, nh&agrave; nh&agrave; c&ugrave;ng nhau l&ecirc;n phố mua sắm chuẩn bị cho ng&agrave;y tết sắp về. @Qu&yacute; Đức</p>\r\n<p><img style=\"height: auto;\" src=\"https://blog.traveloka.com/source/uploads/sites/9/2019/11/goi-y-nhung-dia-diem-du-lich-ngay-tet-o-phia-bac-3-300x200.jpg\" sizes=\"(max-width: 1024px) 100vw, 1024px\" srcset=\"https://blog.traveloka.com/source/uploads/sites/9/2019/11/goi-y-nhung-dia-diem-du-lich-ngay-tet-o-phia-bac-3-300x200.jpg 300w, https://blog.traveloka.com/source/uploads/sites/9/2019/11/goi-y-nhung-dia-diem-du-lich-ngay-tet-o-phia-bac-3-768x512.jpg 768w, https://blog.traveloka.com/source/uploads/sites/9/2019/11/goi-y-nhung-dia-diem-du-lich-ngay-tet-o-phia-bac-3-640x426.jpg 640w, https://blog.traveloka.com/source/uploads/sites/9/2019/11/goi-y-nhung-dia-diem-du-lich-ngay-tet-o-phia-bac-3-585x390.jpg 585w\" alt=\"\" /></p>\r\n<p>C&aacute;c thắng cảnh nổi tiếng như Hồ Gươm, đền Ngọc Sơn, Quốc Tử Gi&aacute;m hay c&aacute;c đền ch&ugrave;a đều thu h&uacute;t người d&acirc;n đến tham quan, chụp ảnh v&agrave; cầu nguyện cho năm mới b&igrave;nh an. @Thuongthuong</p>\r\n<p>Tuyệt vời nhất l&agrave; bạn c&oacute; đủ thời gian để trải nghiệm H&agrave; Nội trước v&agrave; sau dịp Tết nguy&ecirc;n đ&aacute;n. V&igrave; sao? Thời điểm trước Tết, người người, nh&agrave; nh&agrave; n&ocirc; nức đi mua sắm b&aacute;nh mứt, gi&ograve; chả, quần &aacute;o mới, mua c&acirc;y đ&agrave;o, c&acirc;y quất v&agrave; c&ugrave;ng nhau g&oacute;i b&aacute;nh chưng. Tết đến xu&acirc;n về đến ch&uacute;c tết họ h&agrave;ng, bạn b&egrave;, l&ecirc;n ch&ugrave;a cầu an, l&ecirc;n phố chụp ảnh v&agrave; ăn những bữa cơm qu&acirc;y quần b&ecirc;n gia đ&igrave;nh trong niềm hạnh ph&uacute;c v&ocirc; gi&aacute;. Sau Tết l&agrave; khi c&aacute;c lễ hội ở đồng bằng Bắc bộ đồng loạt diễn ra ở tại H&agrave; Nội v&agrave; c&aacute;c tỉnh l&acirc;n cận. Chắc chắn bạn sẽ c&oacute; nhiều trải nghiệm về văn h&oacute;a truyền thống đặc sắc của qu&ecirc; hương.</p>\r\n<h2>2. Sapa, L&agrave;o Cai</h2>\r\n<p>Sapa vẫn chưa bao giờ hết t&igrave;nh, hết thơ trong t&acirc;m thức của kh&aacute;ch du lịch, hơn nữa sắc xu&acirc;n lại tr&agrave;n ngập khắp nơi như vậy. Thời tiết l&uacute;c n&agrave;y kh&aacute; lạnh, c&oacute; những năm, người ta đến Sapa để c&ugrave;ng chi&ecirc;m ngưỡng những t&aacute;c phẩm nghệ thuật từ băng tuyết v&agrave; hoa cỏ, những điều kỳ diệu m&agrave; bạn sẽ kh&oacute; t&igrave;m thấy ở c&aacute;c nước nhiệt đới. Một&nbsp;chuyến bay đến H&agrave; Nội, th&ecirc;m một chuyến xe hoặc một chuyến t&agrave;u xuy&ecirc;n m&agrave;n đ&ecirc;m l&agrave; bạn đ&atilde; c&oacute; thể đến Sapa, điểm du lịch trong mơ của h&agrave;ng triệu người trẻ.</p>\r\n<p><img style=\"height: auto;\" src=\"https://blog.traveloka.com/source/uploads/sites/9/2019/11/goi-y-nhung-dia-diem-du-lich-ngay-tet-o-phia-bac-4.jpg\" sizes=\"(max-width: 682px) 100vw, 682px\" srcset=\"https://blog.traveloka.com/source/uploads/sites/9/2019/11/goi-y-nhung-dia-diem-du-lich-ngay-tet-o-phia-bac-4.jpg 533w, https://blog.traveloka.com/source/uploads/sites/9/2019/11/goi-y-nhung-dia-diem-du-lich-ngay-tet-o-phia-bac-4-200x300.jpg 200w, https://blog.traveloka.com/source/uploads/sites/9/2019/11/goi-y-nhung-dia-diem-du-lich-ngay-tet-o-phia-bac-4-426x640.jpg 426w\" alt=\"địa điểm du lịch tết\" /></p>\r\n<p>Những thước ảnh trong trẻo v&agrave; b&igrave;nh y&ecirc;n của Sapa khi m&ugrave;a xu&acirc;n về. @huyco</p>\r\n<p><img style=\"height: auto;\" src=\"https://blog.traveloka.com/source/uploads/sites/9/2019/11/goi-y-nhung-dia-diem-du-lich-ngay-tet-o-phia-bac-5.jpg\" sizes=\"(max-width: 1024px) 100vw, 1024px\" srcset=\"https://blog.traveloka.com/source/uploads/sites/9/2019/11/goi-y-nhung-dia-diem-du-lich-ngay-tet-o-phia-bac-5.jpg 1024w, https://blog.traveloka.com/source/uploads/sites/9/2019/11/goi-y-nhung-dia-diem-du-lich-ngay-tet-o-phia-bac-5-300x200.jpg 300w, https://blog.traveloka.com/source/uploads/sites/9/2019/11/goi-y-nhung-dia-diem-du-lich-ngay-tet-o-phia-bac-5-768x512.jpg 768w, https://blog.traveloka.com/source/uploads/sites/9/2019/11/goi-y-nhung-dia-diem-du-lich-ngay-tet-o-phia-bac-5-640x427.jpg 640w, https://blog.traveloka.com/source/uploads/sites/9/2019/11/goi-y-nhung-dia-diem-du-lich-ngay-tet-o-phia-bac-5-585x390.jpg 585w\" alt=\"địa điểm du lịch tết\" /></p>\r\n<p>M&ugrave;a n&agrave;o trong năm, Sapa cũng đẹp tuyệt nhưng xu&acirc;n về l&agrave; đẹp nhất. @Miguel &Aacute;ngel Fern&aacute;ndez</p>\r\n<p>Sapa lửng lơ giữa đất trời l&agrave; nơi c&oacute; rất nhiều thước cảnh đẹp h&uacute;t hồn khiến ai cũng nhớ thương như đỉnh Fansipan giữa biển m&acirc;y, thung lũng Mường Hoa rực rỡ sắc m&agrave;u, n&uacute;i H&agrave;m Rồng uy phong hay th&aacute;c Bạc đổ nước tung bọt trắng x&oacute;a.</p>\r\n<h2>3. Tam Đảo, Vĩnh Ph&uacute;c</h2>\r\n<p>Chỉ c&aacute;ch trung t&acirc;m H&agrave; Nội khoảng 80 km lại kho&aacute;c tr&ecirc;n m&igrave;nh chiếc &aacute;o m&ugrave;a xu&acirc;n đẹp nao l&ograve;ng, Tam Đảo chắc chắn l&agrave; một đ&aacute;p &aacute;n l&yacute; tưởng cho c&acirc;u hỏi &ldquo;Tết n&ecirc;n đi du lịch ở đ&acirc;u miền bắc?&rdquo;.</p>\r\n<p><img style=\"height: auto;\" src=\"https://blog.traveloka.com/source/uploads/sites/9/2019/11/goi-y-nhung-dia-diem-du-lich-ngay-tet-o-phia-bac-7-300x300.jpg\" sizes=\"(max-width: 1024px) 100vw, 1024px\" srcset=\"https://blog.traveloka.com/source/uploads/sites/9/2019/11/goi-y-nhung-dia-diem-du-lich-ngay-tet-o-phia-bac-7-300x300.jpg 300w, https://blog.traveloka.com/source/uploads/sites/9/2019/11/goi-y-nhung-dia-diem-du-lich-ngay-tet-o-phia-bac-7-150x150.jpg 150w, https://blog.traveloka.com/source/uploads/sites/9/2019/11/goi-y-nhung-dia-diem-du-lich-ngay-tet-o-phia-bac-7-768x768.jpg 768w, https://blog.traveloka.com/source/uploads/sites/9/2019/11/goi-y-nhung-dia-diem-du-lich-ngay-tet-o-phia-bac-7-640x640.jpg 640w, https://blog.traveloka.com/source/uploads/sites/9/2019/11/goi-y-nhung-dia-diem-du-lich-ngay-tet-o-phia-bac-7-585x585.jpg 585w, https://blog.traveloka.com/source/uploads/sites/9/2019/11/goi-y-nhung-dia-diem-du-lich-ngay-tet-o-phia-bac-7-220x220.jpg 220w, https://blog.traveloka.com/source/uploads/sites/9/2019/11/goi-y-nhung-dia-diem-du-lich-ngay-tet-o-phia-bac-7-80x80.jpg 80w, https://blog.traveloka.com/source/uploads/sites/9/2019/11/goi-y-nhung-dia-diem-du-lich-ngay-tet-o-phia-bac-7.jpg 1080w\" alt=\"địa điểm du lịch tết\" /></p>\r\n<p>Bức ảnh sống ảo từ một g&oacute;c tr&ecirc;n cao của Tam Đảo v&agrave; những ng&ocirc;i nh&agrave; nhiều sắc m&agrave;u. @linhhuongtran97</p>\r\n<p><img style=\"height: auto;\" src=\"https://blog.traveloka.com/source/uploads/sites/9/2019/11/goi-y-nhung-dia-diem-du-lich-ngay-tet-o-phia-bac-8-300x300.jpg\" sizes=\"(max-width: 1024px) 100vw, 1024px\" srcset=\"https://blog.traveloka.com/source/uploads/sites/9/2019/11/goi-y-nhung-dia-diem-du-lich-ngay-tet-o-phia-bac-8-300x300.jpg 300w, https://blog.traveloka.com/source/uploads/sites/9/2019/11/goi-y-nhung-dia-diem-du-lich-ngay-tet-o-phia-bac-8-150x150.jpg 150w, https://blog.traveloka.com/source/uploads/sites/9/2019/11/goi-y-nhung-dia-diem-du-lich-ngay-tet-o-phia-bac-8-768x768.jpg 768w, https://blog.traveloka.com/source/uploads/sites/9/2019/11/goi-y-nhung-dia-diem-du-lich-ngay-tet-o-phia-bac-8-640x640.jpg 640w, https://blog.traveloka.com/source/uploads/sites/9/2019/11/goi-y-nhung-dia-diem-du-lich-ngay-tet-o-phia-bac-8-585x585.jpg 585w, https://blog.traveloka.com/source/uploads/sites/9/2019/11/goi-y-nhung-dia-diem-du-lich-ngay-tet-o-phia-bac-8-220x220.jpg 220w, https://blog.traveloka.com/source/uploads/sites/9/2019/11/goi-y-nhung-dia-diem-du-lich-ngay-tet-o-phia-bac-8-80x80.jpg 80w, https://blog.traveloka.com/source/uploads/sites/9/2019/11/goi-y-nhung-dia-diem-du-lich-ngay-tet-o-phia-bac-8.jpg 1080w\" alt=\"địa điểm du lịch tết\" /></p>\r\n<p>Kh&ocirc;ng gian xanh m&aacute;t bao quanh, khắp nơi bừng tỉnh trong xu&acirc;n mới. @chi_p_l</p>\r\n<p><img style=\"height: auto;\" src=\"https://blog.traveloka.com/source/uploads/sites/9/2019/11/goi-y-nhung-dia-diem-du-lich-ngay-tet-o-phia-bac-9-240x300.jpg\" sizes=\"(max-width: 819px) 100vw, 819px\" srcset=\"https://blog.traveloka.com/source/uploads/sites/9/2019/11/goi-y-nhung-dia-diem-du-lich-ngay-tet-o-phia-bac-9-240x300.jpg 240w, https://blog.traveloka.com/source/uploads/sites/9/2019/11/goi-y-nhung-dia-diem-du-lich-ngay-tet-o-phia-bac-9-768x960.jpg 768w, https://blog.traveloka.com/source/uploads/sites/9/2019/11/goi-y-nhung-dia-diem-du-lich-ngay-tet-o-phia-bac-9-512x640.jpg 512w, https://blog.traveloka.com/source/uploads/sites/9/2019/11/goi-y-nhung-dia-diem-du-lich-ngay-tet-o-phia-bac-9-585x731.jpg 585w, https://blog.traveloka.com/source/uploads/sites/9/2019/11/goi-y-nhung-dia-diem-du-lich-ngay-tet-o-phia-bac-9.jpg 1080w\" alt=\"địa điểm du lịch tết\" /></p>\r\n<p>Những tấm ảnh sống ảo triệu like đẹp bất chấp style, cứ đưa m&aacute;y l&ecirc;n l&agrave; c&oacute; ngay tấm ảnh đẹp. @imtungdesign</p>\r\n<p>Bạn c&oacute; thể đến Tam Đảo v&igrave; m&ecirc; sống ảo, đến ngắm cảnh đẹp, du xu&acirc;n hay đi nghỉ dưỡng v&agrave;o m&ugrave;a xu&acirc;n đều hợp l&yacute;. Th&agrave;nh phố n&agrave;y vẫn ẩn m&igrave;nh trong sương sớm những s&aacute;ng se lạnh, l&atilde;ng mạn v&agrave; đủ trữ t&igrave;nh. Chưa hết, Tam Đảo lại được bao bọc bởi những c&aacute;nh rừng nguy&ecirc;n sinh xanh tươi, v&igrave; thế m&agrave; kh&ocirc;ng kh&iacute; ở đ&acirc;y rất trong l&agrave;nh. M&ugrave;a xu&acirc;n l&agrave; phải như vậy mới đ&uacute;ng l&agrave; m&ugrave;a xu&acirc;n nh&eacute;!</p>\r\n<h2>4. Ch&ugrave;a Hương, H&agrave; Nội</h2>\r\n<p>Từ đ&ecirc;m 30, người H&agrave; Nội đ&atilde; rủ nhau đi ch&ugrave;a Hương từ bao nhi&ecirc;u năm qua như một th&oacute;i quen kh&ocirc;ng thể bỏ qua nh&acirc;n dịp xu&acirc;n mới. Vừa để cầu an, cầu may linh thi&ecirc;ng bậc nhất ph&iacute;a Bắc, quần thể ch&ugrave;a Hương c&ograve;n l&agrave; một trong những đệ nhất danh lam thắng cảnh ở đồng bằng Bắc bộ, nơi m&agrave; bạn c&oacute; thể chứng kiến những dấu hiệu của m&ugrave;a xu&acirc;n sớm nhất, trong trẻo v&agrave; thật tinh kh&ocirc;i.</p>\r\n<p>Lễ hội ch&ugrave;a Hương&nbsp;thường diễn ra từ th&aacute;ng 1 &ndash; 3 &acirc;m lịch h&agrave;ng năm.</p>\r\n<p><img style=\"height: auto;\" src=\"https://blog.traveloka.com/source/uploads/sites/9/2019/11/goi-y-nhung-dia-diem-du-lich-ngay-tet-o-phia-bac-10-300x200.jpg\" sizes=\"(max-width: 1024px) 100vw, 1024px\" srcset=\"https://blog.traveloka.com/source/uploads/sites/9/2019/11/goi-y-nhung-dia-diem-du-lich-ngay-tet-o-phia-bac-10-300x200.jpg 300w, https://blog.traveloka.com/source/uploads/sites/9/2019/11/goi-y-nhung-dia-diem-du-lich-ngay-tet-o-phia-bac-10-768x512.jpg 768w, https://blog.traveloka.com/source/uploads/sites/9/2019/11/goi-y-nhung-dia-diem-du-lich-ngay-tet-o-phia-bac-10-640x427.jpg 640w, https://blog.traveloka.com/source/uploads/sites/9/2019/11/goi-y-nhung-dia-diem-du-lich-ngay-tet-o-phia-bac-10-585x390.jpg 585w, https://blog.traveloka.com/source/uploads/sites/9/2019/11/goi-y-nhung-dia-diem-du-lich-ngay-tet-o-phia-bac-10.jpg 1600w\" alt=\"địa điểm du lịch tết\" /></p>\r\n<p>Lễ hội ch&ugrave;a Hương thường diễn ra từ th&aacute;ng 1 &ndash; 3 (&acirc;m lịch) h&agrave;ng năm. @Tuấn Hải</p>\r\n<p><img style=\"height: auto;\" src=\"https://blog.traveloka.com/source/uploads/sites/9/2019/11/goi-y-nhung-dia-diem-du-lich-ngay-tet-o-phia-bac-11.jpg\" sizes=\"(max-width: 750px) 100vw, 750px\" srcset=\"https://blog.traveloka.com/source/uploads/sites/9/2019/11/goi-y-nhung-dia-diem-du-lich-ngay-tet-o-phia-bac-11.jpg 750w, https://blog.traveloka.com/source/uploads/sites/9/2019/11/goi-y-nhung-dia-diem-du-lich-ngay-tet-o-phia-bac-11-300x160.jpg 300w, https://blog.traveloka.com/source/uploads/sites/9/2019/11/goi-y-nhung-dia-diem-du-lich-ngay-tet-o-phia-bac-11-640x341.jpg 640w, https://blog.traveloka.com/source/uploads/sites/9/2019/11/goi-y-nhung-dia-diem-du-lich-ngay-tet-o-phia-bac-11-585x312.jpg 585w\" alt=\"địa điểm du lịch tết\" /></p>\r\n<p>Suối Yến tấp nập những chuyến đ&ograve; đưa kh&aacute;ch h&agrave;nh hương v&agrave;o nơi đất Phật nh&acirc;n dịp đầu xu&acirc;n. @Sưu tầm</p>\r\n<p><img style=\"height: auto;\" src=\"https://blog.traveloka.com/source/uploads/sites/9/2019/11/goi-y-nhung-dia-diem-du-lich-ngay-tet-o-phia-bac-12-300x200.jpg\" sizes=\"(max-width: 1024px) 100vw, 1024px\" srcset=\"https://blog.traveloka.com/source/uploads/sites/9/2019/11/goi-y-nhung-dia-diem-du-lich-ngay-tet-o-phia-bac-12-300x200.jpg 300w, https://blog.traveloka.com/source/uploads/sites/9/2019/11/goi-y-nhung-dia-diem-du-lich-ngay-tet-o-phia-bac-12-768x512.jpg 768w, https://blog.traveloka.com/source/uploads/sites/9/2019/11/goi-y-nhung-dia-diem-du-lich-ngay-tet-o-phia-bac-12-640x427.jpg 640w, https://blog.traveloka.com/source/uploads/sites/9/2019/11/goi-y-nhung-dia-diem-du-lich-ngay-tet-o-phia-bac-12-585x390.jpg 585w, https://blog.traveloka.com/source/uploads/sites/9/2019/11/goi-y-nhung-dia-diem-du-lich-ngay-tet-o-phia-bac-12.jpg 1200w\" alt=\"địa điểm du lịch tết\" /></p>\r\n<p>M&ugrave;a xu&acirc;n l&agrave; dịp n&uacute;i rừng Hương Sơn thơ mộng v&agrave; rực rỡ nhất với những sắc hoa tươi v&agrave; cỏ c&acirc;y đ&acirc;m trồi, nảy lộc. @Sưu tầm</p>\r\n<h2>5. Quần thể ch&ugrave;a B&aacute;i Đ&iacute;nh, Ninh B&igrave;nh</h2>\r\n<p>Một trong những quần thể t&acirc;m linh, thẳng cảnh đẹp v&agrave; thu h&uacute;t nhất khu vực ph&iacute;a Bắc phải kể đến ch&ugrave;a B&aacute;i Đ&iacute;nh. Ng&ocirc;i ch&ugrave;a linh thi&ecirc;ng nằm giữa một thung lũng với n&uacute;i đ&aacute;, hang động, hồ nước, rừng c&acirc;y v&agrave; c&aacute;nh đồng.&nbsp;Ch&ugrave;a B&aacute;i Đ&iacute;nh thường khai hội v&agrave;o m&ugrave;ng 6 hằng năm, đ&oacute;n h&agrave;ng ng&agrave;n kh&aacute;ch thập phương đến d&acirc;ng hương cầu may năm mới.</p>\r\n<p><img style=\"height: auto;\" src=\"https://blog.traveloka.com/source/uploads/sites/9/2019/11/goi-y-nhung-dia-diem-du-lich-ngay-tet-o-phia-bac-14.jpg\" sizes=\"(max-width: 1000px) 100vw, 1000px\" srcset=\"https://blog.traveloka.com/source/uploads/sites/9/2019/11/goi-y-nhung-dia-diem-du-lich-ngay-tet-o-phia-bac-14.jpg 1000w, https://blog.traveloka.com/source/uploads/sites/9/2019/11/goi-y-nhung-dia-diem-du-lich-ngay-tet-o-phia-bac-14-300x165.jpg 300w, https://blog.traveloka.com/source/uploads/sites/9/2019/11/goi-y-nhung-dia-diem-du-lich-ngay-tet-o-phia-bac-14-768x422.jpg 768w, https://blog.traveloka.com/source/uploads/sites/9/2019/11/goi-y-nhung-dia-diem-du-lich-ngay-tet-o-phia-bac-14-640x351.jpg 640w, https://blog.traveloka.com/source/uploads/sites/9/2019/11/goi-y-nhung-dia-diem-du-lich-ngay-tet-o-phia-bac-14-585x321.jpg 585w\" alt=\"địa điểm du lịch tết\" /></p>\r\n<p>Quần thể ng&ocirc;i ch&ugrave;a linh thi&ecirc;ng tại một g&oacute;c nh&igrave;n từ tr&ecirc;n cao. @B&aacute;o Đầu tư</p>\r\n<p><img style=\"height: auto;\" src=\"https://blog.traveloka.com/source/uploads/sites/9/2019/11/goi-y-nhung-dia-diem-du-lich-ngay-tet-o-phia-bac-15.jpg\" sizes=\"(max-width: 700px) 100vw, 700px\" srcset=\"https://blog.traveloka.com/source/uploads/sites/9/2019/11/goi-y-nhung-dia-diem-du-lich-ngay-tet-o-phia-bac-15.jpg 700w, https://blog.traveloka.com/source/uploads/sites/9/2019/11/goi-y-nhung-dia-diem-du-lich-ngay-tet-o-phia-bac-15-300x205.jpg 300w, https://blog.traveloka.com/source/uploads/sites/9/2019/11/goi-y-nhung-dia-diem-du-lich-ngay-tet-o-phia-bac-15-640x437.jpg 640w, https://blog.traveloka.com/source/uploads/sites/9/2019/11/goi-y-nhung-dia-diem-du-lich-ngay-tet-o-phia-bac-15-585x399.jpg 585w\" alt=\"địa điểm du lịch tết\" /></p>\r\n<p>Tại bến, nh&agrave; đ&ograve; hoạt động tấp nập, đ&ocirc;ng vui nh&acirc;n dịp kh&aacute;ch thập phương du xu&acirc;n gh&eacute; về. @Sưu tầm</p>\r\n<p>Vừa đi lễ ch&ugrave;a đầu xu&acirc;n, bạn c&oacute; vừa c&oacute; thời gian để đi chi&ecirc;m ngưỡng thắng cảnh thi&ecirc;n nhi&ecirc;n tuyệt đẹp của đất trời. Chỉ mất khoảng gần 3 tiếng đi xe từ H&agrave; Nội đến Ninh B&igrave;nh, bạn ho&agrave;n to&agrave;n c&oacute; thể đi v&agrave; về trong ng&agrave;y, rất tiện lợi. Đối với nơi n&agrave;y, bạn c&oacute; thể đi trước, trong v&agrave; sau tết đều th&iacute;ch hợp nh&eacute;!</p>\r\n<h2>6. Cao nguy&ecirc;n Đồng Văn, H&agrave; Giang</h2>\r\n<p>Một chuyến đi phượt trốn cả thế giới, tự thưởng cho m&igrave;nh một kỳ nghỉ th&uacute; vị nh&acirc;n dịp đầu năm th&igrave; đừng qu&ecirc;n c&aacute;c h&agrave;nh tr&igrave;nh vượt n&uacute;i l&ecirc;n v&ugrave;ng cao, tận hưởng c&aacute;i lạnh đầu xu&acirc;n, ngắm n&uacute;i, ngắm rừng, xuy&ecirc;n qua c&aacute;c v&ugrave;ng m&acirc;y như những kỳ quan độc đ&aacute;o của tạo h&oacute;a. Đ&acirc;y cũng l&agrave; m&ugrave;a trăm h&oacute;a đua nở tạo n&ecirc;n những c&aacute;nh đồng lớn với hoa đ&agrave;o, l&ecirc;n, mận, cải nở trắng cả đất trời.</p>\r\n<p><img style=\"height: auto;\" src=\"https://blog.traveloka.com/source/uploads/sites/9/2019/11/goi-y-nhung-dia-diem-du-lich-ngay-tet-o-phia-bac-16-300x300.jpg\" sizes=\"(max-width: 1024px) 100vw, 1024px\" srcset=\"https://blog.traveloka.com/source/uploads/sites/9/2019/11/goi-y-nhung-dia-diem-du-lich-ngay-tet-o-phia-bac-16-300x300.jpg 300w, https://blog.traveloka.com/source/uploads/sites/9/2019/11/goi-y-nhung-dia-diem-du-lich-ngay-tet-o-phia-bac-16-150x150.jpg 150w, https://blog.traveloka.com/source/uploads/sites/9/2019/11/goi-y-nhung-dia-diem-du-lich-ngay-tet-o-phia-bac-16-768x768.jpg 768w, https://blog.traveloka.com/source/uploads/sites/9/2019/11/goi-y-nhung-dia-diem-du-lich-ngay-tet-o-phia-bac-16-640x640.jpg 640w, https://blog.traveloka.com/source/uploads/sites/9/2019/11/goi-y-nhung-dia-diem-du-lich-ngay-tet-o-phia-bac-16-585x585.jpg 585w, https://blog.traveloka.com/source/uploads/sites/9/2019/11/goi-y-nhung-dia-diem-du-lich-ngay-tet-o-phia-bac-16-220x220.jpg 220w, https://blog.traveloka.com/source/uploads/sites/9/2019/11/goi-y-nhung-dia-diem-du-lich-ngay-tet-o-phia-bac-16-80x80.jpg 80w, https://blog.traveloka.com/source/uploads/sites/9/2019/11/goi-y-nhung-dia-diem-du-lich-ngay-tet-o-phia-bac-16.jpg 1080w\" alt=\"địa điểm du lịch tết\" /></p>\r\n<p>Du xu&acirc;n gặp gỡ những người d&acirc;n H&agrave; Giang mến kh&aacute;ch v&agrave; đ&aacute;ng y&ecirc;u. @yezitamy</p>\r\n<p><img style=\"height: auto;\" src=\"https://blog.traveloka.com/source/uploads/sites/9/2019/11/goi-y-nhung-dia-diem-du-lich-ngay-tet-o-phia-bac-18-300x300.jpg\" sizes=\"(max-width: 1024px) 100vw, 1024px\" srcset=\"https://blog.traveloka.com/source/uploads/sites/9/2019/11/goi-y-nhung-dia-diem-du-lich-ngay-tet-o-phia-bac-18-300x300.jpg 300w, https://blog.traveloka.com/source/uploads/sites/9/2019/11/goi-y-nhung-dia-diem-du-lich-ngay-tet-o-phia-bac-18-150x150.jpg 150w, https://blog.traveloka.com/source/uploads/sites/9/2019/11/goi-y-nhung-dia-diem-du-lich-ngay-tet-o-phia-bac-18-768x768.jpg 768w, https://blog.traveloka.com/source/uploads/sites/9/2019/11/goi-y-nhung-dia-diem-du-lich-ngay-tet-o-phia-bac-18-640x640.jpg 640w, https://blog.traveloka.com/source/uploads/sites/9/2019/11/goi-y-nhung-dia-diem-du-lich-ngay-tet-o-phia-bac-18-585x585.jpg 585w, https://blog.traveloka.com/source/uploads/sites/9/2019/11/goi-y-nhung-dia-diem-du-lich-ngay-tet-o-phia-bac-18-220x220.jpg 220w, https://blog.traveloka.com/source/uploads/sites/9/2019/11/goi-y-nhung-dia-diem-du-lich-ngay-tet-o-phia-bac-18-80x80.jpg 80w, https://blog.traveloka.com/source/uploads/sites/9/2019/11/goi-y-nhung-dia-diem-du-lich-ngay-tet-o-phia-bac-18.jpg 1080w\" alt=\"địa điểm du lịch tết\" /></p>\r\n<p>Đứng giữa biển trời bao la, tr&ecirc;n những d&atilde;y n&uacute;i đ&aacute; h&ugrave;ng vĩ của H&agrave; Giang. @alexnguyen1245</p>\r\n<p>D&acirc;n sống ảo th&igrave; kh&ocirc;ng thể bỏ lỡ những m&ugrave;a hoa ở v&ugrave;ng cao nh&eacute;, những khoảnh khắc đẹp xuất sắc nhất trong năm. Một số điểm tham quan m&agrave; bạn phải ghi ngay v&agrave;o &ldquo;checklist&rdquo; gồm Dinh Thự Vua M&egrave;o, Cột Cờ Lũng C&uacute;, phố cổ Đồng Văn, Cung đường hạnh ph&uacute;c v&agrave; đặc biệt kh&ocirc;ng thể bỏ qua M&atilde; P&iacute; L&egrave;ng, nơi m&agrave; tuổi trẻ n&ecirc;n trải nghiệm &iacute;t nhất 1 lần trong đời.</p>\r\n<h2>7. Mai Ch&acirc;u, H&ograve;a B&igrave;nh</h2>\r\n<p>Thung lũng Mai Ch&acirc;u nằm lặng im v&agrave; thanh b&igrave;nh giữa những d&atilde;y n&uacute;i cao v&agrave; được bao bọc bởi những c&aacute;nh rừng bạt ng&agrave;n. Mai Ch&acirc;u chỉ c&aacute;ch H&agrave; Nội khoảng 150 km n&ecirc;n việc di chuyển cũng rất dễ d&agrave;ng bằng xe m&aacute;y hoặc &ocirc; t&ocirc;. Đến Mai Ch&acirc;u bất kỳ m&ugrave;a n&agrave;o trong năm bạn đều c&oacute; thể chi&ecirc;m ngưỡng những thước cảnh đẹp n&agrave;o l&ograve;ng của tự nhi&ecirc;n xanh thẳm. Đặc biệt trong m&ugrave;a xu&acirc;n, Mai Ch&acirc;u sẽ tươi đẹp hơn rất nhiều.</p>\r\n<p><img style=\"height: auto;\" src=\"https://blog.traveloka.com/source/uploads/sites/9/2019/11/goi-y-nhung-dia-diem-du-lich-ngay-tet-o-phia-bac-19-240x300.jpg\" sizes=\"(max-width: 819px) 100vw, 819px\" srcset=\"https://blog.traveloka.com/source/uploads/sites/9/2019/11/goi-y-nhung-dia-diem-du-lich-ngay-tet-o-phia-bac-19-240x300.jpg 240w, https://blog.traveloka.com/source/uploads/sites/9/2019/11/goi-y-nhung-dia-diem-du-lich-ngay-tet-o-phia-bac-19-768x960.jpg 768w, https://blog.traveloka.com/source/uploads/sites/9/2019/11/goi-y-nhung-dia-diem-du-lich-ngay-tet-o-phia-bac-19-512x640.jpg 512w, https://blog.traveloka.com/source/uploads/sites/9/2019/11/goi-y-nhung-dia-diem-du-lich-ngay-tet-o-phia-bac-19-585x731.jpg 585w, https://blog.traveloka.com/source/uploads/sites/9/2019/11/goi-y-nhung-dia-diem-du-lich-ngay-tet-o-phia-bac-19.jpg 960w\" alt=\"địa điểm du lịch tết\" /></p>\r\n<p>Tận hưởng ph&uacute;t gi&acirc;y thư gi&atilde;n, thoải m&aacute;i nhất cho một khởi đầu thật nhẹ nh&agrave;ng. @jodistuivenberg</p>\r\n<p><img style=\"height: auto;\" src=\"https://blog.traveloka.com/source/uploads/sites/9/2019/11/goi-y-nhung-dia-diem-du-lich-ngay-tet-o-phia-bac-20-300x300.jpg\" sizes=\"(max-width: 1024px) 100vw, 1024px\" srcset=\"https://blog.traveloka.com/source/uploads/sites/9/2019/11/goi-y-nhung-dia-diem-du-lich-ngay-tet-o-phia-bac-20-300x300.jpg 300w, https://blog.traveloka.com/source/uploads/sites/9/2019/11/goi-y-nhung-dia-diem-du-lich-ngay-tet-o-phia-bac-20-150x150.jpg 150w, https://blog.traveloka.com/source/uploads/sites/9/2019/11/goi-y-nhung-dia-diem-du-lich-ngay-tet-o-phia-bac-20-768x768.jpg 768w, https://blog.traveloka.com/source/uploads/sites/9/2019/11/goi-y-nhung-dia-diem-du-lich-ngay-tet-o-phia-bac-20-640x640.jpg 640w, https://blog.traveloka.com/source/uploads/sites/9/2019/11/goi-y-nhung-dia-diem-du-lich-ngay-tet-o-phia-bac-20-585x585.jpg 585w, https://blog.traveloka.com/source/uploads/sites/9/2019/11/goi-y-nhung-dia-diem-du-lich-ngay-tet-o-phia-bac-20-220x220.jpg 220w, https://blog.traveloka.com/source/uploads/sites/9/2019/11/goi-y-nhung-dia-diem-du-lich-ngay-tet-o-phia-bac-20-80x80.jpg 80w, https://blog.traveloka.com/source/uploads/sites/9/2019/11/goi-y-nhung-dia-diem-du-lich-ngay-tet-o-phia-bac-20.jpg 1080w\" alt=\"địa điểm du lịch tết\" /></p>\r\n<p>L&uacute;c n&agrave;o cũng l&agrave; những thước cảnh b&igrave;nh y&ecirc;n đến nao l&ograve;ng. @thealiphoto</p>\r\n<p><img style=\"height: auto;\" src=\"https://blog.traveloka.com/source/uploads/sites/9/2019/11/goi-y-nhung-dia-diem-du-lich-ngay-tet-o-phia-bac-21-300x300.jpg\" sizes=\"(max-width: 1024px) 100vw, 1024px\" srcset=\"https://blog.traveloka.com/source/uploads/sites/9/2019/11/goi-y-nhung-dia-diem-du-lich-ngay-tet-o-phia-bac-21-300x300.jpg 300w, https://blog.traveloka.com/source/uploads/sites/9/2019/11/goi-y-nhung-dia-diem-du-lich-ngay-tet-o-phia-bac-21-150x150.jpg 150w, https://blog.traveloka.com/source/uploads/sites/9/2019/11/goi-y-nhung-dia-diem-du-lich-ngay-tet-o-phia-bac-21-768x765.jpg 768w, https://blog.traveloka.com/source/uploads/sites/9/2019/11/goi-y-nhung-dia-diem-du-lich-ngay-tet-o-phia-bac-21-640x638.jpg 640w, https://blog.traveloka.com/source/uploads/sites/9/2019/11/goi-y-nhung-dia-diem-du-lich-ngay-tet-o-phia-bac-21-220x220.jpg 220w, https://blog.traveloka.com/source/uploads/sites/9/2019/11/goi-y-nhung-dia-diem-du-lich-ngay-tet-o-phia-bac-21-80x80.jpg 80w, https://blog.traveloka.com/source/uploads/sites/9/2019/11/goi-y-nhung-dia-diem-du-lich-ngay-tet-o-phia-bac-21-585x583.jpg 585w, https://blog.traveloka.com/source/uploads/sites/9/2019/11/goi-y-nhung-dia-diem-du-lich-ngay-tet-o-phia-bac-21.jpg 1080w\" alt=\"địa điểm du lịch tết\" /></p>\r\n<p>Mai Ch&acirc;u l&agrave; nơi c&oacute; rất nhiều resort xanh đưa bạn trở về với thi&ecirc;n nhi&ecirc;n trong l&agrave;nh. @ndnsfremantle</p>\r\n<p>Hầu hết, người ta đến Mai Ch&acirc;u để nghỉ dưỡng v&agrave; thư gi&atilde;n sau những ng&agrave;y l&agrave;m việc căng thẳng. Mai Ch&acirc;u c&oacute; những homestay v&agrave; resort cực chất, cực đẹp, ẩn m&igrave;nh s&acirc;u trong rừng xanh. Tham khảo v&agrave; đặt ngay một resort tại đ&acirc;y.</p>\r\n<p>Tham khảo:&nbsp;<a href=\"https://blog.traveloka.com/vn/kinh-nghiem-di-mai-chau-ecolodge-2-ngay-1-dem/\" target=\"_blank\" rel=\"noopener\">Kinh nghiệm đi Mai Ch&acirc;u Ecolodge 2 ng&agrave;y 1 đ&ecirc;m tận hưởng Mai Ch&acirc;u y&ecirc;n b&igrave;nh v&agrave; trong l&agrave;nh</a></p>\r\n<h2>8. Mộc Ch&acirc;u, Sơn La</h2>\r\n<p>Từ H&agrave; Nội đến Mộc Ch&acirc;u khoảng 200 km, kh&ocirc;ng qu&aacute; xa. Xu&acirc;n đến l&agrave; m&ugrave;a những c&aacute;nh đồng hoa cải trắng, hoa mận, hoa đ&agrave;o v&agrave; những đồi ch&egrave; xanh non, tươi m&aacute;t bỗng chốc đẹp hơn bao giờ hết. Đ&oacute; l&agrave; l&yacute; do m&agrave; bạn kh&ocirc;ng n&ecirc;n bỏ qua trong dịp tết năm nay.</p>\r\n<p>Ngo&agrave;i kh&iacute; hậu, cảnh đẹp thi&ecirc;n nhi&ecirc;n, Mộc Ch&acirc;u c&ograve;n nổi danh với rất nhiều n&ocirc;ng sản như sữa tươi, đ&agrave;o, mận, c&aacute;c loại rau củ, thịt rừng v&agrave; gạo nếp. Đ&acirc;y to&agrave;n l&agrave; những cực phẩm tuyệt hảo nhất từ trời đất. Đến Mộc Ch&acirc;u kh&ocirc;ng chỉ c&oacute; thỏa sức sống ảo m&agrave; c&ograve;n tha hồ thưởng thức.</p>\r\n<p>Tham khảo:&nbsp;<a href=\"https://blog.traveloka.com/vn/lich-trinh-du-lich-moc-chau-tu-tuc/\" target=\"_blank\" rel=\"noopener\">Mộc Ch&acirc;u &ndash; H&agrave;nh tr&igrave;nh 2 ng&agrave;y 2 đ&ecirc;m t&igrave;m về với thi&ecirc;n nhi&ecirc;n</a></p>\r\n<p><img style=\"height: auto;\" src=\"https://blog.traveloka.com/source/uploads/sites/9/2019/11/goi-y-nhung-dia-diem-du-lich-ngay-tet-o-phia-bac-22-240x300.jpg\" sizes=\"(max-width: 820px) 100vw, 820px\" srcset=\"https://blog.traveloka.com/source/uploads/sites/9/2019/11/goi-y-nhung-dia-diem-du-lich-ngay-tet-o-phia-bac-22-240x300.jpg 240w, https://blog.traveloka.com/source/uploads/sites/9/2019/11/goi-y-nhung-dia-diem-du-lich-ngay-tet-o-phia-bac-22-768x959.jpg 768w, https://blog.traveloka.com/source/uploads/sites/9/2019/11/goi-y-nhung-dia-diem-du-lich-ngay-tet-o-phia-bac-22-512x640.jpg 512w, https://blog.traveloka.com/source/uploads/sites/9/2019/11/goi-y-nhung-dia-diem-du-lich-ngay-tet-o-phia-bac-22-585x731.jpg 585w, https://blog.traveloka.com/source/uploads/sites/9/2019/11/goi-y-nhung-dia-diem-du-lich-ngay-tet-o-phia-bac-22.jpg 1080w\" alt=\"địa điểm du lịch tết\" /></p>\r\n<p>Trong một khoảnh khắc thật đẹp tại đồi ch&egrave; ở Mộc Ch&acirc;u. Đ&acirc;y cũng l&agrave; nơi sắc xu&acirc;n ngập tr&agrave;n đẹp nhất v&agrave; những t&iacute;n đồ sống ảo th&igrave; kh&ocirc;ng được ph&eacute;p bỏ qua. @phanhlee90</p>\r\n<p><img style=\"height: auto;\" src=\"https://blog.traveloka.com/source/uploads/sites/9/2019/11/goi-y-nhung-dia-diem-du-lich-ngay-tet-o-phia-bac-23-240x300.jpg\" sizes=\"(max-width: 819px) 100vw, 819px\" srcset=\"https://blog.traveloka.com/source/uploads/sites/9/2019/11/goi-y-nhung-dia-diem-du-lich-ngay-tet-o-phia-bac-23-240x300.jpg 240w, https://blog.traveloka.com/source/uploads/sites/9/2019/11/goi-y-nhung-dia-diem-du-lich-ngay-tet-o-phia-bac-23-768x960.jpg 768w, https://blog.traveloka.com/source/uploads/sites/9/2019/11/goi-y-nhung-dia-diem-du-lich-ngay-tet-o-phia-bac-23-512x640.jpg 512w, https://blog.traveloka.com/source/uploads/sites/9/2019/11/goi-y-nhung-dia-diem-du-lich-ngay-tet-o-phia-bac-23-585x731.jpg 585w, https://blog.traveloka.com/source/uploads/sites/9/2019/11/goi-y-nhung-dia-diem-du-lich-ngay-tet-o-phia-bac-23.jpg 1080w\" alt=\"địa điểm du lịch tết\" /></p>\r\n<p>Th&aacute;c Dải Yếm tung bọt trắng x&oacute;a, đẹp thanh tao giữa n&uacute;i rừng h&ugrave;ng vĩ. @jonnyrouse7</p>\r\n<p><img style=\"height: auto;\" src=\"https://blog.traveloka.com/source/uploads/sites/9/2019/11/goi-y-nhung-dia-diem-du-lich-ngay-tet-o-phia-bac-24-300x224.jpg\" sizes=\"(max-width: 1024px) 100vw, 1024px\" srcset=\"https://blog.traveloka.com/source/uploads/sites/9/2019/11/goi-y-nhung-dia-diem-du-lich-ngay-tet-o-phia-bac-24-300x224.jpg 300w, https://blog.traveloka.com/source/uploads/sites/9/2019/11/goi-y-nhung-dia-diem-du-lich-ngay-tet-o-phia-bac-24-768x572.jpg 768w, https://blog.traveloka.com/source/uploads/sites/9/2019/11/goi-y-nhung-dia-diem-du-lich-ngay-tet-o-phia-bac-24-640x477.jpg 640w, https://blog.traveloka.com/source/uploads/sites/9/2019/11/goi-y-nhung-dia-diem-du-lich-ngay-tet-o-phia-bac-24-585x436.jpg 585w, https://blog.traveloka.com/source/uploads/sites/9/2019/11/goi-y-nhung-dia-diem-du-lich-ngay-tet-o-phia-bac-24.jpg 1075w\" alt=\"địa điểm du lịch tết\" /></p>\r\n<p>Đường l&ecirc;n Mộc Ch&acirc;u c&aacute;ch H&agrave; Nội kh&ocirc;ng qu&aacute; xa m&agrave; c&aacute;c cung đường đi th&igrave; đẹp h&uacute;t hồn d&acirc;n m&ecirc; phượt. Nếu c&oacute; thời gian v&agrave; chắc tay l&aacute;i, bạn cũng n&ecirc;n đi thử một lần c&ugrave;ng những người bạn th&acirc;n.</p>', '26.jpg', 1000),
(7, '<p>Nắm trong tay c&aacute;ch di chuyển từ s&acirc;n bay Narita v&agrave; trung t&acirc;m thủ đ&ocirc; Tokyo như người bản xứ</p>', '<p><strong><em>Hệ thống giao th&ocirc;ng tại Nhật Bản ph&aacute;t triển rất đa dạng v&agrave; kh&aacute; phức tạp. Với những người lần đầu đến xứ sở mặt trời mọc cũng sẽ gặp nhiều kh&oacute; khăn. Từ s&acirc;n bay Narita v&agrave;o trung t&acirc;m thủ đ&ocirc; Tokyo, bạn c&oacute; thể bắt taxi, đi t&agrave;u điện ngầm hoặc xe bu&yacute;t. Mỗi loại phương tiện c&oacute; mức gi&aacute; v&agrave; c&aacute;c tiện &iacute;ch kh&aacute;c nhau. Tuy nhi&ecirc;n, điểm chung l&agrave; bạn phải nắm r&otilde; lộ tr&igrave;nh để tr&aacute;nh xảy ra hiểm lầm với b&aacute;c t&agrave;i hoặc trong l&uacute;c mua v&eacute; t&agrave;u. Một c&aacute;ch đơn giản nhất, đi đến nơi về đến chốn chỉ trong một đường thẳng, đ&oacute; l&agrave; đặt một chiếc xe đưa đ&oacute;n s&acirc;n bay Narita đến ch&iacute;nh x&aacute;c điểm m&agrave; bạn cần đến.</em></strong></p>\r\n<h2><strong>1. Đặt xe đưa đ&oacute;n s&acirc;n bay Narita tận nơi</strong></h2>\r\n<p>Đ&acirc;y l&agrave; c&aacute;ch đơn giản nhất d&agrave;nh cho c&aacute;c chị em g&agrave; mờ đường x&aacute;, lại lần đầu đặt ch&acirc;n tới Nhật Bản. C&aacute;c bạn người Việt kh&ocirc;ng quen với hệ thống t&agrave;u điện ngầm n&ecirc;n sẽ rất bối rối với c&aacute;c bảng chỉ dẫn lộ tr&igrave;nh. Đặc biệt, ga t&agrave;u l&uacute;c n&agrave;o cũng đ&ocirc;ng đ&uacute;c, t&agrave;u đến v&agrave; đi, đ&oacute;ng v&agrave; mở cửa rất nhanh đ&ocirc;i l&uacute;c khiến người ta phải cho&aacute;ng ngợp v&agrave; bối rối một l&uacute;c thật l&acirc;u.</p>\r\n<p>Tốt nhất v&agrave; tiện lợi nhất, bạn n&ecirc;n đặt một chiếc xe đưa đ&oacute;n bạn từ s&acirc;n bay v&agrave; về tới ch&iacute;nh x&aacute;c nơi m&agrave; bạn cần. Với chỉ v&agrave;i thao t&aacute;c đơn giản tr&ecirc;n điện thoại th&ocirc;ng minh hoặc m&aacute;y t&iacute;nh, bạn c&oacute; thể đặt ngay cho m&igrave;nh một cuốc xe đưa rước với lộ tr&igrave;nh hết sức linh hoạt, t&agrave;i xế đưa đ&oacute;n tận nơi, sẵn s&agrave;ng chờ bạn ngay cả khi chuyến bay c&oacute; bị thay đổi lịch. Bạn chỉ cần nhập điểm đi, điểm đến, giờ giấc chuyến bay, số lượng người đi v&agrave; lưu &yacute; về h&agrave;nh l&yacute; l&agrave; an t&acirc;m c&oacute; người đ&oacute;n.</p>\r\n<h2><strong>2. Xe bu&yacute;t limousine tại s&acirc;n bay</strong></h2>\r\n<p>Một c&aacute;ch kh&aacute;c để đi từ s&acirc;n bay Narita v&agrave;o trung t&acirc;m Tokyo hoặc ngược lại l&agrave; sử dụng xe bu&yacute;t của s&acirc;n bay. Xe &ecirc;m &aacute;i, c&oacute; wifi v&agrave; nh&acirc;n vi&ecirc;n c&oacute; thể giao tiếp được bằng tiếng Anh.</p>\r\n<p>Thời gian di chuyển sẽ v&agrave;o khoảng 100 ph&uacute;t.</p>\r\n<figure id=\"attachment_55826\"><img style=\"height: auto;\" src=\"https://blog.traveloka.com/source/uploads/sites/9/2019/10/%E1%BA%A3nh-1-2-768x576.jpg\" alt=\"đưa đ&oacute;n s&acirc;n bay narita\" width=\"1024\" height=\"768\" />\r\n<figcaption>Xe bu&yacute;t m&agrave;u cam nổi bật tại s&acirc;n bay Narita. @Traveloka</figcaption>\r\n</figure>\r\n<h2><strong>3. T&agrave;u si&ecirc;u tốc Tokyo Skyliner</strong></h2>\r\n<p>Đặc sản của c&aacute;c quốc gia ph&aacute;t triển l&agrave; hệ thống t&agrave;u si&ecirc;u tốc rất th&ocirc;ng minh bởi mạng lưới rộng khắp, kết nối c&aacute;c điểm đến của đất nước v&agrave; đặc biệt l&agrave; rất đ&uacute;ng giờ. Ngo&agrave;i ra, t&agrave;u si&ecirc;u tốc tại Nhật Bản cũng rất &ecirc;m &aacute;i, sạch sẽ, hiện đại, kh&ocirc;ng bị kẹt xe v&agrave; l&ecirc;n t&agrave;u ngủ một giấc l&agrave; đến nơi. Đ&acirc;y l&agrave; tất cả c&aacute;c ưu điểm của t&agrave;u si&ecirc;u tốc.</p>\r\n<p>Tuy nhi&ecirc;n, phương tiện giao th&ocirc;ng n&agrave;y chỉ d&agrave;nh cho những bạn đ&atilde; quen với hệ thống vận h&agrave;nh v&agrave; c&aacute;ch xem lộ tr&igrave;nh. Với những bạn lần đầu tiếp cận với t&agrave;u điện ngầm, t&agrave;u si&ecirc;u tốc, đ&acirc;y kh&ocirc;ng phải l&agrave; một lựa chọn ho&agrave;n hảo, đặc biệt khi bạn đang phải mang v&aacute;c những chiếc vali thật nặng.</p>\r\n<p>Chặng di chuyển khoảng 41 ph&uacute;t giữa s&acirc;n bay Narita v&agrave; ga Ueno tại trung t&acirc;m Tokyo m&agrave; kh&ocirc;ng dừng ở bất kỳ nh&agrave; ga n&agrave;o. T&agrave;u Tokyo Skyliner c&oacute; vận tốc 160 km/h, cabin rộng, c&oacute; khoang h&agrave;nh l&yacute; v&agrave; nội thất rất sang trọng. Giờ hoạt động từ 7:30 đến 22:00. Bạn sẽ phải trả khoảng 2,470 JPY (khoảng 530,000 VND) cho chặng 1 chiều &ndash; đi từ Ga Ueno (hoặc Nippori) đi Narita Airport v&agrave; ngược lại.</p>\r\n<p>Bạn c&oacute; thể mua v&eacute; t&agrave;u Tokyo Skyliner bằng c&aacute;c c&aacute;ch sau:</p>\r\n<p>&ndash; Mua v&eacute; trực tiếp tại Trung t&acirc;m Th&ocirc;ng tin SKYLINER &amp; KEISEI ở s&acirc;n bay Narita Terminal 1 v&agrave; Terminal 2;</p>\r\n<p>&ndash; Mua v&eacute; trực tiếp tại ga Ueno hoặc ga Nippori nếu bạn ở Tokyo;</p>\r\n<p><em>C&aacute;c ch&iacute;nh s&aacute;ch về gi&aacute; v&eacute; được t&iacute;nh như sau:</em></p>\r\n<p>&ndash; V&eacute; trẻ em &aacute;p dụng cho trẻ từ 6-11 tuổi</p>\r\n<p>&ndash; Trẻ từ 12 tuổi trở l&ecirc;n mua v&eacute; người lớn</p>\r\n<p>&ndash; Trẻ dưới 6 tuổi được miễn ph&iacute; v&eacute; (ngồi chung với bố mẹ)</p>\r\n<figure id=\"attachment_55827\"><img style=\"height: auto;\" src=\"https://blog.traveloka.com/source/uploads/sites/9/2019/10/%E1%BA%A3nh-2-2-768x384.jpg\" alt=\"Tokyo Skyliner\" width=\"1024\" height=\"512\" />\r\n<figcaption>T&agrave;u Tokyo Skyliner hiện đại v&agrave; sang trọng. @Traveloka</figcaption>\r\n</figure>\r\n<h2><strong>4. T&agrave;u si&ecirc;u tốc JR Express &ndash; NEX</strong></h2>\r\n<p>Th&ecirc;m một lựa chọn nữa về t&agrave;u si&ecirc;u tốc l&agrave; JR Express. Từ s&acirc;n bay Narita v&agrave;o trung t&acirc;m Tokyo, t&agrave;u hoạt động 8:13 chuyến đầu ti&ecirc;n v&agrave; 21:45 chuyến cuối. Ngược lại, từ Tokyo đến s&acirc;n bay Narita, t&agrave;u hoạt động 7:00 chuyến đầu v&agrave; 19:00 chuyến cuối. Mỗi chặng như vậy khoảng 60 ph&uacute;t.</p>\r\n<p>Gi&aacute; tiền của chặng khứ hồi l&agrave; 4,000 JPY (khoảng 853,000 VND). Đối với v&eacute; một chiều l&agrave; 6,040 JPY / người lớn. Th&ocirc;ng thường, người Nhật sẽ chọn Tokyo Skyliner để tiết kiệm cả chi ph&iacute; lẫn thời gian. Tuy nhi&ecirc;n, JR Express lại tiện lợi cho những ai c&oacute; nhu cầu đi về v&ugrave;ng Kawasaki hay Yokohama.</p>\r\n<p>Bạn c&oacute; thể mua v&eacute; tại:</p>\r\n<p>&ndash; Tầng hầm của nh&agrave; Ga Terminal 2, v&agrave;o quầy v&eacute; của h&atilde;ng t&agrave;u JR để mua.</p>\r\n<figure id=\"attachment_55828\"><img style=\"height: auto;\" src=\"https://blog.traveloka.com/source/uploads/sites/9/2019/10/%E1%BA%A3nh-3-2-768x540.jpg\" alt=\"t&agrave;u NEX\" width=\"1024\" height=\"720\" />\r\n<figcaption>T&agrave;u NEX đang v&agrave;o nh&agrave; ga đ&oacute;n kh&aacute;ch. @Internet</figcaption>\r\n</figure>\r\n<h2><strong>5. T&agrave;u chậm của h&atilde;ng Keisei</strong></h2>\r\n<p>Thời gian di chuyển đối với phương tiện n&agrave;y khoảng 60 ph&uacute;t đến 90 ph&uacute;t nhưng gi&aacute; v&eacute; sẽ rẻ hơn so với 2 loại t&agrave;u si&ecirc;u tốc ở tr&ecirc;n.</p>\r\n<p>Bạn c&oacute; thể mua v&eacute; t&agrave;u tại quầy v&eacute; ở tầng hầm của s&acirc;n bay Narita. Bạn c&oacute; thể chọn c&aacute;c loại t&agrave;u sau:</p>\r\n<p>&ndash; Sky Access line của h&atilde;ng Keisei: gi&aacute; khoảng 1,240 JPY (khoảng 265,000 VND), từ s&acirc;n bay Narita về Ga Ueno, thời gian chạy 60 ph&uacute;t;</p>\r\n<p>&ndash; Keisei Main line: gi&aacute; khoảng 1,030 JPY (khoảng 220,000 VND) từ s&acirc;n bay Narita về ga Ueno, thời gian 70 ph&uacute;t;</p>\r\n<p>&ndash; T&agrave;u local train của JR kết hợp với Subway hoặc c&aacute;c h&atilde;ng kh&aacute;c thời gian khoảng 70 ph&uacute;t.</p>\r\n<figure id=\"attachment_55829\"><img style=\"height: auto;\" src=\"https://blog.traveloka.com/source/uploads/sites/9/2019/10/%E1%BA%A3nh-4-2-768x575.jpg\" alt=\"đưa đ&oacute;n s&acirc;n bay narita\" width=\"1024\" height=\"766\" />\r\n<figcaption>C&aacute;c line v&agrave;o t&agrave;u đặt cạnh nhau, bạn nhớ theo d&otilde;i kỹ biển chỉ dẫn nh&eacute;! @Traveloka</figcaption>\r\n</figure>\r\n<h2><strong>6. Taxi</strong></h2>\r\n<p>Taxi bạn c&oacute; thể bắt ngay khi ra khỏi s&acirc;n bay. Nếu bạn đi theo gia đ&igrave;nh c&oacute; trẻ em v&agrave; người gi&agrave; th&igrave; taxi c&oacute; lẽ l&agrave; một lựa chọn kh&aacute; ổn, kh&ocirc;ng cần chen lấn, chờ đợi, đường đi thẳng v&agrave;o trung t&acirc;m Tokyo, đến tận nơi m&agrave; bạn muốn.</p>\r\n<p>Tuy nhi&ecirc;n, bạn biết đấy, gi&aacute; của taxi th&igrave; kh&aacute; l&agrave; ch&aacute;t. Taxi đưa đ&oacute;n s&acirc;n bay Narita v&agrave;o trung t&acirc;m Tokyo hoặc ngược lại c&oacute; gi&aacute; trung b&igrave;nh khoảng 150 USD &ndash; 200 USD (khoảng 3,5 triệu &ndash; 4,6 triệu đồng).</p>\r\n<figure id=\"attachment_55830\"><img style=\"height: auto;\" src=\"https://blog.traveloka.com/source/uploads/sites/9/2019/10/%E1%BA%A3nh-5-2-768x431.jpg\" alt=\"phương tiện đưa đ&oacute;n s&acirc;n bay narita\" width=\"1024\" height=\"575\" />\r\n<figcaption>Taxi ở c&aacute;c quốc gia ph&aacute;t triển vốn l&agrave; một phương tiện kh&aacute; đắt đỏ v&agrave; tất nhi&ecirc;n bạn c&oacute; thể gặp phải t&igrave;nh trạng kẹt xe trong c&aacute;c giờ cao điểm.</figcaption>\r\n</figure>', '27.jpg', 1000);
INSERT INTO `tin_tuc` (`id_news`, `title_news`, `desc_news`, `image`, `total_views`) VALUES
(8, '<p>Trekking săn m&acirc;y v&agrave; 4 trải nghiệm hấp dẫn chỉ c&oacute; ở Philippines</p>', '<p><img style=\"height: auto;\" title=\"Phi&ecirc;u lưu v&agrave;o hang động Sumaguing: Nếu bạn từng muốn c&ugrave;ng Nicolas Cage truy t&igrave;m &ldquo;Kho b&aacute;u Quốc gia&rdquo;, cuộc phi&ecirc;u lưu v&agrave;o hang động Sumaguing sẽ l&agrave;m thỏa m&atilde;n khao kh&aacute;t của bạn. Ảnh: Jewelclicks.\" src=\"https://cdn3.ivivu.com/2019/11/trekking-san-may-va-4-trai-nghiem-hap-dan-chi-co-o-philippines-ivivu-1-1024x735.jpg\" alt=\"Phi&ecirc;u lưu v&agrave;o hang động Sumaguing: Nếu bạn từng muốn c&ugrave;ng Nicolas Cage truy t&igrave;m &ldquo;Kho b&aacute;u Quốc gia&rdquo;, cuộc phi&ecirc;u lưu v&agrave;o hang động Sumaguing sẽ l&agrave;m thỏa m&atilde;n khao kh&aacute;t của bạn. Ảnh: Jewelclicks.\" width=\"1024\" height=\"735\" /></p>\r\n<p><strong>Phi&ecirc;u lưu v&agrave;o hang động Sumaguing:</strong>&nbsp;Nếu bạn từng muốn c&ugrave;ng Nicolas Cage truy t&igrave;m &ldquo;Kho b&aacute;u Quốc gia&rdquo;, cuộc phi&ecirc;u lưu v&agrave;o hang động Sumaguing sẽ l&agrave;m thỏa m&atilde;n khao kh&aacute;t của bạn. Ảnh: Jewelclicks.</p>\r\n<p><img style=\"height: auto;\" title=\"Kh&ocirc;ng c&oacute; đường bộ nh&acirc;n tạo hay &aacute;nh s&aacute;ng rực rỡ, những hang động tối đầy kỳ b&iacute; như Sumaguing đ&ograve;i hỏi người kh&aacute;m ph&aacute; sức bền v&agrave; &yacute; ch&iacute; cực tốt để c&oacute; thể vượt qua những phiến đ&aacute; v&ocirc;i trơn trượt, khe hở nhỏ v&agrave; v&ugrave;ng nước s&acirc;u dưới &aacute;nh đ&egrave;n đội đầu vừa đủ chiếu s&aacute;ng đường đi. Bạn sẽ thực hiện h&agrave;nh tr&igrave;nh của m&igrave;nh c&ugrave;ng hướng dẫn vi&ecirc;n chuy&ecirc;n nghiệp v&agrave; d&agrave;y kinh nghiệm. Ảnh: Outlookindia.\" src=\"https://cdn3.ivivu.com/2019/11/trekking-san-may-va-4-trai-nghiem-hap-dan-chi-co-o-philippines-ivivu-2.jpg\" alt=\"Kh&ocirc;ng c&oacute; đường bộ nh&acirc;n tạo hay &aacute;nh s&aacute;ng rực rỡ, những hang động tối đầy kỳ b&iacute; như Sumaguing đ&ograve;i hỏi người kh&aacute;m ph&aacute; sức bền v&agrave; &yacute; ch&iacute; cực tốt để c&oacute; thể vượt qua những phiến đ&aacute; v&ocirc;i trơn trượt, khe hở nhỏ v&agrave; v&ugrave;ng nước s&acirc;u dưới &aacute;nh đ&egrave;n đội đầu vừa đủ chiếu s&aacute;ng đường đi. Bạn sẽ thực hiện h&agrave;nh tr&igrave;nh của m&igrave;nh c&ugrave;ng hướng dẫn vi&ecirc;n chuy&ecirc;n nghiệp v&agrave; d&agrave;y kinh nghiệm. Ảnh: Outlookindia.\" width=\"1024\" height=\"683\" /></p>\r\n<p>Kh&ocirc;ng c&oacute; đường bộ nh&acirc;n tạo hay &aacute;nh s&aacute;ng rực rỡ, những hang động tối đầy kỳ b&iacute; như Sumaguing đ&ograve;i hỏi người kh&aacute;m ph&aacute; sức bền v&agrave; &yacute; ch&iacute; cực tốt để c&oacute; thể vượt qua những phiến đ&aacute; v&ocirc;i trơn trượt, khe hở nhỏ v&agrave; v&ugrave;ng nước s&acirc;u dưới &aacute;nh đ&egrave;n đội đầu vừa đủ chiếu s&aacute;ng đường đi. Bạn sẽ thực hiện h&agrave;nh tr&igrave;nh của m&igrave;nh c&ugrave;ng hướng dẫn vi&ecirc;n chuy&ecirc;n nghiệp v&agrave; d&agrave;y kinh nghiệm. Ảnh: Outlookindia.</p>\r\n<p><img style=\"height: auto;\" src=\"https://cdn3.ivivu.com/2019/11/trekking-san-may-va-4-trai-nghiem-hap-dan-chi-co-o-philippines-ivivu-3.jpg\" alt=\"trekking-san-may-va-4-trai-nghiem-hap-dan-chi-co-o-philippines-ivivu-3\" width=\"1024\" height=\"740\" /><img style=\"height: auto;\" src=\"https://cdn3.ivivu.com/2019/11/trekking-san-may-va-4-trai-nghiem-hap-dan-chi-co-o-philippines-ivivu-4.jpg\" alt=\"trekking-san-may-va-4-trai-nghiem-hap-dan-chi-co-o-philippines-ivivu-4\" width=\"1024\" height=\"735\" /><img style=\"height: auto;\" src=\"https://cdn3.ivivu.com/2019/11/trekking-san-may-va-4-trai-nghiem-hap-dan-chi-co-o-philippines-ivivu-5.jpg\" alt=\"trekking-san-may-va-4-trai-nghiem-hap-dan-chi-co-o-philippines-ivivu-5\" width=\"1024\" height=\"768\" /></p>\r\n<p><img style=\"height: auto;\" src=\"https://cdn3.ivivu.com/2019/11/trekking-san-may-va-4-trai-nghiem-hap-dan-chi-co-o-philippines-ivivu-6.jpg\" alt=\"trekking-san-may-va-4-trai-nghiem-hap-dan-chi-co-o-philippines-ivivu-6\" width=\"666\" height=\"404\" /></p>\r\n<p>Ngay cả người th&iacute;ch cảm gi&aacute;c cực mạnh cũng sẽ kinh ngạc v&agrave; th&iacute;ch th&uacute; với cuộc kh&aacute;m ph&aacute; hang Sumaguing. Sự hấp dẫn của hang động với những điều bạn chưa từng trải nghiệm một lần trong đời l&agrave; điều kh&oacute; c&oacute; thể cưỡng lại, d&ugrave; cho đ&oacute; l&agrave; chuyến đi với đ&ocirc;i ch&acirc;n trần tr&ecirc;n c&aacute;c tảng đ&aacute; phủ đầy ph&acirc;n dơi. Ảnh: Jewelclicks, Live.staticflickr, Daniel&rsquo;s Eco-Travels.</p>\r\n<p><img style=\"height: auto;\" title=\"Kh&aacute;m ph&aacute; thung lũng &ldquo;quan t&agrave;i treo&rdquo;: Thung lũng Echo ở v&ugrave;ng n&uacute;i Sagada, Philippines nổi tiếng với những &ldquo;quan t&agrave;i treo&rdquo; kỳ lạ. Qua nhiều thế kỷ, người Igorot trong v&ugrave;ng đ&atilde; thực hiện nghi thức ch&ocirc;n cất những người chết trong những chiếc quan t&agrave;i được treo lơ lửng dọc v&aacute;ch đ&aacute;. Thung lũng Echo kh&ocirc;ng chỉ mang lại cảm gi&aacute;c hồi hộp cho những ai đặt ch&acirc;n đến đ&acirc;y m&agrave; c&ograve;n khiến người ta đ&ocirc;i ch&uacute;t sợ h&atilde;i, r&ugrave;ng m&igrave;nh trước kh&ocirc;ng kh&iacute; ảm đạm v&agrave; ma mị. Ảnh: Straightspeak.\" src=\"https://cdn3.ivivu.com/2019/11/trekking-san-may-va-4-trai-nghiem-hap-dan-chi-co-o-philippines-ivivu-7.jpg\" alt=\"Kh&aacute;m ph&aacute; thung lũng &ldquo;quan t&agrave;i treo&rdquo;: Thung lũng Echo ở v&ugrave;ng n&uacute;i Sagada, Philippines nổi tiếng với những &ldquo;quan t&agrave;i treo&rdquo; kỳ lạ. Qua nhiều thế kỷ, người Igorot trong v&ugrave;ng đ&atilde; thực hiện nghi thức ch&ocirc;n cất những người chết trong những chiếc quan t&agrave;i được treo lơ lửng dọc v&aacute;ch đ&aacute;. Thung lũng Echo kh&ocirc;ng chỉ mang lại cảm gi&aacute;c hồi hộp cho những ai đặt ch&acirc;n đến đ&acirc;y m&agrave; c&ograve;n khiến người ta đ&ocirc;i ch&uacute;t sợ h&atilde;i, r&ugrave;ng m&igrave;nh trước kh&ocirc;ng kh&iacute; ảm đạm v&agrave; ma mị. Ảnh: Straightspeak.\" width=\"620\" height=\"432\" /></p>\r\n<p><strong>Kh&aacute;m ph&aacute; thung lũng &ldquo;quan t&agrave;i treo&rdquo;:</strong>&nbsp;Thung lũng Echo ở v&ugrave;ng n&uacute;i Sagada, Philippines nổi tiếng với những &ldquo;quan t&agrave;i treo&rdquo; kỳ lạ. Qua nhiều thế kỷ, người Igorot trong v&ugrave;ng đ&atilde; thực hiện nghi thức ch&ocirc;n cất những người chết trong những chiếc quan t&agrave;i được treo lơ lửng dọc v&aacute;ch đ&aacute;. Thung lũng Echo kh&ocirc;ng chỉ mang lại cảm gi&aacute;c hồi hộp cho những ai đặt ch&acirc;n đến đ&acirc;y m&agrave; c&ograve;n khiến người ta đ&ocirc;i ch&uacute;t sợ h&atilde;i, r&ugrave;ng m&igrave;nh trước kh&ocirc;ng kh&iacute; ảm đạm v&agrave; ma mị. Ảnh: Straightspeak.</p>\r\n<p><img style=\"height: auto;\" title=\"Ở Sagada, mặt trời mọc kh&aacute; sớm, h&agrave;nh tr&igrave;nh săn m&acirc;y của bạn c&oacute; thể bắt đầu từ l&uacute;c 4h c&ugrave;ng những người hướng dẫn l&agrave;nh nghề. Sagada với cảnh quan h&ugrave;ng vĩ v&agrave; &ldquo;biển m&acirc;y&rdquo; tr&ecirc;n đỉnh đồi Marlboro mang đến khung cảnh b&igrave;nh minh tuyệt vời. Ảnh: Pinterest.\" src=\"https://cdn3.ivivu.com/2019/11/trekking-san-may-va-4-trai-nghiem-hap-dan-chi-co-o-philippines-ivivu-8.jpg\" alt=\"Ở Sagada, mặt trời mọc kh&aacute; sớm, h&agrave;nh tr&igrave;nh săn m&acirc;y của bạn c&oacute; thể bắt đầu từ l&uacute;c 4h c&ugrave;ng những người hướng dẫn l&agrave;nh nghề. Sagada với cảnh quan h&ugrave;ng vĩ v&agrave; &ldquo;biển m&acirc;y&rdquo; tr&ecirc;n đỉnh đồi Marlboro mang đến khung cảnh b&igrave;nh minh tuyệt vời. Ảnh: Pinterest.\" width=\"1328\" height=\"747\" /></p>\r\n<p>Ở Sagada, mặt trời mọc kh&aacute; sớm, h&agrave;nh tr&igrave;nh săn m&acirc;y của bạn c&oacute; thể bắt đầu từ l&uacute;c 4h c&ugrave;ng những người hướng dẫn l&agrave;nh nghề. Sagada với cảnh quan h&ugrave;ng vĩ v&agrave; &ldquo;biển m&acirc;y&rdquo; tr&ecirc;n đỉnh đồi Marlboro mang đến khung cảnh b&igrave;nh minh tuyệt vời. Ảnh: Pinterest.</p>\r\n<p><img style=\"height: auto;\" title=\"Trekking qua những con đường rậm rạp v&agrave; lầy lội, bạn sẽ thấy xứng đ&aacute;ng khi chinh phục đến ngọn đồi Malboro. Tại đ&acirc;y, Malboro trở n&ecirc;n sống động trong những đ&aacute;m m&acirc;y mịn ngập cả v&ugrave;ng trời khi b&igrave;nh minh l&oacute; dạng. Ảnh: C.mi.\" src=\"https://cdn3.ivivu.com/2019/11/trekking-san-may-va-4-trai-nghiem-hap-dan-chi-co-o-philippines-ivivu-9-1024x766.jpg\" alt=\"Trekking qua những con đường rậm rạp v&agrave; lầy lội, bạn sẽ thấy xứng đ&aacute;ng khi chinh phục đến ngọn đồi Malboro. Tại đ&acirc;y, Malboro trở n&ecirc;n sống động trong những đ&aacute;m m&acirc;y mịn ngập cả v&ugrave;ng trời khi b&igrave;nh minh l&oacute; dạng. Ảnh: C.mi.\" width=\"1024\" height=\"766\" /></p>\r\n<p>Trekking qua những con đường rậm rạp v&agrave; lầy lội, bạn sẽ thấy xứng đ&aacute;ng khi chinh phục đến ngọn đồi Malboro. Tại đ&acirc;y, Malboro trở n&ecirc;n sống động trong những đ&aacute;m m&acirc;y mịn ngập cả v&ugrave;ng trời khi b&igrave;nh minh l&oacute; dạng. Ảnh: C.mi.</p>\r\n<p><img style=\"height: auto;\" title=\"Thưởng thức bữa s&aacute;ng tại Yoghurt House: Thường được ca ngợi l&agrave; một trong những qu&aacute;n ăn ngon nhất ở Sagada, Yoghurt House nổi tiếng v&agrave; thu h&uacute;t với m&oacute;n sữa chua nh&agrave; l&agrave;m. Tại đ&acirc;y c&oacute; cả thực đơn cho bữa trưa v&agrave; tối. Ảnh: Thepoortraveler.\" src=\"https://cdn3.ivivu.com/2019/11/trekking-san-may-va-4-trai-nghiem-hap-dan-chi-co-o-philippines-ivivu-10-1024x576.jpg\" alt=\"Thưởng thức bữa s&aacute;ng tại Yoghurt House: Thường được ca ngợi l&agrave; một trong những qu&aacute;n ăn ngon nhất ở Sagada, Yoghurt House nổi tiếng v&agrave; thu h&uacute;t với m&oacute;n sữa chua nh&agrave; l&agrave;m. Tại đ&acirc;y c&oacute; cả thực đơn cho bữa trưa v&agrave; tối. Ảnh: Thepoortraveler.\" width=\"1024\" height=\"576\" /></p>\r\n<p><strong>Thưởng thức bữa s&aacute;ng tại Yoghurt House:</strong>&nbsp;Thường được ca ngợi l&agrave; một trong những qu&aacute;n ăn ngon nhất ở Sagada, Yoghurt House nổi tiếng v&agrave; thu h&uacute;t với m&oacute;n sữa chua nh&agrave; l&agrave;m. Tại đ&acirc;y c&oacute; cả thực đơn cho bữa trưa v&agrave; tối. Ảnh: Thepoortraveler.</p>\r\n<p><img style=\"height: auto;\" title=\"Sữa chua tại Yoghurt House rất dẻo mịn v&agrave; phong ph&uacute;, nổi bật l&agrave; sữa chua mật ong thơm ngon. Đ&acirc;y l&agrave; một nơi tuyệt vời ở Sagada để bạn thưởng thức bữa s&aacute;ng trước khi bắt đầu ng&agrave;y trekking th&uacute; vị. Ảnh: WordPress.\" src=\"https://cdn3.ivivu.com/2019/11/trekking-san-may-va-4-trai-nghiem-hap-dan-chi-co-o-philippines-ivivu-11-1024x682.jpg\" alt=\"Sữa chua tại Yoghurt House rất dẻo mịn v&agrave; phong ph&uacute;, nổi bật l&agrave; sữa chua mật ong thơm ngon. Đ&acirc;y l&agrave; một nơi tuyệt vời ở Sagada để bạn thưởng thức bữa s&aacute;ng trước khi bắt đầu ng&agrave;y trekking th&uacute; vị. Ảnh: WordPress.\" width=\"1024\" height=\"682\" /></p>\r\n<p>Sữa chua tại Yoghurt House rất dẻo mịn v&agrave; phong ph&uacute;, nổi bật l&agrave; sữa chua mật ong thơm ngon. Đ&acirc;y l&agrave; một nơi tuyệt vời ở Sagada để bạn thưởng thức bữa s&aacute;ng trước khi bắt đầu ng&agrave;y trekking th&uacute; vị. Ảnh: WordPress.</p>\r\n<p><img style=\"height: auto;\" title=\"Trải nghiệm ruộng bậc thang Batad Rice v&ugrave;ng Cordillera: Batad l&agrave; một ng&ocirc;i l&agrave;ng tương đối xa, v&igrave; thế đường đến đ&acirc;y kh&aacute; kh&oacute; khăn v&agrave; th&aacute;ch thức. Bạn cần đi xe jeepney từ Sagada đến thị trấn Bontoc, rồi tiếp tục l&ecirc;n xe du lịch cỡ nhỏ kh&aacute;c di chuyển về ph&iacute;a Banaue. Khi ở trung t&acirc;m du lịch tại Banaue, bạn c&oacute; thể sắp xếp một chuyến đi đến Batad. Ảnh: Theculturetrip.\" src=\"https://cdn3.ivivu.com/2019/11/trekking-san-may-va-4-trai-nghiem-hap-dan-chi-co-o-philippines-ivivu-12.jpg\" alt=\"Trải nghiệm ruộng bậc thang Batad Rice v&ugrave;ng Cordillera: Batad l&agrave; một ng&ocirc;i l&agrave;ng tương đối xa, v&igrave; thế đường đến đ&acirc;y kh&aacute; kh&oacute; khăn v&agrave; th&aacute;ch thức. Bạn cần đi xe jeepney từ Sagada đến thị trấn Bontoc, rồi tiếp tục l&ecirc;n xe du lịch cỡ nhỏ kh&aacute;c di chuyển về ph&iacute;a Banaue. Khi ở trung t&acirc;m du lịch tại Banaue, bạn c&oacute; thể sắp xếp một chuyến đi đến Batad. Ảnh: Theculturetrip.\" width=\"1024\" height=\"574\" /></p>\r\n<p><strong>Trải nghiệm ruộng bậc thang Batad Rice v&ugrave;ng Cordillera:</strong>&nbsp;Batad l&agrave; một ng&ocirc;i l&agrave;ng tương đối xa, v&igrave; thế đường đến đ&acirc;y kh&aacute; kh&oacute; khăn v&agrave; th&aacute;ch thức. Bạn cần đi xe jeepney từ Sagada đến thị trấn Bontoc, rồi tiếp tục l&ecirc;n xe du lịch cỡ nhỏ kh&aacute;c di chuyển về ph&iacute;a Banaue. Khi ở trung t&acirc;m du lịch tại Banaue, bạn c&oacute; thể sắp xếp một chuyến đi đến Batad. Ảnh: Theculturetrip</p>\r\n<p><img style=\"height: auto;\" src=\"https://cdn3.ivivu.com/2019/11/trekking-san-may-va-4-trai-nghiem-hap-dan-chi-co-o-philippines-ivivu-13.jpg\" alt=\"trekking-san-may-va-4-trai-nghiem-hap-dan-chi-co-o-philippines-ivivu-13\" width=\"1024\" height=\"678\" /><img style=\"height: auto;\" src=\"https://cdn3.ivivu.com/2019/11/trekking-san-may-va-4-trai-nghiem-hap-dan-chi-co-o-philippines-ivivu-14.jpg\" alt=\"trekking-san-may-va-4-trai-nghiem-hap-dan-chi-co-o-philippines-ivivu-14\" width=\"1024\" height=\"678\" /><img style=\"height: auto;\" src=\"https://cdn3.ivivu.com/2019/11/trekking-san-may-va-4-trai-nghiem-hap-dan-chi-co-o-philippines-ivivu-15.jpg\" alt=\"trekking-san-may-va-4-trai-nghiem-hap-dan-chi-co-o-philippines-ivivu-15\" width=\"640\" height=\"489\" /></p>\r\n<p><img style=\"height: auto;\" src=\"https://cdn3.ivivu.com/2019/11/trekking-san-may-va-4-trai-nghiem-hap-dan-chi-co-o-philippines-ivivu-16.jpg\" alt=\"trekking-san-may-va-4-trai-nghiem-hap-dan-chi-co-o-philippines-ivivu-16\" width=\"640\" height=\"424\" /></p>\r\n<p>Du lịch Philippines, đến Batad, bạn sẽ c&oacute; cơ hội t&igrave;m hiểu về ruộng bậc thang v&agrave; c&aacute;c phong tục trong l&agrave;ng. Đ&acirc;y cũng l&agrave; nơi cho bạn tha hồ trải nghiệm với nhiều hoạt động đa dạng của người bản xứ như mặc quần &aacute;o truyền thống, thu hoạch l&uacute;a v&agrave; dệt bằng tay. Ảnh: Avianquests.</p>\r\n<p><img style=\"height: auto;\" src=\"https://cdn3.ivivu.com/2019/11/trekking-san-may-va-4-trai-nghiem-hap-dan-chi-co-o-philippines-ivivu-17.jpg\" alt=\"trekking-san-may-va-4-trai-nghiem-hap-dan-chi-co-o-philippines-ivivu-17\" width=\"1024\" height=\"678\" /></p>\r\n<p>Batad cũng từng l&agrave; một trong những điểm quay phim &ldquo;Cuộc chiến v&ocirc; cực&rdquo;. Với những đặc sắc về văn h&oacute;a v&agrave; địa l&yacute;, Batad nhất định l&agrave; địa điểm kh&ocirc;ng l&agrave;m bạn thất vọng trong h&agrave;nh tr&igrave;nh đến<strong>&nbsp;du lịch Philippines</strong>&nbsp;của m&igrave;nh.</p>', '29.jpg', 0),
(9, '<p>Top 15 m&oacute;n ngon Ph&uacute; Quốc bạn nhất định phải thử</p>', '<p><strong>Ghẹ H&agrave;m Ninh</strong></p>\r\n<p>Du lịch Ph&uacute; Quốc bạn kh&ocirc;ng thể bỏ qua m&oacute;n ghẹ H&agrave;m Ninh tươi ngon. Ghẹ H&agrave;m Ninh kh&aacute; nhỏ nhưng thịt chắc, ăn rất thơm, vị ngọt đậm đ&agrave;. Người d&acirc;n ở đ&acirc;y khuy&ecirc;n rằng bắt ghẹ tươi sống l&ecirc;n đem luộc ngay, ăn c&ugrave;ng với muối ti&ecirc;u chanh l&agrave; ngon nhất, như vậy mới thưởng thức hết được vị ngọt v&agrave; chắc của thịt ghẹ.</p>\r\n<p><img style=\"height: auto;\" src=\"https://cdn3.ivivu.com/2019/11/nguyen_1.jpg\" alt=\"nguyen_1\" width=\"1024\" height=\"820\" /></p>\r\n<p><em>Địa chỉ:&nbsp;</em></p>\r\n<p><em>- Nh&agrave; h&agrave;ng Biển Xanh ở tỉnh Lộ 47, H&agrave;m Ninh, Ph&uacute; Quốc, Ki&ecirc;n Giang</em></p>\r\n<p><em>- Qu&aacute;n Ghẹ H&agrave;m Ninh &ndash; Muối Ti&ecirc;u ở 112 Bạch Đằng, Ph&uacute; Quốc, Ki&ecirc;n Giang</em></p>\r\n<p><strong>Hải s&acirc;m</strong></p>\r\n<p>Ở v&ugrave;ng biển Ph&uacute; Quốc c&oacute; nhiều hải s&acirc;m m&agrave; nh&acirc;n d&acirc;n gọi l&agrave; &ldquo;đồn đột&rdquo; hoặc &ldquo;đột ngậu&rdquo;. Hải s&acirc;m l&agrave; m&oacute;n hải sản được ưa th&iacute;ch v&igrave; c&oacute; độ dinh dưỡng cao. Những m&oacute;n ngon chế biến từ hải s&acirc;m Ph&uacute; Quốc c&oacute; nhiều như: hải s&acirc;m x&agrave;o rau củ, hải s&acirc;m tiềm chim c&uacute;t, hải s&acirc;m nấu đu đủ, hải s&acirc;m x&agrave;o cải th&igrave;a&hellip; đặc biệt phổ biến v&agrave; được nhiều thực kh&aacute;ch ưa d&ugrave;ng l&agrave; ch&aacute;o hải s&acirc;m.</p>\r\n<p><a href=\"https://cdn3.ivivu.com/2019/11/top-15-mon-ngon-phu-quoc-ngon-kho-cuong-ivivu-4.jpg\"><img style=\"height: auto;\" src=\"https://cdn3.ivivu.com/2019/11/top-15-mon-ngon-phu-quoc-ngon-kho-cuong-ivivu-4.jpg\" alt=\"top-15-mon-ngon-phu-quoc-ngon-kho-cuong-ivivu-4\" width=\"640\" height=\"366\" /></a></p>\r\n<p><em>Địa chỉ: X&atilde; Dương Tơ, Ph&uacute; Quốc</em></p>\r\n<p><strong>Tiết canh cua</strong></p>\r\n<p>Tiết canh cua l&agrave; m&oacute;n ăn của đặc sản của người d&acirc;n miền biển. Cua l&agrave;m tiết canh nhất thiết phải l&agrave; cua biển, n&ecirc;n lựa chọn những con nhiều gạch để m&oacute;n tiết canh cua của bạn mới ngon ngọt v&agrave; đầy bổ dưỡng khi ăn.</p>\r\n<p><a href=\"https://cdn3.ivivu.com/2019/11/top-15-mon-ngon-phu-quoc-ngon-kho-cuong-ivivu-23.png\"><img style=\"height: auto;\" src=\"https://cdn3.ivivu.com/2019/11/top-15-mon-ngon-phu-quoc-ngon-kho-cuong-ivivu-23.png\" alt=\"top-15-mon-ngon-phu-quoc-ngon-kho-cuong-ivivu-23\" width=\"640\" height=\"400\" /></a></p>\r\n<p>Thường một đĩa tiết canh cua nho nhỏ, cũng cần đến v&agrave;i con cua lớn. Người ta sẽ lấy ri&ecirc;ng một con, luộc l&ecirc;n, rồi lấy thịt cua, ướp gia vị để sẵn ra đĩa. Những con cua c&ograve;n lại được lấy tiết, rồi gạch cua đổ v&agrave;o đĩa thịt cua chờ cho đến khi đ&ocirc;ng lại th&agrave;nh tiết canh. Thưởng thức m&oacute;n tiết canh cua Ph&uacute; Quốc sẽ c&oacute; vị hơi l&agrave; lạ nhưng lại v&ocirc; c&ugrave;ng đặc biệt. Phần thịt cua mềm lẫn với tiết cua, c&ugrave;ng với vị ngọt b&eacute;o của gạch cua, mang lại vị mằn mặn, ng&ograve;n ngọt, ngon kh&oacute; tả.</p>\r\n<p><em>Địa chỉ: Qu&aacute;n Ra Khơi &ndash; 131 đường 30/4, TT Dương Đ&ocirc;ng</em></p>\r\n<p><strong>Ốc gai nướng mỡ h&agrave;nh</strong></p>\r\n<p>Ốc gai c&oacute; nhiều tại b&atilde;i Khem, b&atilde;i Sao Ph&uacute; Quốc. Ốc gai c&oacute; nhiều c&aacute;ch chế biến kh&aacute;c nhau như: nướng, luộc, x&agrave;o tỏi, hấp gừng, trộn gỏi, nhưng m&oacute;n ăn được ưa chuộng hơn cả l&agrave; ốc gai nướng mỡ h&agrave;nh. M&oacute;n ốc gai nướng c&oacute; m&ugrave;i vị thơm ngon độc đ&aacute;o. Thịt ốc c&oacute; m&agrave;u trắng đục, to tr&ograve;n, cho v&agrave;o miệng nhai từ từ sẽ cảm thấy ốc vừa b&eacute;o, vừa dai dai, gi&ograve;n gi&ograve;n, hương vị đậm đ&agrave;, k&iacute;ch th&iacute;ch vị gi&aacute;c của bất kỳ du kh&aacute;ch n&agrave;o.</p>\r\n<p><a href=\"https://cdn3.ivivu.com/2019/11/top-15-mon-ngon-phu-quoc-ngon-kho-cuong-ivivu-22.jpg\"><img style=\"height: auto;\" src=\"https://cdn3.ivivu.com/2019/11/top-15-mon-ngon-phu-quoc-ngon-kho-cuong-ivivu-22.jpg\" alt=\"top-15-mon-ngon-phu-quoc-ngon-kho-cuong-ivivu-22\" width=\"640\" height=\"436\" /></a></p>\r\n<p><em>Địa chỉ:&nbsp;</em></p>\r\n<p><em>- Qu&aacute;n Ngh&ecirc;u S&ograve; Ốc Hến &ndash; Đường 30/4, TT Dương Đ&ocirc;ng</em></p>\r\n<p><em>- Ốc 343 &ndash; 343 Đường 30/4, KP 1, TT Dương Đ&ocirc;ng, Cầu gai Ph&uacute; Quốc</em></p>\r\n<p><strong>Nhum biển</strong></p>\r\n<p>Nhum biển hay c&ograve;n gọi l&agrave; cầu gai hoặc nh&iacute;m biển, rất bổ v&agrave; qu&yacute; hiếm. Người d&acirc;n địa phương v&agrave; du kh&aacute;ch thường ăn nhum biển sống với m&ugrave; tạt. Thịt v&agrave; trứng nhum vừa ngọt, vừa b&eacute;o b&eacute;o v&agrave; thơm như gạch cua bể. Nhum ngo&agrave;i ăn sống th&igrave; c&ograve;n c&oacute; thể nấu ch&aacute;o, nướng than lửa hồng với mỡ h&agrave;nh, chế biển kiểu n&agrave;o cũng thu h&uacute;t những thực kh&aacute;ch đến Ph&uacute; Quốc.</p>\r\n<p><a href=\"https://cdn3.ivivu.com/2019/11/top-15-mon-ngon-phu-quoc-ngon-kho-cuong-ivivu-14.jpg\"><img style=\"height: auto;\" src=\"https://cdn3.ivivu.com/2019/11/top-15-mon-ngon-phu-quoc-ngon-kho-cuong-ivivu-14.jpg\" alt=\"top-15-mon-ngon-phu-quoc-ngon-kho-cuong-ivivu-14\" width=\"640\" height=\"427\" /></a></p>\r\n<p><em>Địa chỉ:&nbsp;</em></p>\r\n<p><em>- Ốc 343 &ndash; 343 Đường 30/4, KP 1, TT Dương Đ&ocirc;ng</em></p>\r\n<p><em>- Qu&aacute;n hải sản Nhớ &ndash; Nằm trong chợ đ&ecirc;m Dinh Cậu &ndash; V&otilde; Thị S&aacute;u</em></p>\r\n<p><em>- Qu&aacute;n Biển Xanh &ndash; Cầu H&agrave;m Ninh, TT H&agrave;m Ninh</em></p>\r\n<p><strong>Nấm tr&agrave;m</strong></p>\r\n<p>Khi đến Ph&uacute; Quốc th&igrave; bạn đừng n&ecirc;n bỏ lỡ cơ hội thưởng thức m&oacute;n nấm tr&agrave;m c&oacute; một kh&ocirc;ng hai n&agrave;y, loại nấm đặc sản ri&ecirc;ng của Đảo Ngọc. Nấm tr&agrave;m c&oacute; m&agrave;u t&iacute;m, ph&aacute;t triển trong v&ograve;ng khoảng 1 th&aacute;ng n&ecirc;n ch&uacute;ng &iacute;t c&oacute; mặt tr&ecirc;n thị trường như những loại nấm kh&aacute;c.</p>\r\n<p><a href=\"https://cdn3.ivivu.com/2019/11/top-15-mon-ngon-phu-quoc-ngon-kho-cuong-ivivu-10.jpg\"><img style=\"height: auto;\" src=\"https://cdn3.ivivu.com/2019/11/top-15-mon-ngon-phu-quoc-ngon-kho-cuong-ivivu-10.jpg\" alt=\"top-15-mon-ngon-phu-quoc-ngon-kho-cuong-ivivu-10\" width=\"640\" height=\"457\" /></a></p>\r\n<p>Người d&acirc;n tr&ecirc;n đảo đ&atilde; kết hợp một c&aacute;ch h&agrave;i h&ograve;a giữa nấm tr&agrave;m với hải sản tươi tr&ecirc;n đảo như: Mực, t&ocirc;m, h&agrave;o bao&hellip; tạo ra m&oacute;n canh nấm tr&agrave;m tuyệt hảo, m&ugrave;i vị thơm ngon, hấp dẫn vừa l&agrave; m&oacute;n ăn bổ dưỡng. Thưởng thức m&oacute;n canh nấm tr&agrave;m bạn sẽ cảm nhận được vị ngọt của nấm, thơm nồng của ti&ecirc;u v&agrave; đậm đ&agrave; hơn khi bạn d&ugrave;ng chung với nước mắm c&aacute; cơm Ph&uacute; Quốc.</p>\r\n<p><em>Địa chỉ:&nbsp;</em></p>\r\n<p><em>- Hải sản Ng&acirc;n H&agrave; &ndash; đường Trần Hưng Đạo (đối diện showroom ngọc trai Ngọc Hiền)</em></p>\r\n<p><em>- Bi&ecirc;n Hải Qu&aacute;n&nbsp; &ndash; Hải Sản Bờ Biển &ndash; Tổ 1, Ấp G&agrave;nh Dầu, X&atilde; G&agrave;nh Dầu</em></p>\r\n<p><strong>Gỏi c&aacute; tr&iacute;ch</strong></p>\r\n<p>M&oacute;n gỏi c&aacute; tr&iacute;ch l&agrave; m&oacute;n ăn mang đậm hương vị của v&ugrave;ng biển đảo Ph&uacute; Quốc được nhiều người ưa th&iacute;ch. Sự pha trộn h&agrave;i h&ograve;a giữa thịt c&aacute; c&ograve;n tươi sống với c&aacute;c nguy&ecirc;n liệu h&agrave;nh t&acirc;y, h&agrave;nh t&iacute;m, dừa nạo&hellip; đem lại m&oacute;n ăn mang đậm đ&agrave; hương vị biển. Chế biến gỏi c&aacute; tr&iacute;ch rất kỳ c&ocirc;ng, bạn phải chọn những con c&aacute; nhiều thịt, c&ograve;n tươi sống mới cho thịt b&eacute;o ngọt khi ăn. Trộn đều c&aacute;c nguy&ecirc;n liệu đ&oacute; với c&aacute; tr&iacute;ch c&ugrave;ng nước sốt chua. Đặc biệt kh&ocirc;ng thể thiếu ch&eacute;n nước chấm được l&agrave;m từ ớt, tỏi v&agrave; đậu phộng rang.</p>\r\n<p><a href=\"https://cdn3.ivivu.com/2019/11/top-15-mon-ngon-phu-quoc-ngon-kho-cuong-ivivu-5.jpg\"><img style=\"height: auto;\" src=\"https://cdn3.ivivu.com/2019/11/top-15-mon-ngon-phu-quoc-ngon-kho-cuong-ivivu-5.jpg\" alt=\"top-15-mon-ngon-phu-quoc-ngon-kho-cuong-ivivu-5\" width=\"640\" height=\"427\" /></a></p>\r\n<p><em>Địa chỉ:&nbsp;</em></p>\r\n<p><em>- Nh&agrave; h&agrave;ng Vườn T&aacute;o &ndash; Cửa Lấp, x&atilde; Dương Tơ, Ph&uacute; Quốc</em></p>\r\n<p><em>- Nh&agrave; h&agrave;ng Tr&ugrave;ng Dương Marina &ndash; Đường 30/4, Dương Đ&ocirc;ng, Ph&uacute; Quốc</em></p>\r\n<p><strong>C&aacute; m&uacute; nướng mọi</strong></p>\r\n<p>C&aacute; m&uacute; nướng mọi trở th&agrave;nh một trong những&nbsp;<strong>m&oacute;n ngon Ph&uacute; Quốc</strong>&nbsp;với hương vị thơm ngon ri&ecirc;ng của đảo. Để l&agrave;m được m&oacute;n nướng n&agrave;y, người ta sẽ chọn c&aacute; m&uacute; c&ograve;n tươi nguy&ecirc;n mới đ&aacute;nh bắt, l&agrave;m sạch, nướng trui trong than hồng. Khi vừa ch&iacute;n tới c&oacute; m&ugrave;i thơm v&ocirc; c&ugrave;ng hấp dẫn, từng l&aacute;t thịt c&aacute; trắng phau, n&oacute;ng hổi, dai dai, săn chắc mang hương vị mằn mặn của biển cả, lớp da mỏng gi&ograve;n gi&ograve;n vẫn c&ograve;n phảng phất m&ugrave;i kh&oacute;i kết hợp với ch&uacute;t nước mắm Ph&uacute; Quốc lừng danh c&agrave;ng tăng th&ecirc;m hương vị đậm đ&agrave;.</p>\r\n<p><a href=\"https://cdn3.ivivu.com/2019/11/top-15-mon-ngon-phu-quoc-ngon-kho-cuong-ivivu-15.jpg\"><img style=\"height: auto;\" src=\"https://cdn3.ivivu.com/2019/11/top-15-mon-ngon-phu-quoc-ngon-kho-cuong-ivivu-15.jpg\" alt=\"top-15-mon-ngon-phu-quoc-ngon-kho-cuong-ivivu-15\" width=\"640\" height=\"412\" /></a><em>Địa chỉ:&nbsp;</em></p>\r\n<p><em>- Nh&agrave; h&agrave;ng s&ocirc;ng Xanh &ndash; đường 30/4, thị trấn Dương Đ&ocirc;ng</em></p>\r\n<p><em>- Nh&agrave; h&agrave;ng Tr&ugrave;ng Dương &ndash; đường 30/4, thị trấn Dương Đ&ocirc;ng</em></p>\r\n<p><em>- Nh&agrave; h&agrave;ng Thịnh Ph&aacute;t &ndash; đường 30/4, thị trấn Dương Đ&ocirc;ng</em></p>\r\n<p><strong>C&ograve;i bi&ecirc;n mai nướng muối ớt</strong></p>\r\n<p>C&ograve;i bi&ecirc;n mai nướng muối ớt l&agrave; một trong những m&oacute;n ăn đặc sản nổi tiếng nhất tại Ph&uacute; Quốc. Bi&ecirc;n mai l&agrave; loại s&ograve; biển h&igrave;nh tam gi&aacute;c, to cỡ mu b&agrave;n tay người lớn, ẩn s&acirc;u dưới đ&aacute;y biển. C&ograve;i được nướng tr&ecirc;n than th&igrave; mới thấm v&agrave; ch&iacute;n gi&ograve;n. Để m&oacute;n ăn đậm đ&agrave; hơn, thực kh&aacute;ch c&oacute; thể rưới nước chao ướp l&ecirc;n c&ograve;i tr&ecirc;n vỉ nướng, chao sẽ thấm nhiều v&agrave;o c&ograve;i cho vị thơm v&agrave; ăn vừa miệng hơn. Để thưởng thức hết vị ngon của n&oacute;, thực kh&aacute;ch chỉ n&ecirc;n chấm chao v&agrave; ăn c&ugrave;ng với rau h&uacute;ng, diếp c&aacute;, x&agrave; l&aacute;ch, chuối ch&aacute;t, dưa leo v&agrave; kh&oacute;m. Vị ngọt của c&ograve;i bi&ecirc;n mai h&ograve;a quyện c&ugrave;ng một ch&uacute;t vị mặn của muối, một ch&uacute;t cay của ớt tạo n&ecirc;n hương vị khiến bất kỳ ai cũng nhớ tới.</p>\r\n<p><a href=\"https://cdn3.ivivu.com/2019/11/top-15-mon-ngon-phu-quoc-ngon-kho-cuong-ivivu-20.png\"><img style=\"height: auto;\" src=\"https://cdn3.ivivu.com/2019/11/top-15-mon-ngon-phu-quoc-ngon-kho-cuong-ivivu-20.png\" alt=\"top-15-mon-ngon-phu-quoc-ngon-kho-cuong-ivivu-20\" width=\"640\" height=\"427\" /></a></p>\r\n<p><em>Địa chỉ:&nbsp;</em></p>\r\n<p><em>- Nh&agrave; h&agrave;ng S&ocirc;ng Xanh &ndash; Đường 30/4, Khu Phố 1, Thị trấn Dương Đ&ocirc;ng</em></p>\r\n<p><em>- Ốc 343 &ndash; 343 Đường 30/4, KP 1, Thị trấn Dương Đ&ocirc;ng</em></p>\r\n<p><strong>B&agrave;o ngư</strong></p>\r\n<p>Ở Ph&uacute; Quốc, b&agrave;o ngư tươi sống c&oacute; thể chế biến được nhiều m&oacute;n ăn d&acirc;n d&atilde; như luộc, nướng, x&agrave;o, nấu ch&aacute;o&hellip; Trong đ&oacute; hấp dẫn nhất l&agrave; b&agrave;o ngư nướng lửa than. Thịt b&agrave;o ngư gi&ograve;n gi&ograve;n, căng mọng v&agrave; đậm đ&agrave;. Đ&acirc;y cũng l&agrave; m&oacute;n ăn được nhiều du kh&aacute;ch thưởng thức một lần cho đ&atilde; để biết được hương vị thơm ngon từ biển cả. C&ograve;n g&igrave; tuyệt vời khi thưởng thức m&oacute;n ăn l&uacute;c c&ograve;n n&oacute;ng, bạn mới cảm nhận được c&aacute;i ngon, c&aacute;i ngọt của đặc sản v&ugrave;ng biển.</p>\r\n<p><a href=\"https://cdn3.ivivu.com/2019/11/top-15-mon-ngon-phu-quoc-ngon-kho-cuong-ivivu-11.jpg\"><img style=\"height: auto;\" src=\"https://cdn3.ivivu.com/2019/11/top-15-mon-ngon-phu-quoc-ngon-kho-cuong-ivivu-11.jpg\" alt=\"top-15-mon-ngon-phu-quoc-ngon-kho-cuong-ivivu-11\" width=\"640\" height=\"640\" /></a></p>\r\n<p><em>Địa chỉ:&nbsp;</em></p>\r\n<p><em>- Qu&aacute;n Ngh&ecirc;u S&ograve; Ốc Hến &ndash; Đường 30/4, Dương Đ&ocirc;ng, Ph&uacute; Quốc</em></p>\r\n<p><em>- Qu&aacute;n Ngh&ecirc;u S&ograve; Ph&uacute; Quốc &ndash; Đường 30/4, Dương Đ&ocirc;ng, Ph&uacute; Quốc</em></p>\r\n<p><em>- Qu&aacute;n hải sản Nhớ &ldquo;nằm trong chợ đ&ecirc;m Dinh Cậu&rdquo; &ndash; V&otilde; Thị S&aacute;u, Ph&uacute; Quốc</em></p>\r\n<p><strong>B&aacute;nh t&eacute;t mật cật</strong></p>\r\n<p>B&aacute;nh t&eacute;t mật cật l&agrave; một trong những đặc sản của Ph&uacute; Quốc. B&aacute;nh c&oacute; m&agrave;u xanh ngọc b&iacute;ch của m&agrave;u l&aacute; mật cật, c&oacute; m&ugrave;i thơm đặc biệt c&ugrave;ng với m&ugrave;i thơm của l&aacute; dứa l&agrave;m tăng th&ecirc;m m&ugrave;i thơm của b&aacute;nh. Khi ăn, gạo quyện với nh&acirc;n đậu xanh b&ugrave;i, thịt mỡ b&eacute;o, rất tuyệt vời. Những nguy&ecirc;n liệu quan trọng để l&agrave;m n&ecirc;n b&aacute;nh t&eacute;t bao gồm: gạo nếp, đậu xanh v&agrave; thịt lợn, giống với l&agrave;m b&aacute;nh chưng.</p>\r\n<p><a href=\"https://cdn3.ivivu.com/2019/11/top-15-mon-ngon-phu-quoc-ngon-kho-cuong-ivivu-7.jpg\"><img style=\"height: auto;\" title=\"Ảnh: @chirs.dinh\" src=\"https://cdn3.ivivu.com/2019/11/top-15-mon-ngon-phu-quoc-ngon-kho-cuong-ivivu-7.jpg\" alt=\"Ảnh: @chirs.dinh\" width=\"640\" height=\"484\" /></a></p>\r\n<p>Ảnh: @chirs.dinh</p>\r\n<p><em>Địa chỉ: H&agrave;m Ninh Ph&uacute; Quốc</em></p>\r\n<p><strong>Cơm ghẹ</strong></p>\r\n<p>Bắt nguồn từ m&oacute;n ăn d&acirc;n d&atilde; của ngư d&acirc;n Ph&uacute; Quốc, cơm ghẹ Ph&uacute; Quốc trở th&agrave;nh m&oacute;n ăn hấp dẫn du kh&aacute;ch khi tới đ&acirc;y. C&aacute;ch chế biến của m&oacute;n ăn n&agrave;y cũng kh&ocirc;ng qu&aacute; kh&oacute;, cơm nấu ch&iacute;n để nguội, ghẹ luộc vừa ch&iacute;n lấy thịt x&agrave;o tỏi với nước mắm v&agrave; n&ecirc;m một ch&uacute;t gia vị. Cơm sau khi đ&atilde; se th&igrave; đảo c&ugrave;ng tỏi phi rồi đổ thịt ghẹ, trứng x&agrave;o n&ecirc;m gia vị rắc h&agrave;nh ng&ograve; l&ecirc;n tr&ecirc;n. Nếu thưởng thức cơm ghẹ l&uacute;c n&oacute;ng c&ugrave;ng với dưa chua v&agrave; nước mắm ớt chua ngọt sẽ khiến bạn nhớ m&atilde;i kh&ocirc;ng qu&ecirc;n.</p>\r\n<p><a href=\"https://cdn3.ivivu.com/2019/11/top-15-mon-ngon-phu-quoc-ngon-kho-cuong-ivivu-19.jpg\"><img style=\"height: auto;\" src=\"https://cdn3.ivivu.com/2019/11/top-15-mon-ngon-phu-quoc-ngon-kho-cuong-ivivu-19.jpg\" alt=\"top-15-mon-ngon-phu-quoc-ngon-kho-cuong-ivivu-19\" width=\"640\" height=\"427\" /></a></p>\r\n<p><em>Địa chỉ:</em></p>\r\n<p><em>- Nh&agrave; h&agrave;ng Tr&ugrave;ng Dương Marina &ndash; Đường 30/4, Dương Đ&ocirc;ng</em></p>\r\n<p><em>- Qu&aacute;n Hải Sản Ra Khơi &ndash; Số 131 Bis, Đường 30/4, Dương Đ&ocirc;ng</em></p>\r\n<p><strong>B&aacute;nh canh chả c&aacute;</strong></p>\r\n<p>B&aacute;nh canh chả c&aacute; l&agrave;&nbsp;<em><strong>m&oacute;n ngon Ph&uacute; Quốc</strong></em>&nbsp;nổi tiếng m&agrave; bạn kh&ocirc;ng thể bỏ lỡ. B&aacute;nh canh tại đ&acirc;y chỉ sử dụng c&aacute; thu tươi ngon do ngư d&acirc;n đảo đ&aacute;nh bắt được. C&aacute; thu ngo&agrave;i được nấu tươi, c&ograve;n được l&agrave;m chả c&aacute; chi&ecirc;n, tất cả sẽ c&oacute; trong một t&ocirc; b&aacute;nh canh thơm phức, say l&ograve;ng người. Thưởng thức m&oacute;n ăn n&agrave;y, bạn sẽ được chủ qu&aacute;n hướng dẫn c&aacute;ch vắt chanh để m&oacute;n b&aacute;nh canh c&aacute; thu được ho&agrave;n hảo nhất. Bạn sẽ ngửa miếng chanh l&ecirc;n, cho phần vỏ chanh xuống dưới, vắt chanh theo c&aacute;ch n&agrave;y vừa c&oacute; vị chua thơm của nước cốt chanh lại c&oacute; vị the của vỏ chanh.</p>\r\n<p><em>Địa chỉ:&nbsp;</em></p>\r\n<p><em>- B&aacute;nh Canh Phụng Ph&uacute; Quốc, Nguyễn Trung Trực, Ph&uacute; Quốc</em></p>\r\n<p><em>- Qu&aacute;n ăn L&ecirc; Giang &ndash; 289 Trần Hưng Đạo, Dương Đ&ocirc;ng, Ph&uacute; Quốc</em></p>\r\n<p><em>- Qu&aacute;n b&aacute;nh canh chả c&aacute; Quốc Anh- Đường 30/4, Dương Đ&ocirc;ng, Ph&uacute; Quốc</em></p>\r\n<p><strong>B&uacute;n k&egrave;n</strong></p>\r\n<p>B&uacute;n K&egrave;n l&agrave; một trong những&nbsp;<em>m&oacute;n ngon Ph&uacute; Quốc</em>&nbsp;bạn kh&ocirc;ng thể bỏ qua. T&ocirc; b&uacute;n k&egrave;n hấp dẫn du kh&aacute;ch với nguy&ecirc;n liệu v&ocirc; c&ugrave;ng phong ph&uacute;: sắc trắng của b&uacute;n, gi&aacute;, xanh của rau, đỏ của ớt, v&agrave;ng cam của đu đủ b&agrave;o v&agrave; v&agrave;ng của nước l&egrave;o, c&aacute; xay nhuyễn, c&ugrave;ng với vị b&eacute;o của nước cốt dừa, ngọt đậm đ&agrave; của c&aacute;. Du kh&aacute;ch c&oacute; thể t&ugrave;y &yacute; n&ecirc;m nếm gia vị chua ngọt pha sẵn sao cho hợp khẩu vị.</p>\r\n<p><a href=\"https://cdn3.ivivu.com/2019/11/top-15-mon-ngon-phu-quoc-ngon-kho-cuong-ivivu-6.jpg\"><img style=\"height: auto;\" title=\"Ảnh: @thon.foodie\" src=\"https://cdn3.ivivu.com/2019/11/top-15-mon-ngon-phu-quoc-ngon-kho-cuong-ivivu-6.jpg\" alt=\"Ảnh: @thon.foodie\" width=\"640\" height=\"640\" /></a></p>\r\n<p>Ảnh: @thon.foodie</p>\r\n<p><em>Địa chỉ: &Uacute;t Lượm- 87 Đường 30 Th&aacute;ng 4, Ph&uacute; Quốc</em></p>\r\n<p><strong>Rượu sim</strong></p>\r\n<p>Du lịch Ph&uacute; Quốc kh&ocirc;ng thể kh&ocirc;ng mua đặc sản rượu sim về l&agrave;m qu&agrave; cho bạn b&egrave; v&agrave; người th&acirc;n. Rượu sim được l&agrave;m từ tr&aacute;i sim, c&acirc;y sim c&oacute; ở miền Bắc Việt Nam v&agrave; đảo Ph&uacute; Quốc l&agrave; nhiều nhất. Rượu sim Ph&uacute; Quốc được l&agrave;m từ loại hồng Sim v&igrave; hồng Sim c&oacute; m&ugrave;i thơm hơn, ngọt ch&aacute;t, c&oacute; sắc tố antoxyanozit, tanin, đường nhiều hơn tiểu sim. Khi uống, bạn sẽ cảm nhận được m&ugrave;i thơm của tr&aacute;i sim k&egrave;m vị ngọt nhẹ v&agrave; ch&aacute;t.</p>\r\n<p><a href=\"https://cdn3.ivivu.com/2019/11/top-15-mon-ngon-phu-quoc-ngon-kho-cuong-ivivu-18.jpg\"><img style=\"height: auto;\" src=\"https://cdn3.ivivu.com/2019/11/top-15-mon-ngon-phu-quoc-ngon-kho-cuong-ivivu-18.jpg\" alt=\"top-15-mon-ngon-phu-quoc-ngon-kho-cuong-ivivu-18\" width=\"640\" height=\"353\" /></a><em>Địa chỉ:&nbsp;</em></p>\r\n<p><em>- Rượu sim Bảy G&aacute;o &ndash; 124 Đường 30/4, KP1, Thị trấn Dương Đ&ocirc;ng, Ph&uacute; Quốc</em></p>\r\n<p><em>- Rượu Sim Sơn&nbsp; &ndash; Ấp Cửa Lấp, X&atilde; Dương Tơ, Ph&uacute; Quốc</em></p>', '30.jpg', 0),
(10, '<p>Kh&aacute;m ph&aacute; B&igrave;nh Bi&ecirc;n &ndash; Cổ trấn b&igrave;nh y&ecirc;n ở Trung Quốc</p>', '<p><strong>B&igrave;nh Bi&ecirc;n</strong>&nbsp;l&agrave; một huyện d&acirc;n tộc tự trị người Mi&ecirc;u, c&aacute;ch H&agrave; Khẩu khoảng 100km thuộc Ch&acirc;u Hồng H&agrave;, huyện V&acirc;n Nam, Trung Quốc. Đến đ&acirc;y ngo&agrave;i được chi&ecirc;m ngưỡng phong cảnh tuyệt đẹp th&igrave; bạn c&ograve;n được t&igrave;m hiểu về văn h&oacute;a, phong tục của người Mi&ecirc;u v&ocirc; c&ugrave;ng độc đ&aacute;o.</p>\r\n<p><a href=\"https://cdn3.ivivu.com/2019/11/binh-bien-o-trung-quoc-ivivu-9.jpg\" target=\"_blank\" rel=\"noopener\"><img style=\"height: auto;\" title=\"Ảnh: Huyen Nguyen\" src=\"https://cdn3.ivivu.com/2019/11/binh-bien-o-trung-quoc-ivivu-9.jpg\" alt=\"Ảnh: Huyen Nguyen\" width=\"959\" height=\"720\" /></a></p>\r\n<p>Ảnh: Huyen Nguyen</p>\r\n<p><a href=\"https://cdn3.ivivu.com/2019/11/binh-bien-o-trung-quoc-ivivu-6.jpg\" target=\"_blank\" rel=\"noopener\"><img style=\"height: auto;\" title=\"Ảnh: Huyen Nguyen\" src=\"https://cdn3.ivivu.com/2019/11/binh-bien-o-trung-quoc-ivivu-6.jpg\" alt=\"Ảnh: Huyen Nguyen\" width=\"960\" height=\"720\" /></a></p>\r\n<p>Ảnh: Huyen Nguyen</p>\r\n<p>Nằm ở độ cao 2590m so với mực nước biển,&nbsp;<em><strong>B&igrave;nh Bi&ecirc;n</strong></em>&nbsp;c&oacute; kh&iacute; hậu tương đối m&aacute;t mẻ quanh năm, l&agrave; nơi l&yacute; tưởng nghỉ dưỡng với cảnh đẹp v&agrave; thi&ecirc;n nhi&ecirc;n hoang sơ. Ng&agrave;y nay, B&igrave;nh Bi&ecirc;n đ&atilde; đổi mới v&agrave; kh&aacute; hiện đại với những c&ocirc;ng tr&igrave;nh cao tầng, nhiều kh&aacute;ch sạn, nh&agrave; h&agrave;ng rộng lớn được x&acirc;y mới hiện đại nhưng giữ lại được những n&eacute;t kiến tr&uacute;c theo phong c&aacute;ch thời xưa.</p>\r\n<p><a href=\"https://cdn3.ivivu.com/2019/11/binh-bien-o-trung-quoc-ivivu-4.jpg\" target=\"_blank\" rel=\"noopener\"><img style=\"height: auto;\" title=\"Ảnh: Huyen Nguyen\" src=\"https://cdn3.ivivu.com/2019/11/binh-bien-o-trung-quoc-ivivu-4.jpg\" alt=\"Ảnh: Huyen Nguyen\" width=\"959\" height=\"720\" /></a></p>\r\n<p>Ảnh: Huyen Nguyen</p>\r\n<p><a href=\"https://cdn3.ivivu.com/2019/11/binh-bien-o-trung-quoc-ivivu-5.jpg\" target=\"_blank\" rel=\"noopener\"><img style=\"height: auto;\" title=\"Ảnh: Huyen Nguyen\" src=\"https://cdn3.ivivu.com/2019/11/binh-bien-o-trung-quoc-ivivu-5.jpg\" alt=\"Ảnh: Huyen Nguyen\" width=\"960\" height=\"720\" /></a></p>\r\n<p>Ảnh: Huyen Nguyen</p>\r\n<p>Đến&nbsp;<em>B&igrave;nh Bi&ecirc;n</em>&nbsp;bạn sẽ được gh&eacute; thăm những địa điểm nổi tiếng: Mi&ecirc;u Trại T&iacute;ch Thủy, Phố Gốm, C&ocirc;ng vi&ecirc;n rừng nguy&ecirc;n sinh Đại Vi Sơn, Ch&ugrave;a Linh Ngọc Thiền, L&agrave;ng nghề dệt, C&ocirc;ng vi&ecirc;n &aacute;nh s&aacute;ng&hellip; tất cả đều mang một vẻ đẹp cổ k&iacute;nh, c&ograve;n hoang sơ, chưa bị t&aacute;c động nhiều từ du lịch.</p>\r\n<p><a href=\"https://cdn3.ivivu.com/2019/11/binh-bien-o-trung-quoc-ivivu-2.jpg\" target=\"_blank\" rel=\"noopener\"><img style=\"height: auto;\" title=\"Ảnh: Huyen Nguyen\" src=\"https://cdn3.ivivu.com/2019/11/binh-bien-o-trung-quoc-ivivu-2.jpg\" alt=\"Ảnh: Huyen Nguyen\" width=\"959\" height=\"720\" /></a></p>\r\n<p>Ảnh: Huyen Nguyen</p>\r\n<p><a href=\"https://cdn3.ivivu.com/2019/11/binh-bien-o-trung-quoc-ivivu-1.jpg\" target=\"_blank\" rel=\"noopener\"><img style=\"height: auto;\" title=\"Ảnh: Huyen Nguyen\" src=\"https://cdn3.ivivu.com/2019/11/binh-bien-o-trung-quoc-ivivu-1.jpg\" alt=\"Ảnh: Huyen Nguyen\" width=\"960\" height=\"720\" /></a></p>\r\n<p>Ảnh: Huyen Nguyen</p>\r\n<p><a href=\"https://cdn3.ivivu.com/2019/11/binh-bien-o-trung-quoc-ivivu-7.jpg\" target=\"_blank\" rel=\"noopener\"><img style=\"height: auto;\" title=\"Ảnh: Huyen Nguyen\" src=\"https://cdn3.ivivu.com/2019/11/binh-bien-o-trung-quoc-ivivu-7.jpg\" alt=\"Ảnh: Huyen Nguyen\" width=\"720\" height=\"960\" /></a></p>\r\n<p>Ảnh: Huyen Nguyen</p>\r\n<p><strong>Hướng dẫn di chuyển:</strong></p>\r\n<p>Để đến được với B&igrave;nh Bi&ecirc;n, trước ti&ecirc;n bạn phải di chuyển từ H&agrave; Nội đến với L&agrave;o Cai. Bạn c&oacute; thể lựa chọn đi t&agrave;u hoặc ngồi xe kh&aacute;ch giường nằm. Khi đến được với thị trấn Sapa sẽ di chuyển th&ecirc;m khoảng 35km sẽ đến được cửa khẩu H&agrave; Khẩu. Từ H&agrave; Khẩu tiếp tục đi xe khoảng 2 tiếng nưa l&agrave; đến nơi.</p>\r\n<p><strong>Lưu &yacute;:</strong></p>\r\n<p>- Thời tiết v&agrave;o ban đ&ecirc;m ở B&igrave;nh Bi&ecirc;n rất lạnh, n&ecirc;n đem theo &aacute;o kho&aacute;c ấm hoặc &aacute;o len.</p>\r\n<p>- Đồ ăn Trung Quốc cay, nhiều dầu n&ecirc;n nếu bạn n&agrave;o sợ đ&oacute;i th&igrave; nhớ mang th&ecirc;m m&igrave; hộp hoặc b&aacute;nh ngọt.</p>\r\n<p>- N&ecirc;n mang theo thuốc say xe</p>', '31.jpg', 0);
INSERT INTO `tin_tuc` (`id_news`, `title_news`, `desc_news`, `image`, `total_views`) VALUES
(11, '<p>Vẻ đẹp nơi đồng qu&ecirc; l&agrave;m bối cảnh phim &lsquo;Mắt biếc&rsquo;</p>', '<p><img style=\"height: auto;\" title=\"Những s&agrave;o m&iacute;a xanh tươi đang v&agrave;o vụ xen lẫn l&agrave; những thửa đ&atilde; thu hoạch ở l&agrave;ng H&agrave; Cảng, x&atilde; Quảng Ph&uacute;, huyện Quảng Điền. Đ&acirc;y l&agrave; vựa m&iacute;a lớn nhất của tỉnh Thừa Thi&ecirc;n - Huế, nổi tiếng với giống m&iacute;a Cẩm T&acirc;n khi xưa d&ugrave;ng để tiến vua.\" src=\"https://cdn3.ivivu.com/2019/11/ve-dep-noi-dong-que-lam-boi-canh-phim-mat-biec-ivivu-1.jpg\" alt=\"Những s&agrave;o m&iacute;a xanh tươi đang v&agrave;o vụ xen lẫn l&agrave; những thửa đ&atilde; thu hoạch ở l&agrave;ng H&agrave; Cảng, x&atilde; Quảng Ph&uacute;, huyện Quảng Điền. Đ&acirc;y l&agrave; vựa m&iacute;a lớn nhất của tỉnh Thừa Thi&ecirc;n - Huế, nổi tiếng với giống m&iacute;a Cẩm T&acirc;n khi xưa d&ugrave;ng để tiến vua.\" width=\"680\" height=\"453\" /></p>\r\n<p>Những s&agrave;o m&iacute;a xanh tươi đang v&agrave;o vụ xen lẫn l&agrave; những thửa đ&atilde; thu hoạch ở l&agrave;ng H&agrave; Cảng, x&atilde; Quảng Ph&uacute;, huyện Quảng Điền. Đ&acirc;y l&agrave; vựa m&iacute;a lớn nhất của tỉnh Thừa Thi&ecirc;n &ndash; Huế, nổi tiếng với giống m&iacute;a Cẩm T&acirc;n khi xưa d&ugrave;ng để tiến vua.</p>\r\n<p><img style=\"height: auto;\" title=\"Mỗi s&agrave;o m&iacute;a tại đ&acirc;y c&oacute; từ 2.000 đến 3.000 c&acirc;y, m&ugrave;a thu hoạch bắt đầu v&agrave;o khoảng th&aacute;ng 10. Người d&acirc;n thường canh trồng vụ m&iacute;a để thời gian thu hoạch rơi v&agrave;o dịp Tết, khi c&acirc;y m&iacute;a dễ ti&ecirc;u thụ hơn.\" src=\"https://cdn3.ivivu.com/2019/11/ve-dep-noi-dong-que-lam-boi-canh-phim-mat-biec-ivivu-2.jpg\" alt=\"Mỗi s&agrave;o m&iacute;a tại đ&acirc;y c&oacute; từ 2.000 đến 3.000 c&acirc;y, m&ugrave;a thu hoạch bắt đầu v&agrave;o khoảng th&aacute;ng 10. Người d&acirc;n thường canh trồng vụ m&iacute;a để thời gian thu hoạch rơi v&agrave;o dịp Tết, khi c&acirc;y m&iacute;a dễ ti&ecirc;u thụ hơn.\" width=\"680\" height=\"453\" /></p>\r\n<p>Mỗi s&agrave;o m&iacute;a tại đ&acirc;y c&oacute; từ 2.000 đến 3.000 c&acirc;y, m&ugrave;a thu hoạch bắt đầu v&agrave;o khoảng th&aacute;ng 10. Người d&acirc;n thường canh trồng vụ m&iacute;a để thời gian thu hoạch rơi v&agrave;o dịp Tết, khi c&acirc;y m&iacute;a dễ ti&ecirc;u thụ hơn.</p>\r\n<p><img style=\"height: auto;\" title=\"Anh Minh, chủ của bốn s&agrave;o m&iacute;a đang lột l&aacute;, gia cố c&aacute;c th&acirc;n c&acirc;y bằng d&acirc;y chằng để tr&aacute;nh c&acirc;y bị ng&atilde; đổ. Anh chia sẻ, m&iacute;a năm nay được m&ugrave;a n&ecirc;n người d&acirc;n phấn khởi, c&oacute; th&ecirc;m thu nhập chuẩn bị Tết.\" src=\"https://cdn3.ivivu.com/2019/11/ve-dep-noi-dong-que-lam-boi-canh-phim-mat-biec-ivivu-3.jpg\" alt=\"Anh Minh, chủ của bốn s&agrave;o m&iacute;a đang lột l&aacute;, gia cố c&aacute;c th&acirc;n c&acirc;y bằng d&acirc;y chằng để tr&aacute;nh c&acirc;y bị ng&atilde; đổ. Anh chia sẻ, m&iacute;a năm nay được m&ugrave;a n&ecirc;n người d&acirc;n phấn khởi, c&oacute; th&ecirc;m thu nhập chuẩn bị Tết.\" width=\"680\" height=\"453\" /></p>\r\n<p>Anh Minh, chủ của bốn s&agrave;o m&iacute;a đang lột l&aacute;, gia cố c&aacute;c th&acirc;n c&acirc;y bằng d&acirc;y chằng để tr&aacute;nh c&acirc;y bị ng&atilde; đổ. Anh chia sẻ, m&iacute;a năm nay được m&ugrave;a n&ecirc;n người d&acirc;n phấn khởi, c&oacute; th&ecirc;m thu nhập chuẩn bị Tết.</p>\r\n<p><img style=\"height: auto;\" title=\"C&acirc;y m&iacute;a khi mới lớn được bao bọc bởi những lớp vỏ l&aacute;. L&uacute;c trưởng th&agrave;nh, c&acirc;y được người d&acirc;n lột x&aacute;c l&aacute; gi&agrave; để l&ecirc;n đốt dần. Đến khi c&acirc;y cao hơn đầu người, ph&acirc;n th&agrave;nh những đốt d&agrave;i bằng cỡ ng&oacute;n tay v&agrave; th&acirc;n c&acirc;y chuyển sang m&agrave;u t&iacute;m đậm th&igrave; bắt đầu được thu hoạch.\" src=\"https://cdn3.ivivu.com/2019/11/ve-dep-noi-dong-que-lam-boi-canh-phim-mat-biec-ivivu-4.jpg\" alt=\"C&acirc;y m&iacute;a khi mới lớn được bao bọc bởi những lớp vỏ l&aacute;. L&uacute;c trưởng th&agrave;nh, c&acirc;y được người d&acirc;n lột x&aacute;c l&aacute; gi&agrave; để l&ecirc;n đốt dần. Đến khi c&acirc;y cao hơn đầu người, ph&acirc;n th&agrave;nh những đốt d&agrave;i bằng cỡ ng&oacute;n tay v&agrave; th&acirc;n c&acirc;y chuyển sang m&agrave;u t&iacute;m đậm th&igrave; bắt đầu được thu hoạch.\" width=\"680\" height=\"471\" /></p>\r\n<p>C&acirc;y m&iacute;a khi mới lớn được bao bọc bởi những lớp vỏ l&aacute;. L&uacute;c trưởng th&agrave;nh, c&acirc;y được người d&acirc;n lột x&aacute;c l&aacute; gi&agrave; để l&ecirc;n đốt dần. Đến khi c&acirc;y cao hơn đầu người, ph&acirc;n th&agrave;nh những đốt d&agrave;i bằng cỡ ng&oacute;n tay v&agrave; th&acirc;n c&acirc;y chuyển sang m&agrave;u t&iacute;m đậm th&igrave; bắt đầu được thu hoạch.</p>\r\n<p><img style=\"height: auto;\" title=\"Chị Thủy, chủ một vựa m&iacute;a lớn, đang cột kỹ từng b&oacute; m&iacute;a sau khi đốn để chuẩn bị đem ra xe trung chuyển đưa đi ti&ecirc;u thụ. M&ugrave;a thu hoạch m&iacute;a của người d&acirc;n Quảng Ph&uacute; sẽ k&eacute;o d&agrave;i từ nay đến hết th&aacute;ng 2 năm sau.\" src=\"https://cdn3.ivivu.com/2019/11/ve-dep-noi-dong-que-lam-boi-canh-phim-mat-biec-ivivu-5.jpg\" alt=\"Chị Thủy, chủ một vựa m&iacute;a lớn, đang cột kỹ từng b&oacute; m&iacute;a sau khi đốn để chuẩn bị đem ra xe trung chuyển đưa đi ti&ecirc;u thụ. M&ugrave;a thu hoạch m&iacute;a của người d&acirc;n Quảng Ph&uacute; sẽ k&eacute;o d&agrave;i từ nay đến hết th&aacute;ng 2 năm sau.\" width=\"680\" height=\"453\" /></p>\r\n<p>Chị Thủy, chủ một vựa m&iacute;a lớn, đang cột kỹ từng b&oacute; m&iacute;a sau khi đốn để chuẩn bị đem ra xe trung chuyển đưa đi ti&ecirc;u thụ. M&ugrave;a thu hoạch m&iacute;a của người d&acirc;n Quảng Ph&uacute; sẽ k&eacute;o d&agrave;i từ nay đến hết th&aacute;ng 2 năm sau.</p>\r\n<p><img style=\"height: auto;\" title=\"Người n&ocirc;ng d&acirc;n v&aacute;c những b&oacute; m&iacute;a ra con đường chạy xuy&ecirc;n qua những thửa ruộng để chờ thương l&aacute;i tới chở đi bằng xe tải. Người bu&ocirc;n m&iacute;a tiếp tục bỏ mối cho c&aacute;c chủ b&aacute;n lẻ. M&iacute;a Cẩm T&acirc;n c&oacute; vị nước ngọt lịm, lại bắt mắt về h&igrave;nh thức n&ecirc;n rất được người ti&ecirc;u d&ugrave;ng ưa chuộng.\" src=\"https://cdn3.ivivu.com/2019/11/ve-dep-noi-dong-que-lam-boi-canh-phim-mat-biec-ivivu-6.jpg\" alt=\"Người n&ocirc;ng d&acirc;n v&aacute;c những b&oacute; m&iacute;a ra con đường chạy xuy&ecirc;n qua những thửa ruộng để chờ thương l&aacute;i tới chở đi bằng xe tải. Người bu&ocirc;n m&iacute;a tiếp tục bỏ mối cho c&aacute;c chủ b&aacute;n lẻ. M&iacute;a Cẩm T&acirc;n c&oacute; vị nước ngọt lịm, lại bắt mắt về h&igrave;nh thức n&ecirc;n rất được người ti&ecirc;u d&ugrave;ng ưa chuộng.\" width=\"680\" height=\"453\" /></p>\r\n<p>Người n&ocirc;ng d&acirc;n v&aacute;c những b&oacute; m&iacute;a ra con đường chạy xuy&ecirc;n qua những thửa ruộng để chờ thương l&aacute;i tới chở đi bằng xe tải. Người bu&ocirc;n m&iacute;a tiếp tục bỏ mối cho c&aacute;c chủ b&aacute;n lẻ. M&iacute;a Cẩm T&acirc;n c&oacute; vị nước ngọt lịm, lại bắt mắt về h&igrave;nh thức n&ecirc;n rất được người ti&ecirc;u d&ugrave;ng ưa chuộng.</p>\r\n<p><img style=\"height: auto;\" title=\"Nằm c&aacute;ch trung t&acirc;m TP Huế khoảng 12 km, H&agrave; Cảng đang nhận được nhiều sự quan t&acirc;m gần đ&acirc;y bởi đạo diễn Victor Vũ đ&atilde; chọn nơi n&agrave;y l&agrave;m bối cảnh cho phim &quot;Mắt biếc&quot;, t&aacute;c phẩm chuyển thể từ truyện c&ugrave;ng t&ecirc;n của nh&agrave; văn Nguyễn Nhật &Aacute;nh.\" src=\"https://cdn3.ivivu.com/2019/11/ve-dep-noi-dong-que-lam-boi-canh-phim-mat-biec-ivivu-7.jpg\" alt=\"Nằm c&aacute;ch trung t&acirc;m TP Huế khoảng 12 km, H&agrave; Cảng đang nhận được nhiều sự quan t&acirc;m gần đ&acirc;y bởi đạo diễn Victor Vũ đ&atilde; chọn nơi n&agrave;y l&agrave;m bối cảnh cho phim &quot;Mắt biếc&quot;, t&aacute;c phẩm chuyển thể từ truyện c&ugrave;ng t&ecirc;n của nh&agrave; văn Nguyễn Nhật &Aacute;nh.\" width=\"680\" height=\"453\" /></p>\r\n<p>Nằm c&aacute;ch trung t&acirc;m TP Huế khoảng 12 km, H&agrave; Cảng đang nhận được nhiều sự quan t&acirc;m gần đ&acirc;y bởi đạo diễn Victor Vũ đ&atilde; chọn nơi n&agrave;y l&agrave;m bối cảnh cho phim &ldquo;Mắt biếc&rdquo;, t&aacute;c phẩm chuyển thể từ truyện c&ugrave;ng t&ecirc;n của nh&agrave; văn Nguyễn Nhật &Aacute;nh.</p>\r\n<p><img style=\"height: auto;\" title=\"&ldquo;Cảm gi&aacute;c y&ecirc;n b&igrave;nh &ugrave;a về trong t&ocirc;i khi ngắm nh&igrave;n khung cảnh H&agrave; Cảng. Đồng qu&ecirc; l&agrave; nơi y&ecirc;n ả nhất với t&ocirc;i mỗi khi thấy mệt mỏi. Nơi n&agrave;y c&oacute; phong cảnh đẹp v&agrave; con người ch&acirc;n chất, lu&ocirc;n cởi mở&rdquo;, anh Nguyễn Phong (Huế), t&aacute;c giả bộ ảnh, chia sẻ về nơi được chọn l&agrave; bối cảnh bộ phim.\" src=\"https://cdn3.ivivu.com/2019/11/ve-dep-noi-dong-que-lam-boi-canh-phim-mat-biec-ivivu-8.jpg\" alt=\"&ldquo;Cảm gi&aacute;c y&ecirc;n b&igrave;nh &ugrave;a về trong t&ocirc;i khi ngắm nh&igrave;n khung cảnh H&agrave; Cảng. Đồng qu&ecirc; l&agrave; nơi y&ecirc;n ả nhất với t&ocirc;i mỗi khi thấy mệt mỏi. Nơi n&agrave;y c&oacute; phong cảnh đẹp v&agrave; con người ch&acirc;n chất, lu&ocirc;n cởi mở&rdquo;, anh Nguyễn Phong (Huế), t&aacute;c giả bộ ảnh, chia sẻ về nơi được chọn l&agrave; bối cảnh bộ phim.\" width=\"550\" height=\"786\" /></p>\r\n<p>&ldquo;Cảm gi&aacute;c y&ecirc;n b&igrave;nh &ugrave;a về trong t&ocirc;i khi ngắm nh&igrave;n khung cảnh H&agrave; Cảng. Đồng qu&ecirc; l&agrave; nơi y&ecirc;n ả nhất với t&ocirc;i mỗi khi thấy mệt mỏi. Nơi n&agrave;y c&oacute; phong cảnh đẹp v&agrave; con người ch&acirc;n chất, lu&ocirc;n cởi mở&rdquo;, anh Nguyễn Phong (Huế), t&aacute;c giả bộ ảnh, chia sẻ về nơi được chọn l&agrave; bối cảnh bộ phim trong chuyến du lịch Huế.</p>\r\n<p><img style=\"height: auto;\" title=\"C&acirc;y v&ocirc;ng đồng trong ảnh gắn liền với tuổi thơ của hai nh&acirc;n vật Ngạn v&agrave; H&agrave; Lan.\" src=\"https://cdn3.ivivu.com/2019/11/ve-dep-noi-dong-que-lam-boi-canh-phim-mat-biec-ivivu-9.jpg\" alt=\"C&acirc;y v&ocirc;ng đồng trong ảnh gắn liền với tuổi thơ của hai nh&acirc;n vật Ngạn v&agrave; H&agrave; Lan.\" width=\"680\" height=\"453\" /></p>\r\n<p>C&acirc;y v&ocirc;ng đồng trong ảnh gắn liền với tuổi thơ của hai nh&acirc;n vật Ngạn v&agrave; H&agrave; Lan.</p>\r\n<p><img style=\"height: auto;\" title=\"C&acirc;y v&ocirc;ng đồng n&agrave;y được gọi l&agrave; c&acirc;y c&ocirc; đơn &quot;Mắt Biếc&quot;, nơi ghi h&igrave;nh cảnh Ngạn đ&aacute;nh đ&agrave;n cho H&agrave; Lan. Tới x&oacute;m Ch&ugrave;a, du kh&aacute;ch hỏi người d&acirc;n về gốc c&acirc;y quay phim &quot;Mắt Biếc&quot; để được chỉ dẫn.\" src=\"https://cdn3.ivivu.com/2019/11/ve-dep-noi-dong-que-lam-boi-canh-phim-mat-biec-ivivu-10.jpg\" alt=\"C&acirc;y v&ocirc;ng đồng n&agrave;y được gọi l&agrave; c&acirc;y c&ocirc; đơn &quot;Mắt Biếc&quot;, nơi ghi h&igrave;nh cảnh Ngạn đ&aacute;nh đ&agrave;n cho H&agrave; Lan. Tới x&oacute;m Ch&ugrave;a, du kh&aacute;ch hỏi người d&acirc;n về gốc c&acirc;y quay phim &quot;Mắt Biếc&quot; để được chỉ dẫn.\" width=\"680\" height=\"453\" /></p>\r\n<p>C&acirc;y v&ocirc;ng đồng n&agrave;y được gọi l&agrave; c&acirc;y c&ocirc; đơn &ldquo;Mắt Biếc&rdquo;, nơi ghi h&igrave;nh cảnh Ngạn đ&aacute;nh đ&agrave;n cho H&agrave; Lan.&nbsp;<strong>Du lịch Huế,</strong>&nbsp;tới x&oacute;m Ch&ugrave;a, du kh&aacute;ch hỏi người d&acirc;n về gốc c&acirc;y quay phim &ldquo;Mắt Biếc&rdquo; để được chỉ dẫn.</p>', '32.jpg', 0),
(12, '<p>Băng qua ngọn n&uacute;i lửa thi&ecirc;ng ở Nhật Bản v&agrave;o m&ugrave;a thu</p>', '<p><img style=\"height: auto;\" title=\"Đoạn đường Hakusan Shirakawa-go chạy qua n&uacute;i Hakusan, nối liền th&agrave;nh phố Hakusan v&agrave; l&agrave;ng cổ Shirakawa (tỉnh Gifu). Đ&acirc;y l&agrave; cung đường bạn kh&ocirc;ng thể kh&ocirc;ng dừng ch&acirc;n khi trong h&agrave;nh tr&igrave;nh đến l&agrave;ng Shirakawa. Con đường nhỏ được bao quanh bởi rừng c&acirc;y l&aacute; v&agrave;ng, l&aacute; đỏ, tạo khung cảnh vừa h&ugrave;ng vĩ vừa thơ mộng. Ảnh: Enohagogi.\" src=\"https://cdn3.ivivu.com/2019/11/bang-qua-ngon-nui-lua-thieng-o-nhat-ban-vao-mua-thu-ivivu-1.jpg\" alt=\"Đoạn đường Hakusan Shirakawa-go chạy qua n&uacute;i Hakusan, nối liền th&agrave;nh phố Hakusan v&agrave; l&agrave;ng cổ Shirakawa (tỉnh Gifu). Đ&acirc;y l&agrave; cung đường bạn kh&ocirc;ng thể kh&ocirc;ng dừng ch&acirc;n khi trong h&agrave;nh tr&igrave;nh đến l&agrave;ng Shirakawa. Con đường nhỏ được bao quanh bởi rừng c&acirc;y l&aacute; v&agrave;ng, l&aacute; đỏ, tạo khung cảnh vừa h&ugrave;ng vĩ vừa thơ mộng. Ảnh: Enohagogi.\" width=\"1024\" height=\"683\" /></p>\r\n<p>Đoạn đường<strong>&nbsp;Hakusan Shirakawa-go</strong>&nbsp;chạy qua n&uacute;i Hakusan, nối liền th&agrave;nh phố Hakusan v&agrave; l&agrave;ng cổ Shirakawa (tỉnh Gifu). Đ&acirc;y l&agrave; cung đường bạn kh&ocirc;ng thể kh&ocirc;ng dừng ch&acirc;n khi trong h&agrave;nh tr&igrave;nh đến l&agrave;ng Shirakawa. Con đường nhỏ được bao quanh bởi rừng c&acirc;y l&aacute; v&agrave;ng, l&aacute; đỏ, tạo khung cảnh vừa h&ugrave;ng vĩ vừa thơ mộng. Ảnh: Enohagogi.</p>\r\n<p><img style=\"height: auto;\" title=\"Hakusan l&agrave; n&uacute;i lửa dạng tầng, phun tr&agrave;o lần gần nhất v&agrave;o thế kỷ 17. N&uacute;i Hakusan được người Nhật mệnh danh l&agrave; một trong ba ngọn n&uacute;i th&aacute;nh, c&ugrave;ng với n&uacute;i Ph&uacute; Sĩ v&agrave; n&uacute;i Tateyama. Vườn quốc gia Hakusan tr&ecirc;n n&uacute;i l&agrave; điểm hấp dẫn du kh&aacute;ch trong h&agrave;nh tr&igrave;nh ngắm m&ugrave;a thu. Ảnh: Shodo.sawa.\" src=\"https://cdn3.ivivu.com/2019/11/bang-qua-ngon-nui-lua-thieng-o-nhat-ban-vao-mua-thu-ivivu-2.jpg\" alt=\"Hakusan l&agrave; n&uacute;i lửa dạng tầng, phun tr&agrave;o lần gần nhất v&agrave;o thế kỷ 17. N&uacute;i Hakusan được người Nhật mệnh danh l&agrave; một trong ba ngọn n&uacute;i th&aacute;nh, c&ugrave;ng với n&uacute;i Ph&uacute; Sĩ v&agrave; n&uacute;i Tateyama. Vườn quốc gia Hakusan tr&ecirc;n n&uacute;i l&agrave; điểm hấp dẫn du kh&aacute;ch trong h&agrave;nh tr&igrave;nh ngắm m&ugrave;a thu. Ảnh: Shodo.sawa.\" width=\"1024\" height=\"683\" /></p>\r\n<p>Hakusan l&agrave; n&uacute;i lửa dạng tầng, phun tr&agrave;o lần gần nhất v&agrave;o thế kỷ 17. N&uacute;i Hakusan được người Nhật mệnh danh l&agrave; một trong ba ngọn n&uacute;i th&aacute;nh, c&ugrave;ng với n&uacute;i Ph&uacute; Sĩ v&agrave; n&uacute;i Tateyama. Vườn quốc gia Hakusan tr&ecirc;n n&uacute;i l&agrave; điểm hấp dẫn du kh&aacute;ch trong h&agrave;nh tr&igrave;nh ngắm m&ugrave;a thu. Ảnh: Shodo.sawa.</p>\r\n<p><img style=\"height: auto;\" title=\"Nhờ thảm thực vật phong ph&uacute;, m&ugrave;a thu tr&ecirc;n n&uacute;i Hakusan hấp dẫn nhiều t&iacute;n đồ y&ecirc;u tự nhi&ecirc;n. Đoạn đường dẫn đến l&agrave;ng Shirakawa c&ograve;n được gọi l&agrave; con đường trắng, nằm tr&ecirc;n v&ugrave;ng n&uacute;i cao khoảng 600-1.500 m so với mực nước biển. Ảnh: ichirino, hiropon.\" src=\"https://cdn3.ivivu.com/2019/11/bang-qua-ngon-nui-lua-thieng-o-nhat-ban-vao-mua-thu-ivivu-3.jpg\" alt=\"Nhờ thảm thực vật phong ph&uacute;, m&ugrave;a thu tr&ecirc;n n&uacute;i Hakusan hấp dẫn nhiều t&iacute;n đồ y&ecirc;u tự nhi&ecirc;n. Đoạn đường dẫn đến l&agrave;ng Shirakawa c&ograve;n được gọi l&agrave; con đường trắng, nằm tr&ecirc;n v&ugrave;ng n&uacute;i cao khoảng 600-1.500 m so với mực nước biển. Ảnh: ichirino, hiropon.\" width=\"1024\" height=\"1280\" /></p>\r\n<p>Nhờ thảm thực vật phong ph&uacute;, m&ugrave;a thu tr&ecirc;n n&uacute;i Hakusan hấp dẫn nhiều t&iacute;n đồ y&ecirc;u tự nhi&ecirc;n. Đoạn đường dẫn đến l&agrave;ng Shirakawa c&ograve;n được gọi l&agrave; con đường trắng, nằm tr&ecirc;n v&ugrave;ng n&uacute;i cao khoảng 600-1.500 m so với mực nước biển. Ảnh: ichirino, hiropon.</p>\r\n<p><img style=\"height: auto;\" title=\"Chạy xe tr&ecirc;n con đường trắng, du kh&aacute;ch c&oacute; thể quan s&aacute;t được cảnh n&uacute;i non tr&ugrave;ng điệp của Hakusan với những thảm l&aacute; v&agrave;ng, l&aacute; đỏ phủ k&iacute;n. Đoạn đường Hakusan Shirakawa-go được đ&aacute;nh gi&aacute; l&agrave; hiểm trở. V&igrave; điều kiện m&ugrave;a đ&ocirc;ng khắc nghiệt, đoạn đường chỉ cho ph&eacute;p xe chạy từ th&aacute;ng 6-11 h&agrave;ng năm. Ảnh: nori_studio_2019.\" src=\"https://cdn3.ivivu.com/2019/11/bang-qua-ngon-nui-lua-thieng-o-nhat-ban-vao-mua-thu-ivivu-5.jpg\" alt=\"Chạy xe tr&ecirc;n con đường trắng, du kh&aacute;ch c&oacute; thể quan s&aacute;t được cảnh n&uacute;i non tr&ugrave;ng điệp của Hakusan với những thảm l&aacute; v&agrave;ng, l&aacute; đỏ phủ k&iacute;n. Đoạn đường Hakusan Shirakawa-go được đ&aacute;nh gi&aacute; l&agrave; hiểm trở. V&igrave; điều kiện m&ugrave;a đ&ocirc;ng khắc nghiệt, đoạn đường chỉ cho ph&eacute;p xe chạy từ th&aacute;ng 6-11 h&agrave;ng năm. Ảnh: nori_studio_2019.\" width=\"1024\" height=\"1024\" /></p>\r\n<p>Chạy xe tr&ecirc;n con đường trắng, du kh&aacute;ch c&oacute; thể quan s&aacute;t được cảnh n&uacute;i non tr&ugrave;ng điệp của Hakusan với những thảm l&aacute; v&agrave;ng, l&aacute; đỏ phủ k&iacute;n. Đoạn đường Hakusan Shirakawa-go được đ&aacute;nh gi&aacute; l&agrave; hiểm trở. V&igrave; điều kiện m&ugrave;a đ&ocirc;ng khắc nghiệt, đoạn đường chỉ cho ph&eacute;p xe chạy từ th&aacute;ng 6-11 h&agrave;ng năm. Ảnh: nori_studio_2019.</p>\r\n<p><img style=\"height: auto;\" title=\"Tr&ecirc;n đường ngắm l&aacute; đỏ m&ugrave;a thu, du kh&aacute;ch c&oacute; thể dừng ch&acirc;n chi&ecirc;m ngưỡng những d&ograve;ng th&aacute;c đổ trắng xo&aacute; tr&ecirc;n n&uacute;i Hakusan. C&aacute;c th&aacute;c nước nổi tiếng du kh&aacute;ch kh&ocirc;ng thể bỏ quan l&agrave; Shiritaka, Fukube v&agrave; Ubagataki. Th&aacute;c Ubagataki c&ograve;n c&oacute; cả kh&ocirc;ng gian tắm lộ thi&ecirc;n cho du kh&aacute;ch thư gi&atilde;n. Ảnh: sti_zzz, heart_of_gold_55.\" src=\"https://cdn3.ivivu.com/2019/11/bang-qua-ngon-nui-lua-thieng-o-nhat-ban-vao-mua-thu-ivivu-6-819x1024.jpg\" alt=\"Tr&ecirc;n đường ngắm l&aacute; đỏ m&ugrave;a thu, du kh&aacute;ch c&oacute; thể dừng ch&acirc;n chi&ecirc;m ngưỡng những d&ograve;ng th&aacute;c đổ trắng xo&aacute; tr&ecirc;n n&uacute;i Hakusan. C&aacute;c th&aacute;c nước nổi tiếng du kh&aacute;ch kh&ocirc;ng thể bỏ quan l&agrave; Shiritaka, Fukube v&agrave; Ubagataki. Th&aacute;c Ubagataki c&ograve;n c&oacute; cả kh&ocirc;ng gian tắm lộ thi&ecirc;n cho du kh&aacute;ch thư gi&atilde;n. Ảnh: sti_zzz, heart_of_gold_55.\" width=\"819\" height=\"1024\" /></p>\r\n<p>Tr&ecirc;n đường ngắm l&aacute; đỏ m&ugrave;a thu, du kh&aacute;ch c&oacute; thể dừng ch&acirc;n chi&ecirc;m ngưỡng những d&ograve;ng th&aacute;c đổ trắng xo&aacute; tr&ecirc;n n&uacute;i Hakusan. C&aacute;c th&aacute;c nước nổi tiếng du kh&aacute;ch kh&ocirc;ng thể bỏ quan l&agrave; Shiritaka, Fukube v&agrave; Ubagataki. Th&aacute;c Ubagataki c&ograve;n c&oacute; cả kh&ocirc;ng gian tắm lộ thi&ecirc;n cho du kh&aacute;ch thư gi&atilde;n. Ảnh: sti_zzz, heart_of_gold_55.</p>\r\n<p><img style=\"height: auto;\" title=\"Tr&ecirc;n đoạn đường trắng c&oacute; trạm dừng ch&acirc;n tại đ&agrave;i quan s&aacute;t Hakusan. Từ đ&acirc;y, du kh&aacute;ch c&oacute; thể ngắm nh&igrave;n to&agrave;n cảnh ngọn n&uacute;i Hakusan h&ugrave;ng vĩ. Ảnh: Ichirino.\" src=\"https://cdn3.ivivu.com/2019/11/bang-qua-ngon-nui-lua-thieng-o-nhat-ban-vao-mua-thu-ivivu-8.jpg\" alt=\"Tr&ecirc;n đoạn đường trắng c&oacute; trạm dừng ch&acirc;n tại đ&agrave;i quan s&aacute;t Hakusan. Từ đ&acirc;y, du kh&aacute;ch c&oacute; thể ngắm nh&igrave;n to&agrave;n cảnh ngọn n&uacute;i Hakusan h&ugrave;ng vĩ. Ảnh: Ichirino.\" width=\"1024\" height=\"1024\" /></p>\r\n<p>Tr&ecirc;n đoạn đường trắng c&oacute; trạm dừng ch&acirc;n tại đ&agrave;i quan s&aacute;t Hakusan. Từ đ&acirc;y, du kh&aacute;ch c&oacute; thể ngắm nh&igrave;n to&agrave;n cảnh ngọn n&uacute;i Hakusan h&ugrave;ng vĩ. Ảnh: Ichirino.</p>\r\n<p><img style=\"height: auto;\" title=\"Một trong những hoạt động được nhiều du kh&aacute;ch y&ecirc;u th&iacute;ch khi đi qua Hakusan Shirakawa-go l&agrave; tắm suối n&oacute;ng. N&uacute;i lửa Hakusan đ&atilde; dừng hoạt động n&ecirc;n c&oacute; nhiều suối nước n&oacute;ng, cho du kh&aacute;ch kh&ocirc;ng gian thư gi&atilde;n ngọn n&uacute;i thi&ecirc;ng y&ecirc;n b&igrave;nh. Ảnh: nori_studio_2019.\" src=\"https://cdn3.ivivu.com/2019/11/bang-qua-ngon-nui-lua-thieng-o-nhat-ban-vao-mua-thu-ivivu-9.jpg\" alt=\"Một trong những hoạt động được nhiều du kh&aacute;ch y&ecirc;u th&iacute;ch khi đi qua Hakusan Shirakawa-go l&agrave; tắm suối n&oacute;ng. N&uacute;i lửa Hakusan đ&atilde; dừng hoạt động n&ecirc;n c&oacute; nhiều suối nước n&oacute;ng, cho du kh&aacute;ch kh&ocirc;ng gian thư gi&atilde;n ngọn n&uacute;i thi&ecirc;ng y&ecirc;n b&igrave;nh. Ảnh: nori_studio_2019.\" width=\"1024\" height=\"1024\" /></p>\r\n<p>Một trong những hoạt động được nhiều du kh&aacute;ch y&ecirc;u th&iacute;ch khi đi qua Hakusan Shirakawa-go l&agrave; tắm suối n&oacute;ng. N&uacute;i lửa Hakusan đ&atilde; dừng hoạt động n&ecirc;n c&oacute; nhiều suối nước n&oacute;ng, cho du kh&aacute;ch kh&ocirc;ng gian thư gi&atilde;n b&ecirc;n ngọn n&uacute;i thi&ecirc;ng y&ecirc;n b&igrave;nh.</p>', '33.jpg', 0),
(13, '<p>6 h&agrave;nh tr&igrave;nh đường sắt vĩ đại nhất thế giới</p>', '<p><strong><em>Từ Australia đến Mỹ, 6 h&agrave;nh tr&igrave;nh đường sắt vĩ đại nhất thế giới đ&atilde; được giới thiệu trong cuốn s&aacute;ch &ldquo;Slow Trains To Venice&rdquo;.</em></strong></p>\r\n<h2>6 h&agrave;nh tr&igrave;nh đường sắt vĩ đại nhất thế giới</h2>\r\n<p><img style=\"height: auto;\" title=\"Mỹ: Nhắc tới xứ cờ hoa, nhiều người li&ecirc;n tưởng tới hệ thống s&acirc;n bay v&agrave; đường cao tốc hiện đại. Tuy nhi&ecirc;n, đường sắt l&agrave; ch&igrave;a kh&oacute;a li&ecirc;n kết giữa c&aacute;c v&ugrave;ng đất, nối biển với biển, biểu tượng cho việc chinh phục bờ T&acirc;y nước Mỹ. Đường xuy&ecirc;n lục địa đầu ti&ecirc;n được ho&agrave;n th&agrave;nh v&agrave;o ng&agrave;y 10/5/1869, hiện l&agrave; điểm thu h&uacute;t kh&aacute;ch du lịch. Bạn c&oacute; thể kh&aacute;m ph&aacute; tuyến New York - Chicago chạy dọc theo s&ocirc;ng Hudson h&ugrave;ng vĩ hay Chicago - Seattle để băng qua v&ugrave;ng hoang d&atilde; của đồng bằng ph&iacute;a bắc. Ảnh: Justdial.\" src=\"https://cdn3.ivivu.com/2019/11/6-hanh-trinh-duong-sat-vi-dai-nhat-the-gioi-ivivu-1.jpg\" alt=\"Mỹ: Nhắc tới xứ cờ hoa, nhiều người li&ecirc;n tưởng tới hệ thống s&acirc;n bay v&agrave; đường cao tốc hiện đại. Tuy nhi&ecirc;n, đường sắt l&agrave; ch&igrave;a kh&oacute;a li&ecirc;n kết giữa c&aacute;c v&ugrave;ng đất, nối biển với biển, biểu tượng cho việc chinh phục bờ T&acirc;y nước Mỹ. Đường xuy&ecirc;n lục địa đầu ti&ecirc;n được ho&agrave;n th&agrave;nh v&agrave;o ng&agrave;y 10/5/1869, hiện l&agrave; điểm thu h&uacute;t kh&aacute;ch du lịch. Bạn c&oacute; thể kh&aacute;m ph&aacute; tuyến New York - Chicago chạy dọc theo s&ocirc;ng Hudson h&ugrave;ng vĩ hay Chicago - Seattle để băng qua v&ugrave;ng hoang d&atilde; của đồng bằng ph&iacute;a bắc. Ảnh: Justdial.\" width=\"1024\" height=\"640\" /></p>\r\n<p><strong>Mỹ:</strong>&nbsp;Nhắc tới xứ cờ hoa, nhiều người li&ecirc;n tưởng tới hệ thống s&acirc;n bay v&agrave; đường cao tốc hiện đại. Tuy nhi&ecirc;n, đường sắt l&agrave; ch&igrave;a kh&oacute;a li&ecirc;n kết giữa c&aacute;c v&ugrave;ng đất, nối biển với biển, biểu tượng cho việc chinh phục bờ T&acirc;y nước Mỹ. Đường xuy&ecirc;n lục địa đầu ti&ecirc;n được ho&agrave;n th&agrave;nh v&agrave;o ng&agrave;y 10/5/1869, hiện l&agrave; điểm thu h&uacute;t kh&aacute;ch du lịch. Bạn c&oacute; thể kh&aacute;m ph&aacute; tuyến New York &ndash; Chicago chạy dọc theo s&ocirc;ng Hudson h&ugrave;ng vĩ hay Chicago &ndash; Seattle để băng qua v&ugrave;ng hoang d&atilde; của đồng bằng ph&iacute;a bắc. Ảnh: Justdial.</p>\r\n<p><img style=\"height: auto;\" title=\"Australia: Tuyến đường sắt Ghan chia đ&ocirc;i Australia d&agrave;i gần 3.000 km, từ Adelaide (Nam Australia) đến Darwin (Bắc Australia). Bạn sẽ mất 54 giờ tr&ecirc;n t&agrave;u để xuy&ecirc;n qua sa mạc đỏ n&oacute;ng bỏng, ngắm cảnh sắc thi&ecirc;n nhi&ecirc;n hoang sơ, h&ugrave;ng vĩ của Australia. Ảnh: 99.co.\" src=\"https://cdn3.ivivu.com/2019/11/6-hanh-trinh-duong-sat-vi-dai-nhat-the-gioi-ivivu-2.jpg\" alt=\"Australia: Tuyến đường sắt Ghan chia đ&ocirc;i Australia d&agrave;i gần 3.000 km, từ Adelaide (Nam Australia) đến Darwin (Bắc Australia). Bạn sẽ mất 54 giờ tr&ecirc;n t&agrave;u để xuy&ecirc;n qua sa mạc đỏ n&oacute;ng bỏng, ngắm cảnh sắc thi&ecirc;n nhi&ecirc;n hoang sơ, h&ugrave;ng vĩ của Australia. Ảnh: 99.co.\" width=\"1024\" height=\"683\" /></p>\r\n<p><strong>Australia:</strong>&nbsp;Tuyến đường sắt Ghan chia đ&ocirc;i Australia d&agrave;i gần 3.000 km, từ Adelaide (Nam Australia) đến Darwin (Bắc Australia). Bạn sẽ mất 54 giờ tr&ecirc;n t&agrave;u để xuy&ecirc;n qua sa mạc đỏ n&oacute;ng bỏng, ngắm cảnh sắc thi&ecirc;n nhi&ecirc;n hoang sơ, h&ugrave;ng vĩ của Australia. Ảnh: 99.co.</p>\r\n<p><img style=\"height: auto;\" title=\"Thụy Sĩ: Tuyến đường sắt huyền thoại giữa Zermatt v&agrave; St Moritz cho ph&eacute;p bạn c&oacute; 8 giờ để chi&ecirc;m ngưỡng khung cảnh n&uacute;i rừng h&ugrave;ng vĩ của Thụy Sĩ với c&aacute;c điểm dừng tại Andermatt v&agrave; Chur. Chạy tr&ecirc;n đường ray hẹp, đo&agrave;n t&agrave;u thực hiện h&agrave;nh tr&igrave;nh hơn 291 km, đi qua 91 đường hầm v&agrave; 291 c&acirc;y cầu. Ảnh: Newly Swissed.\" src=\"https://cdn3.ivivu.com/2019/11/6-hanh-trinh-duong-sat-vi-dai-nhat-the-gioi-ivivu-3.jpg\" alt=\"Thụy Sĩ: Tuyến đường sắt huyền thoại giữa Zermatt v&agrave; St Moritz cho ph&eacute;p bạn c&oacute; 8 giờ để chi&ecirc;m ngưỡng khung cảnh n&uacute;i rừng h&ugrave;ng vĩ của Thụy Sĩ với c&aacute;c điểm dừng tại Andermatt v&agrave; Chur. Chạy tr&ecirc;n đường ray hẹp, đo&agrave;n t&agrave;u thực hiện h&agrave;nh tr&igrave;nh hơn 291 km, đi qua 91 đường hầm v&agrave; 291 c&acirc;y cầu. Ảnh: Newly Swissed.\" width=\"1024\" height=\"683\" /></p>\r\n<p><strong>Thụy Sĩ:</strong>&nbsp;Tuyến đường sắt huyền thoại giữa Zermatt v&agrave; St Moritz cho ph&eacute;p bạn c&oacute; 8 giờ để chi&ecirc;m ngưỡng khung cảnh n&uacute;i rừng h&ugrave;ng vĩ của Thụy Sĩ với c&aacute;c điểm dừng tại Andermatt v&agrave; Chur. Chạy tr&ecirc;n đường ray hẹp, đo&agrave;n t&agrave;u thực hiện h&agrave;nh tr&igrave;nh hơn 291 km, đi qua 91 đường hầm v&agrave; 291 c&acirc;y cầu. Ảnh: Newly Swissed.</p>\r\n<p><img style=\"height: auto;\" title=\"Ấn Độ: T&agrave;u hỏa l&agrave; phương tiện giao th&ocirc;ng quan trọng ở Ấn Độ. Hơn 1,3 triệu người l&agrave;m việc trong ng&agrave;nh đường sắt Ấn Độ, nơi vận chuyển 20 triệu h&agrave;nh kh&aacute;ch mỗi ng&agrave;y. Quốc gia n&agrave;y sở hữu những h&agrave;nh tr&igrave;nh đường sắt ngoạn mục như tuyến kh&aacute;m ph&aacute; Tam gi&aacute;c v&agrave;ng từ Delhi đến Agra, Ranthambore v&agrave; Jaipur; đường sắt băng qua 107 hầm; Darjeeling Himalaya... Ảnh: Stars Insider.\" src=\"https://cdn3.ivivu.com/2019/11/6-hanh-trinh-duong-sat-vi-dai-nhat-the-gioi-ivivu-4.jpg\" alt=\"Ấn Độ: T&agrave;u hỏa l&agrave; phương tiện giao th&ocirc;ng quan trọng ở Ấn Độ. Hơn 1,3 triệu người l&agrave;m việc trong ng&agrave;nh đường sắt Ấn Độ, nơi vận chuyển 20 triệu h&agrave;nh kh&aacute;ch mỗi ng&agrave;y. Quốc gia n&agrave;y sở hữu những h&agrave;nh tr&igrave;nh đường sắt ngoạn mục như tuyến kh&aacute;m ph&aacute; Tam gi&aacute;c v&agrave;ng từ Delhi đến Agra, Ranthambore v&agrave; Jaipur; đường sắt băng qua 107 hầm; Darjeeling Himalaya... Ảnh: Stars Insider.\" width=\"1024\" height=\"684\" /></p>\r\n<p><strong>Ấn Độ:</strong>&nbsp;T&agrave;u hỏa l&agrave; phương tiện giao th&ocirc;ng quan trọng ở Ấn Độ. Hơn 1,3 triệu người l&agrave;m việc trong ng&agrave;nh đường sắt Ấn Độ, nơi vận chuyển 20 triệu h&agrave;nh kh&aacute;ch mỗi ng&agrave;y. Quốc gia n&agrave;y sở hữu những&nbsp;<strong>h&agrave;nh tr&igrave;nh đường sắt</strong>&nbsp;ngoạn mục như tuyến kh&aacute;m ph&aacute; Tam gi&aacute;c v&agrave;ng từ Delhi đến Agra, Ranthambore v&agrave; Jaipur; đường sắt băng qua 107 hầm; Darjeeling Himalaya&hellip; Ảnh: Stars Insider.</p>\r\n<p><img style=\"height: auto;\" title=\"Canada: Cuộc h&agrave;nh tr&igrave;nh từ Banff, Alberta tới Vancouver, British Columbia, l&agrave; một trong những chuyến đường sắt vĩ đại nhất thế giới. Đo&agrave;n t&agrave;u sẽ đưa du kh&aacute;ch đi qua d&atilde;y Rocky với những đỉnh n&uacute;i tuyết v&agrave; nhiều s&ocirc;ng, hồ. Cuộc h&agrave;nh tr&igrave;nh mất 2 ng&agrave;y với một điểm dừng qua đ&ecirc;m ở Kamloops. Ảnh: USA Today.\" src=\"https://cdn3.ivivu.com/2019/11/6-hanh-trinh-duong-sat-vi-dai-nhat-the-gioi-ivivu-5.jpg\" alt=\"Canada: Cuộc h&agrave;nh tr&igrave;nh từ Banff, Alberta tới Vancouver, British Columbia, l&agrave; một trong những chuyến đường sắt vĩ đại nhất thế giới. Đo&agrave;n t&agrave;u sẽ đưa du kh&aacute;ch đi qua d&atilde;y Rocky với những đỉnh n&uacute;i tuyết v&agrave; nhiều s&ocirc;ng, hồ. Cuộc h&agrave;nh tr&igrave;nh mất 2 ng&agrave;y với một điểm dừng qua đ&ecirc;m ở Kamloops. Ảnh: USA Today.\" width=\"1024\" height=\"683\" /></p>\r\n<p><strong>Canada:</strong>&nbsp;Cuộc h&agrave;nh tr&igrave;nh từ Banff, Alberta tới Vancouver, British Columbia, l&agrave; một trong những h&agrave;nh tr&igrave;nh đường sắt vĩ đại nhất thế giới. Đo&agrave;n t&agrave;u sẽ đưa du kh&aacute;ch đi qua d&atilde;y Rocky với những đỉnh n&uacute;i tuyết v&agrave; nhiều s&ocirc;ng, hồ. Cuộc h&agrave;nh tr&igrave;nh mất 2 ng&agrave;y với một điểm dừng qua đ&ecirc;m ở Kamloops. Ảnh: USA Today.</p>\r\n<p><img style=\"height: auto;\" title=\"Nga: Tr&ecirc;n tuyến đường sắt xuy&ecirc;n Sibir, bạn sẽ được du h&agrave;nh tới những địa điểm lạ như lạc giữa v&ugrave;ng đất ngo&agrave;i h&agrave;nh tinh. Du kh&aacute;ch c&oacute; thể lựa chọn 3 tuyến đường kh&aacute;c nhau để tới Vladivostok (Nga) hoặc Trung Quốc. Ảnh: Pentapostagma.\" src=\"https://cdn3.ivivu.com/2019/11/6-hanh-trinh-duong-sat-vi-dai-nhat-the-gioi-ivivu-6.jpg\" alt=\"Nga: Tr&ecirc;n tuyến đường sắt xuy&ecirc;n Sibir, bạn sẽ được du h&agrave;nh tới những địa điểm lạ như lạc giữa v&ugrave;ng đất ngo&agrave;i h&agrave;nh tinh. Du kh&aacute;ch c&oacute; thể lựa chọn 3 tuyến đường kh&aacute;c nhau để tới Vladivostok (Nga) hoặc Trung Quốc. Ảnh: Pentapostagma.\" width=\"1024\" height=\"576\" /></p>\r\n<p><strong>Nga:</strong>&nbsp;Tr&ecirc;n tuyến đường sắt xuy&ecirc;n Sibir, bạn sẽ được du h&agrave;nh tới những địa điểm lạ như lạc giữa v&ugrave;ng đất ngo&agrave;i h&agrave;nh tinh. Du kh&aacute;ch c&oacute; thể lựa chọn 3 tuyến đường kh&aacute;c nhau để tới Vladivostok (Nga) hoặc Trung Quốc.</p>', '34.jpg', 0),
(14, '<p>Nh&agrave; thờ Ka Đơn &ndash; linh hồn Churu giữa rừng L&acirc;m Vi&ecirc;n</p>', '<p><img style=\"height: auto;\" title=\"Mọi kế cấu b&ecirc;n trong của nh&agrave; thờ - từ trần nh&agrave;, tường, v&aacute;ch ngăn, b&agrave;n ghế - đều bằng gỗ th&ocirc;ng, nguồn nguy&ecirc;n liệu sẵn c&oacute; từ địa phương\" src=\"https://cdn3.ivivu.com/2019/11/nha-tho-ka-don-linh-hon-churu-giua-rung-lam-vien-ivivu-1.jpg\" alt=\"Mọi kế cấu b&ecirc;n trong của nh&agrave; thờ - từ trần nh&agrave;, tường, v&aacute;ch ngăn, b&agrave;n ghế - đều bằng gỗ th&ocirc;ng, nguồn nguy&ecirc;n liệu sẵn c&oacute; từ địa phương\" width=\"980\" height=\"653\" /></p>\r\n<p>Mọi kế cấu b&ecirc;n trong của nh&agrave; thờ &ndash; từ trần nh&agrave;, tường, v&aacute;ch ngăn, b&agrave;n ghế &ndash; đều bằng gỗ th&ocirc;ng, nguồn nguy&ecirc;n liệu sẵn c&oacute; từ địa phương</p>\r\n<p><img style=\"height: auto;\" title=\"Gian ch&iacute;nh của nh&agrave; thờ được trang tr&iacute; đơn giản. C&aacute;c c&aacute;nh cửa lu&ocirc;n mở, tạo ra cảm gi&aacute;c thoải m&aacute;i, nhẹ nh&agrave;ng, kh&ocirc;ng kh&eacute;p k&iacute;n b&iacute; hiểm như c&aacute;c nh&agrave; thờ thiết kế theo kiểu T&acirc;y phương. B&ecirc;n trong nh&agrave; thờ lu&ocirc;n tr&agrave;n ngập &aacute;nh s&aacute;ng tự nhi&ecirc;n th&ocirc;ng qua c&aacute;c khoảng trống th&ocirc;ng gi&oacute;\" src=\"https://cdn3.ivivu.com/2019/11/nha-tho-ka-don-linh-hon-churu-giua-rung-lam-vien-ivivu-2.jpg\" alt=\"Gian ch&iacute;nh của nh&agrave; thờ được trang tr&iacute; đơn giản. C&aacute;c c&aacute;nh cửa lu&ocirc;n mở, tạo ra cảm gi&aacute;c thoải m&aacute;i, nhẹ nh&agrave;ng, kh&ocirc;ng kh&eacute;p k&iacute;n b&iacute; hiểm như c&aacute;c nh&agrave; thờ thiết kế theo kiểu T&acirc;y phương. B&ecirc;n trong nh&agrave; thờ lu&ocirc;n tr&agrave;n ngập &aacute;nh s&aacute;ng tự nhi&ecirc;n th&ocirc;ng qua c&aacute;c khoảng trống th&ocirc;ng gi&oacute;\" width=\"980\" height=\"653\" /></p>\r\n<p>Gian ch&iacute;nh của nh&agrave; thờ được trang tr&iacute; đơn giản. C&aacute;c c&aacute;nh cửa lu&ocirc;n mở, tạo ra cảm gi&aacute;c thoải m&aacute;i, nhẹ nh&agrave;ng, kh&ocirc;ng kh&eacute;p k&iacute;n b&iacute; hiểm như c&aacute;c nh&agrave; thờ thiết kế theo kiểu T&acirc;y phương. B&ecirc;n trong nh&agrave; thờ lu&ocirc;n tr&agrave;n ngập &aacute;nh s&aacute;ng tự nhi&ecirc;n th&ocirc;ng qua c&aacute;c khoảng trống th&ocirc;ng gi&oacute;</p>\r\n<p>C&aacute;ch Đ&agrave; Lạt 40km v&agrave; kh&ocirc;ng nằm tr&ecirc;n tuyến đường du lịch phổ biến n&ecirc;n c&oacute; lẽ chỉ những người th&iacute;ch kh&aacute;m ph&aacute; gi&aacute; trị văn h&oacute;a qua c&aacute;c c&ocirc;ng tr&igrave;nh kiến tr&uacute;c mới chịu kh&oacute; lặn lội về đ&acirc;y, để ngắm vẻ đẹp rất kh&aacute;c lạ của nh&agrave; thờ n&agrave;y.</p>\r\n<p><strong>Kiến tr&uacute;c đơn giản, đậm chất văn h&oacute;a bản địa</strong></p>\r\n<p>Từ đường số 13 tr&ecirc;n Quốc lộ 27, đi khoảng 7km, bạn sẽ tới một lối nhỏ rợp c&acirc;y xanh dẫn v&agrave;o nh&agrave; thờ. B&oacute;ng d&aacute;ng nh&agrave; thờ đ&atilde; thấp tho&aacute;ng xuất hiện trong khu rừng th&ocirc;ng m&aacute;t rượi. Một kh&ocirc;ng gian ho&agrave;n to&agrave;n mở, kh&ocirc;ng tường r&agrave;o, kh&ocirc;ng c&oacute; những c&aacute;nh cổng sắt ki&ecirc;n cố.</p>\r\n<p>Nh&agrave; thờ được x&acirc;y dựng theo bản vẽ thiết kế &ndash; đồ &aacute;n cao học của đ&ocirc;i vợ chồng kiến tr&uacute;c sư người Việt, Vũ Thị Thu Hương v&agrave; Nguyễn Tuần Dũng &ndash; học tại Trường đại học Kỹ thuật Berlin, dựa tr&ecirc;n nền &yacute; tưởng của cha xứ cai quản địa phận Ka Đơn, Linh mục Nguyễn Đức Ngọc.</p>\r\n<p>Vị linh mục đưa ra &ldquo;đầu b&agrave;i&rdquo; cho c&aacute;c kiến tr&uacute;c sư trẻ khi họ t&igrave;m đến &ocirc;ng: kh&ocirc;ng nhiều m&agrave;u sắc, tận dụng được vẻ đẹp tự nhi&ecirc;n của vật liệu sẵn c&oacute; &aacute;p dụng cho c&ocirc;ng tr&igrave;nh, phản &aacute;nh được n&eacute;t văn h&oacute;a v&agrave; t&iacute;nh c&aacute;ch sống mộc mạc, khi&ecirc;m tốn, h&ograve;a m&igrave;nh v&agrave;o thi&ecirc;n nhi&ecirc;n của người Churu.</p>\r\n<p><img style=\"height: auto;\" title=\"Bao quanh nh&agrave; thờ l&agrave; một rừng th&ocirc;ng, kh&ocirc;ng c&oacute; tường v&acirc;y, để bất cứ ai cũng c&oacute; thể gh&eacute; lại v&agrave; viếng thăm\" src=\"https://cdn3.ivivu.com/2019/11/nha-tho-ka-don-linh-hon-churu-giua-rung-lam-vien-ivivu-3.jpg\" alt=\"Bao quanh nh&agrave; thờ l&agrave; một rừng th&ocirc;ng, kh&ocirc;ng c&oacute; tường v&acirc;y, để bất cứ ai cũng c&oacute; thể gh&eacute; lại v&agrave; viếng thăm\" width=\"980\" height=\"653\" /></p>\r\n<p>Bao quanh nh&agrave; thờ l&agrave; một rừng th&ocirc;ng, kh&ocirc;ng c&oacute; tường v&acirc;y, để bất cứ ai cũng c&oacute; thể gh&eacute; lại v&agrave; viếng thăm</p>\r\n<p>&Yacute; tưởng của vị linh mục được c&aacute;c kiến tr&uacute;c sư đang sống ở ch&acirc;u &Acirc;u tu&acirc;n thủ chặt chẽ. Kh&ocirc;ng một đường n&eacute;t n&agrave;o của kiến tr&uacute;c T&acirc;y phương lẫn v&agrave;o bản sắc văn h&oacute;a Churu bản địa trong bản vẽ thiết kế.</p>\r\n<p>Sau bốn năm x&acirc;y dựng, năm 2014&nbsp;<em>nh&agrave; thờ Ka Đơn</em>&nbsp;đ&atilde; h&igrave;nh th&agrave;nh với m&aacute;i lợp ng&oacute;i, v&aacute;ch tường được kết hợp giữa k&iacute;nh v&agrave; nhiều thanh gỗ th&ocirc;ng gh&eacute;p lại. Người d&acirc;n Churu trong th&ocirc;n h&acirc;n hoan v&agrave; hạnh ph&uacute;c khi cử h&agrave;nh th&aacute;nh lễ được nghe ca đo&agrave;n h&aacute;t th&aacute;nh ca v&agrave; nghe cha xứ rao giảng bằng ch&iacute;nh ng&ocirc;n ngữ của d&acirc;n tộc m&igrave;nh trong một kh&ocirc;ng gian ấm &aacute;p, tr&agrave;n ngập &aacute;nh s&aacute;ng v&agrave; h&ograve;a quyện với khung cảnh thi&ecirc;n nhi&ecirc;n.</p>\r\n<p>C&ocirc;ng tr&igrave;nh được trao giải thưởng lớn về Kiến tr&uacute;c Th&aacute;nh quốc tế lần 6 &ndash; 2016 ở &Yacute;.</p>\r\n<p><strong>Bảo t&agrave;ng Churu thu nhỏ</strong></p>\r\n<p><img style=\"height: auto;\" title=\"Nh&agrave; thờ nh&igrave;n như một gian nh&agrave; r&ocirc;ng, nhưng kh&ocirc;ng c&oacute; bậc thang hay bậc tam cấp, để người người khuyết tật đi xe lăn hay người m&ugrave; dễ d&agrave;ng tiếp cận m&agrave; kh&ocirc;ng bị một cản trở n&agrave;o. L&agrave; nh&agrave; thờ C&ocirc;ng gi&aacute;o nhưng kh&ocirc;ng hề c&oacute; đường n&eacute;t n&agrave;o ảnh hưởng của kiến tr&uacute;c Gothic, kh&ocirc;ng m&aacute;i v&ograve;m, kh&ocirc;ng th&aacute;p chu&ocirc;ng nhọn cao v&uacute;t. Đơn giản như một ng&ocirc;i nh&agrave; r&ocirc;ng d&agrave;nh chung d&agrave;nh cho mọi người. Một kết nối h&ograve;a hợp với thi&ecirc;n nhi&ecirc;n giữa tĩnh lặng của kh&ocirc;ng gian nh&agrave; thờ v&agrave; mảng m&agrave;u tươi xanh của rừng c&acirc;y xanh.\" src=\"https://cdn3.ivivu.com/2019/11/nha-tho-ka-don-linh-hon-churu-giua-rung-lam-vien-ivivu-4.jpg\" alt=\"Nh&agrave; thờ nh&igrave;n như một gian nh&agrave; r&ocirc;ng, nhưng kh&ocirc;ng c&oacute; bậc thang hay bậc tam cấp, để người người khuyết tật đi xe lăn hay người m&ugrave; dễ d&agrave;ng tiếp cận m&agrave; kh&ocirc;ng bị một cản trở n&agrave;o. L&agrave; nh&agrave; thờ C&ocirc;ng gi&aacute;o nhưng kh&ocirc;ng hề c&oacute; đường n&eacute;t n&agrave;o ảnh hưởng của kiến tr&uacute;c Gothic, kh&ocirc;ng m&aacute;i v&ograve;m, kh&ocirc;ng th&aacute;p chu&ocirc;ng nhọn cao v&uacute;t. Đơn giản như một ng&ocirc;i nh&agrave; r&ocirc;ng d&agrave;nh chung d&agrave;nh cho mọi người. Một kết nối h&ograve;a hợp với thi&ecirc;n nhi&ecirc;n giữa tĩnh lặng của kh&ocirc;ng gian nh&agrave; thờ v&agrave; mảng m&agrave;u tươi xanh của rừng c&acirc;y xanh.\" width=\"980\" height=\"653\" /></p>\r\n<p>Nh&agrave; thờ nh&igrave;n như một gian nh&agrave; r&ocirc;ng, nhưng kh&ocirc;ng c&oacute; bậc thang hay bậc tam cấp, để người người khuyết tật đi xe lăn hay người m&ugrave; dễ d&agrave;ng tiếp cận m&agrave; kh&ocirc;ng bị một cản trở n&agrave;o. L&agrave; nh&agrave; thờ C&ocirc;ng gi&aacute;o nhưng kh&ocirc;ng hề c&oacute; đường n&eacute;t n&agrave;o ảnh hưởng của kiến tr&uacute;c Gothic, kh&ocirc;ng m&aacute;i v&ograve;m, kh&ocirc;ng th&aacute;p chu&ocirc;ng nhọn cao v&uacute;t. Đơn giản như một ng&ocirc;i nh&agrave; r&ocirc;ng d&agrave;nh chung d&agrave;nh cho mọi người. Một kết nối h&ograve;a hợp với thi&ecirc;n nhi&ecirc;n giữa tĩnh lặng của kh&ocirc;ng gian nh&agrave; thờ v&agrave; mảng m&agrave;u tươi xanh của rừng c&acirc;y xanh.</p>\r\n<p>Đến với&nbsp;<strong><em>nh&agrave; thờ Ka Đơn</em></strong>, bạn c&ograve;n được t&igrave;m hiểu th&ecirc;m về văn h&oacute;a v&agrave; con người của nhiều thế hệ d&acirc;n tộc Churu qua bộ sưu tập của linh mục Ngọc v&agrave; cộng sự của &ocirc;ng trong những năm th&aacute;ng cai quản gi&aacute;o xứ. Kh&ocirc;ng kh&aacute;c một bảo t&agrave;ng thu nhỏ, nhiều bộ hiện vật về đời sống d&acirc;n d&atilde;, hoang sơ một thời của người Churu được trưng b&agrave;y trong hai gian nh&agrave; thuộc khu&ocirc;n vi&ecirc;n nh&agrave; thờ.</p>\r\n<p>Bạn c&oacute; thể t&igrave;m thấy ở đ&acirc;y dường như mọi thứ thuộc về đời sống sinh hoạt của người Churu, từ bộ đ&agrave;n đ&aacute;, đ&agrave;n t&rsquo;rưng, d&agrave;n cồng chi&ecirc;ng đến bộ vũ kh&iacute; ph&ograve;ng th&acirc;n v&agrave; săn bắn, chiếc b&igrave;nh rượu, c&aacute;i ch&eacute;n đậm chất n&uacute;i rừng T&acirc;y Nguy&ecirc;n.</p>\r\n<p>C&oacute; những vật dụng giờ đ&acirc;y đ&atilde; thật sự l&agrave; di t&iacute;ch của qu&aacute; khứ v&agrave; lịch sử, kh&ocirc;ng c&ograve;n tồn tại trong đời sống hiện tại nữa. Mỗi vật dụng được trưng b&agrave;y kh&ocirc;ng phải l&agrave; những hiện vật v&ocirc; hồn m&agrave; ẩn chứa trong đ&oacute; một n&eacute;t văn h&oacute;a kh&aacute; sinh động, đa dạng, đậm chất rừng n&uacute;i v&agrave; linh hồn T&acirc;y Nguy&ecirc;n.</p>\r\n<p><img style=\"height: auto;\" title=\"Sản phẩm gốm thủ c&ocirc;ng được l&agrave;m ra bởi đ&ocirc;i tay kh&eacute;o l&eacute;o của người Churu được trưng b&agrave;y kh&aacute; nhiều trong gian nh&agrave; lưu niệm do cha xứ lập ra\" src=\"https://cdn3.ivivu.com/2019/11/nha-tho-ka-don-linh-hon-churu-giua-rung-lam-vien-ivivu-6.jpg\" alt=\"Sản phẩm gốm thủ c&ocirc;ng được l&agrave;m ra bởi đ&ocirc;i tay kh&eacute;o l&eacute;o của người Churu được trưng b&agrave;y kh&aacute; nhiều trong gian nh&agrave; lưu niệm do cha xứ lập ra\" width=\"980\" height=\"653\" /></p>\r\n<p>Sản phẩm gốm thủ c&ocirc;ng được l&agrave;m ra bởi đ&ocirc;i tay kh&eacute;o l&eacute;o của người Churu được trưng b&agrave;y kh&aacute; nhiều trong gian nh&agrave; lưu niệm do cha xứ lập ra</p>\r\n<p><img style=\"height: auto;\" title=\"Gian nh&agrave; lưu niệm lưu trữ kh&aacute; nhiểu hiện vật phong ph&uacute; về đời sống sinh họa v&agrave; văn h&oacute;a của người Churu\" src=\"https://cdn3.ivivu.com/2019/11/nha-tho-ka-don-linh-hon-churu-giua-rung-lam-vien-ivivu-7.jpg\" alt=\"Gian nh&agrave; lưu niệm lưu trữ kh&aacute; nhiểu hiện vật phong ph&uacute; về đời sống sinh họa v&agrave; văn h&oacute;a của người Churu\" width=\"980\" height=\"653\" /></p>\r\n<p>Gian nh&agrave; lưu niệm lưu trữ kh&aacute; nhiểu hiện vật phong ph&uacute; về đời sống sinh họa v&agrave; văn h&oacute;a của người Churu</p>\r\n<p><img style=\"height: auto;\" title=\"Những bộ đ&agrave;n đ&aacute; c&ugrave;ng c&aacute;c loại nhạc cụ ti&ecirc;u biểu được người Churu sử dụng trong đời sống văn h&oacute;a v&agrave; tinh thần\" src=\"https://cdn3.ivivu.com/2019/11/nha-tho-ka-don-linh-hon-churu-giua-rung-lam-vien-ivivu-8.jpg\" alt=\"Những bộ đ&agrave;n đ&aacute; c&ugrave;ng c&aacute;c loại nhạc cụ ti&ecirc;u biểu được người Churu sử dụng trong đời sống văn h&oacute;a v&agrave; tinh thần\" width=\"980\" height=\"653\" /></p>\r\n<p>Những bộ đ&agrave;n đ&aacute; c&ugrave;ng c&aacute;c loại nhạc cụ ti&ecirc;u biểu được người Churu sử dụng trong đời sống văn h&oacute;a v&agrave; tinh thần</p>\r\n<p><img style=\"height: auto;\" title=\"Th&aacute;ng 10 cũng l&agrave; l&uacute;c Đơn Dương v&agrave;o m&ugrave;a hoa quỳ nở. Bạn h&atilde;y gh&eacute; qua Ka Đơn để chi&ecirc;m ngưỡng vẻ đẹp của ng&ocirc;i nh&agrave; thờ. Sự b&igrave;nh dị, giản đơn của một kh&ocirc;ng gian t&acirc;m linh h&ograve;a nhịp với sự b&igrave;nh y&ecirc;n, tĩnh lặng của thi&ecirc;n nhi&ecirc;n v&ugrave;ng n&uacute;i chắc chắn sẽ l&agrave;m t&acirc;m hồn bạn thanh tho&aacute;t v&agrave; nhẹ nh&agrave;ng hơn...\" src=\"https://cdn3.ivivu.com/2019/11/nha-tho-ka-don-linh-hon-churu-giua-rung-lam-vien-ivivu-9.jpg\" alt=\"Th&aacute;ng 10 cũng l&agrave; l&uacute;c Đơn Dương v&agrave;o m&ugrave;a hoa quỳ nở. Bạn h&atilde;y gh&eacute; qua Ka Đơn để chi&ecirc;m ngưỡng vẻ đẹp của ng&ocirc;i nh&agrave; thờ. Sự b&igrave;nh dị, giản đơn của một kh&ocirc;ng gian t&acirc;m linh h&ograve;a nhịp với sự b&igrave;nh y&ecirc;n, tĩnh lặng của thi&ecirc;n nhi&ecirc;n v&ugrave;ng n&uacute;i chắc chắn sẽ l&agrave;m t&acirc;m hồn bạn thanh tho&aacute;t v&agrave; nhẹ nh&agrave;ng hơn...\" width=\"980\" height=\"653\" /></p>\r\n<p>Th&aacute;ng 10 cũng l&agrave; l&uacute;c Đơn Dương v&agrave;o m&ugrave;a hoa quỳ nở. Bạn h&atilde;y gh&eacute; qua Ka Đơn để chi&ecirc;m ngưỡng vẻ đẹp của ng&ocirc;i nh&agrave; thờ. Sự b&igrave;nh dị, giản đơn của một kh&ocirc;ng gian t&acirc;m linh h&ograve;a nhịp với sự b&igrave;nh y&ecirc;n, tĩnh lặng của thi&ecirc;n nhi&ecirc;n v&ugrave;ng n&uacute;i chắc chắn sẽ l&agrave;m t&acirc;m hồn bạn thanh tho&aacute;t v&agrave; nhẹ nh&agrave;ng hơn&hellip;</p>', '35.jpg', 0);
INSERT INTO `tin_tuc` (`id_news`, `title_news`, `desc_news`, `image`, `total_views`) VALUES
(15, '<p>Bụi kim cương huyền ảo ở nơi lạnh nhất Nhật Bản</p>', '<p><img style=\"height: auto;\" title=\"Tới Hokkaido (Nhật Bản) m&ugrave;a tuyết, du kh&aacute;ch n&agrave;o cũng ao ước được chi&ecirc;m ngưỡng khung cảnh trắng tinh kh&ocirc;i c&ugrave;ng những b&ocirc;ng tuyết rơi dưới &aacute;nh nắng Mặt Trời lung linh, đẹp tựa kim cương. Hiện tượng bụi kim cương đ&atilde; trở th&agrave;nh &quot;đặc sản&quot; của v&ugrave;ng đất lạnh nhất Nhật Bản v&agrave;o m&ugrave;a đ&ocirc;ng. Ảnh: Nguyễn Thế Vinh.\" src=\"https://cdn3.ivivu.com/2019/11/bui-kim-cuong-huyen-ao-o-noi-lanh-nhat-nhat-ban-ivivu-1.jpg\" alt=\"Tới Hokkaido (Nhật Bản) m&ugrave;a tuyết, du kh&aacute;ch n&agrave;o cũng ao ước được chi&ecirc;m ngưỡng khung cảnh trắng tinh kh&ocirc;i c&ugrave;ng những b&ocirc;ng tuyết rơi dưới &aacute;nh nắng Mặt Trời lung linh, đẹp tựa kim cương. Hiện tượng bụi kim cương đ&atilde; trở th&agrave;nh &quot;đặc sản&quot; của v&ugrave;ng đất lạnh nhất Nhật Bản v&agrave;o m&ugrave;a đ&ocirc;ng. Ảnh: Nguyễn Thế Vinh.\" width=\"1024\" height=\"683\" /></p>\r\n<p>Tới&nbsp;<strong>du lịch Hokkaido</strong>&nbsp;(Nhật Bản) m&ugrave;a tuyết, du kh&aacute;ch n&agrave;o cũng ao ước được chi&ecirc;m ngưỡng khung cảnh trắng tinh kh&ocirc;i c&ugrave;ng những b&ocirc;ng tuyết rơi dưới &aacute;nh nắng Mặt Trời lung linh, đẹp tựa kim cương. Hiện tượng bụi kim cương đ&atilde; trở th&agrave;nh &ldquo;đặc sản&rdquo; của v&ugrave;ng đất lạnh nhất Nhật Bản v&agrave;o m&ugrave;a đ&ocirc;ng. Ảnh: Nguyễn Thế Vinh.</p>\r\n<p><img style=\"height: auto;\" title=\"Bụi kim cương h&igrave;nh th&agrave;nh khi nhiệt độ đ&oacute;ng băng xuống dưới &acirc;m 15 độ C. Những tinh thể tuyết hoặc hơi ẩm kết tinh rồi rơi xuống đ&uacute;ng l&uacute;c &aacute;nh Mặt Trời phản chiếu, tạo th&agrave;nh một luồng bụi s&aacute;ng lấp l&aacute;nh tựa những vi&ecirc;n kim cương. Tương truyền bụi kim cương l&agrave; dấu ch&acirc;n của c&aacute;c ti&ecirc;n nữ. V&agrave;o m&ugrave;a đ&ocirc;ng lạnh gi&aacute;, c&aacute;c ti&ecirc;n nữ gi&aacute;ng trần để gi&uacute;p đỡ những người lang thang, những lữ kh&aacute;ch lạc trong tuyết. Ảnh: yosshy123.\" src=\"https://cdn3.ivivu.com/2019/11/bui-kim-cuong-huyen-ao-o-noi-lanh-nhat-nhat-ban-ivivu-2.jpg\" alt=\"Bụi kim cương h&igrave;nh th&agrave;nh khi nhiệt độ đ&oacute;ng băng xuống dưới &acirc;m 15 độ C. Những tinh thể tuyết hoặc hơi ẩm kết tinh rồi rơi xuống đ&uacute;ng l&uacute;c &aacute;nh Mặt Trời phản chiếu, tạo th&agrave;nh một luồng bụi s&aacute;ng lấp l&aacute;nh tựa những vi&ecirc;n kim cương. Tương truyền bụi kim cương l&agrave; dấu ch&acirc;n của c&aacute;c ti&ecirc;n nữ. V&agrave;o m&ugrave;a đ&ocirc;ng lạnh gi&aacute;, c&aacute;c ti&ecirc;n nữ gi&aacute;ng trần để gi&uacute;p đỡ những người lang thang, những lữ kh&aacute;ch lạc trong tuyết. Ảnh: yosshy123.\" width=\"1024\" height=\"684\" /></p>\r\n<p>Bụi kim cương h&igrave;nh th&agrave;nh khi nhiệt độ đ&oacute;ng băng xuống dưới &acirc;m 15 độ C. Những tinh thể tuyết hoặc hơi ẩm kết tinh rồi rơi xuống đ&uacute;ng l&uacute;c &aacute;nh Mặt Trời phản chiếu, tạo th&agrave;nh một luồng bụi s&aacute;ng lấp l&aacute;nh tựa những vi&ecirc;n kim cương. Tương truyền bụi kim cương l&agrave; dấu ch&acirc;n của c&aacute;c ti&ecirc;n nữ. V&agrave;o m&ugrave;a đ&ocirc;ng lạnh gi&aacute;, c&aacute;c ti&ecirc;n nữ gi&aacute;ng trần để gi&uacute;p đỡ những người lang thang, những lữ kh&aacute;ch lạc trong tuyết. Ảnh: yosshy123.</p>\r\n<p><img style=\"height: auto;\" title=\"Người d&acirc;n Hokkaido cho rằng bụi kim cương l&agrave; m&oacute;n qu&agrave; mẹ thi&ecirc;n nhi&ecirc;n ban tặng cho mảnh đất lạnh gi&aacute;, &iacute;t t&agrave;i nguy&ecirc;n n&agrave;y. D&acirc;n địa phương thường tổ chức lễ hội ngắm bụi kim cương v&agrave;o th&aacute;ng 1-2 h&agrave;ng năm. Ngắm tuyết rơi m&ugrave;a đ&ocirc;ng đ&atilde; trở th&agrave;nh n&eacute;t văn ho&aacute; độc đ&aacute;o ở Hokkaido, được duy tr&igrave; từ thập ni&ecirc;n 70 thế kỷ trước. Ảnh: 4travel.\" src=\"https://cdn3.ivivu.com/2019/11/bui-kim-cuong-huyen-ao-o-noi-lanh-nhat-nhat-ban-ivivu-3.jpg\" alt=\"Người d&acirc;n Hokkaido cho rằng bụi kim cương l&agrave; m&oacute;n qu&agrave; mẹ thi&ecirc;n nhi&ecirc;n ban tặng cho mảnh đất lạnh gi&aacute;, &iacute;t t&agrave;i nguy&ecirc;n n&agrave;y. D&acirc;n địa phương thường tổ chức lễ hội ngắm bụi kim cương v&agrave;o th&aacute;ng 1-2 h&agrave;ng năm. Ngắm tuyết rơi m&ugrave;a đ&ocirc;ng đ&atilde; trở th&agrave;nh n&eacute;t văn ho&aacute; độc đ&aacute;o ở Hokkaido, được duy tr&igrave; từ thập ni&ecirc;n 70 thế kỷ trước. Ảnh: 4travel.\" width=\"1024\" height=\"678\" /></p>\r\n<p>Người d&acirc;n Hokkaido cho rằng bụi kim cương l&agrave; m&oacute;n qu&agrave; mẹ thi&ecirc;n nhi&ecirc;n ban tặng cho mảnh đất lạnh gi&aacute;, &iacute;t t&agrave;i nguy&ecirc;n n&agrave;y. D&acirc;n địa phương thường tổ chức lễ hội ngắm bụi kim cương v&agrave;o th&aacute;ng 1-2 h&agrave;ng năm. Ngắm tuyết rơi m&ugrave;a đ&ocirc;ng đ&atilde; trở th&agrave;nh n&eacute;t văn ho&aacute; độc đ&aacute;o ở Hokkaido, được duy tr&igrave; từ thập ni&ecirc;n 70 thế kỷ trước. Ảnh: 4travel.</p>\r\n<p><img style=\"height: auto;\" title=\"Một trong những điểm ngắm tuyết đẹp tựa cổ t&iacute;ch ở Hokkaido phải kể đến k&ecirc;nh đ&agrave;o Otaru. Đ&acirc;y l&agrave; nơi l&yacute; tưởng cho c&aacute;c cặp đ&ocirc;i hẹn h&ograve; trong m&ugrave;a lạnh ở Nhật Bản. Với vẻ đẹp l&atilde;ng mạn, k&ecirc;nh Otaru được mệnh danh l&agrave; &quot;Venice phương đ&ocirc;ng&quot;. Ảnh: ikatwm.\" src=\"https://cdn3.ivivu.com/2019/11/bui-kim-cuong-huyen-ao-o-noi-lanh-nhat-nhat-ban-ivivu-4.jpg\" alt=\"Một trong những điểm ngắm tuyết đẹp tựa cổ t&iacute;ch ở Hokkaido phải kể đến k&ecirc;nh đ&agrave;o Otaru. Đ&acirc;y l&agrave; nơi l&yacute; tưởng cho c&aacute;c cặp đ&ocirc;i hẹn h&ograve; trong m&ugrave;a lạnh ở Nhật Bản. Với vẻ đẹp l&atilde;ng mạn, k&ecirc;nh Otaru được mệnh danh l&agrave; &quot;Venice phương đ&ocirc;ng&quot;. Ảnh: ikatwm.\" width=\"1024\" height=\"683\" /></p>\r\n<p>Một trong những điểm ngắm tuyết đẹp tựa cổ t&iacute;ch ở Hokkaido phải kể đến k&ecirc;nh đ&agrave;o Otaru. Đ&acirc;y l&agrave; nơi l&yacute; tưởng cho c&aacute;c cặp đ&ocirc;i hẹn h&ograve; trong m&ugrave;a lạnh ở Nhật Bản. Với vẻ đẹp l&atilde;ng mạn, k&ecirc;nh Otaru được mệnh danh l&agrave; &ldquo;Venice phương đ&ocirc;ng&rdquo;. Ảnh: ikatwm.</p>\r\n<p><img style=\"height: auto;\" title=\"Hokkaido l&agrave; h&ograve;n đảo lớn thứ hai của Nhật Bản, nằm về cực Bắc, gần nước Nga n&ecirc;n c&oacute; kh&iacute; hậu lạnh nhất xứ Ph&ugrave; Tang. V&agrave;o m&ugrave;a h&egrave;, nhiệt độ trung b&igrave;nh ở Hokkaido chỉ khoảng 17 độ C, thậm ch&iacute; một v&agrave;i nơi c&oacute; băng tuyết quanh năm. M&ugrave;a đ&ocirc;ng đến, băng tuyết phủ k&iacute;n h&ograve;n đảo với nhiệt độ trung b&igrave;nh khoảng &acirc;m 4 độ C. Ảnh: ai2myyy.\" src=\"https://cdn3.ivivu.com/2019/11/bui-kim-cuong-huyen-ao-o-noi-lanh-nhat-nhat-ban-ivivu-5.jpg\" alt=\"Hokkaido l&agrave; h&ograve;n đảo lớn thứ hai của Nhật Bản, nằm về cực Bắc, gần nước Nga n&ecirc;n c&oacute; kh&iacute; hậu lạnh nhất xứ Ph&ugrave; Tang. V&agrave;o m&ugrave;a h&egrave;, nhiệt độ trung b&igrave;nh ở Hokkaido chỉ khoảng 17 độ C, thậm ch&iacute; một v&agrave;i nơi c&oacute; băng tuyết quanh năm. M&ugrave;a đ&ocirc;ng đến, băng tuyết phủ k&iacute;n h&ograve;n đảo với nhiệt độ trung b&igrave;nh khoảng &acirc;m 4 độ C. Ảnh: ai2myyy.\" width=\"640\" height=\"426\" /></p>\r\n<p>Hokkaido l&agrave; h&ograve;n đảo lớn thứ hai của Nhật Bản, nằm về cực Bắc, gần nước Nga n&ecirc;n c&oacute; kh&iacute; hậu lạnh nhất xứ Ph&ugrave; Tang. V&agrave;o m&ugrave;a h&egrave;, nhiệt độ trung b&igrave;nh ở Hokkaido chỉ khoảng 17 độ C, thậm ch&iacute; một v&agrave;i nơi c&oacute; băng tuyết quanh năm. M&ugrave;a đ&ocirc;ng đến, băng tuyết phủ k&iacute;n h&ograve;n đảo với nhiệt độ trung b&igrave;nh khoảng &acirc;m 4 độ C. Ảnh: ai2myyy.</p>\r\n<p><img style=\"height: auto;\" title=\"Bởi tuyết rơi d&agrave;y, một số nơi ở Hokkaido trở th&agrave;nh điểm trượt tuyết l&yacute; tưởng như khu Niseko, Furano hay Rusutsu. Miền đất n&agrave;y c&ograve;n hấp dẫn kh&aacute;ch du lịch tới v&agrave;o m&ugrave;a đ&ocirc;ng với lễ hội tuyết Sapporo nổi tiếng. Đi dạo quanh những con đường, ngồi tr&ecirc;n chuyến t&agrave;u băng qua biển, ch&igrave;m trong c&aacute;i lạnh &acirc;m độ thấu xương, ngắm nh&igrave;n cảnh vật phủ tuyết trắng x&oacute;a... l&agrave; những trải nghiệm c&aacute;c t&iacute;n đồ du lịch ấn tượng khi gh&eacute; Hokkaido. Ảnh: ai2myyy.\" src=\"https://cdn3.ivivu.com/2019/11/bui-kim-cuong-huyen-ao-o-noi-lanh-nhat-nhat-ban-ivivu-6.jpg\" alt=\"Bởi tuyết rơi d&agrave;y, một số nơi ở Hokkaido trở th&agrave;nh điểm trượt tuyết l&yacute; tưởng như khu Niseko, Furano hay Rusutsu. Miền đất n&agrave;y c&ograve;n hấp dẫn kh&aacute;ch du lịch tới v&agrave;o m&ugrave;a đ&ocirc;ng với lễ hội tuyết Sapporo nổi tiếng. Đi dạo quanh những con đường, ngồi tr&ecirc;n chuyến t&agrave;u băng qua biển, ch&igrave;m trong c&aacute;i lạnh &acirc;m độ thấu xương, ngắm nh&igrave;n cảnh vật phủ tuyết trắng x&oacute;a... l&agrave; những trải nghiệm c&aacute;c t&iacute;n đồ du lịch ấn tượng khi gh&eacute; Hokkaido. Ảnh: ai2myyy.\" width=\"1024\" height=\"682\" /></p>\r\n<p>Bởi tuyết rơi d&agrave;y, một số nơi ở Hokkaido trở th&agrave;nh điểm trượt tuyết l&yacute; tưởng như khu Niseko, Furano hay Rusutsu. Miền đất n&agrave;y c&ograve;n hấp dẫn kh&aacute;ch du lịch tới v&agrave;o m&ugrave;a đ&ocirc;ng với lễ hội tuyết Sapporo nổi tiếng. Đi dạo quanh những con đường, ngồi tr&ecirc;n chuyến t&agrave;u băng qua biển, ch&igrave;m trong c&aacute;i lạnh &acirc;m độ thấu xương, ngắm nh&igrave;n cảnh vật phủ tuyết trắng x&oacute;a&hellip; l&agrave; những trải nghiệm c&aacute;c du kh&aacute;ch ấn tượng khi du lịch Hokkaido. Ảnh: ai2myyy.</p>\r\n<p><img style=\"height: auto;\" src=\"https://cdn3.ivivu.com/2019/11/bui-kim-cuong-huyen-ao-o-noi-lanh-nhat-nhat-ban-ivivu-11.jpg\" alt=\"bui-kim-cuong-huyen-ao-o-noi-lanh-nhat-nhat-ban-ivivu-11\" width=\"954\" height=\"954\" /><img style=\"height: auto;\" src=\"https://cdn3.ivivu.com/2019/11/bui-kim-cuong-huyen-ao-o-noi-lanh-nhat-nhat-ban-ivivu-10-820x1024.jpg\" alt=\"bui-kim-cuong-huyen-ao-o-noi-lanh-nhat-nhat-ban-ivivu-10\" width=\"820\" height=\"1024\" /><img style=\"height: auto;\" src=\"https://cdn3.ivivu.com/2019/11/bui-kim-cuong-huyen-ao-o-noi-lanh-nhat-nhat-ban-ivivu-9.jpg\" alt=\"bui-kim-cuong-huyen-ao-o-noi-lanh-nhat-nhat-ban-ivivu-9\" width=\"1024\" height=\"1024\" /></p>\r\n<p><img style=\"height: auto;\" src=\"https://cdn3.ivivu.com/2019/11/bui-kim-cuong-huyen-ao-o-noi-lanh-nhat-nhat-ban-ivivu-8.jpg\" alt=\"bui-kim-cuong-huyen-ao-o-noi-lanh-nhat-nhat-ban-ivivu-8\" width=\"1024\" height=\"1021\" /></p>\r\n<p>Tại miền đất lạnh gi&aacute;, khắc nghiệt ở xứ Mặt Trời mọc, du kh&aacute;ch sẽ cảm nhận được vẻ đẹp thi&ecirc;n nhi&ecirc;n h&agrave;i h&ograve;a với cuộc sống con người. B&ecirc;n những c&aacute;nh rừng gi&agrave;, du kh&aacute;ch kh&ocirc;ng kh&oacute; bắt gặp những lo&agrave;i vật hoang d&atilde; xứ lạnh như c&aacute;o, gấu, nai&hellip; Đ&acirc;y l&agrave; n&eacute;t đặc trưng hấp dẫn kh&aacute;ch du lịch đến Hokkaido m&ugrave;a tuyết gi&aacute;.</p>', '36.jpg', 0);

