--
-- Cấu trúc bảng cho bảng `tours`
--

CREATE TABLE `tours` (
  `id_tour` int(11) NOT NULL,
  `name_tour` varchar(200) NOT NULL,
  `image` varchar(200) NOT NULL,
  `price` int(11) NOT NULL,
  `sale_price` int(11) NOT NULL,
  `lich_khoi_hanh` varchar(200) NOT NULL,
  `thoi_gian` varchar(200) NOT NULL,
  `phuong_tien` varchar(200) NOT NULL,
  `id_loai_tour` int(11) NOT NULL,
  `chuong_trinh` text NOT NULL,
  `chinh_sach` text NOT NULL,
  `description` text NOT NULL,
  `khoi_hanh_tu` varchar(200) NOT NULL,
  `dieu_khoan` text NOT NULL,
  `luot_khach` int(11) NOT NULL,
  `tour_cu_the` varchar(200) NOT NULL,
  `ngay_khoi_hanh` date NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4;

--
-- Đang đổ dữ liệu cho bảng `tours`
--

INSERT INTO `tours` (`id_tour`, `name_tour`, `image`, `price`, `sale_price`, `lich_khoi_hanh`, `thoi_gian`, `phuong_tien`, `id_loai_tour`, `chuong_trinh`, `chinh_sach`, `description`, `khoi_hanh_tu`, `dieu_khoan`, `luot_khach`, `tour_cu_the`, `ngay_khoi_hanh`) VALUES
(27, 'Du lịch Huế - Hồ Truồi - Đà Nẵng - Suối Khoáng Nóng Núi Thần Tài - KDL Bà Nà', '6.jpg', 6200000, 300000, 'Thứ 7 hàng tuần', '4 ngày 3 đêm', 'Máy bay.svg', 3, '<p><strong>NG&Agrave;Y 1: TP. HỒ CH&Iacute; MINH - Đ&Agrave; NẴNG - HUẾ (Ăn trưa, chiều)</strong><br />Buổi s&aacute;ng,&nbsp;tập trung tại cổng D3 - Ga đi trong nước, s&acirc;n bay T&acirc;n Sơn Nhất. Khởi h&agrave;nh ra Đ&agrave; Nẵng tr&ecirc;n chuyến bay&nbsp;<strong><em>VJ 624 l&uacute;c 08:30</em></strong>. Đến Đ&agrave; Nẵng, đo&agrave;n di chuyển ra Huế. Buổi chiều, qu&yacute; kh&aacute;ch tham quan&nbsp;<em><strong>Di sản Văn h&oacute;a Thế giới Kinh Th&agrave;nh Huế</strong></em>&nbsp;- Ho&agrave;ng cung của 13 vị Vua triều Nguyễn với c&aacute;c c&ocirc;ng tr&igrave;nh ti&ecirc;u biểu:&nbsp;<em><strong>Ngọ M&ocirc;n, điện Th&aacute;i Ho&agrave;, Tử Cấm Th&agrave;nh, Thế Miếu, Hiển L&acirc;m C&aacute;c, Cửu Đỉnh, Bảo tàng C&ocirc;̉ v&acirc;̣t Cung đình Hu&ecirc;́</strong></em>. Viếng&nbsp;<strong><em>Lăng Tự Đức&nbsp;</em></strong>- nơi c&oacute; phong cảnh sơn thủy hữu t&igrave;nh. Đ&acirc;y l&agrave; một trong những lăng tẩm c&oacute; kiến tr&uacute;c đẹp nhất của c&aacute;c vua ch&uacute;a nh&agrave; Nguyễn. Nghỉ đ&ecirc;m tại Huế</p>\r\n<p><img style=\"height: auto;\" src=\"https://bizweb.dktcdn.net/100/299/077/products/bana-hills.jpg?v=1529554384043\" data-thumb=\"original\" /></p>\r\n<p><strong>NG&Agrave;Y 2: HUẾ - HỒ TRUỒI - Đ&Agrave; NẴNG (Ăn s&aacute;ng, trưa, chiều)</strong><br />Buổi s&aacute;ng, đo&agrave;n trả ph&ograve;ng. Xuống thuyền du ngoạn tr&ecirc;n s&ocirc;ng Hương đến viếng&nbsp;<strong><em>ch&ugrave;a Thi&ecirc;n Mụ</em></strong>&nbsp;- ng&ocirc;i ch&ugrave;a cổ v&agrave; nổi tiếng nhất ở đất Cố đ&ocirc;.&nbsp;Khởi h&agrave;nh đến n&uacute;i Bạch M&atilde;, tham quan&nbsp;<strong><em>Hồ Truồi</em></strong>&nbsp;- thuộc rừng quốc gia Bạch M&atilde;, đứng b&ecirc;n n&agrave;y hồ Qu&yacute; kh&aacute;ch sẽ nh&igrave;n thấy được to&agrave;n cảnh&nbsp;<strong><em>Thiền Viện Tr&uacute;c L&acirc;m</em></strong>&nbsp;- Bạch M&atilde; đẹp như một bức tranh thủy mặc. Qu&yacute; kh&aacute;ch sẽ đi thuyền sang tham quan Thiền viện với Ch&aacute;nh Điện, Tổ Đường, Tam Quan, Lầu Chu&ocirc;ng, Th&aacute;p Trống, Phương Trượng. Di chuyển v&agrave;o Đ&agrave; Nẵng. Dừng ngắm cảnh&nbsp;<strong><em>Lăng C&ocirc;&nbsp;</em></strong>- một trong những vịnh biển đẹp nhất thế giới, do C&acirc;u lạc bộ c&aacute;c vịnh biển đẹp nhất thế giới (Worldbays) b&igrave;nh chọn. Đến Đ&agrave; Nẵng, qu&yacute; kh&aacute;ch nhận ph&ograve;ng. Buổi tối, qu&yacute; kh&aacute;ch tự do dạo phố, ngắm&nbsp;<em><strong>Cầu Rồng</strong></em>,<em><strong>&nbsp;Cầu Trần Thị L&yacute;,</strong></em>&nbsp;trải nghiệm&nbsp;<strong><em>&ldquo;V&ograve;ng quay Mặt trời - Sun Wheel&rdquo;&nbsp;</em></strong>thưởng ngoạn cảnh đẹp&nbsp;Đ&agrave; Nẵng&nbsp;với g&oacute;c nh&igrave;n mới từ độ cao 115m (tự t&uacute;c chi ph&iacute;). Nghỉ đ&ecirc;m tại Đ&agrave; Nẵng.</p>\r\n<p><strong>NG&Agrave;Y 3: Đ&Agrave; NẴNG - KDL B&Agrave; N&Agrave; - HỘI AN (Ăn s&aacute;ng, trưa, chiều)</strong><br />Khởi hành đến&nbsp;<strong><em>KDL B&agrave; N&agrave; Hills</em></strong>. L&ecirc;n B&agrave; N&agrave; bằng hệ thống c&aacute;p treo đạt 2 kỷ lục Guinness, ngắm to&agrave;n cảnh n&uacute;i non h&ugrave;ng vỹ v&agrave; tận hưởng kh&iacute; hậu trong l&agrave;nh. Viếng&nbsp;<em><strong>ch&ugrave;a Linh Ứng</strong></em>, kh&aacute;m ph&aacute;&nbsp;<strong><em>Fantasy Park</em></strong>&nbsp;- khu vui chơi giải tr&iacute; trong nh&agrave; đẳng cấp quốc tế (miễn ph&iacute; 105 tr&ograve; chơi) v&agrave; tr&ograve; chơi Hiệp sĩ Thần thoại (M&aacute;ng trượt). Dạo bộ giữa&nbsp;<strong><em>Khu l&agrave;ng Ph&aacute;p&nbsp;</em></strong>một kh&ocirc;ng gian kiến tr&uacute;c t&aacute;i hiện sinh động nước Ph&aacute;p thời cận đại đầy l&atilde;ng mạn, sang trọng v&agrave; lịch l&atilde;m. Trải nghiệm&nbsp;<em><strong>T&agrave;u hỏa leo n&uacute;i, tham quan 9 vườn hoa, Hầm rượu cổ Debay&nbsp;</strong></em>.Tự t&uacute;c chi ph&iacute; kh&aacute;m ph&aacute; Khu trưng b&agrave;y tượng s&aacute;p - duy nhất tại Việt Nam. Buổi chiều, đo&agrave;n tham quan&nbsp;<strong><em>Phố cổ Hội An&nbsp;</em></strong>với c&aacute;c c&ocirc;ng tr&igrave;nh ti&ecirc;u biểu:&nbsp;<strong><em>Ch&ugrave;a Cầu Nhật Bản, ch&ugrave;a &Ocirc;ng, hội qu&aacute;n Ph&uacute;c Kiến, khu phố&nbsp;đ&egrave;n lồng.</em></strong>&nbsp;Trở về Đ&agrave; Nẵng. Buổi tối, qu&yacute; kh&aacute;ch tự do dạo phố. Nghỉ đ&ecirc;m tại Đ&agrave; Nẵng.</p>\r\n<p><strong>NG&Agrave;Y 4: Đ&Agrave; NẴNG - SUỐI KHO&Aacute;NG N&Oacute;NG N&Uacute;I THẦN T&Agrave;I - TP. HỒ CH&Iacute; MINH&nbsp;(Ăn s&aacute;ng, trưa)</strong><br />Bu&ocirc;̉i sáng, tham quan&nbsp;<em><strong>C&ocirc;ng vi&ecirc;n Suối kho&aacute;ng n&oacute;ng N&uacute;i Thần T&agrave;i</strong></em>: tắm kho&aacute;ng thư gi&atilde;n tại c&aacute;c hồ tự nhi&ecirc;n ngo&agrave;i trời, thư gi&atilde;n với x&ocirc;ng hơi kh&ocirc; - ướt, vui chơi tại C&ocirc;ng vi&ecirc;n nước, tham quan v&agrave; ng&acirc;m ch&acirc;n trực tiếp tại một trong những điểm ph&aacute;t lộ tại mỏ kho&aacute;ng (tự t&uacute;c chi ph&iacute; c&aacute;c loại h&igrave;nh dịch vụ: tắm kho&aacute;ng ph&ograve;ng ri&ecirc;ng, tắm Onsen, tắm b&ugrave;n, tắm tr&agrave;-sữa-c&agrave; ph&ecirc;-rượu-sả). Tham quan<em><strong>&nbsp;b&aacute;n đảo Sơn Tr&agrave;</strong></em>, ngắm cảnh cảng Ti&ecirc;n Sa, viếng&nbsp;<strong><em>ch&ugrave;a Linh Ứng B&atilde;i Bụt</em></strong>&nbsp;- ng&ocirc;i ch&ugrave;a lớn nhất ở th&agrave;nh phố Đ&agrave; Nẵng, chi&ecirc;m b&aacute;i&nbsp;<strong><em>tượng Phật Quan Thế &Acirc;m</em></strong>&nbsp;cao nhất Việt Nam. Ra s&acirc;n bay Đ&agrave; Nẵng, trở về TP. HCM tr&ecirc;n chuyến bay&nbsp;<strong><em>VJ 639 l&uacute;c 17:45</em></strong>. Kết th&uacute;c chương tr&igrave;nh (Qu&yacute; kh&aacute;ch tự t&uacute;c phương tiện từ s&acirc;n bay về nh&agrave;).</p>\r\n<div class=\"ddict_btn\" style=\"top: 37px; left: 534.438px;\"><img src=\"chrome-extension://bpggmmljdiliancllaapiggllnkbjocb/logo/48.png\" /></div>', '<p><strong>Ghi ch&uacute;:</strong><br />- Chương tr&igrave;nh đi thuyền du ngoạn tr&ecirc;n s&ocirc;ng Hương phụ thuộc v&agrave;o điều kiện thời tiết. Nếu kh&ocirc;ng đi thuyền được, qu&yacute; kh&aacute;ch sẽ đến viếng ch&ugrave;a Thi&ecirc;n Mụ bằng xe<br />- Điểm tham quan c&oacute; thể sắp xếp lại cho ph&ugrave; hợp m&agrave; vẫn bảo đảm đầy đủ nội dung của từng chương tr&igrave;nh<br />- Giờ bay c&oacute; thể bị thay đổi bởi Vietjet Air</p>\r\n<p><strong>* V&eacute; trẻ em</strong><br />- Trẻ em dưới 2 tuổi: thu 300.000đ/ trẻ. &nbsp;Gia đ&igrave;nh tự lo cho b&eacute; ăn ngủ<br />- Trẻ em từ 2 đến dưới 6 tuổi: mua 100% VMB người lớn. Gia đ&igrave;nh tự lo cho b&eacute; ăn ngủ v&agrave; tự trả ph&iacute; tham quan (nếu c&oacute;).<br />- Hai người lớn chỉ được k&egrave;m một trẻ em dưới 6 tu&ocirc;̉i. Từ trẻ thứ 2 trở l&ecirc;n, mỗi em phải chịu mức giá tương đương trẻ em từ 6 đ&ecirc;́n 11 tu&ocirc;̉i<br />- Trẻ em từ 6 đ&ecirc;́n 11 tuổi: ti&ecirc;u chuẩn gồm VMB, ăn uống và tham quan theo chương tr&igrave;nh, ngủ chung giường với phụ huynh<br />- Trẻ em tr&ecirc;n 11 tu&ocirc;̉i: &aacute;p dụng gi&aacute; v&agrave; c&aacute;c ti&ecirc;u chuẩn dịch vụ như người lớn</p>\r\n<p><strong>* Gi&aacute; tour bao gồm:&nbsp;&nbsp;</strong><br />- Chi ph&iacute; xe m&aacute;y lạnh phục vụ theo chương tr&igrave;nh.<br />- V&eacute; m&aacute;y bay khứ hồi<br />- Chi ph&iacute; kh&aacute;ch sạn theo ti&ecirc;u chuẩn 2 kh&aacute;ch/ph&ograve;ng<br />- Chi ph&iacute; ăn - uống theo chương tr&igrave;nh.<br />- Chi ph&iacute; tham quan v&agrave; t&agrave;u thuyền.<br />- Chi ph&iacute; Hướng dẫn vi&ecirc;n tiếng Việt.<br />- Qu&agrave; tặng: N&oacute;n, nước suối, khăn lạnh, viết</p>', '<p>- Viếng ch&ugrave;a Thi&ecirc;n Mụ - ng&ocirc;i ch&ugrave;a cổ v&agrave; nổi tiếng nhất ở đất Cố đ&ocirc;</p>\r\n<p>- Tham quan Thiền Viện Tr&uacute;c L&acirc;m - Bạch M&atilde; đẹp như một bức tranh thủy mặc</p>\r\n<p>- Vui chơi giải tr&iacute; tại KDL B&agrave; N&agrave; Hills</p>\r\n<p>- Tắm kho&aacute;ng thư gi&atilde;n tại Suối Kho&aacute;ng N&oacute;ng N&uacute;i Thần T&agrave;i</p>\r\n<div class=\"ddict_btn\" style=\"top: 26px; left: 717.65px;\"><img src=\"chrome-extension://bpggmmljdiliancllaapiggllnkbjocb/logo/48.png\" /></div>', 'TP.Hồ Chí Minh', '<p>Điều khoản &amp; Quy định đang cập nhật.</p>', 1602, 'Đà Nẵng', '0000-00-00'),
(28, 'Du lịch Đà Nẵng - KDL Bà Nà - Hội An - Cố Đô Huế', '10.png', 6500000, 200000, 'Thứ 7 hàng tuần', '3 ngày 2 đêm', 'Máy bay.svg', 3, '<p><strong>NG&Agrave;Y 1: HCM - Đ&Agrave; NẴNG - HỘI AN (Ăn trưa, chiều)</strong><br />10:00 Xe v&agrave; hướng dẫn vi&ecirc;n Saigontourist đ&oacute;n Qu&yacute; kh&aacute;ch tại 55B Phan Chu Trinh, H&agrave; Nội đưa ra s&acirc;n bay Nội B&agrave;i đ&aacute;p chuyến bay&nbsp;<em><strong>VN173 (DKCC 12:30)/VN175 (DKCC 14:00)&nbsp;&nbsp;</strong></em>&nbsp;đi Đ&agrave; Nẵng. (Ăn trưa tự t&uacute;c). Tới s&acirc;n bay Đ&agrave; Nẵng xe đưa qu&yacute; kh&aacute;ch về nhận ph&ograve;ng kh&aacute;ch sạn. 15:00 Khởi h&agrave;nh đi Hội An, thăm&nbsp;<strong><em>phố cổ Hội An, Hội Qu&aacute;n Ph&uacute;c Kiến, ch&ugrave;a Cầu Nhật Bản, khu phố Đ&egrave;n Lồng</em></strong>&nbsp;chi&ecirc;m ngưỡng cuộc sống b&igrave;nh dị v&agrave; ấm c&uacute;ng của người d&acirc;n Phố Cổ. Ăn tối tại Hội An.Tự do tham quan v&agrave; vui chơi giải tr&iacute; tại C&ocirc;ng vi&ecirc;n Asean Park . Nghỉ đ&ecirc;m tại Đ&agrave; Nẵng.</p>\r\n<p><img style=\"height: auto;\" src=\"https://bizweb.dktcdn.net/100/299/077/files/du-lich-hue-co-gi-dep-h2.jpg?v=1529554042861\" data-thumb=\"original\" /></p>\r\n<p><strong><em>Phố cổ Hội An</em></strong></p>\r\n<p><strong>NG&Agrave;Y 2: Đ&Agrave; NẴNG - KHU DU LỊCH B&Agrave; N&Agrave; (Ăn s&aacute;ng, trưa, chiều)</strong><br />Qu&yacute; kh&aacute;ch đi&nbsp;<em><strong>khu du lịch B&agrave; N&agrave; - N&uacute;i Ch&uacute;a</strong></em>&nbsp;theo tuyến đường du lịch Nguyễn Tất Th&agrave;nh - tuyến đường ven biển đẹp nhất Miền Trung, ngắm cầu treo Thuận Phước - c&acirc;y cầu d&acirc;y võng d&agrave;i nhất Việt Nam. L&ecirc;n đỉnh&nbsp;<em><strong>B&agrave; N&agrave;&nbsp;</strong></em>bằng hệ thống c&aacute;p treo đạt 2 kỷ lục Guinness, ngắm to&agrave;n cảnh n&uacute;i non h&ugrave;ng vỹ v&agrave; tận hưởng kh&iacute; hậu trong l&agrave;nh, tươi m&aacute;t. (chi ph&iacute; c&aacute;p treo tự t&uacute;c). Đến B&agrave; N&agrave;, tham quan<strong><em>&nbsp;ch&ugrave;a Linh Ứng,</em></strong>&nbsp;kh&aacute;m ph&aacute; khu vui chơi - giải tr&iacute; trong nh&agrave; mang đẳng cấp quốc tế&nbsp;<strong><em>Fantasy Park</em></strong>&nbsp;- nơi c&oacute; rất nhiều tr&ograve; chơi từ vui nhộn đến cảm gi&aacute;c mạnh, ph&ugrave; hợp mọi lứa tuổi. Ăn trưa tại B&agrave; N&agrave;. Sau đ&oacute; quay về trung t&acirc;m, tự do nghỉ ngơi tắm biển. Ăn tối &amp; nghỉ đ&ecirc;m tại Đ&agrave; Nẵng.</p>\r\n<p><strong>NG&Agrave;Y 3: Đ&Agrave; NẴNG - HUẾ (Ăn s&aacute;ng, trưa, chiều)</strong><br />Ăn s&aacute;ng tại kh&aacute;ch sạn, xe đưa qu&yacute; kh&aacute;ch tham quan&nbsp;<strong><em>Bảo T&agrave;ng Đ&agrave; Nẵng, Th&agrave;nh Điện Hải&nbsp;</em></strong>- nơi mở đầu cuộc x&acirc;m lược của Thực d&acirc;n Ph&aacute;p v&agrave;o Việt Nam, &nbsp;chụp h&igrave;nh kỷ niệm tại Trung t&acirc;m H&agrave;nh ch&iacute;nh TP. Đ&agrave; Nẵng,&nbsp;<strong><em>Cầu Rồng, S&ocirc;ng H&agrave;n</em></strong>. Ăn trưa, khởi h&agrave;nh đi Huế. Tới nơi, nhận ph&ograve;ng kh&aacute;ch sạn, tự do nghỉ ngơi. Sau bữa tối, đo&agrave;n l&ecirc;n Thuyền đi Nghe<strong><em>&nbsp;Ca Huế tr&ecirc;n s&ocirc;ng Hương</em></strong>. Nghỉ đ&ecirc;m tại Huế.</p>\r\n<p><strong>NG&Agrave;Y 4: HU&Ecirc; - H&Agrave; NỘI (Ăn s&aacute;ng, trưa, tối)</strong><br />Ăn s&aacute;ng tại kh&aacute;ch sạn, sau đ&oacute; tự do nghỉ ngơi hoặc đi&nbsp;<em><strong>chợ Đ&ocirc;ng Ba</strong></em>&nbsp;mua đặc sản xứ Huế về l&agrave;m qu&agrave; cho người th&acirc;n. Sau bữa trưa, xe đưa đo&agrave;n đi tham quan Kinh Th&agrave;nh Huế với<strong><em>&nbsp;Ngọ M&ocirc;n, Cửu Đỉnh, điện Th&aacute;i H&ograve;a, Ch&ugrave;a Thi&ecirc;n Mụ</em></strong>. Ăn tối tại Nh&agrave; h&agrave;ng. Sau đ&oacute; tiễn đo&agrave;n ra s&acirc;n bay Ph&uacute; B&agrave;i đ&aacute;p chuyến bay về H&agrave; Nội&nbsp;<strong><em>(VN1546 dự kiến Cc 20:50)</em></strong>. Về đến s&acirc;n bay Nội B&agrave;i, xe v&agrave; HDV đ&oacute;n qu&yacute; kh&aacute;ch trở về điểm hẹn ban đầu. Kết th&uacute;c chuyến tham quan.</p>\r\n<div class=\"ddict_btn\" style=\"top: 25px; left: 588.4px;\"><img src=\"chrome-extension://bpggmmljdiliancllaapiggllnkbjocb/logo/48.png\" /></div>', '<p><strong>* Gi&aacute; tour bao gồm:&nbsp;&nbsp;</strong><br />- Chi ph&iacute; v&eacute; m&aacute;y bay khứ hồi chặng HAN - DAD - HUẾ - HAN của Vietnamairlines<br />- Chi ph&iacute; xe m&aacute;y lạnh phục vụ theo chương tr&igrave;nh.<br />- Chi ph&iacute; ăn - uống theo chương tr&igrave;nh<br />- Chi ph&iacute; kh&aacute;ch sạn theo ti&ecirc;u chuẩn 3 sao (2 kh&aacute;ch/ph&ograve;ng, lẻ nam hoặc nữ sẽ nghỉ ph&ograve;ng ba).<br />- Chi ph&iacute; tham quan, hướng dẫn vi&ecirc;n tiếng Việt theo chương tr&igrave;nh.<br />- Nước suối, khăn lạnh tr&ecirc;n xe.<br />- Chi ph&iacute; đưa đ&oacute;n s&acirc;n bay.</p>\r\n<p><strong>* Gi&aacute; v&eacute; d&agrave;nh cho trẻ em:</strong><br />- Trẻ em dưới 2 tuổi :&nbsp; 290.000 VNĐ<br />- Trẻ em từ 2 - 5 tuổi : 1.890.000 VNĐ &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Bố mẹ tự lo ăn nghỉ cho b&eacute;<br />- Trẻ em từ 5 - 11 tuổi : 4.690.000 VNĐ&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Ngủ chung giường với bố mẹ<br />- Trẻ em tr&ecirc;n 11 tuổi : &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;T&iacute;nh như người lớn</p>', '<p>- Thỏa sức vui chơi tại Khu vui chơi giải tr&iacute; Asean Park</p>\r\n<p>- Tham quan Hội An - đ&ocirc; thị cổ nổi tiếng l&agrave; thương cảng sầm uất từ thế kỷ 15</p>\r\n<p>- Khu du lịch B&agrave; N&agrave; với tuyến c&aacute;p treo d&agrave;i nhất thế giới v&agrave; khu vui chơi hấp dẫn</p>\r\n<p>- Huế - th&agrave;nh phố mộng mơ với những n&eacute;t quyến rũ đặc trưng của văn ho&aacute; Cố đ&ocirc;</p>\r\n<div class=\"ddict_btn\" style=\"top: 21px; left: 722.663px;\"><img src=\"chrome-extension://bpggmmljdiliancllaapiggllnkbjocb/logo/48.png\" /></div>', 'TP.Hồ Chí Minh', '<p>Điều khoản &amp; Quy định đang cập nhật.</p>', 0, 'Hà Nội', '0000-00-00'),
(29, 'Du lịch Hà Nội - Ninh Bình - Cát Bà - Hạ Long - Hải Dương', '4.jpg', 7500000, 1100000, 'Thứ 3 hàng tuần', '5 ngày 4 đêm', 'Máy bay.svg', 1, '<p><strong>NG&Agrave;Y 1: TP. HCM - H&Agrave; NỘI - NINH B&Igrave;NH&nbsp;(Ăn trưa, chiều)</strong><br />Buổi s&aacute;ng, qu&yacute; kh&aacute;ch tập trung tại Cổng D4 - Ga đi trong nước - S&acirc;n bay T&acirc;n Sơn Nhất. Khởi h&agrave;nh ra H&agrave; Nội (chuyến bay<strong><em>&nbsp;VJ 126 l&uacute;c 07:10 hoặc VJ 128 l&uacute;c 08:15 t&ugrave;y ng&agrave;y</em></strong>). Đ&aacute;p xuống s&acirc;n bay Nội B&agrave;i, khởi h&agrave;nh đến Ninh B&igrave;nh, thăm Viếng&nbsp;<strong><em>ch&ugrave;a B&aacute;i Đ&iacute;nh&nbsp;</em></strong>- ng&ocirc;i ch&ugrave;a của những kỷ lục như: Khu ch&ugrave;a rộng nhất Việt Nam, thờ Tượng Phật bằng đồng lớn nhất Đ&ocirc;ng Nam &Aacute; v.v&hellip;. Tham quan&nbsp;<strong><em>KDL Tr&agrave;ng An&nbsp;</em></strong>-&nbsp;KDL nằm trong quần thể danh thắng Tr&agrave;ng An đ&atilde; được UNESCO c&ocirc;ng nhận di sản hỗn hợp đầu ti&ecirc;n của Việt Nam v&agrave; khu vực Đ&ocirc;ng Nam &Aacute; (đạt cả hai ti&ecirc;u ch&iacute; về văn h&oacute;a v&agrave; thi&ecirc;n nhi&ecirc;n) v&agrave;o ng&agrave;y 25/6/2014. Đặc biệt, kh&aacute;m ph&aacute; bối cảnh &ldquo;L&agrave;ng thổ d&acirc;n&rdquo; kỳ b&iacute; trong phim bom tấn Kong: Đảo Đầu L&acirc;u được phục dựng nguy&ecirc;n mẫu, với những t&uacute;p lều nứa ch&oacute;p nhọn, những thửa ruộng c&ugrave;ng nhiều dụng cụ sinh hoạt bằng đất, tre, nứa, v&agrave; c&aacute;c thổ d&acirc;n l&agrave; c&aacute;c diễn vi&ecirc;n từng tham gia đ&oacute;ng phim. Nghỉ đ&ecirc;m tại Ninh B&igrave;nh.</p>\r\n<p><img style=\"height: auto;\" src=\"https://bizweb.dktcdn.net/100/299/077/products/jed1367636408.jpg?v=1529554705153\" data-thumb=\"original\" /></p>\r\n<p><strong>Ng&agrave;y 02: NINH B&Igrave;NH - HẠ LONG - C&Aacute;T B&Agrave; (Ăn s&aacute;ng, trưa,chiều)</strong><br />Khởi h&agrave;nh đi Nam Định, viếng&nbsp;<em><strong>ch&ugrave;a Th&aacute;p Phổ Minh</strong></em>. Tiếp tục qua Th&aacute;i B&igrave;nh, Hải Ph&ograve;ng đến Hạ Long. Đo&agrave;n l&ecirc;n ph&agrave; Tuấn Ch&acirc;u, khởi h&agrave;nh đi C&aacute;t B&agrave;. Từ tr&ecirc;n ph&agrave;, qu&yacute; kh&aacute;ch chi&ecirc;m ngưỡng cảnh quan<em><strong>&nbsp;vịnh Hạ Long</strong></em>&nbsp;-&nbsp; một trong 7 kỳ quan thi&ecirc;n nhi&ecirc;n mới của thế giới. Ph&agrave; cập bến Gia Luận, xe đưa đo&agrave;n đi tr&ecirc;n con đường xuy&ecirc;n&nbsp;<em><strong>vườn quốc gia C&aacute;t B&agrave;</strong></em>&nbsp;v&agrave;o thị trấn. Nghỉ đ&ecirc;m tại C&aacute;t B&agrave;.&nbsp;&nbsp;</p>\r\n<p><strong>Ng&agrave;y 03: C&Aacute;T B&Agrave; - HẠ LONG (Ăn s&aacute;ng, trưa, chiều)</strong><br />Buổi s&aacute;ng, tự do nghỉ ngơi đo&agrave;n tự do nghỉ ngơi hoặc đi theo con đường nhỏ men theo v&aacute;ch đ&aacute; tham quan c&aacute;c<em><strong>&nbsp;b&atilde;i tắm C&aacute;t C&ograve;</strong></em>&nbsp;1, 2, 3,... hoặc đến ngắm to&agrave;n cảnh C&aacute;t B&agrave; từ ph&aacute;o đ&agrave;i Thần C&ocirc;ng (tự t&uacute;c chi ph&iacute;). Buổi chiều, đo&agrave;n trở về Hạ Long. Qu&yacute; kh&aacute;ch tự do thăm th&uacute; phố biển Hạ Long. Nghỉ đ&ecirc;m tại Hạ Long.<br />Lựa chọn (tự t&uacute;c chi ph&iacute; di chuyển &amp; tham quan)<br />- Tham quan&nbsp;<em><strong>Khu Du Lịch Đảo Tuần Ch&acirc;u</strong></em>, xem biểu diễn c&aacute; heo - hải cẩu - sư tử biển, xiếc th&uacute;, biểu diễn nhạc nước v&agrave; &aacute;nh s&aacute;ng laser, game trong nh&agrave;, xe điện dụng, chiếu phim 5D, triễn l&atilde;m hoa đăng, biễu diễn ca m&uacute;a nhạc.&nbsp;<br />- Tham quan&nbsp;<em><strong>Quần thể Du lịch - Giải tr&iacute; Sun World Hạ Long Park</strong></em>, gồm 2 khu c&ocirc;ng vi&ecirc;n vui chơi ven biển B&atilde;i Ch&aacute;y v&agrave;&nbsp; tr&ecirc;n n&uacute;i Ba Đ&egrave;o - được kết nối với nhau bởi hệ thống c&aacute;p treo vượt biển Nữ Ho&agrave;ng đạt 2 kỷ lục thế giới (cabin c&oacute; sức chứa lớn nhất thế giới v&agrave; c&aacute;p treo c&oacute; trụ c&aacute;p cao nhất thế giới so với mặt đất). Trải nghiệm c&aacute;c tr&ograve; chơi mạo hiểm, t&agrave;u lượn si&ecirc;u tốc, c&ocirc;ng vi&ecirc;n nước, khu Vườn Nhật, khu tr&ograve; chơi trong nh&agrave; L&acirc;u Đ&agrave;i Huyền B&iacute;, khu trưng b&agrave;y tượng s&aacute;p, v&agrave; đặc biệt l&agrave; V&ograve;ng quay Mặt Trời - một trong những v&ograve;ng quay cao nhất thế giới.</p>\r\n<p><strong>NG&Agrave;Y 4: HẠ LONG - HẢI PH&Ograve;NG - TP. HCM (Ăn s&aacute;ng, trưa)</strong><br />Buổi s&aacute;ng, xe đưa đo&agrave;n ngắm<em><strong>&nbsp;cầu B&atilde;i Ch&aacute;y</strong></em>, mua sắm tại chợ Hạ Long. Trả ph&ograve;ng. Tham quan&nbsp;<strong><em>bảo t&agrave;ng Quảng Ninh</em></strong>. Khởi h&agrave;nh về Hải Ph&ograve;ng, tham quan cụm<em><strong>&nbsp;di t&iacute;ch Bạch Đằng Giang&nbsp;</strong></em>(TT.Minh Đức , H.Thủy Nguy&ecirc;n) với đền thờ, tượng đ&agrave;i L&ecirc; Đại H&agrave;nh, Ng&ocirc; Quyền, Trần Hưng Đạo v&agrave; khu vực t&aacute;i hiện b&atilde;i cọc tr&ecirc;n s&ocirc;ng Bạch Đằng.&nbsp; Xe đưa đo&agrave;n ra s&acirc;n bay C&aacute;t Bi, tự t&uacute;c ăn chiều. Về TP. Hồ Ch&iacute; Minh (<strong><em>chuyến bay VJ283 l&uacute;c 19:50</em></strong>). Kết th&uacute;c chương tr&igrave;nh (Qu&yacute; kh&aacute;ch tự t&uacute;c phương tiện từ s&acirc;n bay về nh&agrave;).</p>\r\n<div class=\"ddict_btn\" style=\"top: 25px; left: 499.675px;\"><img src=\"chrome-extension://bpggmmljdiliancllaapiggllnkbjocb/logo/48.png\" /></div>', '<p><strong>V&eacute; trẻ em</strong><br />- Trẻ em dưới 2 tuổi: thu 300.000đ. Gia đình tự lo cho bé ăn ngủ<br />- Trẻ em từ 2 đến dưới 6 tuổi: mua 100% VMB người lớn. Gia đ&igrave;nh tự lo cho b&eacute; ăn ngủ v&agrave; tự trả ph&iacute; tham quan (nếu c&oacute;).<br />- Hai người lớn chỉ được k&egrave;m 1 trẻ em dưới 6 tu&ocirc;̉i. Từ trẻ thứ 2 trở l&ecirc;n, mỗi em phải đóng bằng gi&aacute; trẻ em từ 6 đến 11 tuổi<br />- Trẻ em từ 6 - 11 tuổi: ti&ecirc;u chuẩn gồm: VMB, ăn uống và tham quan theo chương tr&igrave;nh, ngủ chung giường với phụ huynh<br />- Trẻ em tr&ecirc;n 11 tu&ocirc;̉i: &aacute;p dụng gi&aacute; v&agrave; c&aacute;c ti&ecirc;u chuẩn dịch vụ như người lớn</p>\r\n<p><strong>* Gi&aacute; tour bao gồm: &nbsp;</strong><br />- Chi ph&iacute; xe m&aacute;y lạnh phục vụ theo chương tr&igrave;nh.<br />- V&eacute; m&aacute;y bay khứ hồi.<br />- Chi ph&iacute; kh&aacute;ch sạn theo ti&ecirc;u chuẩn 2 kh&aacute;ch/ph&ograve;ng<br />- Chi ph&iacute; ăn -&nbsp;uống theo chương tr&igrave;nh.<br />- Chi ph&iacute; tham quan v&agrave; t&agrave;u thuyền.<br />- Chi ph&iacute; Hướng dẫn vi&ecirc;n tiếng Việt.<br />- Qu&agrave; tặng: N&oacute;n, nước suối, khăn lạnh, viết.</p>\r\n<p><strong>* Gi&aacute; tour kh&ocirc;ng bao gồm:&nbsp;&nbsp;</strong><br />-&nbsp;Chi ph&iacute; tham quan - ăn uống ngo&agrave;i chương tr&igrave;nh, giặt ủi, điện thoại v&agrave; c&aacute;c chi ph&iacute; c&aacute; nh&acirc;n kh&aacute;c.<br />- Chi ph&iacute; phụ thu người nước ngo&agrave;i, phụ thu ph&ograve;ng đơn (nếu c&oacute;)</p>', '<p>- T&igrave;m hiểu đời sống của d&acirc;n tộc thiểu số tại bản Lao Chải - Tả Van</p>\r\n<p>- Viếng ch&ugrave;a B&aacute;i Đ&iacute;nh - ng&ocirc;i ch&ugrave;a của những kỷ lục</p>\r\n<p>- Tham quan KDL Tr&agrave;ng An - với hệ thống s&ocirc;ng, suối, c&aacute;c hang xuy&ecirc;n thủy động v&agrave; c&aacute;c d&atilde;y n&uacute;i đ&aacute; v&ocirc;i tr&ugrave;ng điệp</p>\r\n<p>- Viếng ch&ugrave;a Trấn Quốc - danh lam cổ tự được x&acirc;y dựng từ thế kỷ VI</p>\r\n<p>- Du ngoạn vịnh Hạ Long - một trong 7 kỳ quan thi&ecirc;n nhi&ecirc;n mới của thế giới</p>\r\n<div class=\"ddict_btn\" style=\"top: 13px; left: 725.25px;\"><img src=\"chrome-extension://bpggmmljdiliancllaapiggllnkbjocb/logo/48.png\" /></div>', 'Đà Nẵng', '<p>Điều khoản &amp; Quy định đang cập nhật.</p>', 1103, 'Hạ Long', '0000-00-00'),
(30, 'Tour Phan Thiết Mũi Né Resort 3,4* - 3 ngày 2 đêm', '13.jpg', 5000000, 0, 'Thứ 3 hàng tuần', '3 ngày 2 đêm', 'Ô tô.svg', 3, '<p><strong>NG&Agrave;Y 1: TP. HCM - PHAN THIẾT (Ăn s&aacute;ng, trưa, chiều)</strong><br />Đ&oacute;n qu&yacute; kh&aacute;ch tại văn ph&ograve;ng Saigontourist, khởi h&agrave;nh đi B&igrave;nh Thuận. Đến Phan Thiết, v&agrave;o khu resort H&agrave;m Tiến - Mũi N&eacute; nhận ph&ograve;ng. Buổi chiều, qu&yacute; kh&aacute;ch đi&nbsp;<em><strong>H&ograve;n Rơm</strong></em>&nbsp;tham quan đồi c&aacute;t v&agrave;ng dưới t&aacute;c động của gi&oacute; biển đ&atilde; tạo n&ecirc;n những h&igrave;nh dạng rất tuyệt vời. Nghỉ đ&ecirc;m tại Mũi N&eacute;.<img style=\"height: auto;\" src=\"https://bizweb.dktcdn.net/100/299/077/files/member-bio-img-16.jpg?v=1529556615352\" data-thumb=\"original\" /></p>\r\n<p><strong>NG&Agrave;Y 2: THAM QUAN PHAN THIẾT (Ăn s&aacute;ng, chiều / Ăn trưa tự t&uacute;c)</strong><br />Buổi s&aacute;ng, qu&yacute; kh&aacute;ch tự do nghỉ dưỡng tại resort. Tự t&uacute;c ăn trưa. Buổi chiều, xe đưa qu&yacute; kh&aacute;ch đến tham quan<em><strong>&nbsp;Trung t&acirc;m B&ugrave;n kho&aacute;ng Mũi N&eacute;</strong></em>. Qu&yacute; kh&aacute;ch tắm kho&aacute;ng (tự t&uacute;c chi ph&iacute; tắm b&ugrave;n c&aacute;c loại). Tham quan&nbsp;<em><strong>L&acirc;u Đ&agrave;i Rượu Vang</strong></em>. Nghỉ đ&ecirc;m tại Mũi N&eacute;.</p>\r\n<p><strong>NG&Agrave;Y 3: PHAN THIẾT - TP. HCM (Ăn s&aacute;ng, trưa)</strong><br />Buổi s&aacute;ng, qu&yacute; kh&aacute;ch tư do nghỉ dưỡng, tắm biển đến giờ trả ph&ograve;ng. Khởi h&agrave;nh về Tp. HCM. Tr&ecirc;n đường về gh&eacute; mua sắm đặc sản Phan Thiết. Kết th&uacute;c chương tr&igrave;nh.</p>\r\n<div class=\"ddict_btn\" style=\"top: 20px; left: 501.6px;\"><img src=\"chrome-extension://bpggmmljdiliancllaapiggllnkbjocb/logo/48.png\" /></div>', '<p><strong>* Gi&aacute; tour bao gồm:&nbsp;&nbsp;</strong><br />- Chi ph&iacute; xe m&aacute;y lạnh phục vụ theo chương tr&igrave;nh.<br />- Chi ph&iacute; ăn -&nbsp;uống theo chương tr&igrave;nh<br />- Chi ph&iacute; kh&aacute;ch sạn ti&ecirc;u chuẩn 2 kh&aacute;ch/ph&ograve;ng.<br />- Chi ph&iacute; tham quan, hướng dẫn vi&ecirc;n tiếng Việt<br />*Qu&agrave; tặng: N&oacute;n, nước suối, khăn lạnh, viết&hellip;</p>\r\n<p><strong>* Gi&aacute; tour kh&ocirc;ng bao gồm:&nbsp;&nbsp;</strong><br />- Ăn uống ngo&agrave;i chương tr&igrave;nh, giặt ủi, điện thoại v&agrave; c&aacute;c chi ph&iacute; c&aacute; nh&acirc;n<br />- Chi ph&iacute; di chuyển v&agrave; xem Show nhạc kịch nước Huyền Thoại L&agrave;ng Ch&agrave;i<br />- Chi ph&iacute; tắm b&ugrave;n kho&aacute;ng c&aacute;c loại</p>\r\n<p><strong>TH&Ocirc;NG TIN HƯỚNG DẪN</strong></p>\r\n<p><strong>* V&eacute; trẻ em:&nbsp;&nbsp;</strong><br />- V&eacute; tour: Trẻ em từ&nbsp; 6 đến 11 tuổi mua một nửa gi&aacute; v&eacute; người lớn, trẻ em tr&ecirc;n 11 tuổi mua v&eacute; như người lớn.<br />- Đối với trẻ em dưới 6 tuổi, gia đ&igrave;nh tự lo cho b&eacute; ăn ngủ v&agrave; tự trả ph&iacute; tham quan (nếu c&oacute;). Hai người lớn chỉ được k&egrave;m một trẻ em. Từ trẻ thứ 2 trở l&ecirc;n, mỗi em phải 50% gi&aacute; v&eacute; người lớn.<br />- Ti&ecirc;u chuẩn 50% gi&aacute; tour bao gồm: Suất ăn, ghế ngồi v&agrave; ngủ gh&eacute;p chung với gia đ&igrave;nh.</p>', '<p>- Đến H&ograve;n Rơm tham quan đồi c&aacute;t v&agrave;ng dưới t&aacute;c động của gi&oacute; biển đ&atilde; tạo n&ecirc;n những h&igrave;nh dạng rất tuyệt vời</p>\r\n<p>- Thư gi&atilde;n tại Trung t&acirc;m B&ugrave;n kho&aacute;ng Mũi N&eacute;</p>\r\n<div class=\"ddict_btn\" style=\"top: 31px; left: 723.575px;\"><img src=\"chrome-extension://bpggmmljdiliancllaapiggllnkbjocb/logo/48.png\" /></div>', 'TP.Hồ Chí Minh', '<p>Điều khoản &amp; Quy định đang cập nhật.</p>', 11, 'Hà Giang', '0000-00-00'),
(31, 'Du lịch Nha Trang - Hòn Tằm - KDL Vinpearl', '9.jpg', 6500000, 0, 'Thứ 4 hàng tuần', '3 ngày 2 đêm', 'Ô tô.svg', 3, '<p><strong>NG&Agrave;Y 1: HCM - NHA TRANG&nbsp;(Ăn chiều)</strong><br />09:30 Xe đ&oacute;n đo&agrave;n tại văn ph&ograve;ng Saigontourist 55B Phan Chu Trinh đ&aacute;p chuyến bay đi Nha Trang&nbsp;<strong><em>VN1563 (Dự kiến cất c&aacute;nh 11:40 - 13:35)</em></strong>. Đến S&acirc;n bay Cam Ranh, xe đưa đo&agrave;n đi thăm viếng ch&ugrave;a&nbsp;<strong><em>T&ograve;ng L&acirc;m L&ocirc; Sơn</em></strong>, tham quan<strong><em>&nbsp;tượng phật A Di Đ&agrave;</em></strong>&nbsp;cao 44 m,<em><strong>&nbsp;th&aacute;p B&agrave; Ponagar</strong></em>. Về kh&aacute;ch sạn nhận ph&ograve;ng. Nghỉ đ&ecirc;m tại Th&agrave;nh phố biển Nha Trang.</p>\r\n<p><strong>NG&Agrave;Y 2: NHA TRANG - H&Ograve;N TẰM (Ăn s&aacute;ng, trưa, chiều)</strong><br />Xe đưa đo&agrave;n ra bến cảng, kh&aacute;m ph&aacute;&nbsp;<em><strong>H&ograve;n Tằm</strong></em>, chi&ecirc;m ngưỡng&nbsp;<em><strong>vịnh&nbsp;Nha Trang&nbsp;</strong></em>- một trong 29 vịnh đẹp nhất thế giới.Tham quan&nbsp;<strong><em>khu L&agrave;ng Cổ</em></strong>với c&aacute;c hoạt động như : Dệt thổ cẩm, l&agrave;m gốm, nấu rượu. Tiếp tục tham quan khu vui chơi d&agrave;nh cho thiếu nhi. Tự do tắm biển, thư gi&atilde;n trong hồ bơi nước ngọt hoặc tham gia c&aacute;c tr&ograve; chơi như: Ch&egrave;o thuyền kayak, chơi golf, tennis,phao bơi v&agrave; c&aacute;c hoạt động giải tr&iacute; dưới nước (tự t&uacute;c chi ph&iacute;). T&agrave;u đưa kh&aacute;ch trở về bến cảng. Bu&ocirc;̉i chi&ecirc;̀u, tự do dạo chơi ph&ocirc;́ bi&ecirc;̉n. Nghỉ đ&ecirc;m tại&nbsp;Nha Trang.</p>\r\n<p><strong>NG&Agrave;Y 3: NHA TRANG - DỐC LẾT - KDL VINPEARL LAND (Ăn s&aacute;ng, trưa)</strong><br />Đo&agrave;n khởi h&agrave;nh đi&nbsp;<em><strong>Dốc Lết</strong></em>&nbsp;(Một trong những b&atilde;i biển &ecirc;m, đẹp, nổi tiếng của tỉnh Kh&aacute;nh H&ograve;a v&agrave; khu vực miền Trung). Tự do tham quan v&agrave; tắm biển. Trở về lại Nha Trang nghỉ trưa.</p>\r\n<p>Chiều: Đo&agrave;n c&oacute; thể lựa chọn tự do nghỉ ngơi hoặc v&agrave;o tham quan&nbsp;<strong><em>Khu vui chơi giải tr&iacute; Vinpearl Land&nbsp;</em></strong>(Chi ph&iacute; tự t&uacute;c). Khu giải tr&iacute; Vinpearl Land - quy m&ocirc; v&agrave; hiện đại, c&oacute; thể s&aacute;nh ngang với nhiều c&ocirc;ng vi&ecirc;n giải tr&iacute; h&agrave;ng đầu trong khu vực:<br />- Di chuyển sang đảo bằng một trong những hệ thống c&aacute;p treo vượt biển d&agrave;i nhất thế giới<br />- Du ngoạn trong l&ograve;ng đại dương nh&acirc;n tạo, chi&ecirc;m ngưỡng h&agrave;ng trăm lo&agrave;i sinh vật biển đa dạng, phong ph&uacute;, rực rỡ sắc m&agrave;u tại Thủy cung lớn v&agrave; hiện đại nhất Việt Nam.<br />- Thỏa sức tận hưởng cảm gi&aacute;c vui nhộn tại Khu tr&ograve; chơi trong nh&agrave; hoặc chinh phục thử th&aacute;ch cao độ từ h&agrave;ng chục tr&ograve; chơi cảm gi&aacute;c mạnh tại Khu tr&ograve; chơi ngo&agrave;i trời v&agrave; C&ocirc;ng vi&ecirc;n nước ngọt tr&ecirc;n b&atilde;i biển đầu ti&ecirc;n &amp; duy nhất tại Việt Nam.<br />- H&ograve;a minh theo những c&acirc;u chuyện ly kỳ hấp dẫn tại ph&ograve;ng chiếu phim 4D, cảm nhận sự ch&acirc;n thực v&agrave; sống động nhờ sự hỗ trợ của c&aacute;c hiệu ứng mới lạ, đặc biệt<br />- Đắm say trước vẻ đẹp lung linh, kỳ ảo trong kh&ocirc;ng gian s&ocirc;i động ho&agrave;nh tr&aacute;ng của chương tr&igrave;nh biểu diễn nhạc nước&nbsp;<br />- Dạo bộ dọc khu phố mua sắm được thiết kế đậm sắc m&agrave;u truyền thống m&agrave; vẫn tinh tế, hiện đại.<br />- Quay về đất liền, Nghỉ đ&ecirc;m tại&nbsp;Nha Trang.</p>\r\n<p><strong>NG&Agrave;Y 4: NHA TRANG - H&Agrave; NỘI&nbsp; (Ăn s&aacute;ng)</strong><br />Xe đưa đo&agrave;n ra s&acirc;n bay Cam Ranh l&agrave;m thủ tục về H&agrave; Nội, dự kiến chuyến bay&nbsp;<em><strong>VN1562.</strong></em>&nbsp;Về đến s&acirc;n bay Nội B&agrave;i, xe đ&oacute;n đo&agrave;n trở về điểm hẹn ban đầu. Kết th&uacute;c chương tr&igrave;nh tham quan.</p>\r\n<div class=\"ddict_btn\" style=\"top: 7px; left: 716.838px;\"><img src=\"chrome-extension://bpggmmljdiliancllaapiggllnkbjocb/logo/48.png\" /></div>', '<p><strong>* Gi&aacute; tour bao gồm:&nbsp;&nbsp;</strong><br />- V&eacute; m&aacute;y bay khứ hồi: H&agrave; Nội - Nha Trang - H&agrave; Nội (H&agrave;ng kh&ocirc;ng Vietnam Airlines)<br />- Chi ph&iacute; xe m&aacute;y lạnh phục vụ theo chương tr&igrave;nh.<br />- Chi ph&iacute; ăn - uống theo chương tr&igrave;nh<br />- Chi ph&iacute; kh&aacute;ch sạn tại Nha Trang ti&ecirc;u chuẩn 3 sao (2 kh&aacute;ch/ph&ograve;ng, nếu lẻ nam hoặc nữ sẽ nghỉ ph&ograve;ng ba).<br />- Chi ph&iacute; tham quan (v&eacute; v&agrave;o cửa 1 lần), hướng dẫn vi&ecirc;n tiếng Việt.<br />- Nước suối, khăn lạnh tr&ecirc;n xe.<br />- Chi ph&iacute; đưa đ&oacute;n s&acirc;n bay.</p>\r\n<p><strong>* G&iacute;a tour kh&ocirc;ng bao gồm:</strong><br />- V&eacute; v&agrave;o cửa Vinpearl, một bữa ăn tối tại Vinpearl<br />- Ăn, uống ngo&agrave;i chương tr&igrave;nh, đồ uống trong bữa ăn<br />- Chi ph&iacute; giặt l&agrave;, điện thoại trong kh&aacute;ch sạn v&agrave; c&aacute;c chi ph&iacute; c&aacute; nh&acirc;n kh&aacute;c<br />- Phụ thu ph&ograve;ng đơn : 1,380,000đ/kh&aacute;ch</p>', '<p>- Viếng thăm th&aacute;p B&agrave; Ponagar - ng&ocirc;i đền cổ được người Chăm x&acirc;y dựng từ thế kỷ thứ VIII</p>\r\n<p>- Vui chơi tại khu giải tr&iacute; Vinpearl Land - rất quy m&ocirc; v&agrave; hiện đại, c&oacute; thể s&aacute;nh ngang với nhiều c&ocirc;ng vi&ecirc;n giải tr&iacute; h&agrave;ng đầu trong khu vực</p>\r\n<p>- Kh&aacute;m ph&aacute; H&ograve;n Tằm, chi&ecirc;m ngưỡng vịnh Nha Trang - một trong 29 vịnh đẹp nhất thế giới</p>\r\n<div class=\"ddict_btn\" style=\"top: 32px; left: 722.413px;\"><img src=\"chrome-extension://bpggmmljdiliancllaapiggllnkbjocb/logo/48.png\" /></div>', 'TP.Hồ Chí Minh', '<p>Điều khoản &amp; Quy định đang cập nhật.</p>', 0, 'Côn Đảo', '0000-00-00'),
(32, 'Du lịch Mỹ Tho - Cần Thơ - Cà Mau - Bạc Liêu - Sóc Trăng', '2.jpg', 6000000, 0, 'Thứ 4 hàng tuần', '4 ngày 3 đêm', 'Tàu thủy.svg', 2, '<p><strong>Ng&agrave;y 01: TP. HỒ CH&Iacute; Minh - MỸ THO - CẦN THƠ&nbsp;(Ăn s&aacute;ng, trưa, chiều)</strong><br />Đ&oacute;n kh&aacute;ch tại văn ph&ograve;ng Saigontourist. Đến Mỹ Tho, tham quan&nbsp;<strong><em>ch&ugrave;a Vĩnh Tr&agrave;ng.</em></strong>&nbsp;Đi du thuyền tr&ecirc;n s&ocirc;ng Mekong ngắm cảnh<strong><em>&nbsp;bốn c&ugrave; lao Long, L&acirc;n Qui, Phụng</em></strong>&hellip;Tham quan&nbsp;<strong><em>cảng c&aacute; Mỹ Tho</em></strong>, l&agrave;ng nu&ocirc;i c&aacute; b&egrave; dọc c&ugrave; lao T&acirc;n Long. Đến c&ugrave; lao Thới Sơn tham quan&nbsp;<strong><em>nh&agrave; d&acirc;n, trại nu&ocirc;i ong</em></strong>, thưởng thức tr&agrave; mật ong, chụp h&igrave;nh với Trăn Gấm. Đi bộ tr&ecirc;n đường l&agrave;ng Thới Sơn, tham quan<strong><em>&nbsp;l&ograve; kẹo dừa</em></strong>, thưởng thức tr&aacute;i c&acirc;y theo m&ugrave;a.&nbsp;<strong><em>Nghe nhạc t&agrave;i tử Nam Bộ</em></strong>. Sang thăm&nbsp;<strong><em>cồn Phụng&nbsp;</em></strong>với&nbsp;<strong><em>di t&iacute;ch Đạo Dừa</em></strong>,&nbsp;<strong><em>trại nu&ocirc;i c&aacute; sấu, vườn th&uacute; mi</em>ni</strong>. Khởi h&agrave;nh đi Cần Thơ. Tối tự do dạo bến Ninh Kiều. Nghỉ đ&ecirc;m tại Cần Thơ.&nbsp;&nbsp;&nbsp;</p>\r\n<p><img style=\"height: auto;\" src=\"https://bizweb.dktcdn.net/100/299/077/files/vt-2-1.jpg?v=1529554946693\" data-thumb=\"original\" /></p>\r\n<p><strong>Ng&agrave;y 02: CẦN THƠ -&nbsp;CẢ MAU &nbsp;(Ăn s&aacute;ng, trưa, chiều)</strong><br />Tham quan<strong><em>&nbsp;chợ nổi C&aacute;i Răng</em></strong>. Viếng&nbsp;<strong><em>Thiền viện Tr&uacute;c L&acirc;m Phương Nam</em></strong>. Khởi h&agrave;nh đi C&agrave; Mau. Nghỉ đ&ecirc;m tại C&agrave; Mau.</p>\r\n<p><strong>NG&Agrave;Y 03: C&Agrave; MAU - BẠC LI&Ecirc;U (Ăn s&aacute;ng, trưa, chiều)</strong><br />Tới bến t&agrave;u đi Mũi C&agrave; Mau, tham quan v&agrave; chụp ảnh lưu niệm tại Khu Văn H&oacute;a Du Lịch Mũi C&agrave; Mau, Điểm Cực Nam Tổ Quốc, tham quan&nbsp;<em><strong>mốc toạ độ quốc gia - Pan&ocirc; biểu tượng Mũi C&agrave; Mau</strong></em>, leo l&ecirc;n vọng l&acirc;m đ&agrave;i cao 20,5 m&eacute;t ngắm nh&igrave;n&nbsp;<em><strong>to&agrave;n cảnh Mũi C&agrave; Mau</strong></em>, Chiều khởi h&agrave;nh đi Bạc Li&ecirc;u. Viếng&nbsp;<strong><em>nh&agrave; thờ Tắc Sậy</em></strong>&nbsp;v&agrave; thăm&nbsp;<strong><em>nơi an nghỉ của Linh mục Trương Bửu Diệp</em></strong>. Nghỉ đ&ecirc;m tại Bạc Li&ecirc;u.</p>\r\n<p><strong>NG&Agrave;Y 04: BẠC LI&Ecirc;U - S&Oacute;C TRĂNG - TP.HỒ CH&Iacute; MINH (Ăn s&aacute;ng, trưa)</strong><br />Buổi s&aacute;ng, tham quan&nbsp;<em><strong>Khu dinh thự C&ocirc;ng tử Bạc Li&ecirc;u</strong></em>,<strong><em>&nbsp;Khu lưu niệm nghệ thuật Đờn Ca T&agrave;i Tử Nam Bộ v&agrave; nhạc sĩ Cao Văn Lầu</em></strong>, người s&aacute;ng t&aacute;c ra b&agrave;i Dạ Cổ Ho&agrave;i Lang. Khởi h&agrave;nh về TP.Hồ Ch&iacute; Minh, gh&eacute; S&oacute;c trăng viếng&nbsp;<strong><em>ch&ugrave;a M&atilde; Tộc</em></strong>&nbsp;(ch&ugrave;a Dơi). Khởi h&agrave;nh về th&agrave;nh phố Hồ Ch&iacute; Minh. Kết th&uacute;c chương tr&igrave;nh.</p>\r\n<div class=\"ddict_btn\" style=\"top: 21px; left: 545.225px;\"><img src=\"chrome-extension://bpggmmljdiliancllaapiggllnkbjocb/logo/48.png\" /></div>', '<p><strong>* Gi&aacute; tour bao gồm:&nbsp;&nbsp;</strong><br />- Chi ph&iacute; xe m&aacute;y lạnh phục vụ theo chương tr&igrave;nh.<br />- Chi ph&iacute; ăn -&nbsp;uống theo chương tr&igrave;nh.<br />- Chi ph&iacute; kh&aacute;ch sạn ti&ecirc;u chuẩn 2 kh&aacute;ch/ph&ograve;ng.<br />- Chi ph&iacute; tham quan, t&agrave;u thuyền, hướng dẫn vi&ecirc;n tiếng Việt<br />- Qu&agrave; tặng: N&oacute;n, nước suối, khăn lạnh, viết&hellip;</p>\r\n<p><strong>* Gi&aacute; tour kh&ocirc;ng bao gồm:&nbsp;&nbsp;</strong><br />- Ăn uống ngo&agrave;i chương tr&igrave;nh, giặt ủi, điện thoại v&agrave; c&aacute;c chi ph&iacute; c&aacute; nh&acirc;n<br />- Chi ph&iacute; phụ thu người nước ngo&agrave;i, phụ thu ph&ograve;ng đơn (nếu c&oacute;)</p>\r\n<p><strong>TH&Ocirc;NG TIN HƯỚNG DẪN<br />* V&eacute; trẻ em:&nbsp;&nbsp;</strong><br />- V&eacute; tour: Trẻ em từ&nbsp; 6 đến 11 tuổi mua một nửa gi&aacute; v&eacute; người lớn, trẻ em tr&ecirc;n 11 tuổi mua v&eacute; như người lớn.<br />- Đối với trẻ em dưới 6 tuổi, gia đ&igrave;nh tự lo cho b&eacute; ăn ngủ v&agrave; tự trả ph&iacute; tham quan (nếu c&oacute;). Hai người lớn chỉ được k&egrave;m một trẻ em. Từ trẻ thứ 2 trở l&ecirc;n, mỗi em phải 50% gi&aacute; v&eacute; người lớn.<br />- Ti&ecirc;u chuẩn 50% gi&aacute; tour bao gồm: Suất ăn, ghế ngồi v&agrave; ngủ gh&eacute;p chung với gia đ&igrave;nh.</p>', '<p>- Đến Mỹ Tho thưởng thức tr&aacute;i c&acirc;y theo m&ugrave;a, nghe nhạc t&agrave;i tử Nam Bộ</p>\r\n<p>- Tham quan chợ nổi C&aacute;i Răng - một chợ nổi lớn của Đồng Bằng s&ocirc;ng Cửu Long</p>\r\n<p>- Tham quan Khu dinh thự C&ocirc;ng tử Bạc Li&ecirc;u, t&igrave;m hiểu cuộc đời của Hắc c&ocirc;ng tử nổi tiếng gi&agrave;u c&oacute;, ăn chơi</p>\r\n<div class=\"ddict_btn\" style=\"top: 15px; left: 721.675px;\"><img src=\"chrome-extension://bpggmmljdiliancllaapiggllnkbjocb/logo/48.png\" /></div>', 'TP.Hồ Chí Minh', '<p>Điều khoản &amp; Quy định đang cập nhật.</p>', 5, 'Cần Thơ', '0000-00-00'),
(33, 'Du lịch Phú Quốc Câu cá - Ngắm san hô', 'huu-percentage-20thanh-percentage-2033-percentage-20-3.jpg', 10000000, 0, 'Thứ 5 hàng tuần', '6 ngày 5 đêm', 'Máy bay.svg', 2, '<p><strong>NG&Agrave;Y 1: TP. HỒ CH&Iacute; MINH - PH&Uacute; QUỐC &nbsp;(Ăn s&aacute;ng, trưa)</strong><br />Đ&oacute;n qu&yacute; kh&aacute;ch tại văn ph&ograve;ng Saigontourist v&agrave; đ&aacute;p chuyến&nbsp;<strong><em>VJ321 l&uacute;c 06:55</em></strong>&nbsp;t&ugrave;y thời điểm. Đến Ph&uacute; Quốc, đo&agrave;n khởi h&agrave;nh đi ph&iacute;a Đ&ocirc;ng đảo, tham quan điểm&nbsp;<strong><em>Trại ch&oacute; xo&aacute;y Ph&uacute; Quốc, Vườn ti&ecirc;u, L&ograve; chế biến rượu Sim rừng Ph&uacute; Quốc, Cơ sở nước mắm Ph&uacute; Quốc</em></strong>. Nhận ph&ograve;ng. Buổi chiều tối tự do dạo&nbsp;<em><strong>chợ đ&ecirc;m Dinh Cậu</strong></em>. Nghỉ đ&ecirc;m tại Ph&uacute; Quốc.</p>\r\n<p><img style=\"height: auto;\" src=\"https://bizweb.dktcdn.net/100/299/077/products/salinda-1331.jpg?v=1529554845807\" data-thumb=\"original\" /></p>\r\n<p><strong>Lựa chọn&nbsp;</strong>(tự t&uacute;c chi ph&iacute; tham quan &amp; di chuyển):<br />- Tham quan<strong><em>&nbsp;Khu vui chơi giải tr&iacute; Vinpearl Land&nbsp;Ph&uacute; Quốc</em></strong>&nbsp;với c&aacute;c tr&ograve; chơi trong nh&agrave;, tr&ograve; chơi ngo&agrave;i trời Đĩa quay si&ecirc;u tốc, Cối xay gi&oacute;, Đĩa bay&hellip;, L&acirc;u đ&agrave;i Cổ t&iacute;ch; Khu c&ocirc;ng vi&ecirc;n nước&nbsp;với c&aacute;c tr&ograve; chơi mạo hiểm: Boomerang khổng lồ, Đường trượt si&ecirc;u l&ograve;ng chảo&hellip;; Phim 5D với nhiều kỹ xảo v&agrave; hiệu ứng hiện đại; Khu Thủy Cung&nbsp;với h&agrave;ng trăm lo&agrave;i sinh vật biển qu&yacute; hiếm: chim C&aacute;nh cụt,&nbsp; c&aacute; Nemo, c&aacute; Napoleon, c&aacute; Mập trắng, cua King Crab&hellip;Thưởng thức chương tr&igrave;nh biểu diễn nhạc nước, biểu diễn c&aacute; heo, n&agrave;ng ti&ecirc;n c&aacute;,&hellip;<br />- Kh&aacute;m ph&aacute;<strong><em>&nbsp;Khu Vinpearl Safari</em></strong>: kh&aacute;m ph&aacute; Vườn Th&uacute; Hoang D&atilde; đầu ti&ecirc;n tại Việt Nam c&ugrave;ng hơn 130 lo&agrave;i động vật qu&yacute; hiếm v&agrave; c&aacute;c chương tr&igrave;nh Biểu diễn động vật, Kh&aacute;m ph&aacute; v&agrave; trải nghiệm Vườn th&uacute; mở trong rừng tự nhi&ecirc;n, gần gũi v&agrave; th&acirc;n thiện với con người<br />- Ng&acirc;m tắm b&ugrave;n kho&aacute;ng n&oacute;ng tại&nbsp;<em><strong>Galina Ph&uacute; Quốc</strong></em>&nbsp;- trung t&acirc;m b&ugrave;n kho&aacute;ng đầu ti&ecirc;n tại Ph&uacute; Quốc, trải nghiệm những gi&acirc;y ph&uacute;t thư gi&atilde;n tuyệt vời dưới lớp b&ugrave;n kho&aacute;ng Galina. Ngo&agrave;i ra c&ograve;n c&oacute; c&aacute;c dịch vụ chăm s&oacute;c sức khỏe như: massage, hệ thống &ocirc;n tuyền thủy liệu ph&aacute;p, bể jaccuzi</p>\r\n<p><strong>NG&Agrave;Y 2: PH&Uacute; QUỐC- C&Acirc;U C&Aacute;- NGẮM SAN H&Ocirc; (Ăn s&aacute;ng, trưa, chiều)</strong><br />Tham quan&nbsp;<em><strong>trung t&acirc;m nu&ocirc;i cấy ngọc trai Ph&uacute; Quốc</strong></em>. L&ecirc;n thuyền du ngoạn tr&ecirc;n biển, thử t&agrave;i&nbsp;<strong><em>c&acirc;u c&aacute;</em></strong>,&nbsp;<em><strong>trải nghiệm cuộc sống của ngư d&acirc;n, bơi lội giữa biển v&agrave; ngắm san h&ocirc;</strong></em>&nbsp;với những sắc m&agrave;u tuyệt đẹp. Tắm&nbsp;<strong>biển B&atilde;i Sao</strong>&nbsp;(hoặc b&atilde;i &Ocirc;ng Lang t&ugrave;y điều kiện thời tiết). Buổi chiều tham quan&nbsp;<strong><em>di t&iacute;ch nh&agrave; t&ugrave; Ph&uacute; Quốc</em></strong>, viếng<strong><em>&nbsp;Dinh Bà</em></strong>&nbsp;- Thủy Long Th&aacute;nh Mẫu (Thần Nước), Dinh Cậu. Trở về kh&aacute;ch sạn nghỉ ngơi. Tối sinh hoạt tự do.</p>\r\n<p><strong>NG&Agrave;Y 3: PH&Uacute; QUỐC - TP. HỒ CH&Iacute; MINH &nbsp;(Ăn s&aacute;ng, trưa)</strong><br />Tự do tắm&nbsp;<em><strong>biển Ph&uacute; Quốc</strong></em>, trả ph&ograve;ng. Xe đưa qu&yacute; kh&aacute;ch ra s&acirc;n bay về Tp.Hồ Ch&iacute; Minh (<strong><em>chuyến VJ 326 l&uacute;c 16:50</em></strong>). Kết th&uacute;c chương tr&igrave;nh. (Qu&yacute; kh&aacute;ch tự t&uacute;c phương tiện từ s&acirc;n bay về lại nh&agrave;).&nbsp;</p>\r\n<p><strong>Ghi ch&uacute;:</strong><br />- Điểm tham quan c&oacute; thể sắp xếp lại thứ tư cho ph&ugrave; hợp m&agrave; vẫn bảo đảm đầy đủ nội dung của từng chương tr&igrave;nh<br />- Tham quan v&agrave; tắm biển B&atilde;i Sao hoặc B&atilde;i &Ocirc;ng Lang sẽ được linh động ho&aacute;n chuyển t&ugrave;y theo t&igrave;nh h&igrave;nh thời tiết, đường s&aacute;<br />- Giờ bay c&oacute; thể bị thay đổi bởi h&atilde;ng h&agrave;ng kh&ocirc;ng Vietjet Air</p>\r\n<div class=\"ddict_btn\" style=\"top: 23px; left: 434.663px;\"><img src=\"chrome-extension://bpggmmljdiliancllaapiggllnkbjocb/logo/48.png\" /></div>', '<p><strong>* V&eacute; trẻ em:</strong><br />- Trẻ em dưới 2 tuổi: thu 300.000đ. Gia đ&igrave;nh tự lo cho b&eacute; ăn ngủ<br />- Trẻ em từ 2 đến dưới 6 tuổi: mua 100% VMB người lớn. Gia đ&igrave;nh tự lo cho b&eacute; ăn ngủ v&agrave; tự trả ph&iacute; tham quan (nếu c&oacute;).<br />- Hai người lớn chỉ được k&egrave;m một trẻ em dưới 6 tu&ocirc;̉i. Từ trẻ thứ 2 trở l&ecirc;n, mỗi em phải chịu mức giá bằng trẻ em từ 6 đ&ecirc;́n 11 tu&ocirc;̉i<br />- Trẻ em từ 6 đ&ecirc;́n 11 tuổi: ti&ecirc;u chuẩn gồm: VMB, ăn uống và tham quan theo chương tr&igrave;nh, ngủ chung giường với phụ huynh<br />- Trẻ em tr&ecirc;n 11 tu&ocirc;̉i: &aacute;p dụng gi&aacute; v&agrave; c&aacute;c ti&ecirc;u chuẩn dịch vụ như người lớn</p>\r\n<p><strong>* Gi&aacute; tour bao gồm:&nbsp;&nbsp;</strong><br />- Chi ph&iacute; xe m&aacute;y lạnh phục vụ theo chương tr&igrave;nh.<br />- V&eacute; m&aacute;y bay khứ hồi.<br />- Chi ph&iacute; kh&aacute;ch sạn theo ti&ecirc;u chuẩn 2 kh&aacute;ch/ph&ograve;ng<br />- Chi ph&iacute; ăn - uống theo chương tr&igrave;nh.<br />- Chi ph&iacute; tham quan v&agrave; t&agrave;u thuyền.<br />- Chi ph&iacute; Hướng dẫn vi&ecirc;n tiếng Việt.<br />- Qu&agrave; tặng: N&oacute;n, nước suối, khăn lạnh, viết.</p>\r\n<p><strong>* Gi&aacute; tour kh&ocirc;ng bao gồm:&nbsp;&nbsp;</strong><br />- Chi ph&iacute; tham quan - ăn uống ngo&agrave;i chương tr&igrave;nh, tắm biển, giặt ủi, điện thoại v&agrave; c&aacute;c chi ph&iacute; c&aacute; nh&acirc;n kh&aacute;c.<br />- V&eacute; tham quan Vinpearlland hoặc Vinpearl Safari, ng&acirc;m tắm b&ugrave;n kho&aacute;ng n&oacute;ng Galina<br />- Chi ph&iacute; phụ thu người nước ngo&agrave;i, phụ thu ph&ograve;ng đơn (nếu c&oacute;)</p>', '<p>- Du ngoạn tr&ecirc;n biển, trải nghiệm cuộc sống của ngư d&acirc;n, bơi lội giữa biển v&agrave; ngắm san h&ocirc;</p>\r\n<p>- Tắm biển B&atilde;i Sao (hoặc b&atilde;i &Ocirc;ng Lang) - một trong những b&atilde;i biển đẹp nh&acirc;́t tại Ph&uacute; Quốc</p>\r\n<div class=\"ddict_btn\" style=\"top: 30px; left: 706.3px;\"><img src=\"chrome-extension://bpggmmljdiliancllaapiggllnkbjocb/logo/48.png\" /></div>', 'TP.Hồ Chí Minh', '<p>Điều khoản &amp; Quy định đang cập nhật.</p>', 22, 'Hồ Chí Minh', '0000-00-00');
INSERT INTO `tours` (`id_tour`, `name_tour`, `image`, `price`, `sale_price`, `lich_khoi_hanh`, `thoi_gian`, `phuong_tien`, `id_loai_tour`, `chuong_trinh`, `chinh_sach`, `description`, `khoi_hanh_tu`, `dieu_khoan`, `luot_khach`, `tour_cu_the`, `ngay_khoi_hanh`) VALUES
(35, 'Du lịch Hà Nội - Mộc Châu - Điện Biên Phủ - Hạ Long', '15.jpg', 89900000, 0, 'Thứ 6 hàng tuần', '7 ngày 6 đêm', 'Máy bay.svg', 1, '<p><strong>TỐI NG&Agrave;Y KHỞI H&Agrave;NH: TP.HCM - LONDON</strong><br />Qu&yacute; kh&aacute;ch tự t&uacute;c tập trung tại lầu 2 Ga đi quốc tế - s&acirc;n bay quốc tế T&acirc;n Sơn Nhất, l&agrave;m thủ tục đ&aacute;p chuyến bay&nbsp;<strong><em>VN51 (00:15-07:40)</em></strong>&nbsp;đi London. Nghỉ đ&ecirc;m v&agrave; ăn s&aacute;ng tr&ecirc;n m&aacute;y bay.</p>\r\n<p><strong>NG&Agrave;Y 1: TP.HCM - LONDON - WINDSOR - LONDON (Ăn trưa, ăn tối)</strong><br />07h40 s&aacute;ng đến London, đo&agrave;n l&agrave;m thủ tục nhập cảnh. Tham quan<strong><em>&nbsp;l&acirc;u đ&agrave;i Windsor</em></strong>&nbsp;- nơi ở của Nữ ho&agrave;ng Elizabeth, được x&acirc;y dựng cho ho&agrave;ng đế William 900 năm trước, nhằm bảo vệ London tr&aacute;nh khỏi sự nh&ograve;m ng&oacute; của phương T&acirc;y. Chi&ecirc;m ngưỡng&nbsp;<strong><em>Nh&agrave; thờ St. George&rsquo;s</em></strong>&nbsp;c&ugrave;ng c&aacute;c khu vực l&acirc;u đ&agrave;i, nh&agrave; b&uacute;p b&ecirc; của Nữ ho&agrave;ng Mary v&agrave; ph&ograve;ng tranh c&aacute;c t&ograve;a l&acirc;u đ&agrave;i. Buổi chiều đo&agrave;n quay về London, dừng ch&acirc;n chụp h&igrave;nh b&ecirc;n ngo&agrave;i&nbsp;<strong><em>Tu viện Westminter&nbsp;</em></strong>- nơi tiến h&agrave;nh lễ đăng quang của c&aacute;c vua v&agrave; nữ ho&agrave;ng Anh, được UNESCO c&ocirc;ng nhận l&agrave; di sản thế giới;&nbsp;<strong><em>Cung điện Buckingham&nbsp;</em></strong>- nơi nữ ho&agrave;ng Anh v&agrave; ho&agrave;ng tộc đang sinh sống, xem nghi thức đổi g&aacute;c tại cổng cung điện. Ngắm biểu tượng nước Anh -&nbsp;<strong><em>th&aacute;p đồng hồ Big Ben</em></strong>&nbsp;từ cầu Lambeth v&agrave;&nbsp;<strong><em>Cầu Th&aacute;p London Tower Bridge</em></strong>&nbsp;- c&ocirc;ng tr&igrave;nh kết hợp cầu treo&nbsp;với cầu n&acirc;ng&nbsp;bắc qua s&ocirc;ng Thames. Tham dự&nbsp;<strong><em>Lễ hội hoa Chelsea</em></strong>&nbsp;- triển l&atilde;m hoa, thiết kế s&acirc;n vườn lớn nhất được tổ chức v&agrave;o th&aacute;ng 5 h&agrave;ng năm. Nghỉ đ&ecirc;m tại London.</p>\r\n<p><img style=\"height: auto;\" src=\"https://bizweb.dktcdn.net/100/299/077/files/nhung-dia-diem-du-lich-dep-o-scotland-6.jpg?v=1529555577450\" data-thumb=\"original\" /></p>\r\n<p><strong>NG&Agrave;Y 2: LONDON - EDINBURGH (Ăn s&aacute;ng, ăn tối)</strong><br />Trả&nbsp;ph&ograve;ng. Qu&yacute; kh&aacute;ch đ&aacute;p chuyến t&agrave;u đi Edinburgh - thủ đ&ocirc; của Scotland. Tự do dạo chơi tại&nbsp;<strong><em>Royal Mile</em></strong>&nbsp;- chuỗi c&aacute;c đường phố thuộc khu phố cổ ở Edinburgh, chứa đầy dấu ấn lịch sử của th&agrave;nh phố. Nghỉ đ&ecirc;m tại Edinburgh.</p>\r\n<p><strong>NG&Agrave;Y 3: EDINBURGH - GLASSGOW - EDINBURGH (Ăn s&aacute;ng, trưa, tối)</strong><br />Khởi h&agrave;nh đến Glasgow - th&agrave;nh phố lớn nhất Scotland nằm b&ecirc;n bờ s&ocirc;ng Clyde thơ mộng. Kh&aacute;m ph&aacute;&nbsp;<strong><em>Bảo t&agrave;ng Giao th&ocirc;ng vận tải&nbsp;</em></strong>- trưng b&agrave;y một bộ sưu tập lớn xe v&agrave; c&aacute;c m&ocirc; h&igrave;nh kể c&acirc;u chuyện về vận tải. Dừng ch&acirc;n chụp h&igrave;nh b&ecirc;n ngo&agrave;i&nbsp;<strong><em>nh&agrave; thờ Glasgow&nbsp;</em></strong>- một minh chứng điển h&igrave;nh tuyệt đẹp cho phong c&aacute;ch kiến tr&uacute;c Gothic từ thời Trung cổ; T<strong><em>&ograve;a nh&agrave; Hội đồng&nbsp;th&agrave;nh phố v&agrave; Quảng trường trung t&acirc;m</em></strong>. Thưởng thức một cốc&nbsp;<strong><em>whisky</em></strong>&nbsp;đậm chất Scoland - trải nghiệm kh&ocirc;ng thể thiếu khi đặt ch&acirc;n đến Scotland. Nghỉ đ&ecirc;m tại Edinburgh.</p>\r\n<p><strong>NG&Agrave;Y 4: EDINBURGH - LIVERPOOL (Ăn s&aacute;ng, trưa, tối)</strong><br />Trả ph&ograve;ng. Kh&aacute;m ph&aacute;&nbsp;<strong><em>L&acirc;u đ&agrave;i Edinburgh</em></strong>&nbsp;- cung điện m&ugrave;a h&egrave; của Ho&agrave;ng gia Anh. Tham quan&nbsp;<em><strong>Cung điện Holyrood</strong></em>&nbsp;được x&acirc;y dựng khoảng thế kỷ 12, l&agrave; nơi ở ch&iacute;nh thức của Vua v&agrave; Ho&agrave;ng Hậu Scotland từ thế kỷ 16. Khởi h&agrave;nh tới Liverpool - th&agrave;nh phố cảng đẹp bậc nhất nước Anh. Nghỉ đ&ecirc;m tại Liverpool.</p>\r\n<p><strong>NG&Agrave;Y 5: LIVERPOOL - MANCHESTER (Ăn s&aacute;ng, trưa, tối)</strong><br />Trả ph&ograve;ng. Tham quan&nbsp;<strong><em>Liverpool Anglican Cathedra</em></strong>l - gi&aacute;o đường lớn nhất của nước Anh;&nbsp;<em><strong>Bảo t&agrave;ng trưng b&agrave;y c&aacute;c hiện vật li&ecirc;n&nbsp;quan&nbsp;đến The&nbsp;Beatles</strong></em>. Di chuyển đến Manchester. Chi&ecirc;m ngưỡng&nbsp;<strong><em>S&acirc;n Vận Động Old Trafford</em></strong>&nbsp;- s&acirc;n nh&agrave; của đội b&oacute;ng Manchester United nổi tiếng với sức chứa tr&ecirc;n 90.000 người v&agrave; bảo t&agrave;ng Manchester United. Tiếp tục tham quan&nbsp;<strong><em>Th&aacute;nh đường Manchester&nbsp;</em></strong>- điểm dừng ch&acirc;n ti&ecirc;u biểu kh&ocirc;ng thể bỏ qua đối với những ai đam m&ecirc; kiến tr&uacute;c v&agrave; lịch sử. Chụp h&igrave;nh b&ecirc;n ngo&agrave;i T&ograve;a thị ch&iacute;nh Manchester. Nghỉ đ&ecirc;m tại Manchester.</p>\r\n<p><strong>NG&Agrave;Y 6: MANCHESTER - STRATFORD-UPON-AVON - OXFORD (Ăn s&aacute;ng, trưa, tối)&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</strong><br />Trả ph&ograve;ng. Di chuyển đến<em><strong>&nbsp;Stratford-Upon-Avon</strong></em>, th&agrave;nh phố nhỏ y&ecirc;n b&igrave;nh của miền Trung nước Anh. Tham quan chụp h&igrave;nh b&ecirc;n ngo&agrave;i ng&ocirc;i nh&agrave; của&nbsp;<strong>William Shakespeare</strong>&nbsp;v&agrave;&nbsp;<strong><em>nh&agrave; thờ Holy Trinity</em></strong>&nbsp;- nơi lưu giữ thi h&agrave;i của Shakespeare. Tiếp tục di chuyển đến Oxford - một th&agrave;nh phố cổ k&iacute;nh nổi tiếng với c&aacute;c trường đại học danh tiếng. Dừng ch&acirc;n chụp h&igrave;nh tại&nbsp;<strong><em>Trinity College v&agrave;&nbsp;Christchurch College</em></strong>&nbsp;- học viện danh gi&aacute; nhất đất nước do Hồng y Wolsey - Quan Chưởng ấn của Vua Henry VIII th&agrave;nh lập năm 1525 tr&ecirc;n nền một tu viện cũ. Nghỉ đ&ecirc;m tại Oxford.</p>\r\n<p><strong>NG&Agrave;Y 7: OXFORD - BICESTER - LONDON(Ăn s&aacute;ng, trưa, tối)&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</strong><br />Khởi h&agrave;nh đi Bicester. Tự do tham quan v&agrave; mua sắm tại&nbsp;<em><strong>Bicester Outlet Village</strong></em>&nbsp;- Thi&ecirc;n đường mua sắm h&agrave;ng đầu Ch&acirc;u &Acirc;u với những thương hiệu nổi tiếng như Prada, Gucci, Armani, Boss, Chlo&eacute;, Dior&hellip; Về London nghỉ đ&ecirc;m.</p>\r\n<p><strong>NG&Agrave;Y 8: LONDON - TP.HCM (Ăn s&aacute;ng)</strong><br />Trả ph&ograve;ng. Ra s&acirc;n bay, đ&aacute;p chuyến bay&nbsp;<em><strong>VN50 (11:00-05:10)</strong>&nbsp;</em>về Tp. HCM. Ăn tối v&agrave; nghỉ đ&ecirc;m tr&ecirc;n m&aacute;y bay.</p>\r\n<p><strong>NG&Agrave;Y 9: TP.HCM</strong><br />05h10 đến s&acirc;n bay T&acirc;n Sơn Nhất. Kết th&uacute;c chương tr&igrave;nh du lịch Anh - Scotland.</p>\r\n<div class=\"ddict_btn\" style=\"top: 26px; left: 610.925px;\"><img src=\"chrome-extension://bpggmmljdiliancllaapiggllnkbjocb/logo/48.png\" /></div>', '<p><strong>* Gi&aacute; tour bao gồm:&nbsp;&nbsp;&nbsp;</strong><br />- Visa nhập cảnh Anh - V&eacute; m&aacute;y bay khứ hồi bao gồm thuế s&acirc;n bay v&agrave; ph&iacute; an ninh. HDV Saigontourist suốt tuyến &amp; t&agrave;i xế địa phương<br />- Kh&aacute;ch sạn 4 sao (2 người/ph&ograve;ng), c&aacute;c bữa ăn theo chương tr&igrave;nh. Nước uống 1 chai 0.5l/ kh&aacute;ch/ ng&agrave;y<br />- Xe đưa đ&oacute;n v&agrave; tham quan theo chương tr&igrave;nh, qu&agrave; lưu niệm của Ant Du lịch&nbsp;(T&uacute;i k&eacute;o, n&oacute;n).</p>\r\n<p><strong>* Gi&aacute; tour kh&ocirc;ng bao gồm:</strong><br />- Chi ph&iacute; l&agrave;m hộ chiếu, c&aacute;c chương tr&igrave;nh tự chọn, nước uống, giặt ủi, điện thoại, h&agrave;nh l&yacute; qu&aacute; cước theo quy định của h&agrave;ng kh&ocirc;ng... v&agrave; c&aacute;c chi ph&iacute; c&aacute; nh&acirc;n kh&aacute;c của kh&aacute;ch ngo&agrave;i chương tr&igrave;nh.<br />- Tiền bồi dưỡng cho hướng dẫn vi&ecirc;n v&agrave; t&agrave;i xế (08 USD/kh&aacute;ch/ng&agrave;y).</p>', '<p>- Chi&ecirc;m ngưỡng thế giới cung điện, l&acirc;u đ&agrave;i v&agrave; cảnh sắc thi&ecirc;n nhi&ecirc;n kỳ th&uacute; trong li&ecirc;n tuyến Anh - Scotland.</p>\r\n<p>- M&atilde;n nh&atilde;n với những địa danh n&ocirc;̉i ti&ecirc;́ng như c&acirc;̀u th&aacute;p London Tower Bridge, th&aacute;p đồng hồ Big Ben, một tho&aacute;ng s&ocirc;ng Thames thơ mộng vắt ngang qua tr&aacute;i tim nước Anh&hellip; .</p>', 'TP.Hồ Chí Minh', '<p>Điều khoản &amp; Quy định đang cập nhật.</p>', 2, 'Huế', '0000-00-00'),
(37, 'Du lịch Cao Bằng - Bản Giốc - Bắc Kạn - Ba Bể - Hà Nội', 'chiemnguonvedepcongviendiachat.jpg', 7929000, 0, 'Thứ 4 hàng tuần', '4 ngày 3 đêm', 'Máy bay.svg', 1, '<p><strong>NG&Agrave;Y 1: TP. HCM - H&Agrave; NỘI - CAO BẰNG&nbsp; (Ăn trưa, chiều)</strong><br />Buổi s&aacute;ng,&nbsp;tập trung tại Cổng D3 - Ga trong nước, s&acirc;n bay T&acirc;n Sơn Nhất, bay đi H&agrave; Nội (<em><strong>chuyến bay VJ 126 l&uacute;c 07:10 hoặc VJ 128 l&uacute;c 08:15 t&ugrave;y ng&agrave;y</strong></em>). Khởi h&agrave;nh đi Cao Bằng. Trong h&agrave;nh tr&igrave;nh, ngang qua tỉnh Th&aacute;i Nguy&ecirc;n -<em><strong>&nbsp;&ldquo;kinh đ&ocirc; ch&egrave; (tr&agrave;)&nbsp;</strong></em>của miền Bắc. Nghỉ đ&ecirc;m tại kh&aacute;ch sạn S&agrave;i G&ograve;n-Bản Giốc.</p>\r\n<p><img style=\"height: auto;\" src=\"https://bizweb.dktcdn.net/100/299/077/products/chiem-nguon-ve-dep-cong-vien-dia-chat-cao-bang-1.jpg?v=1529554586100\" data-thumb=\"original\" /></p>\r\n<p><strong>NG&Agrave;Y 2: THAM QUAN TH&Aacute;C BẢN GIỐC - ĐỘNG NGƯỜM NGAO (Ăn s&aacute;ng, trưa, chiều)</strong><br />Tham dự Lễ ch&agrave;o cờ s&aacute;ng thứ hai ở mảnh đất thi&ecirc;ng li&ecirc;ng miền bi&ecirc;n ải. Viếng<em><strong>&nbsp;Ch&ugrave;a Phật T&iacute;ch Tr&uacute;c L&acirc;m Bản Giốc</strong></em>. Thăm&nbsp;<em><strong>chợ bi&ecirc;n giới</strong></em>&nbsp;(trong phạm vi được ph&eacute;p).Chi&ecirc;m ngưỡng cảnh sắc<em><strong>&nbsp;th&aacute;c Bản Giốc</strong></em>, đi b&egrave; tre ngắm cận cảnh th&aacute;c (tự t&uacute;c chi ph&iacute;), tham quan&nbsp;<em><strong>động Ngườm Ngao</strong></em>. Trả ph&ograve;ng. Buổi chiều di chuyển đến nhận ph&ograve;ng v&agrave; nghỉ đ&ecirc;m tại Cao Bằng</p>\r\n<p><strong>NG&Agrave;Y 3: CAO BẰNG - BẮC KẠN&nbsp;(Ăn s&aacute;ng, trưa, chiều)</strong><br />Trả ph&ograve;ng. Khởi h&agrave;nh tham quan&nbsp;<em><strong>Khu di t&iacute;ch Pắc B&oacute;</strong></em>&nbsp;- nơi chủ tịch Hồ Ch&iacute; Minh từng sống, l&agrave;m việc, tham quan<em><strong>&nbsp;suối L&ecirc; Nin, n&uacute;i C&aacute;c M&aacute;c, hang Cốc B&oacute;, b&agrave;n đ&aacute; lịch sử</strong></em>.&nbsp; Khởi h&agrave;nh đi Bắc Kạn. Nhận ph&ograve;ng v&agrave; nghỉ đ&ecirc;m tại Bắc Kạn</p>\r\n<p><strong>NG&Agrave;Y 4: HỒ BA BỂ - TH&Aacute;I NGUY&Ecirc;N - H&Agrave; NỘI (Ăn s&aacute;ng, trưa, chiều)</strong><br />Trả ph&ograve;ng. Tham quan&nbsp;<strong><em>Vườn quốc gia Ba Bể</em></strong>, ra bến thuyền Buốc Lốm, du ngoạn tr&ecirc;n thuyền dọc theo d&ograve;ng s&ocirc;ng Năng, ngắm nh&igrave;n cảnh quan v&agrave; đời sống của của người d&acirc;n tộc T&agrave;y, N&ugrave;ng ở đ&ocirc;i bờ, cảnh động Pu&ocirc;ng, th&aacute;c Đầu Đẳng (t&ugrave;y theo m&ugrave;a). Đến&nbsp;<strong><em>hồ Ba Bể</em></strong>&nbsp;- vi&ecirc;n ngọc trong xanh giữa n&uacute;i rừng Đ&ocirc;ng Bắc, tham quan&nbsp;<em><strong>đảo An M&atilde;, ao Ti&ecirc;n</strong></em>. Thuyền cập bến. Khởi h&agrave;nh về H&agrave; Nội. Về đến H&agrave; Nội, nhận ph&ograve;ng. Buổi tối, tự do dạo bộ 36 phố phường của cư d&acirc;n Thủ đ&ocirc;. Nghỉ đ&ecirc;m tại H&agrave; Nội</p>\r\n<p><strong>NG&Agrave;Y 5: H&Agrave; NỘI - TP.HỒ CH&Iacute; MINH&nbsp;(Ăn s&aacute;ng, trưa)</strong><br />Trả phòng. Tham quan&nbsp;<strong><em>Văn Miếu, Quốc Tử Gi&aacute;m</em></strong>&nbsp;- từng l&agrave; trung t&acirc;m thờ tự v&agrave; gi&aacute;o dục Nho học lớn nhất. V&atilde;ng cảnh<strong><em>&nbsp;hồ Ho&agrave;n Kiếm</em></strong>, vi&ecirc;́ng<strong><em>đền Ngọc Sơn</em></strong>. Buổi chiều, ra s&acirc;n bay về TP. Hồ Ch&iacute; Minh (<em><strong>chuyến bay VJ157 l&uacute;c 16:15 hoặc VJ 159 l&uacute;c 16:45 t&ugrave;y ng&agrave;y</strong></em>). Kết th&uacute;c chương tr&igrave;nh (Qu&yacute; kh&aacute;ch tự t&uacute;c phương tiện từ s&acirc;n bay về nh&agrave;.</p>\r\n<p><strong>Ghi ch&uacute;:</strong><br />- Điểm tham quan c&oacute; thể sắp xếp lại cho ph&ugrave; hợp m&agrave; vẫn bảo đảm đầy đủ nội dung của từng chương tr&igrave;nh.<br />- Giờ bay c&oacute; thể bị thay đổi bởi Vietjet Air</p>\r\n<div class=\"ddict_btn\" style=\"top: 36px; left: 571.625px;\"><img src=\"chrome-extension://bpggmmljdiliancllaapiggllnkbjocb/logo/48.png\" /></div>', '<p><strong>* V&eacute; trẻ em:</strong><br />- Trẻ em dưới 2 tuổi: thu 300.000đ. Gia đình tự lo cho bé ăn ngủ<br />- Trẻ em từ 2 đến dưới 6 tuổi: mua 100% VMB người lớn. Gia đ&igrave;nh tự lo cho b&eacute; ăn ngủ v&agrave; tự trả ph&iacute; tham quan (nếu c&oacute;).<br />- Hai người lớn chỉ được k&egrave;m 1 trẻ em dưới 6 tu&ocirc;̉i. Từ trẻ thứ 2 trở l&ecirc;n, mỗi em phải đóng bằng gi&aacute; trẻ em từ 6 đến 11 tuổi<br />- Trẻ em từ 6 - 11 tuổi: ti&ecirc;u chuẩn gồm: VMB, ăn uống và tham quan theo chương tr&igrave;nh, ngủ chung giường với phụ huynh<br />- Trẻ em tr&ecirc;n 11 tu&ocirc;̉i: &aacute;p dụng gi&aacute; v&agrave; c&aacute;c ti&ecirc;u chuẩn dịch vụ như người lớn</p>\r\n<p><strong>* Gi&aacute; tour bao gồm:&nbsp;&nbsp;</strong><br />- Chi ph&iacute; xe m&aacute;y lạnh phục vụ theo chương tr&igrave;nh.<br />- V&eacute; m&aacute;y bay khứ hồi.&nbsp;<br />- Chi ph&iacute; kh&aacute;ch sạn theo ti&ecirc;u chuẩn 2 kh&aacute;ch/ph&ograve;ng<br />- Chi ph&iacute; ăn -&nbsp;uống theo chương tr&igrave;nh.<br />- Chi ph&iacute; tham quan v&agrave; t&agrave;u thuyền&nbsp;<br />- Chi ph&iacute; Hướng dẫn vi&ecirc;n tiếng Việt.<br />- Qu&agrave; tặng: N&oacute;n, nước suối, khăn lạnh, viết;</p>\r\n<p><strong>* Gi&aacute; tour kh&ocirc;ng bao gồm:&nbsp;&nbsp;</strong><br />- Chi ph&iacute; tham quan - ăn uống ngo&agrave;i chương tr&igrave;nh, giặt ủi, điện thoại v&agrave; c&aacute;c chi ph&iacute; c&aacute; nh&acirc;n kh&aacute;c<br />- Chi ph&iacute; phụ thu người nước ngo&agrave;i, phụ thu ph&ograve;ng đơn (nếu c&oacute;)</p>', '<p>- Chi&ecirc;m ngưỡng cảnh sắc đầy h&ugrave;ng vĩ v&agrave; thơ mộng của th&aacute;c Bản Giốc</p>\r\n<p>- Tham quan động Ngườm Ngao, kh&aacute;m ph&aacute; vẻ đẹp lung linh của một hang động đ&aacute; v&ocirc;i c&ograve;n nguy&ecirc;n sơ</p>\r\n<p>- Tham quan Khu di t&iacute;ch Pắc B&oacute; - nơi chủ tịch Hồ Ch&iacute; Minh từng sống, l&agrave;m việc</p>\r\n<p>- Dạo bộ 36 phố phường - khu phố cổ với những ng&agrave;nh nghề đặc trưng v&agrave; truyền thống của cư d&acirc;n Thủ đ&ocirc;</p>\r\n<p>- Đến hồ Ba Bể - thưởng ngoạn phong cảnh của một trong 100 hồ nước ngọt đẹp nhất to&agrave;n cầu</p>\r\n<div class=\"ddict_btn\" style=\"top: 33px; left: 723.913px;\"><img src=\"chrome-extension://bpggmmljdiliancllaapiggllnkbjocb/logo/48.png\" /></div>', 'TP.Hồ Chí Minh', '<p>Điều khoản &amp; Quy định đang cập nhật.</p>', 1, 'Cà Mau', '0000-00-00'),
(38, 'Du lịch Hà Nội - Lào Cai - Sapa - Hạ Long', 'tour-sapa-y-ty-percentage-cc-percentage-81-2ngay-1-percentage-c4-percentage-91e-percentage-cc-percentage-82m1.jpg', 10990000, 0, 'Chủ nhật hàng tuần', '4 ngày 3 đêm', 'Máy bay.svg', 1, '<p><strong>NG&Agrave;Y 1: TP. HCM - H&Agrave; NỘI - ĐƯỜNG CAO TỐC - L&Agrave;O CAI - SAPA &nbsp;(Ăn trưa, chiều)</strong><br />Buổi s&aacute;ng, tập trung tại Cổng D4&nbsp;- Ga đi trong nước - S&acirc;n bay T&acirc;n Sơn Nhất đ&aacute;p chuyến bay&nbsp;<em><strong>VJ 126 l&uacute;c 07:10 hoặc VJ128 l&uacute;c 08:15 t&ugrave;y ng&agrave;y</strong></em>. Đ&aacute;p xuống s&acirc;n bay Nội B&agrave;i, khởi h&agrave;nh đến L&agrave;o Cai. Đến L&agrave;o Cai tiếp tục l&ecirc;n Sapa. Chi&ecirc;m ngưỡng&nbsp;<em><strong>d&atilde;y Ho&agrave;ng Li&ecirc;n Sơn</strong></em>&nbsp;tr&ecirc;n đường đi. Đến Sapa v&agrave;o&nbsp;<strong><em>thung lũng Mường Hoa</em></strong>, thăm&nbsp;<strong><em>bản Lao Chải - Tả Van&nbsp;</em></strong>của người M&ocirc;ng, Gi&aacute;y. Buổi tối tự do dạo&nbsp;<strong><em>chợ Sapa</em></strong>. Nghỉ đ&ecirc;m tại Sapa.</p>\r\n<p><img style=\"height: auto;\" src=\"https://bizweb.dktcdn.net/100/299/077/products/5.jpg?v=1529553698327\" data-thumb=\"original\" /></p>\r\n<p><strong>NG&Agrave;Y 2: SAPA - L&Agrave;O CAI &nbsp;(Ăn s&aacute;ng, trưa, chiều)</strong><br />Buổi s&aacute;ng, chinh phục&nbsp;<strong><em>n&uacute;i H&agrave;m Rồng</em></strong>, thưởng ngoạn<strong><em>&nbsp;vườn lan, vườn hoa &ocirc;n đới, vườn đ&agrave;o v&agrave; khu vườn đ&aacute;</em></strong>, xem biểu diễn<strong><em>&nbsp;m&uacute;a h&aacute;t d&acirc;n gian của c&aacute;c d&acirc;n tộc thiểu số</em></strong>, ngắm to&agrave;n cảnh<strong><em>&nbsp;Sapa v&agrave; &ldquo;N&oacute;c nh&agrave; Đ&ocirc;ng Dương&rdquo; - đỉnh Fansipan</em></strong>&nbsp;tại s&acirc;n m&acirc;y.</p>\r\n<p><strong>Lựa chọn&nbsp;</strong>(tự t&uacute;c chi ph&iacute; tham quan) Xe đưa qu&yacute; kh&aacute;ch đến tham quan&nbsp;<strong>khu du lịch c&aacute;p treo Fansipan&nbsp;</strong>với hệ thống c&aacute;p treo 3 d&acirc;y hiện đại vừa kh&aacute;nh th&agrave;nh th&aacute;ng 2/2016. Trải nghiệm cảm gi&aacute;c đi giữa biển m&acirc;y, ngắm to&agrave;n cảnh Sapa v&agrave; thung lũng Mường Hoa, vườn quốc gia Ho&agrave;ng Li&ecirc;n từ tr&ecirc;n cao. Viếng&nbsp;<em><strong>ch&ugrave;a Bảo An</strong></em>, vượt gần 600 bậc thang, chinh phục<em><strong>&nbsp;&ldquo;N&oacute;c nh&agrave; Đ&ocirc;ng Dương&rdquo; - đỉnh Fansipan</strong></em>&nbsp;ở độ cao 3,143m.</p>\r\n<p>Xe đ&oacute;n v&agrave; đưa cả đo&agrave;n trở về L&agrave;o Cai. Tự do kh&aacute;m ph&aacute; TP. L&agrave;o Cai về đ&ecirc;m. Nghỉ đ&ecirc;m tại L&agrave;o Cai</p>\r\n<p><strong>NG&Agrave;Y 3: L&Agrave;O CAI - H&Agrave; NỘI &nbsp;(Ăn s&aacute;ng, trưa, chiều)</strong><br />Buổi s&aacute;ng, trả ph&ograve;ng. Tham quan<strong><em>&nbsp;cửa khẩu Quốc tế L&agrave;o Cai</em></strong>, mua sắm tại chợ Cốc Lếu. Về đến Thủ đ&ocirc; tham quan<strong><em>&nbsp;Hồ Ho&agrave;n Kiếm</em></strong>, viếng<em><strong>&nbsp;đền Ngọc Sơn</strong></em>. Nhận ph&ograve;ng. Buổi t&ocirc;́i, tự do dạo bộ dọc theo&nbsp;<strong><em>&ldquo;36 phố phường</em></strong>&rdquo;- khu phố cổ với những ng&agrave;nh nghề đặc trưng v&agrave; truyền thống của cư d&acirc;n Thủ đ&ocirc;. Nghỉ đ&ecirc;m tại H&agrave; Nội.</p>\r\n<p><strong>NG&Agrave;Y 4: H&Agrave; NỘI - HẠ LONG &nbsp;(Ăn s&aacute;ng, trưa, chiều)</strong><br />Đến tham quan<em><strong>&nbsp;Quảng trường Ba Đ&igrave;nh, khu di t&iacute;ch Phủ Chủ tịch</strong></em>. Ngắm&nbsp;<em><strong>hồ T&acirc;y, hồ Tr&uacute;c Bạch</strong></em>, viếng<strong><em>&nbsp;ch&ugrave;a Trấn Quốc</em></strong>&nbsp;- danh lam cổ tự được x&acirc;y dựng từ thế kỷ VI. Khởi h&agrave;nh đến Hạ Long. Tham quan cầu B&atilde;i Ch&aacute;y. Buổi chiều tối tự do thăm th&uacute; phố biển Hạ Long. Nghỉ đ&ecirc;m tại Hạ Long.</p>\r\n<p><strong>Lựa chọn</strong>&nbsp;(tự t&uacute;c chi ph&iacute; di chuyển &amp; tham quan)<br />- Tham quan&nbsp;<em><strong>Khu Du Lịch Đảo Tuần Ch&acirc;u</strong></em>, xem biểu diễn c&aacute; heo - hải cẩu - sư tử biển, xiếc th&uacute;, biểu diễn vũ điệu nhạc nước v&agrave; &aacute;nh s&aacute;ng laser, game trong nh&agrave;, xe điện dụng, xe ngựa h&agrave;o hoa, chiếu phim 5D, triễn l&atilde;m hoa đăng, biễu diễn ca m&uacute;a nhạc.</p>\r\n<p>- Tham quan&nbsp;<em><strong>Quần thể Du lịch - Giải tr&iacute; Sun World Hạ Long Park</strong></em>, gồm khu c&ocirc;ng vi&ecirc;n vui chơi ven biển B&atilde;i Ch&aacute;y v&agrave; &nbsp;c&ocirc;ng vi&ecirc;n vui chơi tr&ecirc;n n&uacute;i Ba Đ&egrave;o - được kết nối với nhau bởi hệ thống c&aacute;p treo vượt biển Nữ Ho&agrave;ng đạt 2 kỷ lục thế giới (cabin c&oacute; sức chứa lớn nhất thế giới v&agrave; c&aacute;p treo c&oacute; trụ c&aacute;p cao nhất thế giới so với mặt đất). Trải nghiệm Thế giới tr&ograve; chơi mạo hiểm c&ugrave;ng t&agrave;u lượn si&ecirc;u tốc, c&ocirc;ng vi&ecirc;n nước, khu Vườn Nhật, khu tr&ograve; chơi trong nh&agrave; L&acirc;u Đ&agrave;i Huyền B&iacute;, khu trưng b&agrave;y tượng s&aacute;p, v&agrave; đặc biệt l&agrave; V&ograve;ng quay Mặt Trời - một trong những v&ograve;ng quay cao nhất thế giới.</p>\r\n<p><strong>NG&Agrave;Y 5: VỊNH HẠ LONG - BẮC NINH - H&Agrave; NỘI -&nbsp;TP.HCM&nbsp; (Ăn s&aacute;ng, trưa/ Ăn chiều tự t&uacute;c)</strong><br />Bu&ocirc;̉i sáng, l&ecirc;n thuyền du ngoạn<em><strong>&nbsp;vịnh Hạ Long&nbsp;</strong></em>chi&ecirc;m ngưỡng<em><strong>&nbsp;động Thi&ecirc;n Cung, c&aacute;c h&ograve;n Đỉnh Hương - Trống M&aacute;i (G&agrave; Chọi) - Ch&oacute; Đ&aacute;</strong></em>. Khởi h&agrave;nh về H&agrave; Nội. Gh&eacute; Hải Dương, mua đặc sản&nbsp;<strong><em>b&aacute;nh đậu xanh, b&aacute;nh gai</em></strong>. Qua Bắc Ninh, viếng<strong><em>&nbsp;đền Đ&ocirc;&nbsp;</em></strong>- nơi thờ tự 8 vị vua triều L&yacute;. Xe đưa ra s&acirc;n bay Nội B&agrave;i, về TP. Hồ Ch&iacute; Minh (<em><strong>chuyến bay VJ171 L&uacute;c 21:20</strong></em>). Kết th&uacute;c chương tr&igrave;nh (Qu&yacute; kh&aacute;ch tự t&uacute;c phương tiện từ s&acirc;n bay về nh&agrave;.</p>\r\n<div class=\"ddict_btn\" style=\"top: 9px; left: 628px;\"><img src=\"chrome-extension://bpggmmljdiliancllaapiggllnkbjocb/logo/48.png\" /></div>', '<p><strong>* V&eacute; trẻ em</strong><br />- Trẻ em dưới 2 tuổi: thu 300.000đ. Gia đình tự lo cho bé ăn ngủ<br />- Trẻ em từ 2 đến dưới 6 tuổi: mua 100% VMB người lớn. Gia đ&igrave;nh tự lo cho b&eacute; ăn ngủ v&agrave; tự trả ph&iacute; tham quan (nếu c&oacute;).<br />- Hai người lớn chỉ được k&egrave;m 1 trẻ em dưới 6 tu&ocirc;̉i. Từ trẻ thứ 2 trở l&ecirc;n, mỗi em phải đóng bằng gi&aacute; trẻ em từ 6 đến 11 tuổi<br />- Trẻ em từ 6 - 11 tuổi: ti&ecirc;u chuẩn gồm: VMB, ăn uống và tham quan theo chương tr&igrave;nh, ngủ chung giường với phụ huynh<br />- Trẻ em tr&ecirc;n 11 tu&ocirc;̉i: &aacute;p dụng gi&aacute; v&agrave; c&aacute;c ti&ecirc;u chuẩn dịch vụ như người lớn</p>\r\n<p><strong>* Gi&aacute; tour bao gồm:&nbsp;&nbsp;</strong><br />-&nbsp;Chi ph&iacute; xe m&aacute;y lạnh phục vụ theo chương tr&igrave;nh.<br />- V&eacute; m&aacute;y bay khứ hồi.<br />-&nbsp;Chi ph&iacute; kh&aacute;ch sạn theo ti&ecirc;u chuẩn 2 kh&aacute;ch/ph&ograve;ng<br />-&nbsp;Chi ph&iacute; ăn -&nbsp;uống theo chương tr&igrave;nh.<br />-&nbsp;Chi ph&iacute; tham quan v&agrave; t&agrave;u thuyền.<br />-&nbsp;Chi ph&iacute; Hướng dẫn vi&ecirc;n tiếng Việt.<br />-&nbsp;Qu&agrave; tặng: N&oacute;n, nước suối, khăn lạnh, viết.</p>\r\n<p><strong>* Gi&aacute; tour kh&ocirc;ng bao gồm:&nbsp;&nbsp;</strong><br />-&nbsp;Chi ph&iacute; tham quan - ăn uống ngo&agrave;i chương tr&igrave;nh, giặt ủi, điện thoại v&agrave; c&aacute;c chi ph&iacute; c&aacute; nh&acirc;n kh&aacute;c.<br />-&nbsp;V&eacute; c&aacute;p treo tham quan đỉnh Fansipan<br />- Lưu &yacute;: Hạn chế tham gia với những kh&aacute;ch c&oacute; tiền sử bệnh cao huyết &aacute;p, tim mạch, sợ độ cao; người cao tuổi; trẻ em<br />- Chi ph&iacute; phụ thu người nước ngo&agrave;i, phụ thu ph&ograve;ng đơn (nếu c&oacute;)</p>', '<p>- Tham quan Quốc Tử Gi&aacute;m - từng l&agrave; trung t&acirc;m thờ tự v&agrave; gi&aacute;o dục Nho học lớn nhất</p>\r\n<p>- Du ngoạn vịnh Hạ Long - một trong 7 kỳ quan thi&ecirc;n nhi&ecirc;n mới của thế giới</p>\r\n<p>- Ngắm to&agrave;n cảnh Sapa v&agrave; &ldquo;N&oacute;c nh&agrave; Đ&ocirc;ng Dương&rdquo; - đỉnh Fansipan tại s&acirc;n m&acirc;y</p>\r\n<p>- T&igrave;m hiểu đời sống d&acirc;n tộc thiểu số tại bản Lao Chải - Tả Van</p>\r\n<div class=\"ddict_btn\" style=\"top: 28px; left: 720.138px;\"><img src=\"chrome-extension://bpggmmljdiliancllaapiggllnkbjocb/logo/48.png\" /></div>', 'TP.Hồ Chí Minh', '<p>Điều khoản &amp; Quy định đang cập nhật.</p>', 0, 'Sầm Sơn', '0000-00-00'),
(39, 'Du lịch khám phá Cái Bè - Cần Thơ - Châu Đốc - Hà Tiên', 'baitamhonrom.jpg', 10579000, 0, 'Thứ 2 hàng tuần', '4 ngày 3 đêm', 'Máy bay.svg', 2, '<p><strong>Ng&agrave;y 01: TP. HỒ CH&Iacute; Minh - C&Aacute;I B&Egrave; - CẦN THƠ&nbsp; (Ăn s&aacute;ng, trưa, chiều)</strong><br />Đ&oacute;n kh&aacute;ch tại văn ph&ograve;ng Saigontourist. Đến C&aacute;i B&egrave;, tham quan&nbsp;<strong><em>chợ nổi C&aacute;i B&egrave;</em></strong>. T&igrave;m hiểu qui tr&igrave;nh l&ograve; cốm, kẹo dừa, nu&ocirc;i ong v&agrave; thưởng thức tr&agrave; mật ong. L&ecirc;n c&ugrave; lao An B&igrave;nh tham quan&nbsp;<strong><em>nh&agrave; xưa Nam Bộ</em></strong>, tham quan vườn ươm c&acirc;y giống c&aacute;c loại c&acirc;y ăn tr&aacute;i nhiệt đới. Nhận ph&ograve;ng v&agrave; nghỉ đ&ecirc;m tại Cần Thơ.</p>\r\n<p><img style=\"height: auto;\" src=\"https://bizweb.dktcdn.net/100/299/077/products/ha-tien-2919-1200x800.jpg?v=1529553448983\" data-thumb=\"original\" /></p>\r\n<p><strong>Ng&agrave;y 02 : CẦN THƠ - CH&Acirc;U ĐỐC (Ăn s&aacute;ng, trưa, chiều)</strong><br />Buổi s&aacute;ng, đi thuyền tham quan&nbsp;<em><strong>chợ nổi C&aacute;i Răng</strong></em>, viếng&nbsp;<strong><em>Thiền viện Tr&uacute;c L&acirc;m Phương Nam</em></strong>. Khởi h&agrave;nh điCh&acirc;u Đốc. Buổi chiều viếng&nbsp;<strong><em>Miếu B&agrave; Ch&uacute;a Xứ, T&acirc;y An cổ tự, Lăng Thoại Ngọc Hầu, Ch&ugrave;a Hang.</em></strong>&nbsp;Nghỉ đ&ecirc;m tại Ch&acirc;u Đốc.</p>\r\n<p><strong>Ng&agrave;y 03: CH&Acirc;U ĐỐC - H&Agrave; TI&Ecirc;N (Ăn s&aacute;ng, trưa, chiều)</strong><br />Khởi h&agrave;nh đi H&agrave; Ti&ecirc;n. Tr&ecirc;n đường tham quan&nbsp;<strong><em>rừng tr&agrave;m Tr&agrave; Sư</em></strong>, theo những con rạch xuy&ecirc;n qua Lung Sen v&agrave; khu Rừng Giống, l&ecirc;n th&aacute;p vọng cảnh cao 23m nh&igrave;n to&agrave;n cảnh rừng tr&agrave;m v&agrave;<strong><em>&nbsp;tượng Phật Di Lặc, ch&ugrave;a Vạn Linh</em></strong>&nbsp;tr&ecirc;n n&uacute;i Cấm bằng k&iacute;nh viễn vọng. Đi H&agrave; Ti&ecirc;n dọc k&ecirc;nh Vĩnh Tế. Viếng<strong><em>&nbsp;lăng Mạc Cửu</em></strong>; tham quan&nbsp;<em><strong>Thạch Động</strong></em>, b&atilde;i biển&nbsp;<strong><em>Mũi Nai.</em></strong>&nbsp; Nghỉ đ&ecirc;m tại H&agrave; Ti&ecirc;n.</p>\r\n<p><strong>Ng&agrave;y 04: H&Agrave; TI&Ecirc;N - TP. HỒ CH&Iacute; Minh &nbsp;(Ăn s&aacute;ng, trưa)</strong><br />Buổi s&aacute;ng khởi h&agrave;nh về TP. HCM. Đến TP.HCM, đưa qu&yacute; kh&aacute;ch về văn ph&ograve;ng Saigontourist. Kết th&uacute;c chương tr&igrave;nh.&nbsp;</p>\r\n<div class=\"ddict_btn\" style=\"top: 21px; left: 535.95px;\"><img src=\"chrome-extension://bpggmmljdiliancllaapiggllnkbjocb/logo/48.png\" /></div>', '<p><strong>* Gi&aacute; tour bao gồm:&nbsp;&nbsp;</strong><br />- Chi ph&iacute; xe m&aacute;y lạnh phục vụ theo chương tr&igrave;nh.<br />- Chi ph&iacute; ăn -&nbsp;uống theo chương tr&igrave;nh.<br />- Chi ph&iacute; kh&aacute;ch sạn ti&ecirc;u chuẩn 2 kh&aacute;ch/ph&ograve;ng.<br />- Chi ph&iacute; tham quan, t&agrave;u thuyền, hướng dẫn vi&ecirc;n tiếng Việt<br />- Qu&agrave; tặng: N&oacute;n, nước suối, khăn lạnh, viết&hellip;</p>\r\n<p><strong>* Gi&aacute; tour kh&ocirc;ng bao gồm:&nbsp;&nbsp;</strong><br />- Ăn uống ngo&agrave;i chương tr&igrave;nh, giặt ủi, điện thoại v&agrave; c&aacute;c chi ph&iacute; c&aacute; nh&acirc;n<br />- Chi ph&iacute; phụ thu người nước ngo&agrave;i, phụ thu ph&ograve;ng đơn (nếu c&oacute;)</p>\r\n<p><strong>TH&Ocirc;NG TIN HƯỚNG DẪN<br />* V&eacute; trẻ em:&nbsp;&nbsp;</strong><br />-&nbsp;V&eacute; tour: Trẻ em từ&nbsp; 6 đến 11 tuổi mua một nửa gi&aacute; v&eacute; người lớn, trẻ em tr&ecirc;n 11 tuổi mua v&eacute; như người lớn.<br />- Đối với trẻ em dưới 6 tuổi, gia đ&igrave;nh tự lo cho b&eacute; ăn ngủ v&agrave; tự trả ph&iacute; tham quan (nếu c&oacute;). Hai người lớn chỉ được k&egrave;m một trẻ em. Từ trẻ thứ 2 trở l&ecirc;n, mỗi em phải 50% gi&aacute; v&eacute; người lớn.<br />- Ti&ecirc;u chuẩn 50% gi&aacute; tour bao gồm: Suất ăn, ghế ngồi v&agrave; ngủ gh&eacute;p chung với gia đ&igrave;nh.</p>', '<p>- Tham quan nh&agrave; xưa Nam bộ hơn trăm năm tuổi, theo lối kiến tr&uacute;c Ph&aacute;p - Việt (t&ugrave;y theo thủy triều)</p>\r\n<p>- Tham quan chợ nổi C&aacute;i Răng - một chợ nổi lớn của v&ugrave;ng Đồng Bằng s&ocirc;ng Cửu Long</p>\r\n<p>- Viếng Lăng Thoại Ngọc Hầu - danh nh&acirc;n c&oacute; c&ocirc;ng khai mở đất An Giang</p>\r\n<p>- Viếng Ch&ugrave;a Hang - ng&ocirc;i ch&ugrave;a gắn liền với truyền thuyết 2 con rắn lớn biết nghe kinh Phật</p>\r\n<p>- Viếng lăng Mạc Cửu - người c&oacute; c&ocirc;ng khai trấn đất H&agrave; Ti&ecirc;n v&agrave;o thế kỷ 18</p>\r\n<div class=\"ddict_btn\" style=\"top: 24px; left: 724.05px;\"><img src=\"chrome-extension://bpggmmljdiliancllaapiggllnkbjocb/logo/48.png\" /></div>', 'TP.Hồ Chí Minh', '<p>Điều khoản &amp; Quy định đang cập nhật.</p>', 1, 'Bình Đà', '0000-00-00'),
(40, 'Du lịch Phú Quốc - Bãi Sao - Vinpearl Land', '5.jpg', 9230000, 0, 'Thứ 6 hàng tuần', '3 ngày 2 đêm', 'Máy bay.svg', 2, '<p><strong>NG&Agrave;Y 1: TP. HỒ CH&Iacute; MINH - PH&Uacute; QUỐC (Ăn s&aacute;ng, trưa / Ăn chiều tự t&uacute;c)</strong><br />Buổi s&aacute;ng, qu&yacute; kh&aacute;ch tập trung tại Ga đi trong nước, s&acirc;n bay T&acirc;n Sơn Nhất. Khởi h&agrave;nh đi Ph&uacute; Quốc (<em><strong>chuyến VJ321 l&uacute;c 06:55</strong></em>). Đến Ph&uacute; Quốc, đo&agrave;n khởi h&agrave;nh đi ph&iacute;a Đ&ocirc;ng đảo, tham quan điểm&nbsp;<strong><em>Trại ch&oacute; xo&aacute;y Ph&uacute; Quốc, Vườn ti&ecirc;u, L&ograve; chế biến rượu Sim rừng Ph&uacute; Quốc, Cơ sở nước mắm Ph&uacute; Quốc</em></strong>. Nhận ph&ograve;ng. Buổi chiều tối tự do dạo<strong><em>&nbsp;chợ đ&ecirc;m Dinh Cậu</em></strong>, tự t&uacute;c ăn chiều. Nghỉ đ&ecirc;m tại Ph&uacute; Quốc.</p>\r\n<p><strong>Lựa chọn&nbsp;</strong>(tự t&uacute;c chi ph&iacute; tham quan, di chuyển v&agrave; ăn chiều):<br />- Tham quan&nbsp;<em><strong>Khu vui chơi giải tr&iacute; Vinpearl Land&nbsp;Ph&uacute; Quốc&nbsp;</strong></em>với c&aacute;c tr&ograve; chơi trong nh&agrave;, tr&ograve; chơi ngo&agrave;i trời Đĩa quay si&ecirc;u tốc, Cối xay gi&oacute;, Đĩa bay&hellip;, L&acirc;u đ&agrave;i Cổ t&iacute;ch; Khu c&ocirc;ng vi&ecirc;n nước&nbsp;với c&aacute;c tr&ograve; chơi mạo hiểm: Boomerang khổng lồ, Đường trượt si&ecirc;u l&ograve;ng chảo&hellip;; Phim 5D với nhiều kỹ xảo v&agrave; hiệu ứng hiện đại; Khu Thủy Cung&nbsp;với h&agrave;ng trăm lo&agrave;i sinh vật biển qu&yacute; hiếm: chim C&aacute;nh cụt,&nbsp; c&aacute; Nemo, c&aacute; Napoleon, c&aacute; Mập trắng, cua King Crab&hellip;Thưởng thức chương tr&igrave;nh biểu diễn nhạc nước, biểu diễn c&aacute; heo, n&agrave;ng ti&ecirc;n c&aacute;,&hellip;</p>\r\n<p>- Kh&aacute;m ph&aacute;&nbsp;<strong><em>Khu Vinpearl Safari</em></strong>: kh&aacute;m ph&aacute; Vườn Th&uacute; Hoang D&atilde; đầu ti&ecirc;n tại Việt Nam với quy m&ocirc; 180ha, c&ugrave;ng hơn 130 lo&agrave;i động vật qu&yacute; hiếm v&agrave; c&aacute;c chương tr&igrave;nh Biểu diễn động vật, Chụp ảnh với động vật, Kh&aacute;m ph&aacute; v&agrave; trải nghiệm Vườn th&uacute; mở trong rừng tự nhi&ecirc;n, gần gũi v&agrave; th&acirc;n thiện với con người</p>\r\n<p>- Ng&acirc;m tắm b&ugrave;n kho&aacute;ng n&oacute;ng tại<strong><em>&nbsp;Galina Ph&uacute; Quốc</em></strong>&nbsp;- trung t&acirc;m b&ugrave;n kho&aacute;ng đầu ti&ecirc;n tại Ph&uacute; Quốc. thư gi&atilde;n dưới lớp b&ugrave;n kho&aacute;ng được khai th&aacute;c từ mỏ b&ugrave;n ở Ninh H&ograve;a (Kh&aacute;nh H&ograve;a), được xử l&yacute; qua c&ocirc;ng nghệ li t&acirc;m lọc chọn những lớp b&ugrave;n kho&aacute;ng tốt nhất mang tới cho Qu&yacute; kh&aacute;ch. Ngo&agrave;i ra c&ograve;n c&oacute; c&aacute;c dịch vụ chăm s&oacute;c sức khỏe như: massage, hệ thống &ocirc;n tuyền thủy liệu ph&aacute;p, bể jaccuzi</p>\r\n<p><img style=\"height: auto;\" src=\"https://bizweb.dktcdn.net/100/299/077/products/du-l-ch-c-n-o-5.jpg?v=1529553550823\" data-thumb=\"original\" /></p>\r\n<p><strong>NG&Agrave;Y 2: THAM QUAN PH&Uacute; QUỐC&nbsp;(Ăn s&aacute;ng, trưa, chiều)</strong><br />Đo&agrave;n tham quan<strong><em>&nbsp;trung t&acirc;m nu&ocirc;i cấy ngọc trai Ph&uacute; Quốc, di tich nh&agrave; t&ugrave; Ph&uacute; Quốc</em></strong>, viếng&nbsp;<strong><em>Thi&ecirc;n Viện Tr&uacute;c L&acirc;m Hộ Quốc</em></strong>&nbsp;ng&ocirc;i ch&ugrave;a đẹp v&agrave; lớn nhất đảo ngọc. Tắm&nbsp;<em><strong>biển B&atilde;i Sao</strong></em>&nbsp;(hoặc b&atilde;i &Ocirc;ng Lang t&ugrave;y điều kiện thời tiết) - một trong những b&atilde;i biển đẹp nh&acirc;́t tại Ph&uacute; Quốc. Buổi chiều đo&agrave;n viếng&nbsp;<strong><em>dinh Bà</em></strong>&nbsp;- Thủy Long Th&aacute;nh Mẫu (Thần Nước), chi&ecirc;m ngưỡng vẻ đẹp của thắng cảnh dinh Cậu. Trở về kh&aacute;ch sạn nghỉ ngơi. Tối sinh hoạt tự do</p>\r\n<p><strong>NG&Agrave;Y 3: PH&Uacute; QUỐC - TP. HỒ CH&Iacute; MINH (Ăn s&aacute;ng, trưa)</strong><br />Qu&yacute; kh&aacute;ch tự do tắm biển Ph&uacute; Quốc, trả ph&ograve;ng. Xe đưa qu&yacute; kh&aacute;ch ra s&acirc;n bay Ph&uacute; Quốc bay về Tp.Hồ Ch&iacute; Minh (<strong><em>chuyến VJ 326 l&uacute;c 16:50</em></strong>). Kết th&uacute;c chương tr&igrave;nh. (Qu&yacute; kh&aacute;ch tự t&uacute;c phương tiện từ s&acirc;n bay về lại nh&agrave;).&nbsp;</p>\r\n<p><strong>Ghi ch&uacute;:</strong><br />- Điểm tham quan c&oacute; thể sắp xếp lại thứ tư cho ph&ugrave; hợp m&agrave; vẫn bảo đảm đầy đủ nội dung của từng chương tr&igrave;nh<br />- Nếu t&igrave;nh h&igrave;nh thời tiết, đường s&aacute; kh&ocirc;ng thuận lợi, chương tr&igrave;nh tham quan B&agrave;i Sao - Trung t&acirc;m Nu&ocirc;i cấy Ngọc trai - Nh&agrave; t&ugrave; PQ - Thiền viện Tr&uacute;c L&acirc;m sẽ được thay thế bằng chương tr&igrave;nh tham quan B&atilde;i &Ocirc;ng Lang.<br />- Giờ bay c&oacute; thể bị thay đổi bởi h&atilde;ng h&agrave;ng kh&ocirc;ng Vietjet Air</p>\r\n<div class=\"ddict_btn\" style=\"top: 31px; left: 561.05px;\"><img src=\"chrome-extension://bpggmmljdiliancllaapiggllnkbjocb/logo/48.png\" /></div>', '<p><strong>V&eacute; tour:</strong><br />- Trẻ em từ 6 đến 11 tuổi mua một nửa gi&aacute; v&eacute; người lớn, trẻ em tr&ecirc;n 11 tuổi mua v&eacute; như người lớn.<br />- Đối với trẻ em dưới 6 tuổi, gia đ&igrave;nh tự lo cho b&eacute; ăn ngủ v&agrave; tự trả ph&iacute; tham quan (nếu c&oacute;). Hai người lớn chỉ được k&egrave;m một trẻ em. Từ trẻ thứ 2 trở l&ecirc;n, mỗi em phải mua 50% gi&aacute; v&eacute; người lớn.<br />- Ti&ecirc;u chuẩn 50% gi&aacute; v&eacute; bao gồm: Suất ăn, ghế ngồi v&agrave; ngủ gh&eacute;p chung với gia đ&igrave;nh<br />- V&eacute; m&aacute;y bay: Theo quy định của h&atilde;ng h&agrave;ng kh&ocirc;ng Vietjet Air<br />- Trẻ em dưới 2 tuổi: thu ph&iacute; 300.000đ/ trẻ (ngồi chung với phụ huynh)<br />- Trẻ em từ 2 tuổi trở l&ecirc;n: mua 100% gi&aacute; v&eacute; m&aacute;y bay người lớn</p>\r\n<p><strong>* Gi&aacute; tour bao gồm:&nbsp;&nbsp;</strong><br />- Chi ph&iacute; xe m&aacute;y lạnh phục vụ theo chương tr&igrave;nh.<br />- Chi ph&iacute; kh&aacute;ch sạn theo ti&ecirc;u chuẩn 2 kh&aacute;ch/ph&ograve;ng<br />- Chi ph&iacute; ăn- uống theo chương tr&igrave;nh.<br />- Chi ph&iacute; tham quan v&agrave; t&agrave;u thuyền.<br />- Chi ph&iacute; Hướng dẫn vi&ecirc;n tiếng Việt.<br />- Qu&agrave; tặng: N&oacute;n, t&uacute;i x&aacute;ch, nước suối, khăn lạnh, viết<br />- V&eacute; m&aacute;y bay khứ hồi</p>\r\n<p><strong>* Gi&aacute; tour kh&ocirc;ng bao gồm:</strong>&nbsp;&nbsp;<br />- Chi ph&iacute; tham quan - ăn uống ngo&agrave;i chương tr&igrave;nh, tắm biển, giặt ủi, điện thoại v&agrave; c&aacute;c chi ph&iacute; c&aacute; nh&acirc;n kh&aacute;c.<br />- V&eacute; tham quan Vinpearlland hoặc Vinpearl Safari, ng&acirc;m tắm b&ugrave;n kho&aacute;ng n&oacute;ng Galina</p>', '<p>- Tham quan di t&iacute;ch nh&agrave; t&ugrave; Ph&uacute; Quốc</p>\r\n<p>- Viếng Thiền Viện Tr&uacute;c L&acirc;m Hộ Quốc ng&ocirc;i ch&ugrave;a đẹp v&agrave; lớn nhất đảo ngọc</p>\r\n<p>- Tắm biển B&atilde;i Sao (hoặc b&atilde;i &Ocirc;ng Lang) - một trong những b&atilde;i biển đẹp nh&acirc;́t tại Ph&uacute; Quốc</p>\r\n<p>- Chi&ecirc;m ngưỡng vẻ đẹp của thắng cảnh dinh Cậu</p>\r\n<div class=\"ddict_btn\" style=\"top: 31px; left: 721.663px;\"><img src=\"chrome-extension://bpggmmljdiliancllaapiggllnkbjocb/logo/48.png\" /></div>', 'TP.Hồ Chí Minh', '<p>Điều khoản &amp; Quy định đang cập nhật.</p>', 20, 'Thanh Oai', '0000-00-00'),
(41, 'Du lịch Đà Lạt - Kdl Trang Trại Rau & Hoa - Đường Hầm Đất Sét', '16.jpg', 5000000, 1000000, 'Thứ 5 hàng tuần', '3 ngày 2 đêm', 'Máy bay.svg', 3, '<p><strong>NG&Agrave;Y 1: TP. HỒ CH&Iacute; MINH - Đ&Agrave; LẠT (Ăn trưa, chiều)</strong><br />Buổi s&aacute;ng, Qu&yacute; kh&aacute;ch tập trung tại Cổng D4 - Ga đi trong nước, s&acirc;n bay VP Saigontourist. Khởi h&agrave;nh đi Đ&agrave; Lạt (<strong><em>chuy&ecirc;́n bay VJ362 l&uacute;c 09:55</em></strong>). Đ&aacute;p xuống s&acirc;n bay Li&ecirc;n Khương, di chuyển v&agrave;o TP.Đ&agrave; Lạt. Đến Đ&agrave; Lạt qu&yacute; kh&aacute;ch nhận ph&ograve;ng. Buổi chiều viếng<strong><em>&nbsp;Thiền viện Tr&uacute;c L&acirc;m</em></strong>&nbsp;- nơi ngắm được to&agrave;n cảnh&nbsp;<strong><em>hồ Tuyền L&acirc;m</em></strong>;&nbsp;<strong><em>th&aacute;c Datanla</em></strong>&nbsp;(tự t&uacute;c chi ph&iacute; tham gia tr&ograve; chơi m&aacute;ng trượt). Nghỉ đ&ecirc;m tại Đ&agrave; Lạt.</p>\r\n<p><img style=\"height: auto;\" src=\"https://bizweb.dktcdn.net/100/299/077/files/da-lat.jpg?v=1529556404491\" data-thumb=\"original\" /></p>\r\n<p><strong>NG&Agrave;Y 2: THAM QUAN Đ&Agrave; LẠT (Ăn s&aacute;ng, trưa, chiều)</strong><br />Buổi s&aacute;ng, di chuyển đến với&nbsp;<strong><em>khu du lịch Langbian&nbsp;</em></strong>- tham quan<em><strong>&nbsp;đồi Mimosa, thung lũng Trăm Năm</strong></em>, chinh phục<em><strong>&nbsp;n&uacute;i Langbian</strong></em>&nbsp;(tự t&uacute;c ph&iacute; xe Jeep). Tiếp tục tham quan&nbsp;<strong><em>Ph&ograve;ng trưng b&agrave;y hoa sấy nghệ thuật v&agrave; Vườn hoa th&agrave;nh phố</em></strong>. Buổi chiều, xe đưa đo&agrave;n đến tham quan&nbsp;<em><strong>Khu Du Lịch Trang Trại Rau v&agrave; Hoa</strong></em>, trải rộng cả thung lũng với bốn bề l&agrave; rau v&agrave; hoa đẹp tuyệt vời. T&igrave;m hiểu về m&ocirc; h&igrave;nh sản xuất n&ocirc;ng nghiệp c&ocirc;ng nghệ cao cũng như c&aacute;c sản phẩm n&ocirc;ng nghiệp hiện đại như: Lavender, Nữ Ho&agrave;ng xanh, Ngọc Thảo,Hồng M&ocirc;n, B&iacute; khổng lồ, C&agrave; chua đen, Ớt chu&ocirc;ng, D&acirc;u Nhật &amp; New Zealand&hellip; tham quan&nbsp;<em><strong>Đường hầm đi&ecirc;u khắc đất đỏ&nbsp;</strong></em>(Đ&agrave; Lạt Star) - t&aacute;i hiện lịch sử Đ&agrave; Lạt qua hơn 120 năm. Buổi tối tự do dạo phố. Nghỉ đ&ecirc;m tại Đ&agrave; Lạt.</p>\r\n<p><strong>NG&Agrave;Y 3: Đ&Agrave; LẠT - TP.HCM&nbsp;(Ăn s&aacute;ng, trưa)</strong><br />Buổi s&aacute;ng, mua sắm đặc sản tại chợ Đ&agrave; Lạt, tham quan&nbsp;<em><strong>Quảng trường L&acirc;m Vi&ecirc;n&nbsp;</strong></em>- đầy ấn tượng với kh&ocirc;ng gian rộng lớn, tho&aacute;ng m&aacute;t hướng ra hồ Xu&acirc;n Hương c&ugrave;ng c&ocirc;ng tr&igrave;nh nghệ thuật khối b&ocirc;ng hoa d&atilde; quỳ v&agrave; khối nụ hoa Atiso khổng lồ được thiết kế bằng k&iacute;nh m&agrave;u rất đẹp mắt; tham quan&nbsp;<strong><em>nh&agrave; ga xe lửa Đ&agrave; Lạt&nbsp;</em></strong>- nh&agrave; ga cổ k&iacute;nh nhất Việt Nam v&agrave; Đ&ocirc;ng Dương. Trả ph&ograve;ng rồi di chuyển ra s&acirc;n bay Li&ecirc;n Khương. Về TP. HCM (<em><strong>chuy&ecirc;́n bay VJ363 l&uacute;c 16:35</strong></em>). Kết th&uacute;c chương tr&igrave;nh. (Qu&yacute; kh&aacute;ch tự t&uacute;c phương tiện từ s&acirc;n bay về lại nh&agrave;).</p>', '<p><strong>* V&eacute; trẻ em</strong><br />- Trẻ em dưới 2 tuổi: thu 300.000đ/ trẻ. Gia đ&igrave;nh tự lo cho b&eacute; ăn ngủ<br />- Trẻ em từ 2 đến dưới 6 tuổi: mua 100% VMB người lớn. Gia đ&igrave;nh tự lo cho b&eacute; ăn ngủ v&agrave; tự trả ph&iacute; tham quan (nếu c&oacute;).<br />- Hai người lớn chỉ được k&egrave;m một trẻ em dưới 6 tu&ocirc;̉i. Từ trẻ thứ 2 trở l&ecirc;n, mỗi em phải chịu mức giá tương đương trẻ em từ 6 đ&ecirc;́n 11 tu&ocirc;̉i<br />- Trẻ em từ 6 đ&ecirc;́n 11 tuổi: ti&ecirc;u chuẩn gồm VMB, ăn uống và tham quan theo chương tr&igrave;nh, ngủ chung giường với phụ huynh<br />- Trẻ em tr&ecirc;n 11 tu&ocirc;̉i: &aacute;p dụng gi&aacute; v&agrave; c&aacute;c ti&ecirc;u chuẩn dịch vụ như người lớn</p>\r\n<p><strong>* Gi&aacute; tour bao gồm:&nbsp;&nbsp;</strong><br />- Chi ph&iacute; xe m&aacute;y lạnh phục vụ theo chương tr&igrave;nh.<br />- V&eacute; m&aacute;y bay khứ hồi<br />- Chi ph&iacute; kh&aacute;ch sạn theo ti&ecirc;u chuẩn (2 kh&aacute;ch/ph&ograve;ng)<br />- Chi ph&iacute; ăn -&nbsp;uống theo chương tr&igrave;nh.<br />- Chi ph&iacute; tham quan v&agrave; t&agrave;u thuyền.<br />- Chi ph&iacute; Hướng dẫn vi&ecirc;n tiếng Việt.<br />- Qu&agrave; tặng: N&oacute;n, nước suối, khăn lạnh, viết.</p>\r\n<p><strong>* Gi&aacute; tour kh&ocirc;ng bao gồm:&nbsp;&nbsp;</strong><br />- Chi ph&iacute; tham quan - ăn uống ngo&agrave;i chương tr&igrave;nh, giặt ủi, điện thoại v&agrave; c&aacute;c chi ph&iacute; c&aacute; nh&acirc;n kh&aacute;c</p>', '<p>- Tham quan Quảng trường L&acirc;m Vi&ecirc;n - đầy ấn tượng với kh&ocirc;ng gian rộng lớn, tho&aacute;ng m&aacute;t hướng ra hồ Xu&acirc;n Hương c&ugrave;ng c&ocirc;ng tr&igrave;nh nghệ thuật khối b&ocirc;ng hoa d&atilde; quỳ v&agrave; khối nụ hoa Atiso khổng lồ được thiết kế bằng k&iacute;nh m&agrave;u rất đẹp mắt</p>\r\n<p>- Tham quan Khu Du Lịch Trang Trại Rau v&agrave; Hoa, trải rộng cả thung lũng với bốn bề l&agrave; rau v&agrave; hoa đẹp tuyệt vời</p>\r\n<p>- Tham quan Đường hầm đi&ecirc;u khắc đất đỏ (Đ&agrave; Lạt Star) - t&aacute;i hiện lịch sử Đ&agrave; Lạt qua hơn 120 năm</p>', 'Hà Nội', '<p>Điều khoản &amp; Quy định đang cập nhật.</p>', 5, '', '0000-00-00');
INSERT INTO `tours` (`id_tour`, `name_tour`, `image`, `price`, `sale_price`, `lich_khoi_hanh`, `thoi_gian`, `phuong_tien`, `id_loai_tour`, `chuong_trinh`, `chinh_sach`, `description`, `khoi_hanh_tu`, `dieu_khoan`, `luot_khach`, `tour_cu_the`, `ngay_khoi_hanh`) VALUES
(42, 'Du lịch Hồ Phú Ninh - Tam Thanh - Rừng Dừa Bảy Mẫu - Đà Nẵng - Hội An - Bà Nà - Huế', '7.jpg', 6000000, 0, 'Thứ 4 hàng tuần', '4 ngày 3 đêm', 'Ô tô.svg', 3, '<p><strong>NG&Agrave;Y 1: TP. HỒ CH&Iacute; MINH - Đ&Agrave; NẴNG - TAM KỲ - HỘI AN (Ăn trưa, chiều)</strong><br />Buổi s&aacute;ng, qu&yacute; kh&aacute;ch tập trung tại cổng D3 - Ga đi trong nước, s&acirc;n bay T&acirc;n Sơn Nhất. Khởi h&agrave;nh ra Đ&agrave; Nẵng (<em><strong>Chuyến bay VJ 624 l&uacute;c 08:30</strong></em>) Đến Đ&agrave; Nẵng, đo&agrave;n khởi h&agrave;nh đến Tam Kỳ, gh&eacute; thăm<em><strong>&nbsp;L&agrave;ng b&iacute;ch họa Tam Thanh&nbsp;</strong></em>- dưới b&agrave;n tay t&agrave;i hoa của đội ngũ t&igrave;nh nguyện vi&ecirc;n H&agrave;n Quốc, những bức tường cũ kĩ của hơn 100 ng&ocirc;i nh&agrave; trong l&agrave;ng ch&agrave;i đ&atilde; trở th&agrave;nh c&aacute;c t&aacute;c phẩm hội họa đặc sắc. Viếng&nbsp;<em><strong>tượng đ&agrave;i Mẹ Việt Nam anh h&ugrave;ng</strong></em>. Đo&agrave;n tiếp tục đến khu du lịch&nbsp;<strong><em>hồ Ph&uacute; Ninh</em></strong>,&nbsp;thư gi&atilde;n với c&aacute;c hoạt động ch&egrave;o kayak, lướt v&aacute;n, đi thuyền thăm l&ograve;ng hồ thủy lợi,... (chi ph&iacute; tự t&uacute;c). Buổi chiều, đo&agrave;n về Hội An, dạo bộ&nbsp;<strong><em>Phố cổ</em></strong>, tham quan c&aacute;c c&ocirc;ng tr&igrave;nh ti&ecirc;u biểu:&nbsp;<em><strong>Ch&ugrave;a Cầu Nhật Bản, ch&ugrave;a &Ocirc;ng, hội qu&aacute;n Ph&uacute;c Kiến, khu phố&nbsp;đ&egrave;n lồng</strong></em>. Nghỉ đ&ecirc;m tại Hội An.</p>\r\n<p><img style=\"height: auto;\" src=\"https://bizweb.dktcdn.net/100/299/077/files/aifvwk1.jpg?v=1529556271545\" data-thumb=\"original\" /></p>\r\n<p><strong>NG&Agrave;Y 2: HỘI AN - CẨM THANH - Đ&Agrave; NẴNG (Ăn s&aacute;ng, trưa, chiều)</strong><br />Xe đưa đo&agrave;n đến ngắm&nbsp;<em><strong>cầu Cửa Đại</strong></em>&nbsp;- d&agrave;i 1500m, bắc qua s&ocirc;ng Thu Bồn, vừa được kh&aacute;nh th&agrave;nh năm 2016. Đo&agrave;n xuống bến thuyền, đến&nbsp;<strong><em>rừng dừa Bảy Mẫu</em></strong>. Qu&yacute; kh&aacute;ch trải nghiệm cảm gi&aacute;c ngồi tr&ecirc;n chiếc thuyền th&uacute;ng tham quan rừng dừa nước, xem người d&acirc;n địa phương biểu diễn &ldquo;lắc th&uacute;ng, ch&eacute;m nước&rdquo;, tham gia c&aacute;c tr&ograve; chơi d&acirc;n gian,.... Trở về Đ&agrave; Nẵng. Buổi chiều, tham quan&nbsp;<em><strong>b&aacute;n đảo Sơn Tr&agrave;</strong></em>, ngắm<em><strong>&nbsp;cảng Ti&ecirc;n Sa</strong></em>, viếng&nbsp;<strong><em>ch&ugrave;a Linh Ứng B&atilde;i Bụt&nbsp;</em></strong>- ng&ocirc;i ch&ugrave;a lớn nhất ở th&agrave;nh phố Đ&agrave; Nẵng, chi&ecirc;m b&aacute;i<em><strong>&nbsp;tượng Phật Quan Thế &Acirc;m&nbsp;</strong></em>cao nhất Việt Nam. Buổi tối, qu&yacute; kh&aacute;ch tự do dạo phố, ngắm&nbsp;<em><strong>Cầu Rồng&nbsp;</strong></em>- một trong những con rồng th&eacute;p lớn nhất thế giới,&nbsp;<em><strong>Cầu Trần Thị L&yacute;</strong></em>&nbsp;- với kiến tr&uacute;c tựa con thuyền căng buồm vươn ra biển lớn, trải nghiệm&nbsp;<strong><em>&ldquo;V&ograve;ng quay Mặt trời -&nbsp;Sun Wheel&rdquo;&nbsp;</em></strong>thưởng ngoạn cảnh đẹp&nbsp;Đ&agrave; Nẵng&nbsp;với g&oacute;c nh&igrave;n từ độ cao 115m (tự t&uacute;c chi ph&iacute;). Nghỉ đ&ecirc;m tại Đ&agrave; Nẵng.</p>\r\n<p><strong>NG&Agrave;Y 3: Đ&Agrave; NẴNG - B&Agrave; N&Agrave; - HUẾ (Ăn s&aacute;ng, trưa, chiều)</strong><br />Buổi s&aacute;ng, qu&yacute; kh&aacute;ch khởi hành đến&nbsp;<em><strong>KDL B&agrave; N&agrave; Hills</strong></em>. L&ecirc;n B&agrave; N&agrave; bằng hệ thống c&aacute;p treo đạt 2 kỷ lục Guinness, ngắm to&agrave;n cảnh n&uacute;i non h&ugrave;ng vỹ v&agrave; tận hưởng kh&iacute; hậu trong l&agrave;nh. Viếng&nbsp;<em><strong>ch&ugrave;a Linh Ứng</strong></em>, kh&aacute;m ph&aacute;&nbsp;<strong><em>Fantasy Park</em></strong>&nbsp;- khu vui chơi giải tr&iacute; trong nh&agrave; đẳng cấp quốc tế (miễn ph&iacute; 105 tr&ograve; chơi) v&agrave; tr&ograve; chơi Hiệp sĩ Thần thoại (M&aacute;ng trượt). Dạo bộ giữa&nbsp;<strong><em>Khu l&agrave;ng Ph&aacute;p</em></strong>&nbsp;một kh&ocirc;ng gian kiến tr&uacute;c t&aacute;i hiện sinh động nước Ph&aacute;p thời cận đại đầy l&atilde;ng mạn, sang trọng v&agrave; lịch l&atilde;m. Trải nghiệm&nbsp;<em><strong>T&agrave;u hỏa leo n&uacute;i, tham quan 9 vườn hoa, Hầm rượu cổ Debay</strong></em>.Tự t&uacute;c chi ph&iacute; kh&aacute;m ph&aacute;<em><strong>&nbsp;Khu trưng b&agrave;y tượng s&aacute;p</strong></em>&nbsp;- duy nhất tại Việt Nam. Khởi h&agrave;nh ra Huế. Di chuyển qua đường h&acirc;̀m Hải V&acirc;n - h&acirc;̀m đường b&ocirc;̣ hi&ecirc;̣n đại nh&acirc;́t Đ&ocirc;ng Nam Á. Tr&ecirc;n đường, ngắm&nbsp;<em><strong>bi&ecirc;̉n Lăng C&ocirc;</strong></em>&nbsp;- một trong những vịnh biển đẹp nhất thế giới. Đến Huế, nhận ph&ograve;ng. Tự do kh&aacute;m ph&aacute; đất Cố đ&ocirc;. Nghỉ đ&ecirc;m tại Huế.</p>\r\n<p><strong>NG&Agrave;Y 4: HUẾ - TP. HỒ CH&Iacute; MINH (Ăn s&aacute;ng, trưa)</strong><br />Buổi s&aacute;ng, tham quan<strong><em>&nbsp;Di sản Văn h&oacute;a Thế giới Kinh Th&agrave;nh Huế&nbsp;</em></strong>- Ho&agrave;ng cung của 13 vị Vua triều Nguyễn với c&aacute;c c&ocirc;ng tr&igrave;nh ti&ecirc;u biểu:&nbsp;<em><strong>Ngọ M&ocirc;n, điện Th&aacute;i Ho&agrave;, Tử Cấm Th&agrave;nh, Thế Miếu, Hiển L&acirc;m C&aacute;c, Cửu Đỉnh, Bảo tàng C&ocirc;̉ v&acirc;̣t Cung đình Hu&ecirc;́</strong></em>. Viếng&nbsp;<strong><em>ch&ugrave;a Thi&ecirc;n Mụ</em></strong>&nbsp;- ng&ocirc;i ch&ugrave;a cổ v&agrave; nổi tiếng nhất ở đất Cố đ&ocirc;. Trả ph&ograve;ng. Buổi chiều&nbsp;đến&nbsp;viếng&nbsp;<em><strong>lăng vua Khải Định</strong></em>&nbsp;- chi&ecirc;m ngưỡng m&ocirc;̣t c&ocirc;ng trình k&ecirc;́t hợp hài hòa giữa ki&ecirc;́n trúc truy&ecirc;̀n th&ocirc;́ng Hu&ecirc;́ và hi&ecirc;̣n đại của T&acirc;y phương. Ra s&acirc;n bay Ph&uacute; B&agrave;i để về TP. HCM (Chuyến bay&nbsp;<strong><em>VJ 307 l&uacute;c 17:35</em></strong>). Kết th&uacute;c chương tr&igrave;nh. Qu&yacute; kh&aacute;ch tự t&uacute;c ăn chiều nếu chuyến bay về TP.HCM cất c&aacute;nh sau 19:30 v&agrave; tự t&uacute;c phương tiện từ s&acirc;n bay TSN về nh&agrave;.&nbsp;<br /><em><strong>Di sản Văn h&oacute;a Thế giới Kinh Th&agrave;nh Huế</strong></em></p>', '<p><strong>* V&eacute; trẻ em</strong><br />- Trẻ em dưới 2 tuổi: thu 300.000đ/ trẻ. &nbsp;Gia đ&igrave;nh tự lo cho b&eacute; ăn ngủ<br />- Trẻ em từ 2 đến dưới 6 tuổi: mua 100% VMB người lớn. Gia đ&igrave;nh tự lo cho b&eacute; ăn ngủ v&agrave; tự trả ph&iacute; tham quan (nếu c&oacute;).<br />- Hai người lớn chỉ được k&egrave;m một trẻ em dưới 6 tu&ocirc;̉i. Từ trẻ thứ 2 trở l&ecirc;n, mỗi em phải chịu mức giá tương đương trẻ em từ 6 đ&ecirc;́n 11 tu&ocirc;̉i<br />- Trẻ em từ 6 đ&ecirc;́n 11 tuổi: ti&ecirc;u chuẩn gồm VMB, ăn uống và tham quan theo chương tr&igrave;nh, ngủ chung giường với phụ huynh<br />- Trẻ em tr&ecirc;n 11 tu&ocirc;̉i: &aacute;p dụng gi&aacute; v&agrave; c&aacute;c ti&ecirc;u chuẩn dịch vụ như người lớn</p>\r\n<p><strong>* Gi&aacute; tour bao gồm:&nbsp;</strong>&nbsp;<br />- Chi ph&iacute; xe m&aacute;y lạnh phục vụ theo chương tr&igrave;nh.<br />- V&eacute; m&aacute;y bay khứ hồi<br />- Chi ph&iacute; kh&aacute;ch sạn theo ti&ecirc;u chuẩn 2 kh&aacute;ch/ph&ograve;ng<br />- Chi ph&iacute; ăn -&nbsp;uống theo chương tr&igrave;nh.<br />- Chi ph&iacute; tham quan .<br />- Chi ph&iacute; Hướng dẫn vi&ecirc;n tiếng Việt.<br />- Qu&agrave; tặng: N&oacute;n, nước suối, khăn lạnh, viết</p>\r\n<p><strong>* Gi&aacute; tour kh&ocirc;ng bao gồm:&nbsp;&nbsp;</strong><br />- Chi ph&iacute; tham quan - ăn uống ngo&agrave;i chương tr&igrave;nh, tắm biển (thu&ecirc; d&ugrave; ghế, tắm nước ngọt), giặt ủi, điện thoại v&agrave; c&aacute;c chi ph&iacute; c&aacute; nh&acirc;n kh&aacute;c<br />- Chi ph&iacute; tham gia c&aacute;c tr&ograve; chơi gắp th&uacute; b&ocirc;ng, tr&ograve; chơi kỹ năng carnival v&agrave; c&aacute;c tr&ograve; chơi c&oacute; thưởng tại khu Fantasy Park, tham quan Khu trưng b&agrave;y tượng s&aacute;p tại B&agrave; N&agrave; Hills; V&eacute; vui chơi tại c&ocirc;ng vi&ecirc;n Ch&acirc;u &Aacute;</p>', '<p>- Kh&aacute;m ph&aacute; Fantasy Park - khu vui chơi giải tr&iacute; trong nh&agrave; đẳng cấp quốc tế (miễn ph&iacute; 105 tr&ograve; chơi)</p>\r\n<p>- Trải nghiệm cảm gi&aacute;c ngồi tr&ecirc;n chiếc thuyền th&uacute;ng tham quan rừng dừa nước, một &ldquo;Nam Bộ thu nhỏ&rdquo; của xứ Quảng</p>\r\n<p>- Gh&eacute; thăm L&agrave;ng b&iacute;ch họa Tam Thanh - những bức tường cũ kĩ của hơn 100 ng&ocirc;i nh&agrave; trong l&agrave;ng ch&agrave;i đ&atilde; trở th&agrave;nh c&aacute;c t&aacute;c phẩm hội họa đặc sắc</p>', 'Đà Nẵng', '<p>Điều khoản &amp; Quy định đang cập nhật.</p>', 0, '', '0000-00-00'),
(43, 'Du lịch Phan Thiết - Mũi Né - Công Viên Tượng Cát', 'huu-percentage-20thanh-percentage-2033-percentage-20-3.jpg', 5130000, 0, 'Thứ 2 hàng tuần', '3 ngày 2 đêm', 'Ô tô.svg', 3, '<p><strong>Ng&agrave;y 01 : TP. HCM - PHAN THIẾT - C&Ocirc;NG VI&Ecirc;N TƯỢNG C&Aacute;T (Ăn s&aacute;ng, trưa, chiều)</strong><br />Đ&oacute;n kh&aacute;ch tại văn ph&ograve;ng Saigontourist. Đến Phan Thiết, v&agrave;o khu&nbsp;<em><strong>resort H&agrave;m Tiến - Mũi N&eacute;</strong></em>&nbsp;nhận ph&ograve;ng. Buổi chiều, tham quan&nbsp;<em><strong>Forgotten Land - C&ocirc;ng vi&ecirc;n Tượng c&aacute;t&nbsp;</strong></em>đầu ti&ecirc;n tại Việt Nam. Nghỉ đ&ecirc;m tại Mũi N&eacute;.</p>\r\n<p><img style=\"height: auto;\" src=\"https://bizweb.dktcdn.net/100/299/077/products/member-bio-img-4.jpg?v=1529555091687\" data-thumb=\"original\" /></p>\r\n<p><em><strong>Mũi N&eacute;</strong></em></p>\r\n<p><strong>Lựa chọn (Tự t&uacute;c chi ph&iacute; di chuyển v&agrave; tham quan)</strong><br />Buổi tối, qu&yacute; kh&aacute;ch c&oacute; thể đến xem<em><strong>&nbsp;nhạc kịch nước Huyền Thoại L&agrave;ng Ch&agrave;i&nbsp;</strong></em>- show giải tr&iacute; đặc sắc v&agrave; đậm n&eacute;t văn h&oacute;a l&agrave;ng ch&agrave;i của Phan Thiết - B&igrave;nh Thuận. Đ&acirc;y l&agrave; show kịch m&uacute;a được d&agrave;n dựng c&ocirc;ng phu tr&ecirc;n s&acirc;n khấu nhạc nước đầu ti&ecirc;n v&agrave; duy nhất tại Việt Nam, &aacute;p dụng c&ocirc;ng nghệ 2D v&agrave; 3D mới nhất hiện nay. Với thời lượng 1 tiếng c&ugrave;ng kịch bản được d&agrave;y c&ocirc;ng bi&ecirc;n soạn, s&aacute;ng t&aacute;c theo trường ph&aacute;i giả tưởng, dựa theo những truyền thuyết d&acirc;n gian v&agrave; huyền thoại về l&agrave;ng ch&agrave;i v&agrave; biển cả, show tr&igrave;nh diễn hứa hẹn mang lại nhiều cảm x&uacute;c đến kh&aacute;n giả với c&aacute;c cao tr&agrave;o v&agrave; t&igrave;nh tiết của c&acirc;u chuyện c&ugrave;ng những hiệu ứng đặc biệt từ s&acirc;n khấu nhạc nước đầy m&atilde;n nh&atilde;n.</p>\r\n<p><strong>Ng&agrave;y 02 : PHAN THIẾT- TP. HCM&nbsp;(Ăn s&aacute;ng, trưa)</strong><br />Buổi s&aacute;ng, đi v&agrave;o&nbsp;<em><strong>H&ograve;n Rơm&nbsp;</strong></em>tham quan&nbsp;<em><strong>đồi c&aacute;t v&agrave;ng</strong></em>. Về lại resort, tư do nghỉ dưỡng, tắm biển đến giờ trả ph&ograve;ng. Khởi h&agrave;nh về Tp. HCM. Tr&ecirc;n đường về gh&eacute;&nbsp;<em><strong>mua sắm đặc sản Phan Thiết</strong></em>. Kết th&uacute;c chương tr&igrave;nh.</p>', '<p><strong>* Gi&aacute; tour bao gồm:&nbsp;&nbsp;</strong><br />- Chi ph&iacute; xe m&aacute;y lạnh phục vụ theo chương tr&igrave;nh.<br />- Chi ph&iacute; ăn -&nbsp;uống theo chương tr&igrave;nh.<br />- Chi ph&iacute; kh&aacute;ch sạn ti&ecirc;u chuẩn 2 kh&aacute;ch/ph&ograve;ng.<br />- Chi ph&iacute; tham quan, hướng dẫn vi&ecirc;n tiếng Việt<br />- Qu&agrave; tặng: N&oacute;n, nước suối, khăn lạnh, viết&hellip;</p>\r\n<p><strong>* Gi&aacute; tour kh&ocirc;ng bao gồm:&nbsp;&nbsp;</strong><br />-&nbsp;Ăn uống ngo&agrave;i chương tr&igrave;nh, giặt ủi, điện thoại v&agrave; c&aacute;c chi ph&iacute; c&aacute; nh&acirc;n<br />-&nbsp;Chi ph&iacute; di chuyển v&agrave; xem Show nhạc kịch nước Huyền Thoại L&agrave;ng Ch&agrave;i<br />- Chi ph&iacute; phụ thu người nước ngo&agrave;i, phụ thu ph&ograve;ng đơn (nếu c&oacute;)</p>\r\n<p><strong>TH&Ocirc;NG TIN HƯỚNG DẪN<br />* V&eacute; trẻ em:&nbsp;&nbsp;</strong><br />- V&eacute; tour: Trẻ em từ&nbsp; 6 đến 11 tuổi mua một nửa gi&aacute; v&eacute; người lớn, trẻ em tr&ecirc;n 11 tuổi mua v&eacute; như người lớn.<br />- Đối với trẻ em dưới 6 tuổi, gia đ&igrave;nh tự lo cho b&eacute; ăn ngủ v&agrave; tự trả ph&iacute; tham quan (nếu c&oacute;). Hai người lớn chỉ được k&egrave;m một trẻ em. Từ trẻ thứ 2 trở l&ecirc;n, mỗi em phải 50% gi&aacute; v&eacute; người lớn.<br />- Ti&ecirc;u chuẩn 50% gi&aacute; tour bao gồm: Suất ăn, ghế ngồi v&agrave; ngủ gh&eacute;p chung với gia đ&igrave;nh.</p>', '<p>- Tham quan Forgotten Land - C&ocirc;ng vi&ecirc;n Tượng c&aacute;t với c&aacute;c t&aacute;c phẩm nghệ thuật v&ocirc; c&ugrave;ng sống động</p>\r\n<p>- Dạo chơi tr&ecirc;n bờ c&aacute;t trắng để gi&oacute; biển thổi tung những vướng bận đời thường</p>\r\n<p>- Tham quan đồi c&aacute;t v&agrave;ng - ngắm nh&igrave;n những đụn c&aacute;t v&agrave;ng rực rỡ v&agrave;o buổi s&aacute;ng sớm</p>', 'TP.Hồ Chí Minh', '<p>Điều khoản &amp; Quy định đang cập nhật.</p>', 11, 'Phan Thiết', '0000-00-00'),
(44, 'Du lịch Nha Trang - Hòn Lao - Dốc Lết', 'tour-diep-son-nha-trang.jpg', 9400000, 990000, 'Thứ 5 hàng tuần', '4 ngày 3 đêm', 'Máy bay.svg', 3, '<p><strong>Ng&agrave;y 01: TP. HỒ CH&Iacute; MINH - NHA TRANG &nbsp;(Ăn s&aacute;ng, trưa, chiều)</strong><br />Đ&oacute;n kh&aacute;ch tại văn ph&ograve;ng Saigontourist, khởi h&agrave;nh đi Nha Trang. Tr&ecirc;n đường đi, chi&ecirc;m ngưỡng vẻ đẹp của B&atilde;i biển c&aacute;t trắng C&agrave; N&aacute;, tham quan&nbsp;<em><strong>Khu tưởng niệm chiến sĩ Gạc Ma</strong></em>. du kh&aacute;ch nhận ph&ograve;ng. Nghỉ đ&ecirc;m tại Nha Trang.</p>\r\n<p><img style=\"height: auto;\" src=\"https://bizweb.dktcdn.net/100/299/077/products/ponagar-cham-temple-nha-trang-min1.jpg?v=1529554507043\" data-thumb=\"original\" /></p>\r\n<p><strong>Ng&agrave;y 02: NHA TRANG - H&Ograve;N LAO - I RESORT NHA TRANG &nbsp;(Ăn s&aacute;ng, trưa, chiều)</strong><br />Buổi s&aacute;ng, l&ecirc;n thuyền tới khu du lịch<em><strong>&nbsp;H&ograve;n Lao - đảo Khỉ</strong></em>, tắm biển, thư gi&atilde;n, xem xiếc khỉ, xiếc ch&oacute;, d&ecirc;, đua ch&oacute; hoặc thử c&aacute;c m&ocirc;n thể thao b&atilde;i biển: can&ocirc; k&eacute;o d&ugrave;, m&ocirc;t&ocirc; nước... (chi ph&iacute; tự t&uacute;c). Về lại đất liền, tham quan Trung t&acirc;m suối kho&aacute;ng n&oacute;ng Iresort Nha Trang. Du kh&aacute;ch tự do thư gi&atilde;n v&agrave; tắm kho&aacute;ng. Nghỉ đ&ecirc;m tại Nha Trang.</p>\r\n<p><strong>Ng&agrave;y 03: NHA TRANG &ndash; DỐC LẾT - C&Aacute;C THẮNG CẢNH (Ăn s&aacute;ng, trưa)</strong><br />Khởi h&agrave;nh đi Dốc Lết. Tự do tham quan v&agrave; tắm biển. Chiều kh&aacute;ch đi tham quan c&aacute;c thắng cảnh nổi tiếng&nbsp;<strong><em>h&ograve;n Chồng</em></strong>, ngắm cảnh&nbsp;<em><strong>n&uacute;i C&ocirc; Ti&ecirc;n</strong></em>. Viếng&nbsp;<strong><em>th&aacute;p B&agrave; Ponagar</em></strong>, tham quan v&agrave; mua sắm tại&nbsp;<strong><em>chợ Đầm</em></strong>. Tự t&uacute;c ăn tối. Nghỉ đ&ecirc;m tại Nha Trang.</p>\r\n<p><strong>Lựa chọn (tự t&uacute;c chi ph&iacute; di chuyển v&agrave; tham quan):</strong>&nbsp;Qu&yacute; kh&aacute;ch c&oacute; thể tham quan Khu vui chơi giải tr&iacute; Vinpearl Land hệ thống c&aacute;p treo vượt biển d&agrave;i nhất thế giới; Thủy cung lớn v&agrave; hiện đại nhất Việt Nam; Khu tr&ograve; chơi trong nh&agrave; hoặc chinh phục thử th&aacute;ch cao độ từ h&agrave;ng chục tr&ograve; chơi cảm gi&aacute;c mạnh tại Khu tr&ograve; chơi ngo&agrave;i trời v&agrave; C&ocirc;ng vi&ecirc;n nước ngọt tr&ecirc;n b&atilde;i biển đầu ti&ecirc;n &amp; duy nhất tại Việt Nam; ph&ograve;ng chiếu phim 4D, chương tr&igrave;nh biểu diễn nhạc nước</p>\r\n<p><strong>Ng&agrave;y 04: NHA TRANG -&nbsp;TP. HỒ CH&Iacute;&nbsp; MINH (Ăn s&aacute;ng, trưa)</strong><br />Trả ph&ograve;ng, khởi h&agrave;nh về th&agrave;nh phố. Tr&ecirc;n đường dừng ch&acirc;n tại Phan Rang nghỉ giải lao v&agrave; mua đặc sản Ninh Thuận. Đến Tp. Hồ Ch&iacute; Minh, kết th&uacute;c chuyến tham quan.</p>', '<p><strong>* Gi&aacute; tour bao gồm:&nbsp;&nbsp;</strong><br />- Chi ph&iacute; xe m&aacute;y lạnh phục vụ theo chương tr&igrave;nh.<br />- Chi ph&iacute; ăn &ndash; uống theo chương tr&igrave;nh.<br />- Chi ph&iacute; kh&aacute;ch sạn ti&ecirc;u chuẩn 2 kh&aacute;ch/ph&ograve;ng.<br />- Chi ph&iacute; tham quan, hướng dẫn vi&ecirc;n tiếng Việt<br />- Qu&agrave; tặng: N&oacute;n, nước suối, khăn lạnh, viết&hellip;</p>\r\n<p><strong>* Gi&aacute; tour kh&ocirc;ng bao gồm:&nbsp;&nbsp;</strong><br />- Ăn uống ngo&agrave;i chương tr&igrave;nh, giặt ủi, điện thoại v&agrave; c&aacute;c chi ph&iacute; c&aacute; nh&acirc;n<br />- Gi&aacute; v&eacute; Vinpeal Land.<br />- Chi ph&iacute; phụ thu người nước ngo&agrave;i, phụ thu ph&ograve;ng đơn (nếu c&oacute;)</p>\r\n<p><strong>TH&Ocirc;NG TIN HƯỚNG DẪN<br />* V&eacute; trẻ em:&nbsp;&nbsp;</strong><br />- V&eacute; tour: Trẻ em từ&nbsp; 6 đến 11 tuổi mua một nửa gi&aacute; v&eacute; người lớn, trẻ em tr&ecirc;n 11 tuổi mua v&eacute; như người lớn.<br />- Đối với trẻ em dưới 6 tuổi, gia đ&igrave;nh tự lo cho b&eacute; ăn ngủ v&agrave; tự trả ph&iacute; tham quan (nếu c&oacute;). Hai người lớn chỉ được k&egrave;m một trẻ em. Từ trẻ thứ 2 trở l&ecirc;n, mỗi em phải 50% gi&aacute; v&eacute; người lớn.<br />- Ti&ecirc;u chuẩn 50% gi&aacute; tour bao gồm: Suất ăn, ghế ngồi v&agrave; ngủ gh&eacute;p chung với gia đ&igrave;nh.</p>', '<p>- Chi&ecirc;m ngưỡng B&atilde;i biển c&aacute;t trắng C&agrave; N&aacute; - một trong những b&atilde;i biển đẹp nổi tiếng của miền Trung</p>\r\n<p>- Tới khu du lịch H&ograve;n Lao - đảo Khỉ, thư gi&atilde;n, xem xiếc hoặc thử c&aacute;c m&ocirc;n thể thao b&atilde;i biển</p>\r\n<p>- Tắm biển Dốc Lết - Một trong những b&atilde;i biển &ecirc;m, đẹp, nổi tiếng của tỉnh Kh&aacute;nh H&ograve;a</p>', 'TP.Hồ Chí Minh', '<p>Điều khoản &amp; Quy định đang cập nhật.</p>', 0, '', '0000-00-00'),
(45, 'Du lịch Sầm Sơn', '4.jpg', 7500000, 1100000, 'Thứ 3 hàng tuần', '5 ngày 4 đêm', 'Máy bay.svg', 1, '<p><strong>NG&Agrave;Y 1: TP. HCM - H&Agrave; NỘI - NINH B&Igrave;NH&nbsp;(Ăn trưa, chiều)</strong><br />Buổi s&aacute;ng, qu&yacute; kh&aacute;ch tập trung tại Cổng D4 - Ga đi trong nước - S&acirc;n bay T&acirc;n Sơn Nhất. Khởi h&agrave;nh ra H&agrave; Nội (chuyến bay<strong><em>&nbsp;VJ 126 l&uacute;c 07:10 hoặc VJ 128 l&uacute;c 08:15 t&ugrave;y ng&agrave;y</em></strong>). Đ&aacute;p xuống s&acirc;n bay Nội B&agrave;i, khởi h&agrave;nh đến Ninh B&igrave;nh, thăm Viếng&nbsp;<strong><em>ch&ugrave;a B&aacute;i Đ&iacute;nh&nbsp;</em></strong>- ng&ocirc;i ch&ugrave;a của những kỷ lục như: Khu ch&ugrave;a rộng nhất Việt Nam, thờ Tượng Phật bằng đồng lớn nhất Đ&ocirc;ng Nam &Aacute; v.v&hellip;. Tham quan&nbsp;<strong><em>KDL Tr&agrave;ng An&nbsp;</em></strong>-&nbsp;KDL nằm trong quần thể danh thắng Tr&agrave;ng An đ&atilde; được UNESCO c&ocirc;ng nhận di sản hỗn hợp đầu ti&ecirc;n của Việt Nam v&agrave; khu vực Đ&ocirc;ng Nam &Aacute; (đạt cả hai ti&ecirc;u ch&iacute; về văn h&oacute;a v&agrave; thi&ecirc;n nhi&ecirc;n) v&agrave;o ng&agrave;y 25/6/2014. Đặc biệt, kh&aacute;m ph&aacute; bối cảnh &ldquo;L&agrave;ng thổ d&acirc;n&rdquo; kỳ b&iacute; trong phim bom tấn Kong: Đảo Đầu L&acirc;u được phục dựng nguy&ecirc;n mẫu, với những t&uacute;p lều nứa ch&oacute;p nhọn, những thửa ruộng c&ugrave;ng nhiều dụng cụ sinh hoạt bằng đất, tre, nứa, v&agrave; c&aacute;c thổ d&acirc;n l&agrave; c&aacute;c diễn vi&ecirc;n từng tham gia đ&oacute;ng phim. Nghỉ đ&ecirc;m tại Ninh B&igrave;nh.</p>\r\n<p><img style=\"height: auto;\" src=\"https://bizweb.dktcdn.net/100/299/077/products/jed1367636408.jpg?v=1529554705153\" data-thumb=\"original\" /></p>\r\n<p><strong>Ng&agrave;y 02: NINH B&Igrave;NH - HẠ LONG - C&Aacute;T B&Agrave; (Ăn s&aacute;ng, trưa,chiều)</strong><br />Khởi h&agrave;nh đi Nam Định, viếng&nbsp;<em><strong>ch&ugrave;a Th&aacute;p Phổ Minh</strong></em>. Tiếp tục qua Th&aacute;i B&igrave;nh, Hải Ph&ograve;ng đến Hạ Long. Đo&agrave;n l&ecirc;n ph&agrave; Tuấn Ch&acirc;u, khởi h&agrave;nh đi C&aacute;t B&agrave;. Từ tr&ecirc;n ph&agrave;, qu&yacute; kh&aacute;ch chi&ecirc;m ngưỡng cảnh quan<em><strong>&nbsp;vịnh Hạ Long</strong></em>&nbsp;-&nbsp; một trong 7 kỳ quan thi&ecirc;n nhi&ecirc;n mới của thế giới. Ph&agrave; cập bến Gia Luận, xe đưa đo&agrave;n đi tr&ecirc;n con đường xuy&ecirc;n&nbsp;<em><strong>vườn quốc gia C&aacute;t B&agrave;</strong></em>&nbsp;v&agrave;o thị trấn. Nghỉ đ&ecirc;m tại C&aacute;t B&agrave;.&nbsp;&nbsp;</p>\r\n<p><strong>Ng&agrave;y 03: C&Aacute;T B&Agrave; - HẠ LONG (Ăn s&aacute;ng, trưa, chiều)</strong><br />Buổi s&aacute;ng, tự do nghỉ ngơi đo&agrave;n tự do nghỉ ngơi hoặc đi theo con đường nhỏ men theo v&aacute;ch đ&aacute; tham quan c&aacute;c<em><strong>&nbsp;b&atilde;i tắm C&aacute;t C&ograve;</strong></em>&nbsp;1, 2, 3,... hoặc đến ngắm to&agrave;n cảnh C&aacute;t B&agrave; từ ph&aacute;o đ&agrave;i Thần C&ocirc;ng (tự t&uacute;c chi ph&iacute;). Buổi chiều, đo&agrave;n trở về Hạ Long. Qu&yacute; kh&aacute;ch tự do thăm th&uacute; phố biển Hạ Long. Nghỉ đ&ecirc;m tại Hạ Long.<br />Lựa chọn (tự t&uacute;c chi ph&iacute; di chuyển &amp; tham quan)<br />- Tham quan&nbsp;<em><strong>Khu Du Lịch Đảo Tuần Ch&acirc;u</strong></em>, xem biểu diễn c&aacute; heo - hải cẩu - sư tử biển, xiếc th&uacute;, biểu diễn nhạc nước v&agrave; &aacute;nh s&aacute;ng laser, game trong nh&agrave;, xe điện dụng, chiếu phim 5D, triễn l&atilde;m hoa đăng, biễu diễn ca m&uacute;a nhạc.&nbsp;<br />- Tham quan&nbsp;<em><strong>Quần thể Du lịch - Giải tr&iacute; Sun World Hạ Long Park</strong></em>, gồm 2 khu c&ocirc;ng vi&ecirc;n vui chơi ven biển B&atilde;i Ch&aacute;y v&agrave;&nbsp; tr&ecirc;n n&uacute;i Ba Đ&egrave;o - được kết nối với nhau bởi hệ thống c&aacute;p treo vượt biển Nữ Ho&agrave;ng đạt 2 kỷ lục thế giới (cabin c&oacute; sức chứa lớn nhất thế giới v&agrave; c&aacute;p treo c&oacute; trụ c&aacute;p cao nhất thế giới so với mặt đất). Trải nghiệm c&aacute;c tr&ograve; chơi mạo hiểm, t&agrave;u lượn si&ecirc;u tốc, c&ocirc;ng vi&ecirc;n nước, khu Vườn Nhật, khu tr&ograve; chơi trong nh&agrave; L&acirc;u Đ&agrave;i Huyền B&iacute;, khu trưng b&agrave;y tượng s&aacute;p, v&agrave; đặc biệt l&agrave; V&ograve;ng quay Mặt Trời - một trong những v&ograve;ng quay cao nhất thế giới.</p>\r\n<p><strong>NG&Agrave;Y 4: HẠ LONG - HẢI PH&Ograve;NG - TP. HCM (Ăn s&aacute;ng, trưa)</strong><br />Buổi s&aacute;ng, xe đưa đo&agrave;n ngắm<em><strong>&nbsp;cầu B&atilde;i Ch&aacute;y</strong></em>, mua sắm tại chợ Hạ Long. Trả ph&ograve;ng. Tham quan&nbsp;<strong><em>bảo t&agrave;ng Quảng Ninh</em></strong>. Khởi h&agrave;nh về Hải Ph&ograve;ng, tham quan cụm<em><strong>&nbsp;di t&iacute;ch Bạch Đằng Giang&nbsp;</strong></em>(TT.Minh Đức , H.Thủy Nguy&ecirc;n) với đền thờ, tượng đ&agrave;i L&ecirc; Đại H&agrave;nh, Ng&ocirc; Quyền, Trần Hưng Đạo v&agrave; khu vực t&aacute;i hiện b&atilde;i cọc tr&ecirc;n s&ocirc;ng Bạch Đằng.&nbsp; Xe đưa đo&agrave;n ra s&acirc;n bay C&aacute;t Bi, tự t&uacute;c ăn chiều. Về TP. Hồ Ch&iacute; Minh (<strong><em>chuyến bay VJ283 l&uacute;c 19:50</em></strong>). Kết th&uacute;c chương tr&igrave;nh (Qu&yacute; kh&aacute;ch tự t&uacute;c phương tiện từ s&acirc;n bay về nh&agrave;).</p>\r\n<div class=\"ddict_btn\" style=\"top: 25px; left: 499.675px;\"><img src=\"chrome-extension://bpggmmljdiliancllaapiggllnkbjocb/logo/48.png\" /></div>', '<p><strong>V&eacute; trẻ em</strong><br />- Trẻ em dưới 2 tuổi: thu 300.000đ. Gia đình tự lo cho bé ăn ngủ<br />- Trẻ em từ 2 đến dưới 6 tuổi: mua 100% VMB người lớn. Gia đ&igrave;nh tự lo cho b&eacute; ăn ngủ v&agrave; tự trả ph&iacute; tham quan (nếu c&oacute;).<br />- Hai người lớn chỉ được k&egrave;m 1 trẻ em dưới 6 tu&ocirc;̉i. Từ trẻ thứ 2 trở l&ecirc;n, mỗi em phải đóng bằng gi&aacute; trẻ em từ 6 đến 11 tuổi<br />- Trẻ em từ 6 - 11 tuổi: ti&ecirc;u chuẩn gồm: VMB, ăn uống và tham quan theo chương tr&igrave;nh, ngủ chung giường với phụ huynh<br />- Trẻ em tr&ecirc;n 11 tu&ocirc;̉i: &aacute;p dụng gi&aacute; v&agrave; c&aacute;c ti&ecirc;u chuẩn dịch vụ như người lớn</p>\r\n<p><strong>* Gi&aacute; tour bao gồm: &nbsp;</strong><br />- Chi ph&iacute; xe m&aacute;y lạnh phục vụ theo chương tr&igrave;nh.<br />- V&eacute; m&aacute;y bay khứ hồi.<br />- Chi ph&iacute; kh&aacute;ch sạn theo ti&ecirc;u chuẩn 2 kh&aacute;ch/ph&ograve;ng<br />- Chi ph&iacute; ăn -&nbsp;uống theo chương tr&igrave;nh.<br />- Chi ph&iacute; tham quan v&agrave; t&agrave;u thuyền.<br />- Chi ph&iacute; Hướng dẫn vi&ecirc;n tiếng Việt.<br />- Qu&agrave; tặng: N&oacute;n, nước suối, khăn lạnh, viết.</p>\r\n<p><strong>* Gi&aacute; tour kh&ocirc;ng bao gồm:&nbsp;&nbsp;</strong><br />-&nbsp;Chi ph&iacute; tham quan - ăn uống ngo&agrave;i chương tr&igrave;nh, giặt ủi, điện thoại v&agrave; c&aacute;c chi ph&iacute; c&aacute; nh&acirc;n kh&aacute;c.<br />- Chi ph&iacute; phụ thu người nước ngo&agrave;i, phụ thu ph&ograve;ng đơn (nếu c&oacute;)</p>', '<p>- T&igrave;m hiểu đời sống của d&acirc;n tộc thiểu số tại bản Lao Chải - Tả Van</p>\r\n<p>- Viếng ch&ugrave;a B&aacute;i Đ&iacute;nh - ng&ocirc;i ch&ugrave;a của những kỷ lục</p>\r\n<p>- Tham quan KDL Tr&agrave;ng An - với hệ thống s&ocirc;ng, suối, c&aacute;c hang xuy&ecirc;n thủy động v&agrave; c&aacute;c d&atilde;y n&uacute;i đ&aacute; v&ocirc;i tr&ugrave;ng điệp</p>\r\n<p>- Viếng ch&ugrave;a Trấn Quốc - danh lam cổ tự được x&acirc;y dựng từ thế kỷ VI</p>\r\n<p>- Du ngoạn vịnh Hạ Long - một trong 7 kỳ quan thi&ecirc;n nhi&ecirc;n mới của thế giới</p>\r\n<div class=\"ddict_btn\" style=\"top: 13px; left: 725.25px;\"><img src=\"chrome-extension://bpggmmljdiliancllaapiggllnkbjocb/logo/48.png\" /></div>', 'Đà Nẵng', '<p>Điều khoản &amp; Quy định đang cập nhật.</p>', 1102, 'Hạ Long', '0000-00-00');

